{
  "name": "The Mystic Dial: Thai Secret Resolver",
  "description": "A mystical 3-layered concentric puzzle game where users align rotating rings to unlock secret codes and ancient wisdom powered by Gemini AI."
}

import React from 'react';
import ReactDOM from 'react-dom/client';
import App from './App';

const rootElement = document.getElementById('root');
if (!rootElement) {
  throw new Error("Could not find root element to mount to");
}

const root = ReactDOM.createRoot(rootElement);
root.render(
  <React.StrictMode>
    <App />
  </React.StrictMode>
);

export interface RingState {
  rotation: number;
  segments: number;
  symbols: string[];
}

export interface GameState {
  inner: RingState;
  middle: RingState;
  outer: RingState;
  isSolved: boolean;
  secretMessage: string;
  hint: string;
}

export enum RingLayer {
  INNER = 'inner',
  MIDDLE = 'middle',
  OUTER = 'outer'
}

import { GoogleGenAI, Type } from "@google/genai";

const ai = new GoogleGenAI({ apiKey: process.env.API_KEY });

export async function generateSecretChallenge() {
  const response = await ai.models.generateContent({
    model: "gemini-3-flash-preview",
    contents: "สร้างรหัสลับหรือคำคมสั้นๆ สำหรับคนไทย (Thai language) พร้อมคำใบ้สั้นๆ 1 บรรทัด เพื่อใช้เป็นรางวัลในเกมปลดล็อคกุญแจ",
    config: {
      responseMimeType: "application/json",
      responseSchema: {
        type: Type.OBJECT,
        properties: {
          secret: {
            type: Type.STRING,
            description: "The secret message or code in Thai.",
          },
          hint: {
            type: Type.STRING,
            description: "A small hint in Thai related to the secret.",
          },
        },
        required: ["secret", "hint"],
      },
    },
  });

  try {
    return JSON.parse(response.text);
  } catch (e) {
    return {
      secret: "ความสำเร็จอยู่ไม่ไกล",
      hint: "สิ่งที่ทุกคนต้องการในการทำงาน"
    };
  }
}

import React from 'react';

interface DialRingProps {
  radius: number;
  thickness: number;
  rotation: number;
  segments: number;
  symbols: string[];
  color: string;
  glowColor: string;
  isActive: boolean;
  onRotate: () => void;
}

const DialRing: React.FC<DialRingProps> = ({
  radius,
  thickness,
  rotation,
  segments,
  symbols,
  color,
  glowColor,
  isActive,
  onRotate
}) => {
  const anglePerSegment = 360 / segments;

  return (
    <g 
      className="cursor-pointer transition-transform duration-500 ease-out"
      style={{ transform: `rotate(${rotation}deg)`, transformOrigin: 'center' }}
      onClick={onRotate}
    >
      {/* Background Circle */}
      <circle
        cx="250"
        cy="250"
        r={radius}
        fill="transparent"
        stroke={color}
        strokeWidth={thickness}
        className={`transition-all duration-300 ${isActive ? 'opacity-90' : 'opacity-40'}`}
        style={{ filter: isActive ? `drop-shadow(0 0 8px ${glowColor})` : 'none' }}
      />

      {/* Symbols and Ticks */}
      {symbols.map((symbol, i) => {
        const angle = i * anglePerSegment;
        const radian = (angle * Math.PI) / 180;
        const textRadius = radius;
        const x = 250 + textRadius * Math.sin(radian);
        const y = 250 - textRadius * Math.cos(radian);

        return (
          <g key={i}>
            <line
              x1={250 + (radius - thickness / 2) * Math.sin(radian)}
              y1={250 - (radius - thickness / 2) * Math.cos(radian)}
              x2={250 + (radius + thickness / 2) * Math.sin(radian)}
              y2={250 - (radius + thickness / 2) * Math.cos(radian)}
              stroke={glowColor}
              strokeWidth="2"
              opacity="0.5"
            />
            <text
              x={x}
              y={y}
              fill="white"
              fontSize="20"
              fontWeight="bold"
              textAnchor="middle"
              alignmentBaseline="middle"
              className="select-none pointer-events-none"
              style={{ transform: `rotate(${angle}deg)`, transformOrigin: `${x}px ${y}px` }}
            >
              {symbol}
            </text>
          </g>
        );
      })}
    </g>
  );
};

export default DialRing;

import React, { useState, useEffect, useCallback } from 'react';
import { RefreshCw, Lock, Unlock, Zap, HelpCircle } from 'lucide-react';
import DialRing from './components/DialRing';
import { generateSecretChallenge } from './services/geminiService';
import { RingLayer } from './types';

const SYMBOLS_POOL = ["✧", "❂", "☸", "♾", "⚛", "⌬", "⌘", "❖"];

const App: React.FC = () => {
  const [loading, setLoading] = useState(false);
  const [solved, setSolved] = useState(false);
  const [secret, setSecret] = useState({ secret: "", hint: "" });
  const [showHint, setShowHint] = useState(false);

  // Rotation states in degrees
  const [rotations, setRotations] = useState({
    [RingLayer.OUTER]: Math.floor(Math.random() * 8) * 45,
    [RingLayer.MIDDLE]: Math.floor(Math.random() * 8) * 45,
    [RingLayer.INNER]: Math.floor(Math.random() * 8) * 45,
  });

  // Target alignment is all 0 degrees (or multiples of 360)
  const checkSolved = useCallback((newRotations: any) => {
    const isAligned = 
      newRotations[RingLayer.OUTER] % 360 === 0 &&
      newRotations[RingLayer.MIDDLE] % 360 === 0 &&
      newRotations[RingLayer.INNER] % 360 === 0;

    if (isAligned) {
      setSolved(true);
    }
  }, []);

  const handleRotate = (layer: RingLayer) => {
    if (solved) return;
    setRotations(prev => {
      const next = { ...prev, [layer]: prev[layer] + 45 };
      checkSolved(next);
      return next;
    });
  };

  const initGame = async () => {
    setLoading(true);
    setSolved(false);
    setShowHint(false);
    const data = await generateSecretChallenge();
    setSecret(data);

    // Randomize initial rotations ensuring they aren't all zero
    setRotations({
      [RingLayer.OUTER]: (Math.floor(Math.random() * 7) + 1) * 45,
      [RingLayer.MIDDLE]: (Math.floor(Math.random() * 7) + 1) * 45,
      [RingLayer.INNER]: (Math.floor(Math.random() * 7) + 1) * 45,
    });
    setLoading(false);
  };

  useEffect(() => {
    initGame();
  }, []);

  return (
    <div className="min-h-screen flex flex-col items-center justify-center p-4 bg-slate-950 text-slate-100 overflow-hidden relative">
      {/* Background Decorative Elements */}
      <div className="absolute top-0 left-0 w-full h-full pointer-events-none overflow-hidden opacity-20">
        <div className="absolute top-[-10%] left-[-10%] w-[50%] h-[50%] rounded-full bg-blue-600 blur-[120px]" />
        <div className="absolute bottom-[-10%] right-[-10%] w-[50%] h-[50%] rounded-full bg-indigo-600 blur-[120px]" />
      </div>

      <header className="text-center mb-8 z-10">
        <h1 className="text-4xl md:text-5xl font-bold tracking-widest text-transparent bg-clip-text bg-gradient-to-r from-cyan-400 to-indigo-500 mb-2 drop-shadow-lg">
          MYSTIC DIAL
        </h1>
        <p className="text-slate-400 max-w-md mx-auto">
          หมุนวงแหวนทั้ง 3 ชั้นให้สัญลักษณ์ "✧" ตรงกันที่ด้านบนสุดเพื่อปลดล็อครหัสลับ
        </p>
      </header>

      <div className="relative z-10 flex flex-col items-center">
        {/* The Main Puzzle Dial */}
        <div className="relative w-80 h-80 md:w-[500px] md:h-[500px] group">
          {/* Target Marker */}
          <div className="absolute top-0 left-1/2 -translate-x-1/2 -translate-y-4 text-cyan-400 flex flex-col items-center animate-pulse">
            <Zap size={24} fill="currentColor" />
            <div className="w-1 h-8 bg-gradient-to-b from-cyan-400 to-transparent mt-1" />
          </div>

          <svg viewBox="0 0 500 500" className="w-full h-full drop-shadow-[0_0_30px_rgba(34,211,238,0.2)]">
            <DialRing
              radius={200}
              thickness={60}
              rotation={rotations[RingLayer.OUTER]}
              segments={8}
              symbols={SYMBOLS_POOL}
              color="rgba(15, 23, 42, 0.8)"
              glowColor="#22d3ee"
              isActive={!solved}
              onRotate={() => handleRotate(RingLayer.OUTER)}
            />
            <DialRing
              radius={135}
              thickness={60}
              rotation={rotations[RingLayer.MIDDLE]}
              segments={8}
              symbols={SYMBOLS_POOL}
              color="rgba(15, 23, 42, 0.8)"
              glowColor="#818cf8"
              isActive={!solved}
              onRotate={() => handleRotate(RingLayer.MIDDLE)}
            />
            <DialRing
              radius={70}
              thickness={60}
              rotation={rotations[RingLayer.INNER]}
              segments={8}
              symbols={SYMBOLS_POOL}
              color="rgba(15, 23, 42, 0.8)"
              glowColor="#c084fc"
              isActive={!solved}
              onRotate={() => handleRotate(RingLayer.INNER)}
            />

            {/* Center Lock Status */}
            <circle cx="250" cy="250" r="35" fill="#0f172a" stroke="#334155" strokeWidth="2" />
            <g transform="translate(235, 235)">
              {solved ? (
                <Unlock size={30} className="text-green-400 animate-bounce" />
              ) : (
                <Lock size={30} className="text-slate-500" />
              )}
            </g>
          </svg>
        </div>

        {/* Controls and Feedback */}
        <div className="mt-12 w-full max-w-md space-y-4">
          {solved ? (
            <div className="bg-slate-900/80 border border-green-500/50 p-6 rounded-2xl text-center shadow-xl animate-in fade-in zoom-in duration-500 backdrop-blur-md">
              <h2 className="text-green-400 font-bold text-xl mb-4 flex items-center justify-center gap-2">
                <Unlock size={24} /> ปลดล็อคสำเร็จ!
              </h2>
              <div className="text-2xl font-semibold text-white mb-2 py-4 bg-slate-800 rounded-lg border border-slate-700">
                {secret.secret}
              </div>
              <button
                onClick={initGame}
                className="w-full bg-indigo-600 hover:bg-indigo-500 text-white font-bold py-3 px-6 rounded-xl transition-all flex items-center justify-center gap-2"
              >
                <RefreshCw size={20} className={loading ? "animate-spin" : ""} />
                เริ่มปริศนาใหม่
              </button>
            </div>
          ) : (
            <div className="grid grid-cols-1 gap-4">
              <div className="flex justify-center gap-4">
                <button
                  onClick={() => setShowHint(!showHint)}
                  className="p-4 bg-slate-800 hover:bg-slate-700 rounded-xl transition-all border border-slate-700 text-slate-300 flex items-center gap-2"
                >
                  <HelpCircle size={20} />
                  {showHint ? "ซ่อนคำใบ้" : "ขอคำใบ้"}
                </button>
                <button
                  onClick={initGame}
                  className="p-4 bg-slate-800 hover:bg-slate-700 rounded-xl transition-all border border-slate-700 text-slate-300 flex items-center gap-2"
                >
                  <RefreshCw size={20} className={loading ? "animate-spin" : ""} />
                  สุ่มสัญลักษณ์ใหม่
                </button>
              </div>

              {showHint && (
                <div className="p-4 bg-cyan-950/30 border border-cyan-500/30 rounded-xl text-cyan-300 italic text-center animate-in slide-in-from-top duration-300">
                  {secret.hint || "กำลังคิดคำใบ้..."}
                </div>
              )}

              <div className="grid grid-cols-3 gap-2 mt-4">
                <button 
                  onClick={() => handleRotate(RingLayer.OUTER)}
                  className="bg-slate-900 hover:bg-cyan-900/50 border border-slate-800 p-3 rounded-lg text-xs font-bold transition-all text-cyan-400"
                >
                  หมุนวงนอก
                </button>
                <button 
                  onClick={() => handleRotate(RingLayer.MIDDLE)}
                  className="bg-slate-900 hover:bg-indigo-900/50 border border-slate-800 p-3 rounded-lg text-xs font-bold transition-all text-indigo-400"
                >
                  หมุนวงกลาง
                </button>
                <button 
                  onClick={() => handleRotate(RingLayer.INNER)}
                  className="bg-slate-900 hover:bg-purple-900/50 border border-slate-800 p-3 rounded-lg text-xs font-bold transition-all text-purple-400"
                >
                  หมุนวงใน
                </button>
              </div>
            </div>
          )}
        </div>
      </div>

      {loading && !solved && (
        <div className="fixed inset-0 bg-slate-950/80 backdrop-blur-sm z-50 flex flex-col items-center justify-center space-y-4">
          <div className="w-16 h-16 border-4 border-cyan-500 border-t-transparent rounded-full animate-spin"></div>
          <p className="text-cyan-400 font-medium animate-pulse">กำลังเตรียมปริศนาลับจาก AI...</p>
        </div>
      )}

      <footer className="mt-12 text-slate-500 text-sm z-10">
        &copy; 2024 Mystic Dial • Powered by Gemini AI
      </footer>
    </div>
  );
};

export default App;

import React from 'react';
import { RingLayer } from '../types';

interface DialRingProps {
  layer: RingLayer;
  radius: number;
  thickness: number;
  rotation: number;
  segments: number;
  symbols: string[];
  color: string;
  glowColor: string;
  isActive: boolean;
  isDragging: boolean;
  onDragStart: (layer: RingLayer, e: React.MouseEvent | React.TouchEvent) => void;
}

const DialRing: React.FC<DialRingProps> = ({
  layer,
  radius,
  thickness,
  rotation,
  segments,
  symbols,
  color,
  glowColor,
  isActive,
  isDragging,
  onDragStart
}) => {
  const anglePerSegment = 360 / segments;

  return (
    <g 
      className="cursor-grab active:cursor-grabbing"
      onMouseDown={(e) => onDragStart(layer, e)}
      onTouchStart={(e) => onDragStart(layer, e)}
    >
      {/* Background Circle - Hit Area */}
      <circle
        cx="250"
        cy="250"
        r={radius}
        fill="transparent"
        stroke={color}
        strokeWidth={thickness}
        className={`transition-opacity duration-300 ${isActive ? (isDragging ? 'opacity-100' : 'opacity-70') : 'opacity-40'}`}
        style={{ 
          filter: (isActive && isDragging) ? `drop-shadow(0 0 15px ${glowColor})` : (isActive ? `drop-shadow(0 0 5px ${glowColor})` : 'none'),
          transition: isDragging ? 'none' : 'transform 0.5s cubic-bezier(0.175, 0.885, 0.32, 1.275), opacity 0.3s'
        }}
      />

      {/* Visual Ring and Symbols */}
      <g style={{ 
        transform: `rotate(${rotation}deg)`, 
        transformOrigin: '250px 250px',
        transition: isDragging ? 'none' : 'transform 0.5s cubic-bezier(0.175, 0.885, 0.32, 1.275)'
      }}>
        {symbols.map((symbol, i) => {
          const angle = i * anglePerSegment;
          const radian = (angle * Math.PI) / 180;
          const textRadius = radius;
          const x = 250 + textRadius * Math.sin(radian);
          const y = 250 - textRadius * Math.cos(radian);

          return (
            <g key={i}>
              <line
                x1={250 + (radius - thickness / 2.5) * Math.sin(radian)}
                y1={250 - (radius - thickness / 2.5) * Math.cos(radian)}
                x2={250 + (radius + thickness / 2.5) * Math.sin(radian)}
                y2={250 - (radius + thickness / 2.5) * Math.cos(radian)}
                stroke={glowColor}
                strokeWidth="2"
                opacity={isDragging ? "0.8" : "0.4"}
              />
              <text
                x={x}
                y={y}
                fill="white"
                fontSize="22"
                fontWeight="bold"
                textAnchor="middle"
                alignmentBaseline="middle"
                className="select-none pointer-events-none"
                style={{ transform: `rotate(${angle}deg)`, transformOrigin: `${x}px ${y}px` }}
              >
                {symbol}
              </text>
            </g>
          );
        })}
      </g>
    </g>
  );
};

export default DialRing;

import React, { useState, useEffect, useCallback, useRef } from 'react';
import { RefreshCw, Lock, Unlock, Zap, HelpCircle, Move } from 'lucide-react';
import DialRing from './components/DialRing';
import { generateSecretChallenge } from './services/geminiService';
import { RingLayer } from './types';

const SYMBOLS_POOL = ["✧", "❂", "☸", "♾", "⚛", "⌬", "⌘", "❖"];

const App: React.FC = () => {
  const [loading, setLoading] = useState(false);
  const [solved, setSolved] = useState(false);
  const [secret, setSecret] = useState({ secret: "", hint: "" });
  const [showHint, setShowHint] = useState(false);

  const [rotations, setRotations] = useState({
    [RingLayer.OUTER]: (Math.floor(Math.random() * 7) + 1) * 45,
    [RingLayer.MIDDLE]: (Math.floor(Math.random() * 7) + 1) * 45,
    [RingLayer.INNER]: (Math.floor(Math.random() * 7) + 1) * 45,
  });

  const [activeLayer, setActiveLayer] = useState<RingLayer | null>(null);
  const dragStartAngle = useRef<number>(0);
  const initialRotation = useRef<number>(0);
  const containerRef = useRef<HTMLDivElement>(null);

  const getAngle = (clientX: number, clientY: number) => {
    if (!containerRef.current) return 0;
    const rect = containerRef.current.getBoundingClientRect();
    const centerX = rect.left + rect.width / 2;
    const centerY = rect.top + rect.height / 2;
    const dx = clientX - centerX;
    const dy = clientY - centerY;
    // Math.atan2 returns radians, convert to degrees
    return Math.atan2(dy, dx) * (180 / Math.PI);
  };

  const handleDragStart = (layer: RingLayer, e: React.MouseEvent | React.TouchEvent) => {
    if (solved || loading) return;

    const clientX = 'touches' in e ? e.touches[0].clientX : e.clientX;
    const clientY = 'touches' in e ? e.touches[0].clientY : e.clientY;

    setActiveLayer(layer);
    dragStartAngle.current = getAngle(clientX, clientY);
    initialRotation.current = rotations[layer];
  };

  const handleDragMove = useCallback((e: MouseEvent | TouchEvent) => {
    if (activeLayer === null || solved) return;

    const clientX = 'touches' in e ? e.touches[0].clientX : e.clientX;
    const clientY = 'touches' in e ? e.touches[0].clientY : e.clientY;

    const currentAngle = getAngle(clientX, clientY);
    const angleDiff = currentAngle - dragStartAngle.current;

    setRotations(prev => ({
      ...prev,
      [activeLayer]: initialRotation.current + angleDiff
    }));
  }, [activeLayer, solved]);

  const handleDragEnd = useCallback(() => {
    if (activeLayer === null) return;

    // Snap to nearest 45 degrees
    setRotations(prev => {
      const currentRot = prev[activeLayer];
      // Normalize to 0-360 range and snap
      const snapped = Math.round(currentRot / 45) * 45;
      const nextRotations = { ...prev, [activeLayer]: snapped };

      // Check if solved
      const isAligned = 
        nextRotations[RingLayer.OUTER] % 360 === 0 &&
        nextRotations[RingLayer.MIDDLE] % 360 === 0 &&
        nextRotations[RingLayer.INNER] % 360 === 0;

      if (isAligned) setSolved(true);

      return nextRotations;
    });

    setActiveLayer(null);
  }, [activeLayer]);

  useEffect(() => {
    if (activeLayer !== null) {
      window.addEventListener('mousemove', handleDragMove);
      window.addEventListener('mouseup', handleDragEnd);
      window.addEventListener('touchmove', handleDragMove, { passive: false });
      window.addEventListener('touchend', handleDragEnd);
    } else {
      window.removeEventListener('mousemove', handleDragMove);
      window.removeEventListener('mouseup', handleDragEnd);
      window.removeEventListener('touchmove', handleDragMove);
      window.removeEventListener('touchend', handleDragEnd);
    }
    return () => {
      window.removeEventListener('mousemove', handleDragMove);
      window.removeEventListener('mouseup', handleDragEnd);
      window.removeEventListener('touchmove', handleDragMove);
      window.removeEventListener('touchend', handleDragEnd);
    };
  }, [activeLayer, handleDragMove, handleDragEnd]);

  const initGame = async () => {
    setLoading(true);
    setSolved(false);
    setShowHint(false);
    const data = await generateSecretChallenge();
    setSecret(data);

    setRotations({
      [RingLayer.OUTER]: (Math.floor(Math.random() * 7) + 1) * 45,
      [RingLayer.MIDDLE]: (Math.floor(Math.random() * 7) + 1) * 45,
      [RingLayer.INNER]: (Math.floor(Math.random() * 7) + 1) * 45,
    });
    setLoading(false);
  };

  useEffect(() => {
    initGame();
  }, []);

  return (
    <div className="min-h-screen flex flex-col items-center justify-center p-4 bg-slate-950 text-slate-100 overflow-hidden relative select-none">
      {/* Background Decorative Elements */}
      <div className="absolute top-0 left-0 w-full h-full pointer-events-none overflow-hidden opacity-20">
        <div className="absolute top-[-10%] left-[-10%] w-[50%] h-[50%] rounded-full bg-blue-600 blur-[120px]" />
        <div className="absolute bottom-[-10%] right-[-10%] w-[50%] h-[50%] rounded-full bg-indigo-600 blur-[120px]" />
      </div>

      <header className="text-center mb-6 z-10">
        <h1 className="text-4xl md:text-5xl font-bold tracking-widest text-transparent bg-clip-text bg-gradient-to-r from-cyan-400 to-indigo-500 mb-2 drop-shadow-lg">
          MYSTIC DIAL
        </h1>
        <p className="text-slate-400 max-w-sm mx-auto text-sm">
          ลากหมุนวงแหวนให้สัญลักษณ์ <span className="text-cyan-400 font-bold">✧</span> อยู่ด้านบนสุดทั้ง 3 วง
        </p>
      </header>

      <div className="relative z-10 flex flex-col items-center">
        {/* The Main Puzzle Dial */}
        <div 
          ref={containerRef}
          className="relative w-[340px] h-[340px] md:w-[500px] md:h-[500px]"
        >
          {/* Target Marker */}
          <div className="absolute top-0 left-1/2 -translate-x-1/2 -translate-y-6 text-cyan-400 flex flex-col items-center z-20">
            <Zap size={24} fill="currentColor" className="animate-pulse" />
            <div className="w-1 h-12 bg-gradient-to-b from-cyan-400 to-transparent mt-1" />
          </div>

          <svg viewBox="0 0 500 500" className="w-full h-full drop-shadow-[0_0_40px_rgba(34,211,238,0.15)] overflow-visible">
            <DialRing
              layer={RingLayer.OUTER}
              radius={200}
              thickness={65}
              rotation={rotations[RingLayer.OUTER]}
              segments={8}
              symbols={SYMBOLS_POOL}
              color="rgba(30, 41, 59, 0.7)"
              glowColor="#22d3ee"
              isActive={!solved}
              isDragging={activeLayer === RingLayer.OUTER}
              onDragStart={handleDragStart}
            />
            <DialRing
              layer={RingLayer.MIDDLE}
              radius={130}
              thickness={65}
              rotation={rotations[RingLayer.MIDDLE]}
              segments={8}
              symbols={SYMBOLS_POOL}
              color="rgba(30, 41, 59, 0.8)"
              glowColor="#818cf8"
              isActive={!solved}
              isDragging={activeLayer === RingLayer.MIDDLE}
              onDragStart={handleDragStart}
            />
            <DialRing
              layer={RingLayer.INNER}
              radius={60}
              thickness={65}
