import React from 'react';
import { RingLayer } from '../types';

interface DialRingProps {
  layer: RingLayer;
  radius: number;
  thickness: number;
  rotation: number;
  segments: number;
  symbols: string[];
  color: string;
  glowColor: string;
  isActive: boolean;
  isDragging: boolean;
  onDragStart: (layer: RingLayer, e: React.MouseEvent | React.TouchEvent) => void;
}

const DialRing: React.FC<DialRingProps> = ({
  layer,
  radius,
  thickness,
  rotation,
  segments,
  symbols,
  color,
  glowColor,
  isActive,
  isDragging,
  onDragStart
}) => {
  const anglePerSegment = 360 / segments;

  return (
    <g 
      className="cursor-grab active:cursor-grabbing"
      onMouseDown={(e) => onDragStart(layer, e)}
      onTouchStart={(e) => onDragStart(layer, e)}
    >
      {/* Stone Ring Texture with Inner/Outer Borders */}
      <circle
        cx="250"
        cy="250"
        r={radius}
        fill="none"
        stroke="#1e1b4b"
        strokeWidth={thickness}
        className="opacity-80"
      />

      {/* Gold Borders */}
      <circle cx="250" cy="250" r={radius - thickness/2} fill="none" stroke="#b45309" strokeWidth="2" opacity="0.6" />
      <circle cx="250" cy="250" r={radius + thickness/2} fill="none" stroke="#b45309" strokeWidth="2" opacity="0.6" />

      {/* Glow Effect when interacting */}
      <circle
        cx="250"
        cy="250"
        r={radius}
        fill="transparent"
        stroke={glowColor}
        strokeWidth={thickness}
        className={`transition-opacity duration-700 ${isActive ? (isDragging ? 'opacity-30' : 'opacity-10') : 'opacity-5'}`}
        style={{ 
          filter: `blur(8px)`,
        }}
      />

      {/* Rotating Content */}
      <g style={{ 
        transform: `rotate(${rotation}deg)`, 
        transformOrigin: '250px 250px',
        transition: isDragging ? 'none' : 'transform 0.8s cubic-bezier(0.34, 1.56, 0.64, 1)'
      }}>
        {symbols.map((symbol, i) => {
          const angle = i * anglePerSegment;
          const radian = (angle * Math.PI) / 180;
          const textRadius = radius;
          const x = 250 + textRadius * Math.sin(radian);
          const y = 250 - textRadius * Math.cos(radian);

          return (
            <g key={i}>
              {/* Decorative Ticks */}
              <circle
                cx={250 + (radius) * Math.sin(radian)}
                cy={250 - (radius) * Math.cos(radian)}
                r="18"
                fill="#0f172a"
                stroke="#451a03"
                strokeWidth="1"
              />
              <text
                x={x}
                y={y}
                fill={i === 0 ? "#fbbf24" : "#e2e8f0"}
                fontSize="24"
                fontWeight="bold"
                textAnchor="middle"
                alignmentBaseline="middle"
                className="select-none pointer-events-none rune-font"
                style={{ 
                  transform: `rotate(${angle}deg)`, 
                  transformOrigin: `${x}px ${y}px`,
                  filter: i === 0 ? `drop-shadow(0 0 8px #fbbf24)` : 'none'
                }}
              >
                {symbol}
              </text>
            </g>
          );
        })}
      </g>
    </g>
  );
};

export default DialRing;

import React, { useState, useEffect, useCallback, useRef } from 'react';
import { RefreshCw, Lock, Unlock, Moon, HelpCircle, Sparkles, BookOpen } from 'lucide-react';
import DialRing from './components/DialRing';
import { generateSecretChallenge } from './services/geminiService';
import { RingLayer } from './types';

// Mystical Runes from various ancient scripts
const SYMBOLS_POOL = ["᚛", "ᛖ", "ᚨ", "ᚦ", "ᛟ", "ᚱ", "ᚲ", "ᚷ"];

const App: React.FC = () => {
  const [loading, setLoading] = useState(false);
  const [solved, setSolved] = useState(false);
  const [secret, setSecret] = useState({ secret: "", hint: "" });
  const [showHint, setShowHint] = useState(false);
  const [particles, setParticles] = useState<any[]>([]);

  const [rotations, setRotations] = useState({
    [RingLayer.OUTER]: (Math.floor(Math.random() * 7) + 1) * 45,
    [RingLayer.MIDDLE]: (Math.floor(Math.random() * 7) + 1) * 45,
    [RingLayer.INNER]: (Math.floor(Math.random() * 7) + 1) * 45,
  });

  const [activeLayer, setActiveLayer] = useState<RingLayer | null>(null);
  const dragStartAngle = useRef<number>(0);
  const initialRotation = useRef<number>(0);
  const containerRef = useRef<HTMLDivElement>(null);

  // Magic particles initialization
  useEffect(() => {
    const newParticles = Array.from({ length: 30 }).map((_, i) => ({
      id: i,
      left: Math.random() * 100,
      size: Math.random() * 4 + 1,
      duration: Math.random() * 10 + 5,
      delay: Math.random() * 5
    }));
    setParticles(newParticles);
  }, []);

  const getAngle = (clientX: number, clientY: number) => {
    if (!containerRef.current) return 0;
    const rect = containerRef.current.getBoundingClientRect();
    const centerX = rect.left + rect.width / 2;
    const centerY = rect.top + rect.height / 2;
    const dx = clientX - centerX;
    const dy = clientY - centerY;
    return Math.atan2(dy, dx) * (180 / Math.PI);
  };

  const handleDragStart = (layer: RingLayer, e: React.MouseEvent | React.TouchEvent) => {
    if (solved || loading) return;
    const clientX = 'touches' in e ? e.touches[0].clientX : e.clientX;
    const clientY = 'touches' in e ? e.touches[0].clientY : e.clientY;
    setActiveLayer(layer);
    dragStartAngle.current = getAngle(clientX, clientY);
    initialRotation.current = rotations[layer];
  };

  const handleDragMove = useCallback((e: MouseEvent | TouchEvent) => {
    if (activeLayer === null || solved) return;
    const clientX = 'touches' in e ? e.touches[0].clientX : e.clientX;
    const clientY = 'touches' in e ? e.touches[0].clientY : e.clientY;
    const currentAngle = getAngle(clientX, clientY);
    const angleDiff = currentAngle - dragStartAngle.current;
    setRotations(prev => ({ ...prev, [activeLayer]: initialRotation.current + angleDiff }));
  }, [activeLayer, solved]);

  const handleDragEnd = useCallback(() => {
    if (activeLayer === null) return;
    setRotations(prev => {
      const currentRot = prev[activeLayer];
      const snapped = Math.round(currentRot / 45) * 45;
      const nextRotations = { ...prev, [activeLayer]: snapped };
      const isAligned = 
        nextRotations[RingLayer.OUTER] % 360 === 0 &&
        nextRotations[RingLayer.MIDDLE] % 360 === 0 &&
        nextRotations[RingLayer.INNER] % 360 === 0;
      if (isAligned) setSolved(true);
      return nextRotations;
    });
    setActiveLayer(null);
  }, [activeLayer]);

  useEffect(() => {
    if (activeLayer !== null) {
      window.addEventListener('mousemove', handleDragMove);
      window.addEventListener('mouseup', handleDragEnd);
      window.addEventListener('touchmove', handleDragMove, { passive: false });
      window.addEventListener('touchend', handleDragEnd);
    }
    return () => {
      window.removeEventListener('mousemove', handleDragMove);
      window.removeEventListener('mouseup', handleDragEnd);
      window.removeEventListener('touchmove', handleDragMove);
      window.removeEventListener('touchend', handleDragEnd);
    };
  }, [activeLayer, handleDragMove, handleDragEnd]);

  const initGame = async () => {
    setLoading(true);
    setSolved(false);
    setShowHint(false);
    const data = await generateSecretChallenge();
    setSecret(data);
    setRotations({
      [RingLayer.OUTER]: (Math.floor(Math.random() * 7) + 1) * 45,
      [RingLayer.MIDDLE]: (Math.floor(Math.random() * 7) + 1) * 45,
      [RingLayer.INNER]: (Math.floor(Math.random() * 7) + 1) * 45,
    });
    setLoading(false);
  };

  useEffect(() => { initGame(); }, []);

  return (
    <div className="min-h-screen flex flex-col items-center justify-center p-4 text-slate-100 overflow-hidden relative select-none">
      {/* Magic Particles */}
      {particles.map(p => (
        <div 
          key={p.id} 
          className="particle" 
          style={{ 
            left: `${p.left}%`, 
            width: `${p.size}px`, 
            height: `${p.size}px`, 
            animationDuration: `${p.duration}s`, 
            animationDelay: `${p.delay}s`,
            bottom: '-20px'
          }} 
        />
      ))}

      {/* Ornate Header */}
      <header className="text-center mb-8 z-10 flex flex-col items-center">
        <div className="flex items-center gap-3 mb-2">
          <Sparkles className="text-amber-400" size={24} />
          <h1 className="text-4xl md:text-6xl font-bold tracking-[0.2em] text-transparent bg-clip-text bg-gradient-to-b from-amber-200 via-amber-400 to-amber-700 drop-shadow-[0_2px_10px_rgba(251,191,36,0.5)]">
            ARCANE DIAL
          </h1>
          <Sparkles className="text-amber-400" size={24} />
        </div>
        <p className="text-indigo-200/80 font-serif italic text-lg tracking-wide max-w-sm">
          "จงหมุนศิลาอักขระให้ตรงรอยแยกแห่งความจริง"
        </p>
      </header>

      <div className="relative z-10 flex flex-col items-center">
        {/* The Magic Circle */}
        <div 
          ref={containerRef}
          className="relative w-[340px] h-[340px] md:w-[500px] md:h-[500px]"
        >
          {/* Ornate Target Marker (The Sun) */}
          <div className="absolute top-0 left-1/2 -translate-x-1/2 -translate-y-8 text-amber-500 flex flex-col items-center z-20">
            <div className="w-10 h-10 rounded-full bg-amber-500/20 border-2 border-amber-500 flex items-center justify-center animate-pulse shadow-[0_0_20px_#f59e0b]">
              <Moon size={20} fill="currentColor" />
            </div>
            <div className="w-1 h-14 bg-gradient-to-b from-amber-500 to-transparent mt-1" />
          </div>

          <svg viewBox="0 0 500 500" className="w-full h-full drop-shadow-[0_0_50px_rgba(124,58,237,0.3)] overflow-visible">
            {/* Background mystical patterns */}
            <circle cx="250" cy="250" r="240" fill="none" stroke="#1e1b4b" strokeWidth="1" opacity="0.3" strokeDasharray="10 5" />

            <DialRing
              layer={RingLayer.OUTER}
              radius={200}
              thickness={70}
              rotation={rotations[RingLayer.OUTER]}
              segments={8}
              symbols={SYMBOLS_POOL}
              color="rgba(30, 41, 59, 0.5)"
              glowColor="#7c3aed"
              isActive={!solved}
              isDragging={activeLayer === RingLayer.OUTER}
              onDragStart={handleDragStart}
            />
            <DialRing
              layer={RingLayer.MIDDLE}
              radius={125}
              thickness={70}
              rotation={rotations[RingLayer.MIDDLE]}
              segments={8}
              symbols={SYMBOLS_POOL}
              color="rgba(30, 41, 59, 0.6)"
              glowColor="#6366f1"
              isActive={!solved}
              isDragging={activeLayer === RingLayer.MIDDLE}
              onDragStart={handleDragStart}
            />
            <DialRing
              layer={RingLayer.INNER}
              radius={50}
              thickness={70}
              rotation={rotations[RingLayer.INNER]}
              segments={8}
              symbols={SYMBOLS_POOL}
              color="rgba(30, 41, 59, 0.7)"
              glowColor="#ec4899"
              isActive={!solved}
              isDragging={activeLayer === RingLayer.INNER}
              onDragStart={handleDragStart}
            />

            {/* Center Lock - The Mystic Eye */}
            <circle cx="250" cy="250" r="28" fill="#020617" stroke="#b45309" strokeWidth="3" />
            <g transform="translate(238, 238)">
              {solved ? (
                <Unlock size={24} className="text-amber-400 animate-pulse" />
              ) : (
                <Lock size={24} className="text-indigo-900" />
              )}
            </g>
          </svg>
        </div>

        {/* Fantasy Themed Controls */}
        <div className="mt-12 w-full max-w-sm space-y-6">
          {solved ? (
            <div className="bg-indigo-950/80 border-2 border-amber-600/50 p-8 rounded-[2rem] text-center shadow-[0_0_40px_rgba(245,158,11,0.2)] animate-in zoom-in slide-in-from-bottom-10 duration-1000 backdrop-blur-2xl relative overflow-hidden">
              <div className="absolute top-0 left-0 w-full h-1 bg-gradient-to-r from-transparent via-amber-500 to-transparent"></div>
              <h2 className="text-amber-400 font-bold text-2xl mb-4 flex items-center justify-center gap-2 tracking-widest">
                <BookOpen size={24} /> คำพยากรณ์ถูกเปิดเผย
              </h2>
              <div className="text-2xl font-medium text-white mb-8 leading-relaxed font-serif italic py-6 px-4 bg-indigo-900/30 rounded-3xl border border-indigo-800/50">
                "{secret.secret}"
              </div>
              <button
                onClick={initGame}
                className="w-full bg-gradient-to-r from-amber-700 via-amber-600 to-amber-800 hover:from-amber-600 hover:to-amber-700 text-white font-bold py-5 px-6 rounded-2xl transition-all flex items-center justify-center gap-3 shadow-[0_5px_15px_rgba(0,0,0,0.4)] border-t border-amber-400/30"
              >
                <RefreshCw size={20} className={loading ? "animate-spin" : ""} />
                เริ่มพิธีกรรมใหม่
              </button>
            </div>
          ) : (
            <div className="space-y-6">
              <div className="flex gap-4">
                <button
                  onClick={() => setShowHint(!showHint)}
                  className="flex-1 p-5 bg-indigo-950/40 hover:bg-indigo-900/60 rounded-2xl transition-all border border-indigo-800/50 text-amber-200/70 flex items-center justify-center gap-2 font-serif"
                >
                  <HelpCircle size={22} className="text-amber-500" />
                  {showHint ? "ปิดนิมิต" : "ขอนิมิต"}
                </button>
                <button
                  onClick={initGame}
                  className="p-5 bg-indigo-950/40 hover:bg-indigo-900/60 rounded-2xl transition-all border border-indigo-800/50 text-amber-200/70 flex items-center justify-center"
                >
                  <RefreshCw size={22} className={loading ? "animate-spin" : "text-amber-500"} />
                </button>
              </div>

              {showHint && (
                <div className="p-5 bg-amber-950/10 border border-amber-500/20 rounded-2xl text-amber-100/80 italic text-center text-base animate-in fade-in slide-in-from-top-4 duration-500 font-serif">
