  layer,
  radius,
  thickness,
  rotation,
  segments,
  color,
  isActive,
  isDragging,
  onDragStart
}) => {
  // We'll draw custom shards for each ring to simulate the puzzle
  const renderShards = () => {
    const shards = [];
    const angleStep = 360 / segments;

    for (let i = 0; i < segments; i++) {
      // Logic to make only some parts "aligned" when at 0 deg
      // We simulate specific silhouettes
      const angle = i * angleStep;
      const isKeyShard = i % 3 === 0; // Only every 3rd shard is part of the final symbol logic for complexity

      const startRadian = (angle - 20) * Math.PI / 180;
      const endRadian = (angle + 20) * Math.PI / 180;

      const innerR = radius - thickness / 2;
      const outerR = radius + thickness / 2;

      const x1 = 250 + innerR * Math.sin(startRadian);
      const y1 = 250 - innerR * Math.cos(startRadian);
      const x2 = 250 + outerR * Math.sin(startRadian);
      const y2 = 250 - outerR * Math.cos(startRadian);
      const x3 = 250 + outerR * Math.sin(endRadian);
      const y3 = 250 - outerR * Math.cos(endRadian);
      const x4 = 250 + innerR * Math.sin(endRadian);
      const y4 = 250 - innerR * Math.cos(endRadian);

      shards.push(
        <path
          key={i}
          d={`M ${x1} ${y1} L ${x2} ${y2} L ${x3} ${y3} L ${x4} ${y4} Z`}
          fill={color}
          fillOpacity={isKeyShard ? 0.7 : 0.3}
          stroke="#000"
          strokeWidth="3"
          style={{ filter: isActive ? 'none' : 'brightness(1.5) saturate(1.5)' }}
        />
      );
    }
    return shards;
  };

  return (
    <g 
      className="cursor-grab active:cursor-grabbing transition-opacity duration-500"
      onMouseDown={(e) => onDragStart(layer, e)}
      onTouchStart={(e) => onDragStart(layer, e)}
      style={{ opacity: isActive ? 1 : 0.9 }}
    >
      <g style={{ 
        transform: `rotate(${rotation}deg)`, 
        transformOrigin: '250px 250px',
        transition: isDragging ? 'none' : 'transform 0.8s cubic-bezier(0.4, 0, 0.2, 1)'
      }}>
        {renderShards()}
      </g>
    </g>
  );
};

export default DialRing;

import React, { useState, useEffect, useCallback, useRef } from 'react';
