        <div className="absolute top-0 right-0 w-1/4 h-full bg-indigo-950/40 border-l border-indigo-900/20 backdrop-blur-sm"></div>

        {/* Candles */}
        <div className="absolute bottom-20 left-[15%] flex flex-col items-center">
          <div className="w-4 h-40 bg-stone-300 rounded-t-lg shadow-inner"></div>
          <div className="w-8 h-8 bg-orange-500 candle-light rounded-full -mt-44 blur-xl"></div>
          <Flame className="text-orange-400 absolute -top-12 animate-pulse" size={32} />
        </div>
        <div className="absolute bottom-20 right-[15%] flex flex-col items-center">
          <div className="w-4 h-40 bg-stone-300 rounded-t-lg shadow-inner"></div>
          <div className="w-8 h-8 bg-orange-500 candle-light rounded-full -mt-44 blur-xl"></div>
          <Flame className="text-orange-400 absolute -top-12 animate-pulse" size={32} />
        </div>
      </div>

      <header className="text-center mb-8 z-10">
        <h1 className="text-3xl md:text-5xl font-bold tracking-[0.3em] text-stone-400 uppercase drop-shadow-lg mb-2">
          Church Puzzle
        </h1>
        <div className="h-1 w-32 bg-stone-800 mx-auto"></div>
      </header>

      <div className="relative z-10 flex flex-col items-center">
        {/* The Stained Glass Window */}
        <div 
          ref={containerRef}
          className="relative w-[340px] h-[340px] md:w-[500px] md:h-[500px] rounded-full border-[12px] border-stone-800 shadow-[0_0_100px_rgba(0,0,0,0.8)] overflow-hidden bg-black"
        >
          <svg viewBox="0 0 500 500" className={`w-full h-full transition-all duration-1000 ${solved ? 'brightness-150 saturate-150' : 'brightness-75'}`}>
            {/* Background Grid */}
            <circle cx="250" cy="250" r="250" fill="#0c0a09" />
            <path d="M 0 250 L 500 250 M 250 0 L 250 500" stroke="#1c1917" strokeWidth="2" />

            <DialRing
              layer={RingLayer.OUTER}
              radius={200}
              thickness={80}
              rotation={rotations[RingLayer.OUTER]}
              segments={12}
              color={COLORS[RingLayer.OUTER]}
              isActive={!solved}
              isDragging={activeLayer === RingLayer.OUTER}
              onDragStart={handleDragStart}
            />
            <DialRing
              layer={RingLayer.MIDDLE}
              radius={130}
              thickness={70}
              rotation={rotations[RingLayer.MIDDLE]}
              segments={12}
              color={COLORS[RingLayer.MIDDLE]}
              isActive={!solved}
              isDragging={activeLayer === RingLayer.MIDDLE}
              onDragStart={handleDragStart}
            />
            <DialRing
