import React from 'react';
import { RingLayer } from '../types';

interface DialRingProps {
  layer: RingLayer;
  radius: number;
  thickness: number;
  rotation: number;
  color: string;
  isActive: boolean;
  isDragging: boolean;
  onDragStart: (layer: RingLayer, e: React.MouseEvent | React.TouchEvent) => void;
}

const DialRing: React.FC<DialRingProps> = ({
  layer,
  radius,
  thickness,
  rotation,
  color,
  isActive,
  isDragging,
  onDragStart
}) => {
  const innerR = radius - thickness / 2;
  const outerR = radius + thickness / 2;
  const clipId = `clip-${layer}`;

  // This is the "Master Symbol" that will be rendered in each ring
  // but clipped to only show the donut shape of that specific ring.
  const MasterSymbol = () => (
    <g>
      {/* Mosaic Background Fragments for this layer */}
      <path d="M 250 50 L 450 250 L 250 450 L 50 250 Z" fill={color} fillOpacity="0.2" />
      <path d="M 0 0 L 500 500 M 500 0 L 0 500" stroke="black" strokeWidth="2" opacity="0.3" />

      {/* The Los Illuminados Bug Symbol */}
      <g transform="translate(250, 250) scale(4.5)">
        {/* Wings / Body silhouette */}
        <path 
          d="M 0 -40 C -15 -35, -45 -10, -35 15 C -25 35, -10 25, 0 15 C 10 25, 25 35, 35 15 C 45 -10, 15 -35, 0 -40" 
          fill="none" 
          stroke={color} 
          strokeWidth="4" 
          strokeLinecap="round"
          strokeOpacity="0.8"
        />
        <path 
          d="M -5 -45 L 0 -35 L 5 -45 M 0 -35 L 0 40 M -20 0 L 20 0 M -15 20 L 15 20" 
          fill="none" 
          stroke={color} 
          strokeWidth="2"
          strokeOpacity="0.6"
        />
        {/* Decorative Inner Patterns */}
        <circle r="15" fill="none" stroke={color} strokeWidth="1" strokeDasharray="2 2" opacity="0.5" />
      </g>
    </g>
  );

  return (
    <g 
      className="cursor-grab active:cursor-grabbing"
      onMouseDown={(e) => onDragStart(layer, e)}
      onTouchStart={(e) => onDragStart(layer, e)}
    >
      <defs>
        <clipPath id={clipId}>
          {/* Donut shape mask */}
          <path d={`
            M 250, ${250 - outerR}
            A ${outerR},${outerR} 0 1,1 250,${250 + outerR}
            A ${outerR},${outerR} 0 1,1 250,${250 - outerR}
            Z
            M 250, ${250 - innerR}
            A ${innerR},${innerR} 0 1,0 250,${250 + innerR}
            A ${innerR},${innerR} 0 1,0 250,${250 - innerR}
            Z
          `} fillRule="evenodd" />
        </clipPath>
      </defs>

      {/* The Rotating Layer */}
      <g clipPath={`url(#${clipId})`}>
        {/* Visual background for the glass */}
        <circle cx="250" cy="250" r={outerR} fill="#1c1917" />

        <g style={{ 
          transform: `rotate(${rotation}deg)`, 
          transformOrigin: '250px 250px',
          transition: isDragging ? 'none' : 'transform 0.8s cubic-bezier(0.16, 1, 0.3, 1)'
        }}>
          <MasterSymbol />
        </g>
      </g>

      {/* Lead lines (The black borders of the glass) */}
      <circle cx="250" cy="250" r={outerR} fill="none" stroke="#000" strokeWidth="4" />
      <circle cx="250" cy="250" r={innerR} fill="none" stroke="#000" strokeWidth="4" />
    </g>
  );
};

