                      <div className="w-full h-full flex items-center justify-center opacity-30">
                        <div className="w-1 h-6 bg-white rounded-full -translate-y-2"></div>
                      </div>
                    </div>
                    <span className={`text-[10px] font-bold tracking-widest ${activeLayer === knob.layer ? 'text-white' : 'text-stone-500'}`}>{knob.label}</span>
                  </div>
                ))}
              </div>

              <div className="flex gap-3">
                <button
                  onClick={() => setShowHint(!showHint)}
                  className="flex-1 p-4 bg-stone-900/80 hover:bg-stone-800 text-stone-400 rounded border border-stone-800 transition-all flex items-center justify-center gap-2 text-xs font-bold tracking-[0.2em] uppercase"
                >
