                  {showHint ? "CLOSE EYES" : "OPEN EYES"}
                </button>
                <button
                  onClick={initGame}
                  className="p-4 bg-stone-900/80 hover:bg-stone-800 text-stone-500 rounded border border-stone-800"
                >
                  <RefreshCw size={18} className={loading ? "animate-spin" : ""} />
                </button>
              </div>

              {showHint && (
                <div className="p-4 bg-amber-950/10 border-l-2 border-amber-900 text-amber-200/60 text-center text-xs italic animate-in slide-in-from-left duration-500">
                  {secret.hint}
                </div>
              )}

              <p className="text-center text-stone-700 text-[9px] tracking-[0.3em] font-bold uppercase mt-2">
                Drag each glass ring to align the pattern
              </p>
            </div>
          )}
        </div>
      </div>

      {loading && !solved && (
        <div className="fixed inset-0 bg-black/98 z-50 flex flex-col items-center justify-center space-y-8 backdrop-blur-md">
          <div className="relative w-20 h-20">
            <div className="absolute inset-0 border-2 border-stone-900 rounded-full"></div>
            <div className="absolute inset-0 border-2 border-t-amber-900 rounded-full animate-spin"></div>
            <Shield size={32} className="absolute inset-0 m-auto text-amber-950 animate-pulse" />
          </div>
          <p className="text-amber-950 font-bold tracking-[0.5em] text-[10px] animate-pulse">COMMUNING WITH SADLER...</p>
        </div>
      )}

      <footer className="mt-8 text-stone-800 text-[9px] tracking-[0.4em] uppercase z-10 font-bold">
        LOS ILLUMINADOS • FAITH REGAINED
      </footer>
    </div>
  );
};

