import React from 'react';
import { RingLayer } from '../types';

interface DialRingProps {
  layer: RingLayer;
  radius: number;
  thickness: number;
  rotation: number;
  color: string;
  isActive: boolean;
  isDragging: boolean;
  onDragStart: (layer: RingLayer, e: React.MouseEvent | React.TouchEvent) => void;
}

const DialRing: React.FC<DialRingProps> = ({
  layer,
  radius,
  thickness,
  rotation,
  color,
  isActive,
  isDragging,
  onDragStart
}) => {
  const innerR = radius - thickness / 2;
  const outerR = radius + thickness / 2;
  const clipId = `clip-${layer}`;
  const gradId = `grad-${layer}`;

  // High-detail Plaga Symbol (Los Illuminados)
  const MasterSymbol = () => (
    <g>
      <defs>
        <radialGradient id={gradId} cx="50%" cy="50%" r="50%">
          <stop offset="0%" stopColor={color} stopOpacity="0.8" />
          <stop offset="100%" stopColor="#000" stopOpacity="0.4" />
        </radialGradient>
      </defs>

      {/* Decorative mosaic background patterns */}
      <g opacity="0.4">
        {[...Array(12)].map((_, i) => (
          <line 
            key={i}
            x1="250" y1="250" 
            x2={250 + 300 * Math.cos(i * 30 * Math.PI / 180)} 
            y2={250 + 300 * Math.sin(i * 30 * Math.PI / 180)} 
            stroke="black" strokeWidth="2" 
          />
        ))}
        <circle cx="250" cy="250" r="140" fill="none" stroke="black" strokeWidth="1" />
      </g>

      {/* Intricate Plaga Parasite Symbol */}
      <g transform="translate(250, 250) scale(4.8)">
        {/* WINGS - Detailed Silhouette */}
        <path 
          d="M 0 -35 
             C -10 -35, -25 -25, -40 -10
             C -50 0, -45 15, -35 25
             C -25 35, -15 30, -5 15
             L 0 10 L 5 15
             C 15 30, 25 35, 35 25
             C 45 15, 50 0, 40 -10
             C 25 -25, 10 -35, 0 -35" 
          fill={`url(#${gradId})`}
          stroke="black"
          strokeWidth="1.5"
          className="transition-all duration-1000"
        />

        {/* Wing Veins and Details */}
        <path 
          d="M -15 -25 Q -30 -15, -35 5 M -10 -20 Q -20 -10, -25 10 M 15 -25 Q 30 -15, 35 5 M 10 -20 Q 20 -10, 25 10" 
          fill="none" stroke="black" strokeWidth="0.5" opacity="0.6"
        />

        {/* LEGS / APPENDAGES */}
        <path 
          d="M -5 -15 L -15 -10 M -5 -5 L -20 0 M -5 5 L -18 15
             M 5 -15 L 15 -10 M 5 -5 L 20 0 M 5 5 L 18 15" 
          stroke="black" strokeWidth="1.5" strokeLinecap="round"
        />

        {/* BODY CENTER - Main Segments */}
        <path 
          d="M 0 -38 C -4 -38, -6 -34, -6 -30 C -6 -24, -4 -20, 0 -18 C 4 -20, 6 -24, 6 -30 C 6 -34, 4 -38, 0 -38
             M -2 -18 L -3 10 L 0 35 L 3 10 L 2 -18 Z" 
          fill="rgba(0,0,0,0.6)" stroke="black" strokeWidth="1"
        />

        {/* Small detail circles */}
        <circle cy="-30" r="2" fill={color} opacity="0.8" />
      </g>
    </g>
  );

  return (
    <g 
      className="cursor-grab active:cursor-grabbing"
      onMouseDown={(e) => onDragStart(layer, e)}
      onTouchStart={(e) => onDragStart(layer, e)}
    >
      <defs>
        <clipPath id={clipId}>
          <path d={`
            M 250, ${250 - outerR}
            A ${outerR},${outerR} 0 1,1 250,${250 + outerR}
            A ${outerR},${outerR} 0 1,1 250,${250 - outerR}
            Z
            M 250, ${250 - innerR}
            A ${innerR},${innerR} 0 1,0 250,${250 + innerR}
            A ${innerR},${innerR} 0 1,0 250,${250 - innerR}
            Z
          `} fillRule="evenodd" />
        </clipPath>
      </defs>

      {/* Glass Layer with Texture */}
      <g clipPath={`url(#${clipId})`}>
        {/* Base dark glass */}
        <circle cx="250" cy="250" r={outerR} fill="#0f172a" />

        {/* Animated Master Image */}
        <g style={{ 
          transform: `rotate(${rotation}deg)`, 
          transformOrigin: '250px 250px',
          transition: isDragging ? 'none' : 'transform 1.2s cubic-bezier(0.22, 1, 0.36, 1)'
        }}>
          <MasterSymbol />
        </g>

        {/* Shine effect that doesn't rotate */}
        <ellipse cx="250" cy="200" rx={outerR} ry={outerR/2} fill="white" fillOpacity="0.03" transform="rotate(-30, 250, 250)" pointerEvents="none" />
      </g>

      {/* Thick Lead Borders */}
      <circle cx="250" cy="250" r={outerR} fill="none" stroke="#000" strokeWidth="5" />
      <circle cx="250" cy="250" r={innerR} fill="none" stroke="#000" strokeWidth="5" />
    </g>
  );
};

