import React, { useState, useEffect, useCallback, useRef } from 'react';
import { RefreshCw, Lock, Unlock, Flame, HelpCircle, Eye, Shield, Target, Sparkles } from 'lucide-react';
import DialRing from './components/DialRing';
import { generateSecretChallenge } from './services/geminiService';
import { RingLayer } from './types';

const App: React.FC = () => {
  const [loading, setLoading] = useState(false);
  const [solved, setSolved] = useState(false);
  const [secret, setSecret] = useState({ secret: "", hint: "" });
  const [showHint, setShowHint] = useState(false);

  // High-saturation RGB for better visuals
  const COLORS = {
    [RingLayer.OUTER]: "#2563eb", // Deep Blue
    [RingLayer.MIDDLE]: "#16a34a", // Emerald Green
    [RingLayer.INNER]: "#dc2626",  // Ruby Red
  };

  const [rotations, setRotations] = useState({
    [RingLayer.OUTER]: (Math.floor(Math.random() * 3) + 1) * 120,
    [RingLayer.MIDDLE]: (Math.floor(Math.random() * 3) + 1) * 120,
    [RingLayer.INNER]: (Math.floor(Math.random() * 3) + 1) * 120,
  });

  const [activeLayer, setActiveLayer] = useState<RingLayer | null>(null);
  const dragStartAngle = useRef<number>(0);
  const initialRotation = useRef<number>(0);
  const containerRef = useRef<HTMLDivElement>(null);

  const getAngle = (clientX: number, clientY: number) => {
    if (!containerRef.current) return 0;
    const rect = containerRef.current.getBoundingClientRect();
    const centerX = rect.left + rect.width / 2;
    const centerY = rect.top + rect.height / 2;
    const dx = clientX - centerX;
    const dy = clientY - centerY;
    return Math.atan2(dy, dx) * (180 / Math.PI);
  };

  const handleDragStart = (layer: RingLayer, e: React.MouseEvent | React.TouchEvent) => {
    if (solved || loading) return;
    const clientX = 'touches' in e ? e.touches[0].clientX : e.clientX;
    const clientY = 'touches' in e ? e.touches[0].clientY : e.clientY;
    setActiveLayer(layer);
    dragStartAngle.current = getAngle(clientX, clientY);
    initialRotation.current = rotations[layer];
  };

  const handleDragMove = useCallback((e: MouseEvent | TouchEvent) => {
    if (activeLayer === null || solved) return;
    const clientX = 'touches' in e ? e.touches[0].clientX : e.clientX;
    const clientY = 'touches' in e ? e.touches[0].clientY : e.clientY;
    const currentAngle = getAngle(clientX, clientY);
    const angleDiff = currentAngle - dragStartAngle.current;

    setRotations(prev => ({ ...prev, [activeLayer]: initialRotation.current + angleDiff }));
  }, [activeLayer, solved]);

  const handleDragEnd = useCallback(() => {
    if (activeLayer === null) return;
    setRotations(prev => {
      const currentRot = prev[activeLayer];
      // RE4 puzzle usually snaps to 120-degree positions
      const snapped = Math.round(currentRot / 120) * 120;
      const nextRotations = { ...prev, [activeLayer]: snapped };

      const isAligned = 
        nextRotations[RingLayer.OUTER] % 360 === 0 &&
        nextRotations[RingLayer.MIDDLE] % 360 === 0 &&
        nextRotations[RingLayer.INNER] % 360 === 0;

      if (isAligned) {
        setSolved(true);
        // Add a small delay for the "solved" feedback
      }
      return nextRotations;
    });
    setActiveLayer(null);
  }, [activeLayer]);

  useEffect(() => {
    if (activeLayer !== null) {
      window.addEventListener('mousemove', handleDragMove);
      window.addEventListener('mouseup', handleDragEnd);
      window.addEventListener('touchmove', handleDragMove, { passive: false });
      window.addEventListener('touchend', handleDragEnd);
    }
    return () => {
      window.removeEventListener('mousemove', handleDragMove);
      window.removeEventListener('mouseup', handleDragEnd);
      window.removeEventListener('touchmove', handleDragMove);
      window.removeEventListener('touchend', handleDragEnd);
    };
  }, [activeLayer, handleDragMove, handleDragEnd]);

  const initGame = async () => {
    setLoading(true);
    setSolved(false);
    setShowHint(false);
    const data = await generateSecretChallenge();
    setSecret(data);
    setRotations({
      [RingLayer.OUTER]: (Math.random() > 0.5 ? 120 : 240),
      [RingLayer.MIDDLE]: (Math.random() > 0.5 ? 120 : 240),
      [RingLayer.INNER]: (Math.random() > 0.5 ? 120 : 240),
    });
    setLoading(false);
  };

  useEffect(() => { initGame(); }, []);

  return (
    <div className="min-h-screen flex flex-col items-center justify-center p-4 text-stone-300 overflow-hidden relative select-none bg-[#0a0908]">
      {/* Immersive Church Ambience */}
      <div className="absolute inset-0 pointer-events-none">
        <div className="absolute top-0 left-0 w-full h-full bg-[radial-gradient(circle_at_50%_50%,rgba(60,20,20,0.15)_0%,transparent_100%)]"></div>

        {/* Stone Pillar Shadows */}
        <div className="absolute top-0 left-0 w-32 h-full bg-black/40 blur-3xl"></div>
        <div className="absolute top-0 right-0 w-32 h-full bg-black/40 blur-3xl"></div>

        {/* Flickering Candle Stations */}
        <div className="absolute bottom-10 left-[8%] hidden md:flex flex-col items-center opacity-80">
          <div className="w-8 h-80 bg-stone-400 rounded-t-xl shadow-2xl border-x border-stone-500"></div>
          <div className="w-16 h-16 bg-orange-600/40 candle-light rounded-full -mt-[310px] blur-3xl"></div>
          <Flame className="text-orange-500 absolute -top-12 animate-pulse" size={48} fill="currentColor" />
        </div>
        <div className="absolute bottom-10 right-[8%] hidden md:flex flex-col items-center opacity-80">
          <div className="w-8 h-80 bg-stone-400 rounded-t-xl shadow-2xl border-x border-stone-500"></div>
          <div className="w-16 h-16 bg-orange-600/40 candle-light rounded-full -mt-[310px] blur-3xl"></div>
          <Flame className="text-orange-500 absolute -top-12 animate-pulse" size={48} fill="currentColor" />
        </div>
      </div>

      <header className="text-center mb-8 z-10 flex flex-col items-center">
        <h1 className="text-3xl md:text-5xl font-bold tracking-[0.5em] text-stone-400 uppercase drop-shadow-[0_4px_12px_rgba(0,0,0,1)] mb-1 font-serif">
          ILLUMINADOS SEAL
        </h1>
        <div className="flex items-center gap-4 text-stone-600">
          <div className="h-px w-20 bg-stone-800"></div>
          <span className="text-[10px] tracking-[0.3em] uppercase italic">Sacrifice the Light</span>
          <div className="h-px w-20 bg-stone-800"></div>
        </div>
      </header>

      <div className="relative z-10 flex flex-col items-center">
        {/* Main Stained Glass Window Frame */}
        <div 
          ref={containerRef}
          className={`relative w-[340px] h-[340px] md:w-[540px] md:h-[540px] rounded-full border-[20px] border-[#1c1917] shadow-[0_0_120px_rgba(0,0,0,1),inset_0_0_60px_rgba(0,0,0,0.8)] overflow-hidden transition-all duration-1000 ${solved ? 'ring-[15px] ring-amber-500/20' : ''}`}
        >
          {/* Light Volume Effect */}
          {solved && (
            <div className="absolute inset-0 bg-gradient-to-b from-white/10 via-amber-500/10 to-transparent animate-pulse pointer-events-none mix-blend-overlay z-20"></div>
          )}

          <svg viewBox="0 0 500 500" className={`w-full h-full transition-all duration-2000 ${solved ? 'brightness-150 saturate-125' : 'brightness-75'}`}>
            <DialRing
              layer={RingLayer.OUTER}
              radius={185}
              thickness={75}
              rotation={rotations[RingLayer.OUTER]}
              color={COLORS[RingLayer.OUTER]}
              isActive={!solved}
              isDragging={activeLayer === RingLayer.OUTER}
              onDragStart={handleDragStart}
            />
            <DialRing
              layer={RingLayer.MIDDLE}
              radius={115}
              thickness={65}
              rotation={rotations[RingLayer.MIDDLE]}
              color={COLORS[RingLayer.MIDDLE]}
              isActive={!solved}
              isDragging={activeLayer === RingLayer.MIDDLE}
              onDragStart={handleDragStart}
            />
            <DialRing
              layer={RingLayer.INNER}
              radius={60}
              thickness={45}
              rotation={rotations[RingLayer.INNER]}
              color={COLORS[RingLayer.INNER]}
              isActive={!solved}
              isDragging={activeLayer === RingLayer.INNER}
              onDragStart={handleDragStart}
            />

            {/* The Plaga Center (Fixed Body) */}
            <g transform="translate(250, 250) scale(1.1)">
              {/* Central Parasite Body */}
              <path 
                d="M -5 -25 C -5 -35, 5 -35, 5 -25 L 5 15 L 0 45 L -5 15 Z" 
                fill={solved ? "#fbbf24" : "#22110a"} 
                stroke="#000" 
                strokeWidth="3"
                className="transition-colors duration-1000"
              />
              <circle r="8" fill={solved ? "#fff" : "#000"} stroke="#000" strokeWidth="2" className="transition-all duration-1000" />
            </g>

            {/* Static Iron Grid Overlays */}
            <circle cx="250" cy="250" r="235" fill="none" stroke="#000" strokeWidth="15" />
            <circle cx="250" cy="250" r="148" fill="none" stroke="#000" strokeWidth="8" opacity="0.9" />
            <circle cx="250" cy="250" r="82" fill="none" stroke="#000" strokeWidth="8" opacity="0.9" />

            {/* Cross Lines */}
            <g opacity="0.4">
              <line x1="250" y1="20" x2="250" y2="480" stroke="#000" strokeWidth="4" />
              <line x1="20" y1="250" x2="480" y2="250" stroke="#000" strokeWidth="4" />
            </g>
          </svg>
        </div>

        {/* Gothic UI Pedestal */}
        <div className="mt-12 w-full max-w-lg space-y-8 z-20">
          {solved ? (
            <div className="bg-[#1c1917]/90 border-2 border-amber-900/50 p-10 rounded-2xl shadow-[0_0_60px_rgba(217,119,6,0.2)] animate-in fade-in zoom-in duration-1000 backdrop-blur-xl text-center relative">
              <Sparkles className="absolute top-4 left-4 text-amber-500/40" size={20} />
              <Sparkles className="absolute bottom-4 right-4 text-amber-500/40" size={20} />

              <h2 className="text-amber-500 font-bold text-2xl mb-6 tracking-[0.4em] uppercase flex items-center justify-center gap-4">
                <Target size={28} /> ASCENSION GRANTED
              </h2>
              <div className="text-2xl font-medium text-stone-100 mb-10 py-8 px-6 bg-black/40 rounded-xl italic border border-stone-800 leading-relaxed shadow-inner">
                "{secret.secret}"
              </div>
              <button
                onClick={initGame}
                className="w-full bg-gradient-to-b from-stone-800 to-stone-900 hover:from-stone-700 hover:to-stone-800 text-amber-600 font-bold py-5 rounded-xl border border-amber-900/30 transition-all flex items-center justify-center gap-4 uppercase tracking-[0.2em] shadow-lg active:scale-95"
              >
                <RefreshCw size={22} className={loading ? "animate-spin" : ""} />
                RESET THE FAITH
              </button>
            </div>
          ) : (
            <div className="space-y-8">
              {/* Ornate Knob Selector */}
              <div className="flex justify-around items-center bg-stone-900/80 p-8 rounded-2xl border border-stone-800 shadow-[inset_0_2px_10px_rgba(0,0,0,0.5)]">
                {[
                  { layer: RingLayer.OUTER, color: 'bg-blue-800', label: 'AZURE' },
                  { layer: RingLayer.MIDDLE, color: 'bg-emerald-800', label: 'EMERALD' },
                  { layer: RingLayer.INNER, color: 'bg-rose-900', label: 'RUBY' }
                ].map((knob) => (
                  <div key={knob.layer} className="flex flex-col items-center gap-3">
                    <div 
                      className={`w-16 h-16 rounded-full border-4 ${activeLayer === knob.layer ? 'border-amber-500 shadow-[0_0_25px_rgba(245,158,11,0.5)]' : 'border-stone-800'} ${knob.color} transition-all transform ${activeLayer === knob.layer ? 'scale-115 rotate-12' : ''} cursor-pointer relative shadow-xl`}
                    >
                      <div className="absolute inset-2 border border-white/10 rounded-full"></div>
                      <div className="absolute top-1 left-1/2 -translate-x-1/2 w-1.5 h-4 bg-amber-400/80 rounded-full shadow-[0_0_5px_rgba(0,0,0,1)]"></div>
                    </div>
                    <span className={`text-[11px] font-bold tracking-[0.3em] ${activeLayer === knob.layer ? 'text-amber-500' : 'text-stone-600'}`}>{knob.label}</span>
                  </div>
                ))}
              </div>

              <div className="flex gap-4">
                <button
                  onClick={() => setShowHint(!showHint)}
                  className="flex-1 p-5 bg-stone-900/90 hover:bg-stone-800 text-stone-400 rounded-xl border border-stone-800 transition-all flex items-center justify-center gap-3 text-sm font-bold tracking-[0.3em] uppercase group"
                >
                  <Eye size={20} className="group-hover:text-amber-500 transition-colors" />
                  {showHint ? "CONCEAL TRUTH" : "REVEAL TRUTH"}
                </button>
              </div>

              {showHint && (
                <div className="p-6 bg-amber-950/10 border-l-4 border-amber-900/50 text-amber-200/50 text-center text-sm italic animate-in slide-in-from-left duration-700 font-serif tracking-wide">
                  "{secret.hint}"
                </div>
              )}
            </div>
          )}
        </div>
      </div>

      {loading && !solved && (
        <div className="fixed inset-0 bg-[#050505]/98 z-50 flex flex-col items-center justify-center space-y-10 backdrop-blur-2xl">
          <div className="relative">
            <div className="w-24 h-24 border-4 border-stone-900 rounded-full"></div>
            <div className="absolute inset-0 border-4 border-t-amber-800 border-r-transparent rounded-full animate-spin"></div>
            <Shield size={32} className="absolute inset-0 m-auto text-amber-950/40 animate-pulse" />
          </div>
          <div className="flex flex-col items-center gap-2">
            <p className="text-amber-900/60 font-bold tracking-[0.6em] text-xs animate-pulse">GLORY TO LAS ILLUMINADOS</p>
            <div className="h-0.5 w-48 bg-gradient-to-r from-transparent via-amber-950/30 to-transparent"></div>
          </div>
        </div>
      )}

      <footer className="mt-12 text-stone-800/40 text-[10px] tracking-[0.6em] uppercase z-10 font-bold font-serif">
        EST. 2005 • LOS ILLUMINADOS ORDER
      </footer>
    </div>
  );
};

export default App;

import React from 'react';
import { RingLayer } from '../types';

interface DialRingProps {
  layer: RingLayer;
  radius: number;
  thickness: number;
  rotation: number;
  color: string;
  isActive: boolean;
  isDragging: boolean;
  onDragStart: (layer: RingLayer, e: React.MouseEvent | React.TouchEvent) => void;
}

const DialRing: React.FC<DialRingProps> = ({
  layer,
  radius,
  thickness,
  rotation,
  color,
  isActive,
  isDragging,
  onDragStart
}) => {
  const innerR = radius - thickness / 2;
  const outerR = radius + thickness / 2;
  const clipId = `clip-${layer}`;
  const gradId = `grad-${layer}`;
  const shadowId = `shadow-${layer}`;

  // This Master Symbol represents the full intricate stained glass window content
  const MasterSymbol = () => (
    <g>
      <defs>
        <radialGradient id={gradId} cx="50%" cy="50%" r="60%">
          <stop offset="0%" stopColor={color} stopOpacity="0.9" />
          <stop offset="70%" stopColor={color} stopOpacity="0.4" />
          <stop offset="100%" stopColor="#000" stopOpacity="0.6" />
        </radialGradient>
        <filter id={shadowId}>
          <feDropShadow dx="0" dy="0" stdDeviation="2" floodOpacity="0.8" />
        </filter>
      </defs>

      {/* Decorative Mosaic Backdrop - making it feel like real glass shards */}
      <g opacity="0.15">
        {[...Array(16)].map((_, i) => (
          <line 
            key={i}
            x1="250" y1="250" 
            x2={250 + 400 * Math.cos(i * 22.5 * Math.PI / 180)} 
            y2={250 + 400 * Math.sin(i * 22.5 * Math.PI / 180)} 
            stroke="black" strokeWidth="3" 
          />
        ))}
        <circle cx="250" cy="250" r="140" fill="none" stroke="black" strokeWidth="2" />
        <circle cx="250" cy="250" r="190" fill="none" stroke="black" strokeWidth="2" />
      </g>

      {/* Intricate Plaga Parasite Symbol - Detailed Geometry */}
      <g transform="translate(250, 250) scale(4.2)">
        {/* WINGS - Primary Shape with intricate curves */}
        <path 
          d="M 0 -42 
             C -15 -42, -35 -30, -50 -15
             C -65 5, -55 25, -40 35
             C -30 42, -15 35, -8 20
             L 0 10 L 8 20
             C 15 35, 30 42, 40 35
             C 55 25, 65 5, 50 -15
             C 35 -30, 15 -42, 0 -42" 
          fill={`url(#${gradId})`}
          stroke="black"
          strokeWidth="1.2"
          style={{ filter: `url(#${shadowId})` }}
        />

        {/* Wing "Veins" - Inner detail shards */}
        <g opacity="0.7" stroke="black" strokeWidth="0.4" fill="none">
          <path d="M -10 -35 Q -30 -30, -45 -10" />
          <path d="M -8 -30 Q -25 -20, -38 5" />
          <path d="M -5 -25 Q -20 -10, -30 15" />
          <path d="M 10 -35 Q 30 -30, 45 -10" />
          <path d="M 8 -30 Q 25 -20, 38 5" />
          <path d="M 5 -25 Q 20 -10, 30 15" />
          {/* Horizontal connecting veins */}
          <path d="M -25 -30 Q -20 -20, -15 -10" />
          <path d="M 25 -30 Q 20 -20, 15 -10" />
        </g>

        {/* APPENDAGES - Segmented legs */}
        <g stroke="black" strokeWidth="1.8" strokeLinecap="round" fill="none">
          <path d="M -6 -18 L -20 -15 L -25 -25" />
          <path d="M -6 -8 L -24 -8 L -30 0" />
          <path d="M -6 5 L -22 18 L -20 28" />
          <path d="M 6 -18 L 20 -15 L 25 -25" />
          <path d="M 6 -8 L 24 -8 L 30 0" />
          <path d="M 6 5 L 22 18 L 20 28" />
        </g>

        {/* CENTRAL BODY - Multi-segmented core */}
        <g stroke="black" strokeWidth="0.8">
          {/* Head Segment */}
          <path 
            d="M 0 -42 C -5 -42, -8 -38, -8 -32 C -8 -26, -5 -20, 0 -18 C 5 -20, 8 -26, 8 -32 C 8 -38, 5 -42, 0 -42" 
            fill="rgba(20,10,5,0.8)" 
          />
          {/* Thorax and Abdomen */}
          <path d="M -4 -18 L -6 5 L 0 38 L 6 5 L 4 -18 Z" fill="rgba(10,5,2,0.9)" />
          {/* Ribbed detail on body */}
          {[...Array(6)].map((_, i) => (
            <line key={i} x1="-3" y1={-15 + i * 8} x2="3" y2={-15 + i * 8} stroke="rgba(255,255,255,0.1)" strokeWidth="0.5" />
          ))}
        </g>

        {/* Glowing Eyes / Jewels */}
        <circle cx="-3" cy="-34" r="1.2" fill={color} filter="blur(0.5px)" />
        <circle cx="3" cy="-34" r="1.2" fill={color} filter="blur(0.5px)" />
      </g>
    </g>
  );

  return (
    <g 
      className="cursor-grab active:cursor-grabbing"
      onMouseDown={(e) => onDragStart(layer, e)}
      onTouchStart={(e) => onDragStart(layer, e)}
    >
      <defs>
        <clipPath id={clipId}>
          <path d={`
            M 250, ${250 - outerR}
            A ${outerR},${outerR} 0 1,1 250,${250 + outerR}
            A ${outerR},${outerR} 0 1,1 250,${250 - outerR}
            Z
            M 250, ${250 - innerR}
            A ${innerR},${innerR} 0 1,0 250,${250 + innerR}
            A ${innerR},${innerR} 0 1,0 250,${250 - innerR}
            Z
          `} fillRule="evenodd" />
        </clipPath>
      </defs>

      {/* Glass Layer */}
      <g clipPath={`url(#${clipId})`}>
        {/* Background depth for the glass */}
        <circle cx="250" cy="250" r={outerR} fill="#020617" />

        {/* Rotating Content */}
        <g style={{ 
          transform: `rotate(${rotation}deg)`, 
          transformOrigin: '250px 250px',
          transition: isDragging ? 'none' : 'transform 1.4s cubic-bezier(0.16, 1, 0.3, 1)'
        }}>
          <MasterSymbol />
        </g>

        {/* Fixed Reflection Overlay - giving it that "curved glass" look */}
        <ellipse 
          cx="250" cy="180" 
          rx={outerR * 0.9} ry={outerR * 0.4} 
          fill="white" fillOpacity="0.04" 
          transform="rotate(-15, 250, 250)" 
          pointerEvents="none" 
        />
        <circle cx="250" cy="250" r={outerR} fill="none" stroke="rgba(255,255,255,0.05)" strokeWidth="1" />
      </g>

      {/* Ornate Lead Caming (The black metal borders) */}
      <circle cx="250" cy="250" r={outerR} fill="none" stroke="#000" strokeWidth="6" />
      <circle cx="250" cy="250" r={innerR} fill="none" stroke="#000" strokeWidth="6" />

      {/* Decorative rivets on the lead lines */}
      {[...Array(8)].map((_, i) => (
        <circle 
          key={i}
          cx={250 + outerR * Math.cos(i * 45 * Math.PI / 180)}
          cy={250 + outerR * Math.sin(i * 45 * Math.PI / 180)}
          r="3" fill="#1c1917" stroke="#000" strokeWidth="1"
        />
      ))}
    </g>
  );
};

export default DialRing;

import React, { useState, useEffect, useCallback, useRef } from 'react';
import { RefreshCw, Flame, Eye, Target, Sparkles, ShieldCheck } from 'lucide-react';
import DialRing from './components/DialRing';
import { generateSecretChallenge } from './services/geminiService';
import { RingLayer } from './types';

const App: React.FC = () => {
  const [loading, setLoading] = useState(false);
  const [solved, setSolved] = useState(false);
  const [secret, setSecret] = useState({ secret: "", hint: "" });
  const [showHint, setShowHint] = useState(false);

  // Rich colors inspired by the RE4 Remake church puzzle
  const COLORS = {
    [RingLayer.OUTER]: "#1d4ed8", // Sapphire Blue
    [RingLayer.MIDDLE]: "#15803d", // Emerald Green
    [RingLayer.INNER]: "#b91c1c",  // Ruby Red
  };

  const [rotations, setRotations] = useState({
    [RingLayer.OUTER]: (Math.floor(Math.random() * 3) + 1) * 120,
    [RingLayer.MIDDLE]: (Math.floor(Math.random() * 3) + 1) * 120,
    [RingLayer.INNER]: (Math.floor(Math.random() * 3) + 1) * 120,
  });

  const [activeLayer, setActiveLayer] = useState<RingLayer | null>(null);
  const dragStartAngle = useRef<number>(0);
  const initialRotation = useRef<number>(0);
  const containerRef = useRef<HTMLDivElement>(null);

  const getAngle = (clientX: number, clientY: number) => {
    if (!containerRef.current) return 0;
    const rect = containerRef.current.getBoundingClientRect();
    const centerX = rect.left + rect.width / 2;
    const centerY = rect.top + rect.height / 2;
    const dx = clientX - centerX;
    const dy = clientY - centerY;
    return Math.atan2(dy, dx) * (180 / Math.PI);
  };

  const handleDragStart = (layer: RingLayer, e: React.MouseEvent | React.TouchEvent) => {
    if (solved || loading) return;
    const clientX = 'touches' in e ? e.touches[0].clientX : e.clientX;
    const clientY = 'touches' in e ? e.touches[0].clientY : e.clientY;
    setActiveLayer(layer);
    dragStartAngle.current = getAngle(clientX, clientY);
    initialRotation.current = rotations[layer];
  };

  const handleDragMove = useCallback((e: MouseEvent | TouchEvent) => {
    if (activeLayer === null || solved) return;
    const clientX = 'touches' in e ? (e as TouchEvent).touches[0].clientX : (e as MouseEvent).clientX;
    const clientY = 'touches' in e ? (e as TouchEvent).touches[0].clientY : (e as MouseEvent).clientY;
    const currentAngle = getAngle(clientX, clientY);
    const angleDiff = currentAngle - dragStartAngle.current;

    setRotations(prev => ({ ...prev, [activeLayer]: initialRotation.current + angleDiff }));
  }, [activeLayer, solved]);

  const handleDragEnd = useCallback(() => {
    if (activeLayer === null) return;
    setRotations(prev => {
      const currentRot = prev[activeLayer];
      const snapped = Math.round(currentRot / 120) * 120;
      const nextRotations = { ...prev, [activeLayer]: snapped };

      const isAligned = 
        nextRotations[RingLayer.OUTER] % 360 === 0 &&
        nextRotations[RingLayer.MIDDLE] % 360 === 0 &&
        nextRotations[RingLayer.INNER] % 360 === 0;

      if (isAligned) setSolved(true);
      return nextRotations;
    });
    setActiveLayer(null);
  }, [activeLayer]);

  useEffect(() => {
    if (activeLayer !== null) {
      window.addEventListener('mousemove', handleDragMove);
      window.addEventListener('mouseup', handleDragEnd);
      window.addEventListener('touchmove', handleDragMove, { passive: false });
      window.addEventListener('touchend', handleDragEnd);
    }
    return () => {
      window.removeEventListener('mousemove', handleDragMove);
      window.removeEventListener('mouseup', handleDragEnd);
      window.removeEventListener('touchmove', handleDragMove);
      window.removeEventListener('touchend', handleDragEnd);
    };
  }, [activeLayer, handleDragMove, handleDragEnd]);

  const initGame = async () => {
    setLoading(true);
    setSolved(false);
    setShowHint(false);
    const data = await generateSecretChallenge();
    setSecret(data);
    setRotations({
      [RingLayer.OUTER]: (Math.random() > 0.5 ? 120 : 240),
      [RingLayer.MIDDLE]: (Math.random() > 0.5 ? 120 : 240),
      [RingLayer.INNER]: (Math.random() > 0.5 ? 120 : 240),
    });
    setLoading(false);
  };

  useEffect(() => { initGame(); }, []);

  return (
    <div className="min-h-screen flex flex-col items-center justify-center p-4 text-stone-300 overflow-hidden relative select-none bg-[#050505]">
      {/* Immersive Environment */}
      <div className="absolute inset-0 pointer-events-none">
        {/* Stone Wall Textures */}
        <div className="absolute inset-0 opacity-10 bg-[url('https://www.transparenttextures.com/patterns/dark-leather.png')]"></div>
        <div className="absolute inset-0 bg-[radial-gradient(circle_at_50%_50%,rgba(120,40,40,0.1)_0%,transparent_100%)]"></div>

        {/* Curtains & Pillars */}
        <div className="absolute top-0 left-0 w-48 h-full bg-gradient-to-r from-stone-950 to-transparent blur-2xl"></div>
        <div className="absolute top-0 right-0 w-48 h-full bg-gradient-to-l from-stone-950 to-transparent blur-2xl"></div>

        {/* Flickering Candles (Desktop) */}
        <div className="absolute bottom-10 left-[5%] hidden lg:flex flex-col items-center opacity-60">
          <div className="w-10 h-96 bg-stone-700 rounded-t-2xl shadow-2xl border-x border-stone-800 relative">
            <div className="absolute inset-0 bg-gradient-to-b from-transparent to-stone-900/50"></div>
          </div>
          <div className="w-20 h-20 bg-orange-600/30 candle-light rounded-full -mt-[340px] blur-3xl"></div>
          <Flame className="text-orange-500 absolute -top-14 animate-pulse" size={56} fill="currentColor" />
        </div>
        <div className="absolute bottom-10 right-[5%] hidden lg:flex flex-col items-center opacity-60">
          <div className="w-10 h-96 bg-stone-700 rounded-t-2xl shadow-2xl border-x border-stone-800 relative">
            <div className="absolute inset-0 bg-gradient-to-b from-transparent to-stone-900/50"></div>
          </div>
          <div className="w-20 h-20 bg-orange-600/30 candle-light rounded-full -mt-[340px] blur-3xl"></div>
          <Flame className="text-orange-500 absolute -top-14 animate-pulse" size={56} fill="currentColor" />
        </div>
      </div>

      <header className="text-center mb-10 z-10">
        <h1 className="text-4xl md:text-6xl font-bold tracking-[0.6em] text-stone-500 uppercase drop-shadow-[0_10px_20px_rgba(0,0,0,1)] mb-2 font-serif">
          ILLUMINADOS
        </h1>
        <div className="flex items-center justify-center gap-6 text-stone-700">
          <div className="h-[2px] w-24 bg-gradient-to-r from-transparent to-stone-800"></div>
          <span className="text-xs tracking-[0.4em] uppercase font-bold">The Great Seal</span>
          <div className="h-[2px] w-24 bg-gradient-to-l from-transparent to-stone-800"></div>
        </div>
      </header>

      <div className="relative z-10 flex flex-col items-center">
        {/* High-End Stained Glass Window Component */}
        <div 
          ref={containerRef}
          className={`relative w-[340px] h-[340px] md:w-[580px] md:h-[580px] rounded-full border-[22px] border-[#1c1917] shadow-[0_0_150px_rgba(0,0,0,1),inset_0_0_80px_rgba(0,0,0,0.9)] overflow-hidden transition-all duration-1000 ${solved ? 'ring-[20px] ring-amber-500/10' : ''}`}
        >
          {/* Volumetric Light Shafts when Solved */}
          {solved && (
            <div className="absolute inset-0 overflow-hidden pointer-events-none z-20">
              <div className="absolute inset-0 bg-gradient-to-b from-white/15 via-amber-500/10 to-transparent animate-pulse mix-blend-screen scale-150 -rotate-12"></div>
              <div className="absolute inset-0 bg-gradient-to-b from-transparent via-amber-200/5 to-transparent animate-pulse delay-500 mix-blend-overlay scale-150 rotate-12"></div>
            </div>
          )}

          <svg viewBox="0 0 500 500" className={`w-full h-full transition-all duration-[3000ms] ${solved ? 'brightness-150 saturate-[1.4]' : 'brightness-[0.85]'}`}>
            <DialRing
              layer={RingLayer.OUTER}
              radius={190}
              thickness={78}
              rotation={rotations[RingLayer.OUTER]}
              color={COLORS[RingLayer.OUTER]}
              isActive={!solved}
              isDragging={activeLayer === RingLayer.OUTER}
              onDragStart={handleDragStart}
            />
            <DialRing
              layer={RingLayer.MIDDLE}
              radius={118}
              thickness={68}
              rotation={rotations[RingLayer.MIDDLE]}
              color={COLORS[RingLayer.MIDDLE]}
              isActive={!solved}
              isDragging={activeLayer === RingLayer.MIDDLE}
              onDragStart={handleDragStart}
            />
            <DialRing
              layer={RingLayer.INNER}
              radius={60}
              thickness={50}
              rotation={rotations[RingLayer.INNER]}
              color={COLORS[RingLayer.INNER]}
              isActive={!solved}
              isDragging={activeLayer === RingLayer.INNER}
              onDragStart={handleDragStart}
            />

            {/* The Central Parasite Spine (Fixed Piece) */}
            <g transform="translate(250, 250) scale(1.2)">
              <path 
                d="M -4 -25 C -4 -32, 4 -32, 4 -25 L 4 10 L 0 42 L -4 10 Z" 
                fill={solved ? "#fbbf24" : "#1a0f0a"} 
                stroke="#000" 
                strokeWidth="2.5"
                className="transition-colors duration-1000"
              />
              <circle r="6" fill={solved ? "#fff" : "#000"} stroke="#000" strokeWidth="1.5" className="transition-all duration-1000 shadow-xl" />
            </g>

            {/* Heavily Ornate Stone Gridwork Overlays */}
            <circle cx="250" cy="250" r="238" fill="none" stroke="#000" strokeWidth="18" />
            <circle cx="250" cy="250" r="150" fill="none" stroke="#000" strokeWidth="10" opacity="0.95" />
            <circle cx="250" cy="250" r="82" fill="none" stroke="#000" strokeWidth="10" opacity="0.95" />

            {/* Iron Cross Grille */}
            <g opacity="0.3">
              <line x1="250" y1="12" x2="250" y2="488" stroke="#000" strokeWidth="6" />
              <line x1="12" y1="250" x2="488" y2="250" stroke="#000" strokeWidth="6" />
              <circle cx="250" cy="250" r="248" fill="none" stroke="#1c1917" strokeWidth="4" />
            </g>
          </svg>
        </div>

        {/* Gothic Pedestal Console */}
        <div className="mt-14 w-full max-w-lg space-y-10 z-20">
          {solved ? (
            <div className="bg-[#1c1917]/95 border-2 border-amber-900/40 p-12 rounded-3xl shadow-[0_0_100px_rgba(217,119,6,0.25)] animate-in fade-in zoom-in duration-1000 backdrop-blur-2xl text-center relative overflow-hidden group">
              <div className="absolute inset-0 bg-gradient-to-tr from-amber-500/5 to-transparent opacity-0 group-hover:opacity-100 transition-opacity"></div>
              <Sparkles className="absolute top-6 left-6 text-amber-500/30 animate-pulse" size={24} />
              <ShieldCheck className="absolute top-6 right-6 text-amber-500/30 animate-pulse" size={24} />

              <h2 className="text-amber-500 font-bold text-3xl mb-8 tracking-[0.5em] uppercase flex items-center justify-center gap-5 font-serif">
                THE WAY IS OPEN
              </h2>
              <div className="text-2xl font-medium text-stone-100 mb-12 py-10 px-8 bg-black/50 rounded-2xl italic border border-stone-800 leading-relaxed shadow-inner font-serif">
                "{secret.secret}"
              </div>
              <button
                onClick={initGame}
                className="w-full bg-gradient-to-b from-stone-800 to-stone-900 hover:from-stone-700 hover:to-stone-800 text-amber-600 font-bold py-6 rounded-2xl border border-amber-900/30 transition-all flex items-center justify-center gap-5 uppercase tracking-[0.3em] shadow-2xl active:scale-[0.98] relative overflow-hidden group"
              >
                <div className="absolute inset-0 bg-amber-500/5 opacity-0 group-hover:opacity-100 transition-opacity"></div>
                <RefreshCw size={24} className={loading ? "animate-spin" : ""} />
                RESTART THE CEREMONY
              </button>
            </div>
          ) : (
            <div className="space-y-10">
              {/* Tactical Knob Selection UI */}
              <div className="flex justify-around items-center bg-stone-900/90 p-10 rounded-3xl border border-stone-800/50 shadow-[0_20px_50px_rgba(0,0,0,0.5),inset_0_2px_15px_rgba(0,0,0,0.5)]">
                {[
                  { layer: RingLayer.OUTER, color: 'bg-blue-900', label: 'SAPPHIRE' },
                  { layer: RingLayer.MIDDLE, color: 'bg-emerald-900', label: 'EMERALD' },
                  { layer: RingLayer.INNER, color: 'bg-red-950', label: 'RUBY' }
                ].map((knob) => (
                  <div key={knob.layer} className="flex flex-col items-center gap-4">
                    <div 
                      className={`w-20 h-20 rounded-full border-[6px] transition-all transform duration-500 ${activeLayer === knob.layer ? 'border-amber-500 scale-125 -rotate-12 shadow-[0_0_40px_rgba(245,158,11,0.6)] ring-4 ring-amber-500/20' : 'border-stone-800'} ${knob.color} cursor-pointer relative shadow-2xl overflow-hidden`}
                    >
                      <div className="absolute inset-2 border-2 border-white/5 rounded-full"></div>
                      <div className="absolute top-2 left-1/2 -translate-x-1/2 w-2.5 h-6 bg-amber-500/80 rounded-full shadow-[0_0_10px_rgba(0,0,0,1)]"></div>
