import React, { useMemo } from 'react';
import { RingLayer } from '../types';

interface DialRingProps {
  layer: RingLayer;
  radius: number;
  thickness: number;
  rotation: number;
  color: string;
  isActive: boolean;
  isDragging: boolean;
  onDragStart: (layer: RingLayer, e: React.MouseEvent | React.TouchEvent) => void;
}

const DialRing: React.FC<DialRingProps> = ({
  layer,
  radius,
  thickness,
  rotation,
  color,
  isActive,
  isDragging,
  onDragStart
}) => {
  const innerR = radius - thickness / 2;
  const outerR = radius + thickness / 2;
  const clipId = `clip-${layer}`;

  // สร้างชิ้นส่วน Mosaic แบบสุ่มแต่คงที่สำหรับแต่ละวงแหวน
  const shards = useMemo(() => {
    const shardList = [];
    const radialSegments = 3; // แบ่งตามความหนา 3 ชั้นย่อย
    const angularSegments = 24; // แบ่งรอบวง 24 ชิ้น

    for (let r = 0; r < radialSegments; r++) {
      for (let a = 0; a < angularSegments; a++) {
        const rStart = innerR + (r * (thickness / radialSegments));
        const rEnd = innerR + ((r + 1) * (thickness / radialSegments));
        const aStart = (a * (360 / angularSegments)) * (Math.PI / 180);
        const aEnd = ((a + 1) * (360 / angularSegments)) * (Math.PI / 180);

        // คำนวณจุดยอดของสี่เหลี่ยมคางหมูโค้ง (Trapazoid-ish shard)
        const p1x = 250 + rStart * Math.cos(aStart);
        const p1y = 250 + rStart * Math.sin(aStart);
        const p2x = 250 + rEnd * Math.cos(aStart);
        const p2y = 250 + rEnd * Math.sin(aStart);
        const p3x = 250 + rEnd * Math.cos(aEnd);
        const p3y = 250 + rEnd * Math.sin(aEnd);
        const p4x = 250 + rStart * Math.cos(aEnd);
        const p4y = 250 + rStart * Math.sin(aEnd);

        shardList.push({
          d: `M ${p1x} ${p1y} L ${p2x} ${p2y} A ${rEnd} ${rEnd} 0 0 1 ${p3x} ${p3y} L ${p4x} ${p4y} A ${rStart} ${rStart} 0 0 0 ${p1x} ${p1y} Z`,
          opacity: 0.3 + Math.random() * 0.4
        });
      }
    }
    return shardList;
  }, [innerR, thickness]);

  // สัญลักษณ์หลักที่จะถูกวาดทับลงบน Mosaic
  const SymbolOverlay = () => (
    <g transform="translate(250, 250) scale(4.5)">
      {/* วาดโครงร่างสัญลักษณ์ด้วยเส้นตะกั่วหนาๆ */}
      <path 
        d="M 0 -40 C -15 -40, -40 -20, -50 0 C -60 20, -45 35, -30 40 C -20 45, -10 30, 0 15 C 10 30, 20 45, 30 40 C 45 35, 60 20, 50 0 C 40 -20, 15 -40, 0 -40" 
        fill={color} 
        fillOpacity="0.7"
        stroke="#000" 
        strokeWidth="1.5"
      />
      {/* รายละเอียดเส้นข้างในของปีก */}
      <path d="M -15 -30 L -35 -10 M -10 -25 L -30 5 M 15 -30 L 35 -10 M 10 -25 L 30 5" stroke="#000" strokeWidth="1" opacity="0.8" />
      {/* ลำตัวแมลงส่วนกลาง */}
      <path d="M -5 -35 Q -8 -20, 0 45 Q 8 -20, 5 -35 Z" fill="#1a0f0a" stroke="#000" strokeWidth="1.5" />
      <circle cy="-32" r="4" fill="#000" />
    </g>
  );

  return (
    <g 
      className="cursor-grab active:cursor-grabbing"
      onMouseDown={(e) => onDragStart(layer, e)}
      onTouchStart={(e) => onDragStart(layer, e)}
    >
      <defs>
        <clipPath id={clipId}>
          <path d={`
            M 250, ${250 - outerR}
            A ${outerR},${outerR} 0 1,1 250,${250 + outerR}
            A ${outerR},${outerR} 0 1,1 250,${250 - outerR}
            Z
            M 250, ${250 - innerR}
            A ${innerR},${innerR} 0 1,0 250,${250 + innerR}
            A ${innerR},${innerR} 0 1,0 250,${250 - innerR}
            Z
          `} fillRule="evenodd" />
        </clipPath>
      </defs>

      {/* พื้นที่กระจกพร้อมเอฟเฟกต์ Mosaic */}
      <g clipPath={`url(#${clipId})`}>
        <circle cx="250" cy="250" r={outerR} fill="#050505" />

        {/* กลุ่มชิ้นส่วนที่หมุนได้ */}
        <g style={{ 
          transform: `rotate(${rotation}deg)`, 
          transformOrigin: '250px 250px',
          transition: isDragging ? 'none' : 'transform 1.2s cubic-bezier(0.19, 1, 0.22, 1)'
        }}>
          {/* พื้นหลัง Mosaic Shards */}
          <g>
            {shards.map((shard, i) => (
              <path 
                key={i} 
                d={shard.d} 
                fill={color} 
                fillOpacity={shard.opacity} 
                stroke="#000" 
                strokeWidth="0.8"
              />
            ))}
          </g>

          {/* สัญลักษณ์ที่ประกอบขึ้นจากกระจกชิ้นใหญ่กว่า */}
          <SymbolOverlay />
        </g>

        {/* เอฟเฟกต์แสงสะท้อนบนผิวกระจกที่นิ่งอยู่กับที่ */}
        <circle cx="250" cy="250" r={outerR} fill="none" stroke="rgba(255,255,255,0.05)" strokeWidth="2" />
        <ellipse cx="200" cy="200" rx={outerR * 0.8} ry={outerR * 0.3} fill="white" fillOpacity="0.03" transform="rotate(-45, 250, 250)" />
      </g>

      {/* เส้นตะกั่วกั้นระหว่างวงแหวน */}
      <circle cx="250" cy="250" r={outerR} fill="none" stroke="#000" strokeWidth="6" />
      <circle cx="250" cy="250" r={innerR} fill="none" stroke="#000" strokeWidth="6" />
    </g>
  );
};

export default DialRing;

import React, { useState, useEffect, useCallback, useRef } from 'react';
import { RefreshCw, Eye, Sparkles, ShieldCheck, Flame } from 'lucide-react';
import DialRing from './components/DialRing';
import { generateSecretChallenge } from './services/geminiService';
import { RingLayer } from './types';

const App: React.FC = () => {
  const [loading, setLoading] = useState(false);
  const [solved, setSolved] = useState(false);
  const [secret, setSecret] = useState({ secret: "", hint: "" });
  const [showHint, setShowHint] = useState(false);

  // สีสันที่สดใสสไตล์กระจกสีมหาวิหาร
  const COLORS = {
    [RingLayer.OUTER]: "#1e40af", // Deep Sapphire
    [RingLayer.MIDDLE]: "#166534", // Forest Emerald
    [RingLayer.INNER]: "#991b1b",  // Blood Ruby
  };

  const [rotations, setRotations] = useState({
    [RingLayer.OUTER]: (Math.floor(Math.random() * 3) + 1) * 120,
    [RingLayer.MIDDLE]: (Math.floor(Math.random() * 3) + 1) * 120,
    [RingLayer.INNER]: (Math.floor(Math.random() * 3) + 1) * 120,
  });

  const [activeLayer, setActiveLayer] = useState<RingLayer | null>(null);
  const dragStartAngle = useRef<number>(0);
  const initialRotation = useRef<number>(0);
  const containerRef = useRef<HTMLDivElement>(null);

  const getAngle = (clientX: number, clientY: number) => {
    if (!containerRef.current) return 0;
    const rect = containerRef.current.getBoundingClientRect();
    const centerX = rect.left + rect.width / 2;
    const centerY = rect.top + rect.height / 2;
    const dx = clientX - centerX;
    const dy = clientY - centerY;
    return Math.atan2(dy, dx) * (180 / Math.PI);
  };

  const handleDragStart = (layer: RingLayer, e: React.MouseEvent | React.TouchEvent) => {
    if (solved || loading) return;
    const clientX = 'touches' in e ? e.touches[0].clientX : e.clientX;
    const clientY = 'touches' in e ? e.touches[0].clientY : e.clientY;
    setActiveLayer(layer);
    dragStartAngle.current = getAngle(clientX, clientY);
    initialRotation.current = rotations[layer];
  };

  const handleDragMove = useCallback((e: MouseEvent | TouchEvent) => {
    if (activeLayer === null || solved) return;
    const clientX = 'touches' in e ? (e as TouchEvent).touches[0].clientX : (e as MouseEvent).clientX;
    const clientY = 'touches' in e ? (e as TouchEvent).touches[0].clientY : (e as MouseEvent).clientY;
    const currentAngle = getAngle(clientX, clientY);
    const angleDiff = currentAngle - dragStartAngle.current;

    setRotations(prev => ({ ...prev, [activeLayer]: initialRotation.current + angleDiff }));
  }, [activeLayer, solved]);

  const handleDragEnd = useCallback(() => {
    if (activeLayer === null) return;
    setRotations(prev => {
      const currentRot = prev[activeLayer];
      const snapped = Math.round(currentRot / 120) * 120;
      const nextRotations = { ...prev, [activeLayer]: snapped };

      const isAligned = 
        nextRotations[RingLayer.OUTER] % 360 === 0 &&
        nextRotations[RingLayer.MIDDLE] % 360 === 0 &&
        nextRotations[RingLayer.INNER] % 360 === 0;

      if (isAligned) setSolved(true);
      return nextRotations;
    });
    setActiveLayer(null);
  }, [activeLayer]);

  useEffect(() => {
    if (activeLayer !== null) {
      window.addEventListener('mousemove', handleDragMove);
      window.addEventListener('mouseup', handleDragEnd);
      window.addEventListener('touchmove', handleDragMove, { passive: false });
      window.addEventListener('touchend', handleDragEnd);
    }
    return () => {
      window.removeEventListener('mousemove', handleDragMove);
      window.removeEventListener('mouseup', handleDragEnd);
      window.removeEventListener('touchmove', handleDragMove);
      window.removeEventListener('touchend', handleDragEnd);
    };
  }, [activeLayer, handleDragMove, handleDragEnd]);

  const initGame = async () => {
    setLoading(true);
    setSolved(false);
    setShowHint(false);
    const data = await generateSecretChallenge();
    setSecret(data);
    setRotations({
      [RingLayer.OUTER]: (Math.random() > 0.5 ? 120 : 240),
      [RingLayer.MIDDLE]: (Math.random() > 0.5 ? 120 : 240),
      [RingLayer.INNER]: (Math.random() > 0.5 ? 120 : 240),
    });
    setLoading(false);
  };

  useEffect(() => { initGame(); }, []);

  return (
    <div className="min-h-screen flex flex-col items-center justify-center p-4 text-stone-300 overflow-hidden relative select-none bg-[#0a0a0a]">
      {/* Background Ambience */}
      <div className="absolute inset-0 pointer-events-none">
        <div className="absolute inset-0 bg-[radial-gradient(circle_at_50%_50%,rgba(80,20,20,0.1)_0%,transparent_100%)] opacity-50"></div>
        {/* Stone Pillar Shadows */}
        <div className="absolute top-0 left-0 w-64 h-full bg-gradient-to-r from-black to-transparent opacity-80 blur-3xl"></div>
        <div className="absolute top-0 right-0 w-64 h-full bg-gradient-to-l from-black to-transparent opacity-80 blur-3xl"></div>
      </div>

      <header className="text-center mb-12 z-10 font-serif">
        <h1 className="text-4xl md:text-6xl font-bold tracking-[0.4em] text-stone-500 uppercase drop-shadow-[0_10px_30px_rgba(0,0,0,1)] mb-4">
          MOSAIC OF FAITH
        </h1>
        <p className="text-[10px] tracking-[0.5em] text-stone-700 uppercase">Align the glass shards to reveal the truth</p>
      </header>

      <div className="relative z-10 flex flex-col items-center">
        {/* The Stained Glass Window Component */}
        <div 
          ref={containerRef}
          className={`relative w-[320px] h-[320px] md:w-[560px] md:h-[560px] rounded-full border-[24px] border-[#18181b] shadow-[0_0_100px_rgba(0,0,0,1),inset_0_0_50px_rgba(0,0,0,0.9)] overflow-hidden transition-all duration-1000 ${solved ? 'ring-[20px] ring-blue-500/10 scale-105' : ''}`}
        >
          {/* แสงลอดผ่านกระจกเมื่อประกอบเสร็จ */}
          {solved && (
            <div className="absolute inset-0 z-20 pointer-events-none">
              <div className="absolute inset-0 bg-gradient-to-b from-blue-400/20 via-transparent to-red-400/20 mix-blend-screen animate-pulse"></div>
              <div className="absolute inset-0 bg-[radial-gradient(circle_at_50%_50%,rgba(255,255,255,0.1)_0%,transparent_100%)]"></div>
            </div>
          )}

          <svg viewBox="0 0 500 500" className={`w-full h-full transition-all duration-[2000ms] ${solved ? 'brightness-125 saturate-150' : 'brightness-75'}`}>
            <DialRing
              layer={RingLayer.OUTER}
              radius={195}
              thickness={85}
              rotation={rotations[RingLayer.OUTER]}
              color={COLORS[RingLayer.OUTER]}
              isActive={!solved}
              isDragging={activeLayer === RingLayer.OUTER}
              onDragStart={handleDragStart}
            />
            <DialRing
              layer={RingLayer.MIDDLE}
              radius={120}
              thickness={75}
              rotation={rotations[RingLayer.MIDDLE]}
              color={COLORS[RingLayer.MIDDLE]}
              isActive={!solved}
              isDragging={activeLayer === RingLayer.MIDDLE}
              onDragStart={handleDragStart}
            />
            <DialRing
              layer={RingLayer.INNER}
              radius={60}
              thickness={65}
              rotation={rotations[RingLayer.INNER]}
              color={COLORS[RingLayer.INNER]}
              isActive={!solved}
              isDragging={activeLayer === RingLayer.INNER}
              onDragStart={handleDragStart}
            />

            {/* แกนกลางคงที่ */}
            <g transform="translate(250, 250)">
              <circle r="12" fill="#000" />
              <path d="M -2 -15 L 2 -15 L 0 35 Z" fill={solved ? "#fbbf24" : "#221a0f"} />
            </g>

            {/* โครงสร้างตะกั่วภายนอก */}
            <circle cx="250" cy="250" r="238" fill="none" stroke="#000" strokeWidth="12" />
            <circle cx="250" cy="250" r="148" fill="none" stroke="#000" strokeWidth="8" opacity="0.9" />
            <circle cx="250" cy="250" r="82" fill="none" stroke="#000" strokeWidth="8" opacity="0.9" />
          </svg>
        </div>

        {/* UI Controls */}
        <div className="mt-16 w-full max-w-lg space-y-10 z-20">
          {solved ? (
            <div className="bg-[#121212]/95 border-2 border-stone-800 p-10 rounded-3xl shadow-2xl animate-in fade-in zoom-in duration-1000 backdrop-blur-3xl text-center relative overflow-hidden group">
              <Sparkles className="absolute top-6 left-6 text-blue-500/30 animate-pulse" size={24} />
              <ShieldCheck className="absolute bottom-6 right-6 text-red-500/30 animate-pulse" size={24} />

              <h2 className="text-stone-100 font-bold text-3xl mb-8 tracking-[0.4em] uppercase font-serif">
                REVELATION
              </h2>
              <div className="text-xl md:text-2xl font-medium text-stone-300 mb-12 py-10 px-8 bg-black/40 rounded-2xl italic border border-stone-900 leading-relaxed shadow-inner">
                "{secret.secret}"
              </div>
              <button
                onClick={initGame}
                className="w-full bg-stone-900 hover:bg-stone-800 text-stone-100 font-bold py-5 rounded-2xl border border-stone-800 transition-all flex items-center justify-center gap-4 uppercase tracking-[0.3em] active:scale-95"
              >
                <RefreshCw size={22} className={loading ? "animate-spin" : ""} />
                RESTORE THE SHARDS
              </button>
            </div>
          ) : (
            <div className="space-y-10">
              {/* Dial Info Cards */}
              <div className="grid grid-cols-3 gap-4">
                {[
                  { layer: RingLayer.OUTER, color: 'bg-blue-900', label: 'OUTER' },
                  { layer: RingLayer.MIDDLE, color: 'bg-green-900', label: 'MID' },
                  { layer: RingLayer.INNER, color: 'bg-red-900', label: 'INNER' }
                ].map((knob) => (
                  <div key={knob.layer} className={`flex flex-col items-center p-4 rounded-2xl border transition-all ${activeLayer === knob.layer ? 'bg-stone-900 border-stone-700 shadow-lg' : 'bg-stone-950/50 border-stone-900 opacity-60'}`}>
                    <div className={`w-12 h-12 rounded-full mb-3 ${knob.color} shadow-inner flex items-center justify-center border-2 border-black/40`}>
                      <div className="w-1 h-4 bg-white/20 rounded-full"></div>
                    </div>
                    <span className="text-[10px] font-bold tracking-[0.3em] text-stone-500">{knob.label}</span>
                  </div>
                ))}
