import { GoogleGenAI, Type } from "@google/genai";

const ai = new GoogleGenAI({ apiKey: process.env.API_KEY });

export async function generateSecretChallenge() {
  const response = await ai.models.generateContent({
    model: "gemini-3-flash-preview",
    contents: "คุณคือผู้พิทักษ์หอศาสตราแห่งเทพเจ้า จงสร้าง 'คำพยากรณ์ศักดิ์สิทธิ์' หรือ 'รหัสลับแห่งอาวุธเทพ' สั้นๆ 1 ประโยคที่เป็นภาษาไทย (เช่น 'เมื่อหอกหักสะบั้น สวรรค์จะร่ำไห้') พร้อมคำใบ้ที่ฟังดูขลังเกี่ยวกับแสง สี และการประกอบศาสตรา เพื่อใช้ในเกมปริศนากระจกสี",
    config: {
      responseMimeType: "application/json",
      responseSchema: {
        type: Type.OBJECT,
        properties: {
          secret: {
            type: Type.STRING,
            description: "A divine prophecy or secret message about a godly weapon in Thai.",
          },
          hint: {
            type: Type.STRING,
            description: "A mystical hint about aligning the shards of light.",
          },
        },
        required: ["secret", "hint"],
      },
    },
  });

  try {
    return JSON.parse(response.text);
  } catch (e) {
    return {
      secret: "เมื่อศาสตราทั้งสามรวมเป็นหนึ่ง พลังแห่งเทพจะตื่นขึ้น",
      hint: "จงตามหาหัวใจสีทองที่แตกกระจายอยู่ในมวลหมู่เมฆสีคราม"
    };
  }
}

import React, { useMemo } from 'react';
import { RingLayer } from '../types';

interface DialRingProps {
  layer: RingLayer;
  radius: number;
  thickness: number;
  rotation: number;
  color: string;
  isActive: boolean;
  isDragging: boolean;
  onDragStart: (layer: RingLayer, e: React.MouseEvent | React.TouchEvent) => void;
}

const DialRing: React.FC<DialRingProps> = ({
  layer,
  radius,
  thickness,
  rotation,
  color,
  isActive,
  isDragging,
  onDragStart
}) => {
  const innerR = radius - thickness / 2;
  const outerR = radius + thickness / 2;
  const clipId = `clip-${layer}`;

  // สร้างเศษกระจกเล็กๆ (Shards) แบบสุ่มที่มีความเป็นอินทรีย์มากขึ้น
  const shards = useMemo(() => {
    const shardList = [];
    const radialSegments = 4; 
    const angularSegments = 32; 

    for (let r = 0; r < radialSegments; r++) {
      for (let a = 0; a < angularSegments; a++) {
        // เพิ่มความเบี่ยงเบนเพื่อให้ชิ้นส่วนดูไม่เป็นระเบียบเกินไป
        const jitter = (Math.random() - 0.5) * 5;
        const rStart = innerR + (r * (thickness / radialSegments));
        const rEnd = innerR + ((r + 1) * (thickness / radialSegments));
        const aStart = (a * (360 / angularSegments) + jitter) * (Math.PI / 180);
        const aEnd = ((a + 1) * (360 / angularSegments) + jitter) * (Math.PI / 180);

        const p1x = 250 + rStart * Math.cos(aStart);
        const p1y = 250 + rStart * Math.sin(aStart);
        const p2x = 250 + rEnd * Math.cos(aStart);
        const p2y = 250 + rEnd * Math.sin(aStart);
        const p3x = 250 + rEnd * Math.cos(aEnd);
        const p3y = 250 + rEnd * Math.sin(aEnd);
        const p4x = 250 + rStart * Math.cos(aEnd);
        const p4y = 250 + rStart * Math.sin(aEnd);

        shardList.push({
          d: `M ${p1x} ${p1y} L ${p2x} ${p2y} A ${rEnd} ${rEnd} 0 0 1 ${p3x} ${p3y} L ${p4x} ${p4y} A ${rStart} ${rStart} 0 0 0 ${p1x} ${p1y} Z`,
          opacity: 0.2 + Math.random() * 0.5,
          brightness: 0.8 + Math.random() * 0.4
        });
      }
    }
    return shardList;
  }, [innerR, thickness]);

  // สัญลักษณ์ "ศาสตราเทพ" (Divine Spear/Staff)
  const DivineWeapon = () => (
    <g transform="translate(250, 250) scale(4.8)">
      {/* ส่วนใบหอก (Spear Head) - สีนวลทอง */}
      <path 
        d="M 0 -45 L -12 -25 L -4 -20 L -4 10 L 4 10 L 4 -20 L 12 -25 Z" 
        fill={color} 
        fillOpacity="0.8"
        stroke="#000" 
        strokeWidth="1.2"
      />
      {/* ลายประดับทองคำ */}
      <path d="M -8 -28 L 8 -28 M -6 -35 L 6 -35" stroke="#000" strokeWidth="0.8" />

      {/* วงแหวนพลังรอบศาสตรา */}
      <circle r="35" fill="none" stroke={color} strokeWidth="0.5" strokeDasharray="2 4" opacity="0.3" />
      <circle r="25" fill="none" stroke={color} strokeWidth="1" opacity="0.4" />

      {/* ด้ามจับ (Staff Handle) */}
      <rect x="-2" y="10" width="4" height="40" fill="#1c1917" stroke="#000" strokeWidth="1" />

      {/* เพชรประดับ (Gemstones) */}
      <path d="M 0 -15 L -3 -10 L 0 -5 L 3 -10 Z" fill="#fff" fillOpacity="0.9" />
    </g>
  );

  return (
    <g 
      className="cursor-grab active:cursor-grabbing"
      onMouseDown={(e) => onDragStart(layer, e)}
      onTouchStart={(e) => onDragStart(layer, e)}
    >
      <defs>
        <clipPath id={clipId}>
          <path d={`
            M 250, ${250 - outerR}
            A ${outerR},${outerR} 0 1,1 250,${250 + outerR}
            A ${outerR},${outerR} 0 1,1 250,${250 - outerR}
            Z
            M 250, ${250 - innerR}
            A ${innerR},${innerR} 0 1,0 250,${250 + innerR}
            A ${innerR},${innerR} 0 1,0 250,${250 - innerR}
            Z
          `} fillRule="evenodd" />
        </clipPath>
      </defs>

      <g clipPath={`url(#clip-${layer})`}>
        {/* ชั้นกระจกพื้นหลัง */}
        <circle cx="250" cy="250" r={outerR} fill="#020617" />

        {/* ส่วนที่หมุน */}
        <g style={{ 
          transform: `rotate(${rotation}deg)`, 
          transformOrigin: '250px 250px',
          transition: isDragging ? 'none' : 'transform 1.5s cubic-bezier(0.16, 1, 0.3, 1)'
        }}>
          {/* Mosaic Background Shards */}
          <g>
            {shards.map((shard, i) => (
              <path 
                key={i} 
                d={shard.d} 
                fill={color} 
                fillOpacity={shard.opacity} 
                stroke="#000" 
                strokeWidth="0.5"
                style={{ filter: `brightness(${shard.brightness})` }}
              />
            ))}
          </g>

          {/* ส่วนของอาวุธมหาเทพ */}
          <DivineWeapon />
        </g>

        {/* Shine Overlay (Static) */}
        <circle cx="250" cy="250" r={outerR} fill="none" stroke="rgba(255,255,255,0.05)" strokeWidth="2" />
        <ellipse cx="200" cy="180" rx={outerR * 0.7} ry={outerR * 0.2} fill="white" fillOpacity="0.05" transform="rotate(-30, 250, 250)" />
      </g>

      {/* กรอบตะกั่ว Metal Frames */}
      <circle cx="250" cy="250" r={outerR} fill="none" stroke="#1c1917" strokeWidth="6" />
      <circle cx="250" cy="250" r={innerR} fill="none" stroke="#1c1917" strokeWidth="6" />
    </g>
  );
};

export default DialRing;

import React, { useState, useEffect, useCallback, useRef } from 'react';
import { RefreshCw, Eye, Sparkles, ShieldCheck, Flame, Sword, Crown } from 'lucide-react';
import DialRing from './components/DialRing';
import { generateSecretChallenge } from './services/geminiService';
import { RingLayer } from './types';

const App: React.FC = () => {
  const [loading, setLoading] = useState(false);
  const [solved, setSolved] = useState(false);
  const [secret, setSecret] = useState({ secret: "", hint: "" });
  const [showHint, setShowHint] = useState(false);

  // โทนสีเทพเจ้า: ทอง, ฟ้าคราม, แดงสุริยัน
  const COLORS = {
    [RingLayer.OUTER]: "#0c4a6e", // Deep Sky Blue
    [RingLayer.MIDDLE]: "#0d9488", // Celestial Teal
    [RingLayer.INNER]: "#ca8a04",  // Divine Gold
  };

  const [rotations, setRotations] = useState({
    [RingLayer.OUTER]: (Math.floor(Math.random() * 3) + 1) * 120,
    [RingLayer.MIDDLE]: (Math.floor(Math.random() * 3) + 1) * 120,
    [RingLayer.INNER]: (Math.floor(Math.random() * 3) + 1) * 120,
  });

  const [activeLayer, setActiveLayer] = useState<RingLayer | null>(null);
  const dragStartAngle = useRef<number>(0);
  const initialRotation = useRef<number>(0);
  const containerRef = useRef<HTMLDivElement>(null);

  const getAngle = (clientX: number, clientY: number) => {
    if (!containerRef.current) return 0;
    const rect = containerRef.current.getBoundingClientRect();
    const centerX = rect.left + rect.width / 2;
    const centerY = rect.top + rect.height / 2;
    const dx = clientX - centerX;
    const dy = clientY - centerY;
    return Math.atan2(dy, dx) * (180 / Math.PI);
  };

  const handleDragStart = (layer: RingLayer, e: React.MouseEvent | React.TouchEvent) => {
    if (solved || loading) return;
    const clientX = 'touches' in e ? e.touches[0].clientX : e.clientX;
    const clientY = 'touches' in e ? e.touches[0].clientY : e.clientY;
    setActiveLayer(layer);
    dragStartAngle.current = getAngle(clientX, clientY);
    initialRotation.current = rotations[layer];
  };

  const handleDragMove = useCallback((e: MouseEvent | TouchEvent) => {
    if (activeLayer === null || solved) return;
    const clientX = 'touches' in e ? (e as TouchEvent).touches[0].clientX : (e as MouseEvent).clientX;
    const clientY = 'touches' in e ? (e as TouchEvent).touches[0].clientY : (e as MouseEvent).clientY;
    const currentAngle = getAngle(clientX, clientY);
    const angleDiff = currentAngle - dragStartAngle.current;

    setRotations(prev => ({ ...prev, [activeLayer]: initialRotation.current + angleDiff }));
  }, [activeLayer, solved]);

  const handleDragEnd = useCallback(() => {
    if (activeLayer === null) return;
    setRotations(prev => {
      const currentRot = prev[activeLayer];
      const snapped = Math.round(currentRot / 120) * 120;
      const nextRotations = { ...prev, [activeLayer]: snapped };

      const isAligned = 
        nextRotations[RingLayer.OUTER] % 360 === 0 &&
        nextRotations[RingLayer.MIDDLE] % 360 === 0 &&
        nextRotations[RingLayer.INNER] % 360 === 0;

      if (isAligned) setSolved(true);
      return nextRotations;
    });
    setActiveLayer(null);
  }, [activeLayer]);

  useEffect(() => {
    if (activeLayer !== null) {
      window.addEventListener('mousemove', handleDragMove);
      window.addEventListener('mouseup', handleDragEnd);
      window.addEventListener('touchmove', handleDragMove, { passive: false });
      window.addEventListener('touchend', handleDragEnd);
    }
    return () => {
      window.removeEventListener('mousemove', handleDragMove);
      window.removeEventListener('mouseup', handleDragEnd);
      window.removeEventListener('touchmove', handleDragMove);
      window.removeEventListener('touchend', handleDragEnd);
    };
  }, [activeLayer, handleDragMove, handleDragEnd]);

  const initGame = async () => {
    setLoading(true);
    setSolved(false);
    setShowHint(false);
    const data = await generateSecretChallenge();
    setSecret(data);
    setRotations({
      [RingLayer.OUTER]: (Math.random() > 0.5 ? 120 : 240),
      [RingLayer.MIDDLE]: (Math.random() > 0.5 ? 120 : 240),
      [RingLayer.INNER]: (Math.random() > 0.5 ? 120 : 240),
    });
    setLoading(false);
  };

  useEffect(() => { initGame(); }, []);

  return (
    <div className="min-h-screen flex flex-col items-center justify-center p-4 text-stone-300 overflow-hidden relative select-none bg-[#020617]">
      {/* Divine Atmosphere */}
      <div className="absolute inset-0 pointer-events-none">
        <div className="absolute inset-0 bg-[radial-gradient(circle_at_50%_40%,rgba(234,179,8,0.08)_0%,transparent_100%)] opacity-70"></div>
        {/* Divine Light Rays */}
        <div className="absolute top-0 left-1/2 -translate-x-1/2 w-full h-full bg-gradient-to-b from-white/5 to-transparent opacity-20 blur-2xl"></div>
      </div>

      <header className="text-center mb-12 z-10 font-serif">
        <div className="flex items-center justify-center gap-2 mb-2">
          <Crown className="text-amber-500 animate-pulse" size={32} />
        </div>
        <h1 className="text-4xl md:text-6xl font-bold tracking-[0.5em] text-amber-500 uppercase drop-shadow-[0_0_20px_rgba(245,158,11,0.5)] mb-4">
          THE DIVINE ARMORY
        </h1>
        <p className="text-[10px] tracking-[0.6em] text-amber-900 uppercase font-black">Reassemble the Sacred Spear of the Heavens</p>
      </header>

      <div className="relative z-10 flex flex-col items-center">
        {/* The Stained Glass Window Component */}
        <div 
          ref={containerRef}
          className={`relative w-[320px] h-[320px] md:w-[580px] md:h-[580px] rounded-full border-[28px] border-[#1c1917] shadow-[0_0_120px_rgba(0,0,0,1),inset_0_0_60px_rgba(0,0,0,0.9)] overflow-hidden transition-all duration-1000 ${solved ? 'ring-[30px] ring-amber-500/20 scale-105' : ''}`}
        >
          {/* แสงสวรรค์เมื่อประกอบเสร็จ */}
          {solved && (
            <div className="absolute inset-0 z-20 pointer-events-none">
              <div className="absolute inset-0 bg-gradient-to-b from-amber-400/20 via-white/10 to-transparent mix-blend-screen animate-pulse"></div>
              <div className="absolute inset-0 bg-[radial-gradient(circle_at_50%_50%,rgba(255,255,255,0.2)_0%,transparent_70%)] animate-ping duration-[3000ms]"></div>
            </div>
          )}

          <svg viewBox="0 0 500 500" className={`w-full h-full transition-all duration-[2000ms] ${solved ? 'brightness-150 saturate-150' : 'brightness-90'}`}>
            <DialRing
              layer={RingLayer.OUTER}
              radius={200}
              thickness={80}
              rotation={rotations[RingLayer.OUTER]}
              color={COLORS[RingLayer.OUTER]}
              isActive={!solved}
              isDragging={activeLayer === RingLayer.OUTER}
              onDragStart={handleDragStart}
            />
            <DialRing
              layer={RingLayer.MIDDLE}
              radius={125}
              thickness={70}
              rotation={rotations[RingLayer.MIDDLE]}
              color={COLORS[RingLayer.MIDDLE]}
              isActive={!solved}
              isDragging={activeLayer === RingLayer.MIDDLE}
              onDragStart={handleDragStart}
            />
            <DialRing
              layer={RingLayer.INNER}
              radius={60}
              thickness={60}
              rotation={rotations[RingLayer.INNER]}
              color={COLORS[RingLayer.INNER]}
              isActive={!solved}
              isDragging={activeLayer === RingLayer.INNER}
              onDragStart={handleDragStart}
            />

            {/* แกนกลางศักดิ์สิทธิ์ */}
            <g transform="translate(250, 250)">
              <circle r="14" fill="#000" />
              <path d="M -3 -10 L 3 -10 L 0 30 Z" fill={solved ? "#fde047" : "#422006"} />
              <circle r="4" fill={solved ? "#fff" : "#000"} />
            </g>

            {/* โครงสร้างโลหะรอบหน้าต่าง */}
            <circle cx="250" cy="250" r="242" fill="none" stroke="#1c1917" strokeWidth="16" />
            <circle cx="250" cy="250" r="148" fill="none" stroke="#1c1917" strokeWidth="8" opacity="0.9" />
            <circle cx="250" cy="250" r="82" fill="none" stroke="#1c1917" strokeWidth="8" opacity="0.9" />
          </svg>
        </div>

        {/* UI Controls */}
        <div className="mt-16 w-full max-w-lg space-y-10 z-20">
          {solved ? (
            <div className="bg-stone-900/95 border-2 border-amber-500/30 p-10 rounded-3xl shadow-[0_0_80px_rgba(234,179,8,0.2)] animate-in fade-in zoom-in duration-1000 backdrop-blur-3xl text-center relative overflow-hidden group">
              <div className="absolute top-0 left-0 w-full h-1 bg-amber-500 animate-pulse"></div>
              <Sparkles className="absolute top-6 left-6 text-amber-500/30 animate-pulse" size={24} />
              <Sword className="absolute bottom-6 right-6 text-amber-500/30 animate-pulse" size={24} />

              <h2 className="text-amber-500 font-bold text-3xl mb-8 tracking-[0.5em] uppercase font-serif">
                DIVINE PROPHECY
              </h2>
              <div className="text-xl md:text-2xl font-medium text-stone-200 mb-12 py-10 px-8 bg-black/60 rounded-2xl italic border border-amber-900/20 leading-relaxed shadow-inner font-serif">
                "{secret.secret}"
              </div>
              <button
                onClick={initGame}
                className="w-full bg-amber-900/20 hover:bg-amber-900/40 text-amber-500 font-bold py-5 rounded-2xl border border-amber-500/40 transition-all flex items-center justify-center gap-4 uppercase tracking-[0.4em] active:scale-95"
              >
                <RefreshCw size={22} className={loading ? "animate-spin" : ""} />
                REFORGE THE ARMORY
              </button>
            </div>
          ) : (
            <div className="space-y-10">
              {/* Dial Info Cards */}
              <div className="grid grid-cols-3 gap-6">
                {[
                  { layer: RingLayer.OUTER, color: 'bg-sky-900', label: 'CELESTIAL' },
                  { layer: RingLayer.MIDDLE, color: 'bg-teal-900', label: 'TEAL' },
                  { layer: RingLayer.INNER, color: 'bg-amber-900', label: 'DIVINE' }
                ].map((knob) => (
                  <div key={knob.layer} className={`flex flex-col items-center p-5 rounded-3xl border transition-all duration-500 ${activeLayer === knob.layer ? 'bg-amber-950/20 border-amber-500/50 shadow-[0_0_20px_rgba(234,179,8,0.2)]' : 'bg-stone-950/60 border-stone-800 opacity-60'}`}>
                    <div className={`w-14 h-14 rounded-full mb-3 ${knob.color} shadow-inner flex items-center justify-center border-2 border-white/10 relative overflow-hidden`}>
                      <div className="absolute inset-0 bg-gradient-to-tr from-black/40 to-white/10"></div>
                      <div className="w-1.5 h-6 bg-amber-500/40 rounded-full blur-[1px]"></div>
                    </div>
                    <span className="text-[10px] font-black tracking-[0.4em] text-stone-500">{knob.label}</span>
                  </div>
                ))}
              </div>

              <div className="flex gap-4">
                <button
                  onClick={() => setShowHint(!showHint)}
                  className="flex-1 p-6 bg-stone-900/40 hover:bg-stone-800/60 text-amber-600 rounded-2xl border border-amber-900/20 transition-all flex items-center justify-center gap-4 text-xs font-black tracking-[0.4em] uppercase"
                >
                  <Eye size={22} />
                  {showHint ? "CONCEAL LIGHT" : "READ THE STARS"}
                </button>
                <button
                  onClick={initGame}
                  className="p-6 bg-stone-900/40 hover:bg-stone-800/60 text-amber-600 rounded-2xl border border-amber-900/20 transition-all"
                >
                  <RefreshCw size={22} className={loading ? "animate-spin" : ""} />
                </button>
              </div>

              {showHint && (
                <div className="p-8 bg-amber-950/5 border-l-4 border-amber-600/50 text-amber-200/40 text-center text-sm italic animate-in slide-in-from-left duration-1000 font-serif leading-relaxed tracking-widest">
                  "{secret.hint}"
                </div>
              )}
            </div>
          )}
        </div>
      </div>

      <footer className="mt-20 text-amber-900/30 text-[10px] tracking-[1em] uppercase z-10 font-black font-serif">
        HEAVENLY CRAFT • DIVINE ARTIFACTS
      </footer>
    </div>
  );
};

export default App;

export interface RingState {
  rotation: number;
  segments: number;
  symbols: string[];
}

export interface GameState {
  inner: RingState;
  innerMid: RingState;
  middle: RingState;
  outerMid: RingState;
  outer: RingState;
  isSolved: boolean;
  secretMessage: string;
  hint: string;
}

export enum RingLayer {
  INNER = 'inner',
  INNER_MID = 'innerMid',
  MIDDLE = 'middle',
  OUTER_MID = 'outerMid',
  OUTER = 'outer'
}

import React, { useMemo } from 'react';
import { RingLayer } from '../types';

interface DialRingProps {
  layer: RingLayer;
  radius: number;
  thickness: number;
  rotation: number;
  color: string;
  isActive: boolean;
  isDragging: boolean;
  onDragStart: (layer: RingLayer, e: React.MouseEvent | React.TouchEvent) => void;
}

const DialRing: React.FC<DialRingProps> = ({
  layer,
  radius,
  thickness,
  rotation,
  color,
  isActive,
  isDragging,
  onDragStart
}) => {
  const innerR = radius - thickness / 2;
  const outerR = radius + thickness / 2;
  const clipId = `clip-${layer}`;

  // สร้างเศษกระจก (Shards) ที่มีความละเอียดสูงสำหรับ 5 วง
  const shards = useMemo(() => {
    const shardList = [];
    const radialSegments = 2; // ลดชั้นย่อยลงเพื่อให้ดูเคลียร์ขึ้นใน 5 วง
    const angularSegments = layer === RingLayer.OUTER ? 48 : 32; 

    for (let r = 0; r < radialSegments; r++) {
      for (let a = 0; a < angularSegments; a++) {
        const jitter = (Math.random() - 0.5) * 3;
        const rStart = innerR + (r * (thickness / radialSegments));
        const rEnd = innerR + ((r + 1) * (thickness / radialSegments));
        const aStart = (a * (360 / angularSegments) + jitter) * (Math.PI / 180);
        const aEnd = ((a + 1) * (360 / angularSegments) + jitter) * (Math.PI / 180);

        const p1x = 250 + rStart * Math.cos(aStart);
        const p1y = 250 + rStart * Math.sin(aStart);
        const p2x = 250 + rEnd * Math.cos(aStart);
        const p2y = 250 + rEnd * Math.sin(aStart);
        const p3x = 250 + rEnd * Math.cos(aEnd);
        const p3y = 250 + rEnd * Math.sin(aEnd);
        const p4x = 250 + rStart * Math.cos(aEnd);
        const p4y = 250 + rStart * Math.sin(aEnd);

        shardList.push({
          d: `M ${p1x} ${p1y} L ${p2x} ${p2y} A ${rEnd} ${rEnd} 0 0 1 ${p3x} ${p3y} L ${p4x} ${p4y} A ${rStart} ${rStart} 0 0 0 ${p1x} ${p1y} Z`,
          opacity: 0.15 + Math.random() * 0.4,
          brightness: 0.7 + Math.random() * 0.5
        });
      }
    }
    return shardList;
  }, [innerR, thickness, layer]);

  // สัญลักษณ์อาวุธที่ถูกแบ่งเป็นส่วนๆ (Segmented Weapon)
  const DivineWeaponSegment = () => {
    const scale = 4.8;
    return (
      <g transform="translate(250, 250) scale(4.8)">
        {/* วาดชิ้นส่วนอาวุธที่แตกต่างกันไปตามชั้น */}
        {layer === RingLayer.OUTER && (
          <path d="M 0 -48 L -15 -35 L 0 -42 L 15 -35 Z" fill={color} fillOpacity="0.9" stroke="#000" strokeWidth="0.5" />
        )}
        {layer === RingLayer.OUTER_MID && (
          <path d="M -12 -35 L -8 -20 L 8 -20 L 12 -35 Z" fill={color} fillOpacity="0.8" stroke="#000" strokeWidth="0.5" />
        )}
        {layer === RingLayer.MIDDLE && (
          <path d="M -8 -20 L -6 10 L 6 10 L 8 -20 Z" fill={color} fillOpacity="0.7" stroke="#000" strokeWidth="0.5" />
        )}
        {layer === RingLayer.INNER_MID && (
          <path d="M -6 10 L -4 35 L 4 35 L 6 10 Z" fill={color} fillOpacity="0.8" stroke="#000" strokeWidth="0.5" />
        )}
        {layer === RingLayer.INNER && (
          <g>
            <path d="M -4 35 L 0 45 L 4 35 Z" fill={color} fillOpacity="1" stroke="#000" strokeWidth="0.5" />
            <circle cy="0" r="4" fill="#fff" fillOpacity="0.9" />
          </g>
        )}

        {/* ลวดลายเชื่อมต่อ (Connecting Lines) */}
        <circle r={radius/scale} fill="none" stroke={color} strokeWidth="0.2" opacity="0.3" strokeDasharray="1 2" />
      </g>
    );
  };

  return (
    <g 
      className="cursor-grab active:cursor-grabbing"
      onMouseDown={(e) => onDragStart(layer, e)}
      onTouchStart={(e) => onDragStart(layer, e)}
    >
      <defs>
        <clipPath id={clipId}>
          <path d={`
            M 250, ${250 - outerR}
            A ${outerR},${outerR} 0 1,1 250,${250 + outerR}
            A ${outerR},${outerR} 0 1,1 250,${250 - outerR}
            Z
            M 250, ${250 - innerR}
            A ${innerR},${innerR} 0 1,0 250,${250 + innerR}
            A ${innerR},${innerR} 0 1,0 250,${250 - innerR}
            Z
          `} fillRule="evenodd" />
        </clipPath>
      </defs>

      <g clipPath={`url(#${clipId})`}>
        <circle cx="250" cy="250" r={outerR} fill="#020412" />

        <g style={{ 
          transform: `rotate(${rotation}deg)`, 
          transformOrigin: '250px 250px',
          transition: isDragging ? 'none' : 'transform 1.8s cubic-bezier(0.16, 1, 0.3, 1)'
        }}>
          {/* Mosaic Shards */}
          <g>
            {shards.map((shard, i) => (
              <path 
                key={i} 
                d={shard.d} 
                fill={color} 
                fillOpacity={shard.opacity} 
                stroke="#000" 
                strokeWidth="0.4"
                style={{ filter: `brightness(${shard.brightness})` }}
              />
            ))}
          </g>

          <DivineWeaponSegment />
        </g>

        {/* Shine Overlay */}
        <circle cx="250" cy="250" r={outerR} fill="none" stroke="rgba(255,255,255,0.03)" strokeWidth="1" />
      </g>

      {/* Metal Lead Borders */}
      <circle cx="250" cy="250" r={outerR} fill="none" stroke="#111" strokeWidth="5" />
      <circle cx="250" cy="250" r={innerR} fill="none" stroke="#111" strokeWidth="5" />
    </g>
  );
};

export default DialRing;

import React, { useState, useEffect, useCallback, useRef } from 'react';
import { RefreshCw, Eye, Sparkles, ShieldCheck, Flame, Sword, Crown, Zap } from 'lucide-react';
import DialRing from './components/DialRing';
import { generateSecretChallenge } from './services/geminiService';
import { RingLayer } from './types';

const App: React.FC = () => {
  const [loading, setLoading] = useState(false);
  const [solved, setSolved] = useState(false);
  const [secret, setSecret] = useState({ secret: "", hint: "" });
  const [showHint, setShowHint] = useState(false);

  // 5 Divine Colors
  const COLORS = {
    [RingLayer.OUTER]: "#1e3a8a",     // Sapphire Blue
    [RingLayer.OUTER_MID]: "#0f766e", // Celestial Teal
    [RingLayer.MIDDLE]: "#166534",    // Forest Emerald
    [RingLayer.INNER_MID]: "#991b1b", // Sacred Ruby
    [RingLayer.INNER]: "#ca8a04",     // Imperial Gold
  };

  const [rotations, setRotations] = useState({
    [RingLayer.OUTER]: (Math.floor(Math.random() * 3) + 1) * 120,
    [RingLayer.OUTER_MID]: (Math.floor(Math.random() * 3) + 1) * 120,
    [RingLayer.MIDDLE]: (Math.floor(Math.random() * 3) + 1) * 120,
    [RingLayer.INNER_MID]: (Math.floor(Math.random() * 3) + 1) * 120,
    [RingLayer.INNER]: (Math.floor(Math.random() * 3) + 1) * 120,
  });

  const [activeLayer, setActiveLayer] = useState<RingLayer | null>(null);
  const dragStartAngle = useRef<number>(0);
  const initialRotation = useRef<number>(0);
  const containerRef = useRef<HTMLDivElement>(null);

  const getAngle = (clientX: number, clientY: number) => {
    if (!containerRef.current) return 0;
    const rect = containerRef.current.getBoundingClientRect();
    const centerX = rect.left + rect.width / 2;
    const centerY = rect.top + rect.height / 2;
    const dx = clientX - centerX;
    const dy = clientY - centerY;
    return Math.atan2(dy, dx) * (180 / Math.PI);
  };

  const handleDragStart = (layer: RingLayer, e: React.MouseEvent | React.TouchEvent) => {
    if (solved || loading) return;
    const clientX = 'touches' in e ? (e as TouchEvent).touches[0].clientX : (e as MouseEvent).clientX;
    const clientY = 'touches' in e ? (e as TouchEvent).touches[0].clientY : (e as MouseEvent).clientY;
    setActiveLayer(layer);
    dragStartAngle.current = getAngle(clientX, clientY);
    initialRotation.current = rotations[layer];
  };

  const handleDragMove = useCallback((e: MouseEvent | TouchEvent) => {
    if (activeLayer === null || solved) return;
    const clientX = 'touches' in e ? (e as TouchEvent).touches[0].clientX : (e as MouseEvent).clientX;
    const clientY = 'touches' in e ? (e as TouchEvent).touches[0].clientY : (e as MouseEvent).clientY;
    const currentAngle = getAngle(clientX, clientY);
    const angleDiff = currentAngle - dragStartAngle.current;

    setRotations(prev => ({ ...prev, [activeLayer]: initialRotation.current + angleDiff }));
  }, [activeLayer, solved]);

  const handleDragEnd = useCallback(() => {
    if (activeLayer === null) return;
    setRotations(prev => {
      const currentRot = prev[activeLayer];
      const snapped = Math.round(currentRot / 120) * 120;
      const nextRotations = { ...prev, [activeLayer]: snapped };

      const isAligned = Object.values(nextRotations).every(rot => rot % 360 === 0);

      if (isAligned) setSolved(true);
      return nextRotations;
    });
    setActiveLayer(null);
  }, [activeLayer]);

  useEffect(() => {
    if (activeLayer !== null) {
      window.addEventListener('mousemove', handleDragMove);
      window.addEventListener('mouseup', handleDragEnd);
      window.addEventListener('touchmove', handleDragMove, { passive: false });
      window.addEventListener('touchend', handleDragEnd);
    }
    return () => {
      window.removeEventListener('mousemove', handleDragMove);
      window.removeEventListener('mouseup', handleDragEnd);
      window.removeEventListener('touchmove', handleDragMove);
      window.removeEventListener('touchend', handleDragEnd);
    };
  }, [activeLayer, handleDragMove, handleDragEnd]);

  const initGame = async () => {
    setLoading(true);
    setSolved(false);
    setShowHint(false);
    const data = await generateSecretChallenge();
    setSecret(data);
    setRotations({
      [RingLayer.OUTER]: (Math.random() > 0.5 ? 120 : 240),
      [RingLayer.OUTER_MID]: (Math.random() > 0.5 ? 120 : 240),
      [RingLayer.MIDDLE]: (Math.random() > 0.5 ? 120 : 240),
      [RingLayer.INNER_MID]: (Math.random() > 0.5 ? 120 : 240),
      [RingLayer.INNER]: (Math.random() > 0.5 ? 120 : 240),
    });
    setLoading(false);
  };

  useEffect(() => { initGame(); }, []);

  return (
    <div className="min-h-screen flex flex-col items-center justify-center p-4 text-stone-300 overflow-hidden relative select-none bg-[#02040a]">
      {/* Divine Ambience */}
      <div className="absolute inset-0 pointer-events-none">
        <div className="absolute inset-0 bg-[radial-gradient(circle_at_50%_45%,rgba(234,179,8,0.05)_0%,transparent_100%)]"></div>
        <div className="absolute top-0 left-0 w-full h-1/3 bg-gradient-to-b from-blue-950/20 to-transparent"></div>
      </div>

      <header className="text-center mb-8 z-10 font-serif">
        <div className="flex items-center justify-center gap-4 mb-2">
          <Zap className="text-blue-500 animate-pulse" size={20} />
          <Crown className="text-amber-500" size={36} />
          <Zap className="text-blue-500 animate-pulse" size={20} />
        </div>
        <h1 className="text-4xl md:text-5xl font-bold tracking-[0.4em] text-amber-500 uppercase drop-shadow-[0_0_15px_rgba(245,158,11,0.4)] mb-2">
          THE FIVE SEALS
        </h1>
        <p className="text-[10px] tracking-[0.8em] text-stone-600 uppercase font-black">Align the five layers of the sacred mosaic</p>
      </header>

      <div className="relative z-10 flex flex-col items-center">
        {/* The 5-Layer Stained Glass Window */}
        <div 
          ref={containerRef}
          className={`relative w-[340px] h-[340px] md:w-[600px] md:h-[600px] rounded-full border-[20px] border-[#121214] shadow-[0_0_100px_rgba(0,0,0,1),inset_0_0_40px_rgba(0,0,0,0.8)] overflow-hidden transition-all duration-1000 ${solved ? 'ring-[20px] ring-amber-500/10 scale-105' : ''}`}
        >
          {solved && (
            <div className="absolute inset-0 z-20 pointer-events-none">
              <div className="absolute inset-0 bg-gradient-to-b from-white/10 via-amber-500/10 to-transparent mix-blend-screen animate-pulse"></div>
              <div className="absolute inset-0 bg-[radial-gradient(circle_at_50%_50%,rgba(255,255,255,0.15)_0%,transparent_80%)]"></div>
            </div>
          )}

          <svg viewBox="0 0 500 500" className={`w-full h-full transition-all duration-[2500ms] ${solved ? 'brightness-125 saturate-150' : 'brightness-[0.85]'}`}>
            <DialRing
              layer={RingLayer.OUTER}
              radius={215}
              thickness={45}
              rotation={rotations[RingLayer.OUTER]}
              color={COLORS[RingLayer.OUTER]}
              isActive={!solved}
              isDragging={activeLayer === RingLayer.OUTER}
              onDragStart={handleDragStart}
            />
            <DialRing
              layer={RingLayer.OUTER_MID}
              radius={170}
              thickness={45}
              rotation={rotations[RingLayer.OUTER_MID]}
              color={COLORS[RingLayer.OUTER_MID]}
              isActive={!solved}
              isDragging={activeLayer === RingLayer.OUTER_MID}
              onDragStart={handleDragStart}
            />
            <DialRing
              layer={RingLayer.MIDDLE}
              radius={125}
              thickness={45}
              rotation={rotations[RingLayer.MIDDLE]}
              color={COLORS[RingLayer.MIDDLE]}
              isActive={!solved}
              isDragging={activeLayer === RingLayer.MIDDLE}
              onDragStart={handleDragStart}
            />
            <DialRing
              layer={RingLayer.INNER_MID}
              radius={80}
              thickness={45}
              rotation={rotations[RingLayer.INNER_MID]}
              color={COLORS[RingLayer.INNER_MID]}
              isActive={!solved}
              isDragging={activeLayer === RingLayer.INNER_MID}
              onDragStart={handleDragStart}
            />
            <DialRing
              layer={RingLayer.INNER}
              radius={35}
              thickness={45}
              rotation={rotations[RingLayer.INNER]}
              color={COLORS[RingLayer.INNER]}
              isActive={!solved}
              isDragging={activeLayer === RingLayer.INNER}
              onDragStart={handleDragStart}
            />

            {/* Sacred Core */}
            <circle cx="250" cy="250" r="10" fill={solved ? "#fff" : "#000"} className="transition-all duration-1000" />
            <circle cx="250" cy="250" r="15" fill="none" stroke="#000" strokeWidth="2" />
          </svg>
        </div>

        {/* 5-Layer UI Controller */}
        <div className="mt-12 w-full max-w-xl space-y-8 z-20">
          {solved ? (
            <div className="bg-stone-900/95 border-2 border-amber-500/20 p-8 rounded-3xl shadow-2xl animate-in fade-in zoom-in duration-1000 backdrop-blur-3xl text-center relative overflow-hidden">
              <Sparkles className="absolute top-4 left-4 text-amber-500/20 animate-pulse" size={20} />
              <Sword className="absolute bottom-4 right-4 text-amber-500/20 animate-pulse" size={20} />

              <h2 className="text-amber-500 font-bold text-2xl mb-6 tracking-[0.4em] uppercase font-serif">
                THE ASCENSION
              </h2>
              <div className="text-xl md:text-2xl font-medium text-stone-200 mb-8 py-8 px-6 bg-black/40 rounded-2xl italic border border-stone-800 leading-relaxed font-serif">
                "{secret.secret}"
              </div>
              <button
                onClick={initGame}
                className="w-full bg-stone-800 hover:bg-stone-700 text-amber-500 font-bold py-4 rounded-xl border border-stone-700 transition-all flex items-center justify-center gap-4 uppercase tracking-[0.3em] active:scale-95"
              >
                <RefreshCw size={20} className={loading ? "animate-spin" : ""} />
                RESET THE ARMORY
              </button>
            </div>
          ) : (
            <div className="space-y-6">
              {/* 5 Layer Controls */}
              <div className="grid grid-cols-5 gap-3">
                {[
                  { layer: RingLayer.OUTER, color: 'bg-blue-900', label: 'I' },
                  { layer: RingLayer.OUTER_MID, color: 'bg-teal-900', label: 'II' },
                  { layer: RingLayer.MIDDLE, color: 'bg-green-900', label: 'III' },
                  { layer: RingLayer.INNER_MID, color: 'bg-red-900', label: 'IV' },
                  { layer: RingLayer.INNER, color: 'bg-amber-900', label: 'V' }
                ].map((knob) => (
                  <div 
                    key={knob.layer}
                    onClick={() => setActiveLayer(knob.layer)} 
                    className={`flex flex-col items-center p-3 rounded-2xl border transition-all cursor-pointer ${activeLayer === knob.layer ? 'bg-amber-950/20 border-amber-500/40 shadow-lg scale-105' : 'bg-stone-950/60 border-stone-900 opacity-70'}`}
                  >
                    <div className={`w-10 h-10 rounded-full mb-2 ${knob.color} shadow-inner flex items-center justify-center border-2 border-white/5 relative`}>
                      <div className="w-1 h-3 bg-amber-500/30 rounded-full"></div>
                    </div>
                    <span className="text-[10px] font-black text-stone-600">{knob.label}</span>
                  </div>
                ))}
              </div>

              <div className="flex gap-4">
                <button
                  onClick={() => setShowHint(!showHint)}
                  className="flex-1 p-5 bg-stone-900/60 hover:bg-stone-800/80 text-amber-700 rounded-2xl border border-amber-900/10 transition-all flex items-center justify-center gap-4 text-xs font-black tracking-[0.4em] uppercase"
                >
                  <Eye size={20} />
                  {showHint ? "CONCEAL LIGHT" : "DIVINE HINT"}
                </button>
              </div>

              {showHint && (
                <div className="p-6 bg-stone-950/40 border-l-4 border-amber-900/30 text-stone-500 text-center text-sm italic animate-in slide-in-from-left duration-700 font-serif tracking-widest leading-relaxed">
                  "{secret.hint}"
                </div>
              )}
            </div>
          )}
        </div>
      </div>

      <footer className="mt-16 text-stone-900 text-[9px] tracking-[1.2em] uppercase z-10 font-bold opacity-40">
        SACRED PENTAGONAL ASSEMBLY • MK-V
      </footer>
    </div>
  );
};

