import React, { useMemo } from 'react';
import { RingLayer } from '../types';

interface DialRingProps {
  layer: RingLayer;
  radius: number;
  thickness: number;
  rotation: number;
  color: string;
  isActive: boolean;
  isDragging: boolean;
  onDragStart: (layer: RingLayer, e: React.MouseEvent | React.TouchEvent) => void;
}

const DialRing: React.FC<DialRingProps> = ({
  layer,
  radius,
  thickness,
  rotation,
  color,
  isActive,
  isDragging,
  onDragStart
}) => {
  const innerR = radius - thickness / 2;
  const outerR = radius + thickness / 2;
  const clipId = `clip-${layer}`;

  // สร้างเศษกระจกที่ดูเหมือนถูกทุบ (Aggressive Shards)
  const shards = useMemo(() => {
    const shardList = [];
    const radialSegments = 3; 
    const angularSegments = layer === RingLayer.OUTER ? 40 : 24; 

    for (let r = 0; r < radialSegments; r++) {
      for (let a = 0; a < angularSegments; a++) {
        const jitter = (Math.random() - 0.5) * 6; // เพิ่ม jitter ให้ดูแตกหักมากขึ้น
        const rStart = innerR + (r * (thickness / radialSegments));
        const rEnd = innerR + ((r + 1) * (thickness / radialSegments));
        const aStart = (a * (360 / angularSegments) + jitter) * (Math.PI / 180);
        const aEnd = ((a + 1) * (360 / angularSegments) + jitter) * (Math.PI / 180);

        const p1x = 250 + rStart * Math.cos(aStart);
        const p1y = 250 + rStart * Math.sin(aStart);
        const p2x = 250 + rEnd * Math.cos(aStart);
        const p2y = 250 + rEnd * Math.sin(aStart);
        const p3x = 250 + rEnd * Math.cos(aEnd);
        const p3y = 250 + rEnd * Math.sin(aEnd);
        const p4x = 250 + rStart * Math.cos(aEnd);
        const p4y = 250 + rStart * Math.sin(aEnd);

        shardList.push({
          d: `M ${p1x} ${p1y} L ${p2x} ${p2y} A ${rEnd} ${rEnd} 0 0 1 ${p3x} ${p3y} L ${p4x} ${p4y} A ${rStart} ${rStart} 0 0 0 ${p1x} ${p1y} Z`,
          opacity: 0.1 + Math.random() * 0.5,
          darkness: 0.4 + Math.random() * 0.6
        });
      }
    }
    return shardList;
  }, [innerR, thickness, layer]);

  // สัญลักษณ์ "มีดสั้นสังหาร" (The Murder Dagger)
  const DaggerSegment = () => {
    return (
      <g transform="translate(250, 250) scale(4.5)">
        {layer === RingLayer.OUTER && (
          // ปลายมีด (Tip)
          <path d="M 0 -48 L -6 -35 L 6 -35 Z" fill={color} fillOpacity="0.9" stroke="#000" strokeWidth="0.8" />
        )}
        {layer === RingLayer.OUTER_MID && (
          // ตัวใบมีด (Blade Body)
          <path d="M -6 -35 L -8 -15 L 8 -15 L 6 -35 Z" fill={color} fillOpacity="0.8" stroke="#000" strokeWidth="0.8" />
        )}
        {layer === RingLayer.MIDDLE && (
          // โกร่งมีด (Crossguard)
          <g>
            <rect x="-18" y="-15" width="36" height="6" fill="#1c1917" stroke="#000" strokeWidth="0.8" />
            <path d="M -8 -15 L 8 -15 L 6 -8 L -6 -8 Z" fill={color} fillOpacity="0.7" stroke="#000" strokeWidth="0.8" />
          </g>
        )}
        {layer === RingLayer.INNER_MID && (
          // ด้ามจับ (Grip)
          <rect x="-5" y="-9" width="10" height="30" rx="2" fill="#292524" stroke="#000" strokeWidth="0.8" />
        )}
        {layer === RingLayer.INNER && (
          // ท้ายด้าม (Pommel)
          <g>
            <circle cy="25" r="7" fill="#1c1917" stroke="#000" strokeWidth="0.8" />
            <circle cy="25" r="3" fill="#ef4444" fillOpacity="0.8" />
          </g>
        )}

        {/* เลือดหยด (Blood Drops) - สุ่มปรากฏในชั้นที่มีใบมีด */}
        {(layer === RingLayer.OUTER || layer === RingLayer.OUTER_MID) && (
          <circle cx={(Math.random()-0.5)*10} cy="-25" r="1.5" fill="#991b1b" />
        )}
      </g>
    );
  };

  return (
    <g 
      className="cursor-grab active:cursor-grabbing"
      onMouseDown={(e) => onDragStart(layer, e)}
      onTouchStart={(e) => onDragStart(layer, e)}
    >
      <defs>
        <clipPath id={clipId}>
          <path d={`
            M 250, ${250 - outerR}
            A ${outerR},${outerR} 0 1,1 250,${250 + outerR}
            A ${outerR},${outerR} 0 1,1 250,${250 - outerR}
            Z
            M 250, ${250 - innerR}
            A ${innerR},${innerR} 0 1,0 250,${250 + innerR}
            A ${innerR},${innerR} 0 1,0 250,${250 - innerR}
            Z
          `} fillRule="evenodd" />
        </clipPath>
      </defs>

      <g clipPath={`url(#${clipId})`}>
        {/* พื้นหลังกระจกฝ้าหม่นๆ */}
        <circle cx="250" cy="250" r={outerR} fill="#0c0a09" />

        <g style={{ 
          transform: `rotate(${rotation}deg)`, 
          transformOrigin: '250px 250px',
          transition: isDragging ? 'none' : 'transform 1.4s cubic-bezier(0.19, 1, 0.22, 1)'
        }}>
          {/* Shards */}
          <g>
            {shards.map((shard, i) => (
              <path 
                key={i} 
                d={shard.d} 
                fill={color} 
                fillOpacity={shard.opacity} 
                stroke="#000" 
                strokeWidth="0.6"
                style={{ filter: `brightness(${shard.darkness}) contrast(1.2)` }}
              />
            ))}
          </g>

          <DaggerSegment />
        </g>
      </g>

      {/* กรอบโลหะขึ้นสนิม */}
      <circle cx="250" cy="250" r={outerR} fill="none" stroke="#1c1917" strokeWidth="4" />
      <circle cx="250" cy="250" r={innerR} fill="none" stroke="#1c1917" strokeWidth="4" />
    </g>
  );
};

export default DialRing;

import React, { useState, useEffect, useCallback, useRef } from 'react';
import { RefreshCw, Eye, Sparkles, ShieldCheck, Flame, Sword, Skull, Fingerprint, Ghost, Timer, Search } from 'lucide-react';
import DialRing from './components/DialRing';
import { generateSecretChallenge } from './services/geminiService';
import { RingLayer } from './types';

const App: React.FC = () => {
  const [loading, setLoading] = useState(false);
  const [solved, setSolved] = useState(false);
  const [secret, setSecret] = useState({ secret: "", hint: "" });
  const [showHint, setShowHint] = useState(false);

  // Mystery Palette: Blood, Steel, Shadow
  const COLORS = {
    [RingLayer.OUTER]: "#44403c",     // Cold Iron
    [RingLayer.OUTER_MID]: "#991b1b", // Dried Blood
    [RingLayer.MIDDLE]: "#1c1917",    // Obsidian
    [RingLayer.INNER_MID]: "#292524", // Old Leather
    [RingLayer.INNER]: "#7f1d1d",     // Fresh Blood Gem
  };

  const [rotations, setRotations] = useState<Record<RingLayer, number>>({
    [RingLayer.OUTER]: (Math.floor(Math.random() * 3) + 1) * 120,
    [RingLayer.OUTER_MID]: (Math.floor(Math.random() * 3) + 1) * 120,
    [RingLayer.MIDDLE]: (Math.floor(Math.random() * 3) + 1) * 120,
    [RingLayer.INNER_MID]: (Math.floor(Math.random() * 3) + 1) * 120,
    [RingLayer.INNER]: (Math.floor(Math.random() * 3) + 1) * 120,
  });

  const [activeLayer, setActiveLayer] = useState<RingLayer | null>(null);
  const dragStartAngle = useRef<number>(0);
  const initialRotation = useRef<number>(0);
  const containerRef = useRef<HTMLDivElement>(null);

  const getAngle = (clientX: number, clientY: number) => {
    if (!containerRef.current) return 0;
    const rect = containerRef.current.getBoundingClientRect();
    const centerX = rect.left + rect.width / 2;
    const centerY = rect.top + rect.height / 2;
    const dx = clientX - centerX;
    const dy = clientY - centerY;
    return Math.atan2(dy, dx) * (180 / Math.PI);
  };

  const handleDragStart = (layer: RingLayer, e: React.MouseEvent | React.TouchEvent) => {
    if (solved || loading) return;
    const clientX = 'touches' in e ? (e as TouchEvent).touches[0].clientX : (e as MouseEvent).clientX;
    const clientY = 'touches' in e ? (e as TouchEvent).touches[0].clientY : (e as MouseEvent).clientY;
    setActiveLayer(layer);
    dragStartAngle.current = getAngle(clientX, clientY);
    initialRotation.current = rotations[layer];
  };

  const handleDragMove = useCallback((e: MouseEvent | TouchEvent) => {
    const layer = activeLayer;
    if (layer === null || solved) return;
    const clientX = 'touches' in e ? (e as TouchEvent).touches[0].clientX : (e as MouseEvent).clientX;
    const clientY = 'touches' in e ? (e as TouchEvent).touches[0].clientY : (e as MouseEvent).clientY;
    const currentAngle = getAngle(clientX, clientY);
    const angleDiff = currentAngle - dragStartAngle.current;

    setRotations(prev => ({ ...prev, [layer]: initialRotation.current + angleDiff }));
  }, [activeLayer, solved]);

  const handleDragEnd = useCallback(() => {
    const layer = activeLayer;
    if (layer === null) return;
    setRotations(prev => {
      const currentRot = prev[layer];
      const snapped = Math.round(currentRot / 120) * 120;
      const nextRotations = { ...prev, [layer]: snapped };
      const isAligned = Object.values(nextRotations).every((rot: number) => Math.abs(rot % 360) < 1);
      if (isAligned) setSolved(true);
      return nextRotations;
    });
    setActiveLayer(null);
  }, [activeLayer]);

  useEffect(() => {
    if (activeLayer !== null) {
      window.addEventListener('mousemove', handleDragMove);
      window.addEventListener('mouseup', handleDragEnd);
      window.addEventListener('touchmove', handleDragMove, { passive: false });
      window.addEventListener('touchend', handleDragEnd);
    }
    return () => {
      window.removeEventListener('mousemove', handleDragMove);
      window.removeEventListener('mouseup', handleDragEnd);
      window.removeEventListener('touchmove', handleDragMove);
      window.removeEventListener('touchend', handleDragEnd);
    };
  }, [activeLayer, handleDragMove, handleDragEnd]);

  const initGame = async () => {
    setLoading(true);
    setSolved(false);
    setShowHint(false);
    const data = await generateSecretChallenge();
    setSecret(data);
    setRotations({
      [RingLayer.OUTER]: (Math.random() > 0.5 ? 120 : 240),
      [RingLayer.OUTER_MID]: (Math.random() > 0.5 ? 120 : 240),
      [RingLayer.MIDDLE]: (Math.random() > 0.5 ? 120 : 240),
      [RingLayer.INNER_MID]: (Math.random() > 0.5 ? 120 : 240),
      [RingLayer.INNER]: (Math.random() > 0.5 ? 120 : 240),
    });
    setLoading(false);
  };

  useEffect(() => { initGame(); }, []);

  return (
    <div className="min-h-screen flex flex-col items-center justify-center p-4 text-stone-400 overflow-hidden relative select-none bg-[#0c0a09]">
      {/* Crime Scene Ambience */}
      <div className="absolute inset-0 pointer-events-none">
        <div className="absolute inset-0 bg-[radial-gradient(circle_at_50%_50%,rgba(153,27,27,0.05)_0%,transparent_100%)]"></div>
        {/* Shadow Overlay */}
        <div className="absolute inset-0 bg-black/20"></div>
      </div>

      <header className="text-center mb-10 z-10 font-serif">
        <div className="flex items-center justify-center gap-3 mb-3">
          <Ghost className="text-stone-700 animate-pulse" size={24} />
          <Skull className="text-red-900" size={40} />
          <Timer className="text-stone-700 animate-pulse" size={24} />
        </div>
        <h1 className="text-4xl md:text-5xl font-bold tracking-[0.3em] text-red-900 uppercase drop-shadow-[0_0_10px_rgba(153,27,27,0.3)] mb-2">
          THE MURDER DIAL
        </h1>
        <p className="text-[10px] tracking-[0.6em] text-stone-600 uppercase font-black">Reassemble the murder weapon to reveal the killer</p>
      </header>

      <div className="relative z-10 flex flex-col items-center">
        {/* The 5-Layer Murder Window */}
        <div 
          ref={containerRef}
          className={`relative w-[340px] h-[340px] md:w-[580px] md:h-[580px] rounded-full border-[24px] border-[#1c1917] shadow-[0_0_80px_rgba(0,0,0,1),inset_0_0_60px_rgba(0,0,0,0.9)] overflow-hidden transition-all duration-1000 ${solved ? 'border-red-900/40 ring-[15px] ring-red-900/10' : ''}`}
        >
          {solved && (
            <div className="absolute inset-0 z-20 pointer-events-none">
              <div className="absolute inset-0 bg-gradient-to-b from-red-900/10 via-black/40 to-black/80 mix-blend-multiply"></div>
              <div className="absolute inset-0 bg-[radial-gradient(circle_at_50%_50%,rgba(153,27,27,0.15)_0%,transparent_70%)] animate-pulse"></div>
            </div>
          )}

          <svg viewBox="0 0 500 500" className={`w-full h-full transition-all duration-[2000ms] ${solved ? 'brightness-110 saturate-[1.2]' : 'brightness-[0.7] grayscale-[0.3]'}`}>
            <DialRing
              layer={RingLayer.OUTER}
              radius={215}
              thickness={45}
              rotation={rotations[RingLayer.OUTER]}
              color={COLORS[RingLayer.OUTER]}
              isActive={!solved}
              isDragging={activeLayer === RingLayer.OUTER}
              onDragStart={handleDragStart}
            />
            <DialRing
              layer={RingLayer.OUTER_MID}
              radius={170}
              thickness={45}
              rotation={rotations[RingLayer.OUTER_MID]}
              color={COLORS[RingLayer.OUTER_MID]}
              isActive={!solved}
              isDragging={activeLayer === RingLayer.OUTER_MID}
              onDragStart={handleDragStart}
            />
            <DialRing
              layer={RingLayer.MIDDLE}
              radius={125}
              thickness={45}
              rotation={rotations[RingLayer.MIDDLE]}
              color={COLORS[RingLayer.MIDDLE]}
              isActive={!solved}
              isDragging={activeLayer === RingLayer.MIDDLE}
              onDragStart={handleDragStart}
            />
            <DialRing
              layer={RingLayer.INNER_MID}
              radius={80}
              thickness={45}
              rotation={rotations[RingLayer.INNER_MID]}
              color={COLORS[RingLayer.INNER_MID]}
              isActive={!solved}
              isDragging={activeLayer === RingLayer.INNER_MID}
              onDragStart={handleDragStart}
            />
            <DialRing
              layer={RingLayer.INNER}
              radius={35}
              thickness={45}
              rotation={rotations[RingLayer.INNER]}
              color={COLORS[RingLayer.INNER]}
              isActive={!solved}
              isDragging={activeLayer === RingLayer.INNER}
              onDragStart={handleDragStart}
            />

            {/* Dark Core */}
            <circle cx="250" cy="250" r="10" fill={solved ? "#ef4444" : "#1c1917"} className="transition-all duration-1000 shadow-[0_0_10px_rgba(239,68,68,0.5)]" />
          </svg>
        </div>

        {/* Escape Room Controls */}
        <div className="mt-12 w-full max-w-xl space-y-8 z-20">
          {solved ? (
            <div className="bg-stone-950/95 border-2 border-red-900/30 p-10 rounded-3xl shadow-2xl animate-in fade-in zoom-in duration-1000 backdrop-blur-3xl text-center relative overflow-hidden group">
              <div className="absolute top-0 left-0 w-full h-1 bg-gradient-to-r from-transparent via-red-900 to-transparent"></div>
              <Fingerprint className="absolute top-6 left-6 text-red-900/20 animate-pulse" size={24} />
              <Search className="absolute bottom-6 right-6 text-red-900/20 animate-pulse" size={24} />

              <h2 className="text-red-700 font-bold text-2xl mb-6 tracking-[0.5em] uppercase font-serif">
                THE CONFESSION
              </h2>
              <div className="text-xl md:text-2xl font-medium text-stone-300 mb-10 py-10 px-8 bg-black/60 rounded-2xl italic border border-red-950 leading-relaxed font-serif tracking-wide">
                "{secret.secret}"
              </div>
              <button
                onClick={initGame}
                className="w-full bg-red-950/20 hover:bg-red-950/40 text-red-700 font-bold py-5 rounded-2xl border border-red-900/30 transition-all flex items-center justify-center gap-4 uppercase tracking-[0.4em] active:scale-95"
              >
                <RefreshCw size={22} className={loading ? "animate-spin" : ""} />
