        const p3y = 250 + rEnd * Math.sin(aEnd);
        const p4x = 250 + rStart * Math.cos(aEnd);
        const p4y = 250 + rStart * Math.sin(aEnd);

        shardList.push({
          d: `M ${p1x} ${p1y} L ${p2x} ${p2y} A ${rEnd} ${rEnd} 0 0 1 ${p3x} ${p3y} L ${p4x} ${p4y} A ${rStart} ${rStart} 0 0 0 ${p1x} ${p1y} Z`,
          opacity: 0.05 + Math.random() * 0.2,
          darkness: 0.2 + Math.random() * 0.4
        });
      }
    }
    return shardList;
  }, [innerR, thickness, layer]);

  // สัญลักษณ์ "มีดสั้นสังหาร" แบบปรับปรุงใหม่ (Realistic Dagger Segments)
  const DaggerSegment = () => {
    // หมุนไปที่ -90 องศาเพื่อให้แนวมีดตั้งตรงที่ 12 นาฬิกา (0 deg ของการหมุนภายนอก)
    return (
      <g transform="translate(250, 250) rotate(-90)">
        <defs>
          <linearGradient id="bladeGrad" x1="0%" y1="0%" x2="0%" y2="100%">
            <stop offset="0%" stopColor="#d1d5db" />
            <stop offset="50%" stopColor="#9ca3af" />
            <stop offset="100%" stopColor="#4b5563" />
          </linearGradient>
        </defs>

        {layer === RingLayer.OUTER && (
          // ปลายมีดแหลม (The Pointed Tip)
          <g>
            <path d="M 210 0 L 175 -12 L 175 12 Z" fill="url(#bladeGrad)" stroke="#000" strokeWidth="1.5" />
            {/* คราบเลือดปลายมีด */}
            <path d="M 210 0 L 190 -7 L 190 7 Z" fill="#7f1d1d" fillOpacity="0.8" />
          </g>
        )}

        {layer === RingLayer.OUTER_MID && (
          // ตัวใบมีดส่วนกลาง (Main Blade)
          <g>
            <path d="M 175 -12 L 130 -15 L 130 15 L 175 12 Z" fill="url(#bladeGrad)" stroke="#000" strokeWidth="1.5" />
            {/* รอยหยดเลือดข้างมีด */}
            <circle cx="150" cy="8" r="3" fill="#991b1b" fillOpacity="0.7" />
            <path d="M 155 -5 L 140 -2 L 145 -8 Z" fill="#7f1d1d" />
          </g>
        )}

        {layer === RingLayer.MIDDLE && (
          // โกร่งมีด (The Crossguard)
          <g>
            {/* ส่วนต่อของใบมีด */}
            <path d="M 130 -15 L 105 -18 L 105 18 L 130 15 Z" fill="url(#bladeGrad)" stroke="#000" strokeWidth="1.5" />
            {/* ตัวกั้นมือ (The Guard) */}
            <rect x="90" y="-35" width="15" height="70" rx="3" fill="#1c1917" stroke="#000" strokeWidth="1.5" />
            {/* ตราประทับลึกลับบนโกร่งมีด */}
            <circle cx="97.5" cy="0" r="4" fill="none" stroke="#ef4444" strokeWidth="0.5" opacity="0.6" />
          </g>
        )}

        {layer === RingLayer.INNER_MID && (
          // ด้ามจับ (The Wrapped Hilt)
          <g>
            <rect x="50" y="-12" width="40" height="24" rx="4" fill="#292524" stroke="#000" strokeWidth="1.5" />
            {/* ลวดลายการพันด้ามจับ */}
            {[58, 66, 74, 82].map(x => (
              <line key={x} x1={x} y1="-10" x2={x-3} y2="10" stroke="#1c1917" strokeWidth="1.5" opacity="0.8" />
            ))}
          </g>
        )}

        {layer === RingLayer.INNER && (
          // ท้ายด้าม (The Pommel)
          <g>
            <path d="M 50 -12 L 30 -12 L 30 12 L 50 12 Z" fill="#292524" stroke="#000" strokeWidth="1" />
            <circle cx="20" cy="0" r="12" fill="#1c1917" stroke="#000" strokeWidth="1.5" />
            {/* อัญมณีสีเลือดท้ายมีด */}
            <path d="M 20 -6 L 14 0 L 20 6 L 26 0 Z" fill="#ef4444" className="animate-pulse" />
          </g>
        )}
      </g>
    );
  };

  return (
    <g 
      className="cursor-grab active:cursor-grabbing"
      onMouseDown={(e) => onDragStart(layer, e)}
      onTouchStart={(e) => onDragStart(layer, e)}
    >
      <defs>
        <clipPath id={clipId}>
          <path d={`
            M 250, ${250 - outerR}
            A ${outerR},${outerR} 0 1,1 250,${250 + outerR}
            A ${outerR},${outerR} 0 1,1 250,${250 - outerR}
            Z
            M 250, ${250 - innerR}
            A ${innerR},${innerR} 0 1,0 250,${250 + innerR}
            A ${innerR},${innerR} 0 1,0 250,${250 - innerR}
            Z
          `} fillRule="evenodd" />
        </clipPath>
      </defs>

      <g clipPath={`url(#${clipId})`}>
        <circle cx="250" cy="250" r={outerR} fill="#050505" />

        <g style={{ 
          transform: `rotate(${rotation}deg)`, 
          transformOrigin: '250px 250px',
          transition: isDragging ? 'none' : 'transform 1.2s cubic-bezier(0.19, 1, 0.22, 1)'
        }}>
          {/* Shards background */}
          <g>
            {shards.map((shard, i) => (
              <path 
                key={i} 
                d={shard.d} 
                fill={color} 
                fillOpacity={shard.opacity} 
                stroke="#000" 
                strokeWidth="0.4"
              />
            ))}
          </g>

          <DaggerSegment />
        </g>
      </g>

      {/* กรอบโลหะ (Metal Rims) */}
      <circle cx="250" cy="250" r={outerR} fill="none" stroke="#111" strokeWidth="3" opacity="0.8" />
      <circle cx="250" cy="250" r={innerR} fill="none" stroke="#111" strokeWidth="3" opacity="0.8" />
    </g>
  );
};

export default DialRing;

import React, { useState, useEffect, useCallback, useRef } from 'react';
import { RefreshCw, Eye, Sparkles, ShieldCheck, Flame, Sword, Skull, Fingerprint, Ghost, Timer, Search, ChevronDown } from 'lucide-react';
import DialRing from './components/DialRing';
import { generateSecretChallenge } from './services/geminiService';
import { RingLayer } from './types';

const App: React.FC = () => {
  const [loading, setLoading] = useState(false);
  const [solved, setSolved] = useState(false);
  const [secret, setSecret] = useState({ secret: "", hint: "" });
  const [showHint, setShowHint] = useState(false);

  // Mystery Palette: More dramatic contrasts
  const COLORS = {
    [RingLayer.OUTER]: "#27272a",     // Dark Steel
    [RingLayer.OUTER_MID]: "#450a0a", // Deep Blood
    [RingLayer.MIDDLE]: "#18181b",    // Dark Iron
    [RingLayer.INNER_MID]: "#27272a", // Darker Steel
    [RingLayer.INNER]: "#7f1d1d",     // Core Ruby
  };

  const [rotations, setRotations] = useState<Record<RingLayer, number>>({
    [RingLayer.OUTER]: (Math.floor(Math.random() * 3) + 1) * 120,
    [RingLayer.OUTER_MID]: (Math.floor(Math.random() * 3) + 1) * 120,
    [RingLayer.MIDDLE]: (Math.floor(Math.random() * 3) + 1) * 120,
    [RingLayer.INNER_MID]: (Math.floor(Math.random() * 3) + 1) * 120,
    [RingLayer.INNER]: (Math.floor(Math.random() * 3) + 1) * 120,
  });

  const [activeLayer, setActiveLayer] = useState<RingLayer | null>(null);
  const dragStartAngle = useRef<number>(0);
  const initialRotation = useRef<number>(0);
  const containerRef = useRef<HTMLDivElement>(null);

  const getAngle = (clientX: number, clientY: number) => {
    if (!containerRef.current) return 0;
    const rect = containerRef.current.getBoundingClientRect();
    const centerX = rect.left + rect.width / 2;
    const centerY = rect.top + rect.height / 2;
    const dx = clientX - centerX;
    const dy = clientY - centerY;
    return Math.atan2(dy, dx) * (180 / Math.PI);
  };

  const handleDragStart = (layer: RingLayer, e: React.MouseEvent | React.TouchEvent) => {
    if (solved || loading) return;
    const clientX = 'touches' in e ? (e as TouchEvent).touches[0].clientX : (e as MouseEvent).clientX;
    const clientY = 'touches' in e ? (e as TouchEvent).touches[0].clientY : (e as MouseEvent).clientY;
    setActiveLayer(layer);
    dragStartAngle.current = getAngle(clientX, clientY);
    initialRotation.current = rotations[layer];
  };

  const handleDragMove = useCallback((e: MouseEvent | TouchEvent) => {
    const layer = activeLayer;
    if (layer === null || solved) return;
    const clientX = 'touches' in e ? (e as TouchEvent).touches[0].clientX : (e as MouseEvent).clientX;
    const clientY = 'touches' in e ? (e as TouchEvent).touches[0].clientY : (e as MouseEvent).clientY;
    const currentAngle = getAngle(clientX, clientY);
    const angleDiff = currentAngle - dragStartAngle.current;

    setRotations(prev => ({ ...prev, [layer]: initialRotation.current + angleDiff }));
  }, [activeLayer, solved]);

  const handleDragEnd = useCallback(() => {
    const layer = activeLayer;
    if (layer === null) return;
    setRotations(prev => {
      const currentRot = prev[layer];
      const snapped = Math.round(currentRot / 120) * 120;
      const nextRotations = { ...prev, [layer]: snapped };

      // ตรวจสอบความแม่นยำ (0 deg คือตำแหน่งที่มีดประกอบกันสมบูรณ์ที่ 12 นาฬิกา)
      const isAligned = Object.values(nextRotations).every((rot: number) => {
        const normalized = ((rot % 360) + 360) % 360;
        return normalized < 1 || normalized > 359;
      });

      if (isAligned) setSolved(true);
      return nextRotations;
    });
    setActiveLayer(null);
  }, [activeLayer]);

  useEffect(() => {
    if (activeLayer !== null) {
      window.addEventListener('mousemove', handleDragMove);
      window.addEventListener('mouseup', handleDragEnd);
      window.addEventListener('touchmove', handleDragMove, { passive: false });
      window.addEventListener('touchend', handleDragEnd);
    }
    return () => {
      window.removeEventListener('mousemove', handleDragMove);
      window.removeEventListener('mouseup', handleDragEnd);
      window.removeEventListener('touchmove', handleDragMove);
      window.removeEventListener('touchend', handleDragEnd);
    };
  }, [activeLayer, handleDragMove, handleDragEnd]);

  const initGame = async () => {
    setLoading(true);
    setSolved(false);
    setShowHint(false);
    const data = await generateSecretChallenge();
    setSecret(data);
    setRotations({
      [RingLayer.OUTER]: (Math.random() > 0.5 ? 120 : 240),
      [RingLayer.OUTER_MID]: (Math.random() > 0.5 ? 120 : 240),
      [RingLayer.MIDDLE]: (Math.random() > 0.5 ? 120 : 240),
      [RingLayer.INNER_MID]: (Math.random() > 0.5 ? 120 : 240),
      [RingLayer.INNER]: (Math.random() > 0.5 ? 120 : 240),
    });
    setLoading(false);
  };

  useEffect(() => { initGame(); }, []);

  return (
    <div className="min-h-screen flex flex-col items-center justify-center p-4 text-stone-500 overflow-hidden relative select-none bg-[#080707]">
      {/* Dark Mystery Background */}
      <div className="absolute inset-0 pointer-events-none">
        <div className="absolute inset-0 bg-[radial-gradient(circle_at_50%_50%,rgba(127,29,29,0.08)_0%,transparent_100%)] opacity-60"></div>
        {/* Shadow Corners */}
        <div className="absolute inset-0 bg-[linear-gradient(to_bottom_right,rgba(0,0,0,0.8),transparent_40%),linear-gradient(to_top_left,rgba(0,0,0,0.8),transparent_40%)]"></div>
      </div>

      <header className="text-center mb-8 z-10 font-serif">
        <div className="flex items-center justify-center gap-4 mb-2">
          <Fingerprint className="text-red-950 opacity-50" size={24} />
          <Skull className="text-red-900" size={42} />
          <Fingerprint className="text-red-950 opacity-50" size={24} />
        </div>
        <h1 className="text-4xl md:text-5xl font-bold tracking-[0.2em] text-red-950 uppercase mb-1">
          MURDER CASE #09
        </h1>
        <p className="text-[9px] tracking-[1em] text-stone-700 uppercase font-black">Align the fragments of the dagger at the top mark</p>
      </header>

      <div className="relative z-10 flex flex-col items-center">
        {/* The 5-Layer Murder Window */}
        <div className="relative">
          {/* Target Alignment Guide (ลูกศรชี้ที่ตำแหน่ง 12 นาฬิกา) */}
          <div className="absolute -top-10 left-1/2 -translate-x-1/2 z-30 flex flex-col items-center opacity-80">
            <span className="text-[10px] text-red-900 font-black tracking-widest mb-1">ALIGN HERE</span>
            <ChevronDown className="text-red-900 animate-bounce" size={32} />
          </div>

          <div 
            ref={containerRef}
            className={`relative w-[340px] h-[340px] md:w-[580px] md:h-[580px] rounded-full border-[28px] border-[#121111] shadow-[0_0_100px_rgba(0,0,0,1),inset_0_0_80px_rgba(0,0,0,0.9)] overflow-hidden transition-all duration-1000 ${solved ? 'ring-[20px] ring-red-900/20' : ''}`}
          >
            {solved && (
              <div className="absolute inset-0 z-20 pointer-events-none">
                <div className="absolute inset-0 bg-red-950/20 mix-blend-overlay animate-pulse"></div>
              </div>
            )}

            <svg viewBox="0 0 500 500" className={`w-full h-full transition-all duration-[2000ms] ${solved ? 'brightness-125 saturate-150' : 'brightness-[0.75] contrast-125'}`}>
              <DialRing
                layer={RingLayer.OUTER}
                radius={215}
                thickness={45}
                rotation={rotations[RingLayer.OUTER]}
                color={COLORS[RingLayer.OUTER]}
                isActive={!solved}
                isDragging={activeLayer === RingLayer.OUTER}
                onDragStart={handleDragStart}
              />
              <DialRing
                layer={RingLayer.OUTER_MID}
                radius={170}
                thickness={45}
                rotation={rotations[RingLayer.OUTER_MID]}
                color={COLORS[RingLayer.OUTER_MID]}
                isActive={!solved}
                isDragging={activeLayer === RingLayer.OUTER_MID}
                onDragStart={handleDragStart}
              />
              <DialRing
                layer={RingLayer.MIDDLE}
                radius={125}
                thickness={45}
                rotation={rotations[RingLayer.MIDDLE]}
                color={COLORS[RingLayer.MIDDLE]}
                isActive={!solved}
                isDragging={activeLayer === RingLayer.MIDDLE}
                onDragStart={handleDragStart}
              />
              <DialRing
                layer={RingLayer.INNER_MID}
                radius={80}
                thickness={45}
                rotation={rotations[RingLayer.INNER_MID]}
                color={COLORS[RingLayer.INNER_MID]}
                isActive={!solved}
                isDragging={activeLayer === RingLayer.INNER_MID}
                onDragStart={handleDragStart}
              />
              <DialRing
                layer={RingLayer.INNER}
                radius={35}
                thickness={45}
                rotation={rotations[RingLayer.INNER]}
                color={COLORS[RingLayer.INNER]}
                isActive={!solved}
                isDragging={activeLayer === RingLayer.INNER}
                onDragStart={handleDragStart}
              />

              {/* แกนกลางลึกลับ */}
              <circle cx="250" cy="250" r="12" fill="#000" />
              <circle cx="250" cy="250" r="4" fill={solved ? "#ef4444" : "#111"} className="transition-all duration-1000" />
            </svg>
          </div>
        </div>

        {/* Investigative Controls */}
        <div className="mt-12 w-full max-w-xl space-y-8 z-20">
          {solved ? (
            <div className="bg-stone-950/95 border-2 border-red-900/40 p-8 rounded-3xl shadow-2xl animate-in fade-in zoom-in duration-1000 backdrop-blur-3xl text-center relative overflow-hidden group">
              <div className="absolute top-0 left-0 w-full h-1 bg-red-900/60"></div>
              <Search className="absolute top-6 left-6 text-red-900/20" size={20} />

              <h2 className="text-red-800 font-bold text-xl mb-4 tracking-[0.6em] uppercase font-serif">
                CRIME REVEALED
              </h2>
              <div className="text-lg md:text-xl font-medium text-stone-300 mb-8 py-8 px-6 bg-black/40 rounded-2xl italic border border-red-950/20 leading-relaxed font-serif">
                "{secret.secret}"
              </div>
              <button
                onClick={initGame}
                className="w-full bg-stone-900 hover:bg-stone-800 text-red-700 font-bold py-4 rounded-xl border border-red-900/20 transition-all flex items-center justify-center gap-4 uppercase tracking-[0.3em]"
              >
                <RefreshCw size={20} className={loading ? "animate-spin" : ""} />
                NEW INVESTIGATION
              </button>
            </div>
          ) : (
            <div className="space-y-6">
              {/* Layer Selection Hub */}
              <div className="grid grid-cols-5 gap-3">
                {[
                  { layer: RingLayer.OUTER, color: 'bg-zinc-800', label: 'TIP' },
                  { layer: RingLayer.OUTER_MID, color: 'bg-red-950', label: 'BLADE' },
                  { layer: RingLayer.MIDDLE, color: 'bg-zinc-900', label: 'GUARD' },
                  { layer: RingLayer.INNER_MID, color: 'bg-zinc-800', label: 'HILT' },
                  { layer: RingLayer.INNER, color: 'bg-red-900', label: 'BASE' }
                ].map((knob) => (
                  <div 
                    key={knob.layer}
                    onClick={() => setActiveLayer(knob.layer)} 
                    className={`flex flex-col items-center p-3 rounded-2xl border transition-all cursor-pointer ${activeLayer === knob.layer ? 'bg-red-950/30 border-red-900/60 shadow-lg scale-105' : 'bg-stone-950/60 border-stone-900 opacity-60'}`}
                  >
                    <div className={`w-10 h-10 rounded-full mb-2 ${knob.color} shadow-inner flex items-center justify-center border-2 border-black/50 relative`}>
                      <div className="w-1 h-1 bg-red-500/20 rounded-full"></div>
                    </div>
                    <span className="text-[7px] font-black tracking-widest text-stone-700">{knob.label}</span>
                  </div>
                ))}
              </div>

              <div className="flex gap-4">
                <button
                  onClick={() => setShowHint(!showHint)}
                  className="flex-1 p-5 bg-stone-900/40 hover:bg-stone-800/60 text-stone-600 rounded-2xl border border-stone-900/50 transition-all flex items-center justify-center gap-4 text-xs font-black tracking-[0.4em] uppercase"
                >
                  <Search size={18} />
                  {showHint ? "CONCEAL" : "HINT"}
                </button>
              </div>

              {showHint && (
                <div className="p-6 bg-black/40 border-l-4 border-red-950/60 text-stone-600 text-center text-sm italic animate-in slide-in-from-left duration-700 font-serif tracking-widest leading-relaxed">
                  "{secret.hint}"
                </div>
              )}
            </div>
          )}
        </div>
      </div>

      <footer className="mt-12 text-stone-900 text-[8px] tracking-[1.5em] uppercase z-10 font-bold opacity-30">
        EVIDENCE RECONSTRUCTION • UNIT 7
      </footer>
    </div>
  );
};

export default App;

import React, { useMemo } from 'react';
import { RingLayer } from '../types';

interface DialRingProps {
  layer: RingLayer;
  radius: number;
  thickness: number;
  rotation: number;
  color: string;
  isActive: boolean;
  isDragging: boolean;
  onDragStart: (layer: RingLayer, e: React.MouseEvent | React.TouchEvent) => void;
}

const DialRing: React.FC<DialRingProps> = ({
  layer,
  radius,
  thickness,
  rotation,
  color,
  isActive,
  isDragging,
  onDragStart
}) => {
  const innerR = radius - thickness / 2;
  const outerR = radius + thickness / 2;
  const clipId = `clip-${layer}`;

  // สร้างเศษกระจกพื้นหลัง (Background Mosaic)
  const shards = useMemo(() => {
    const shardList = [];
    const radialSegments = 2; 
    const angularSegments = layer === RingLayer.OUTER ? 36 : 24; 

    for (let r = 0; r < radialSegments; r++) {
      for (let a = 0; a < angularSegments; a++) {
        const jitter = (Math.random() - 0.5) * 5;
        const rStart = innerR + (r * (thickness / radialSegments));
        const rEnd = innerR + ((r + 1) * (thickness / radialSegments));
        const aStart = (a * (360 / angularSegments) + jitter) * (Math.PI / 180);
        const aEnd = ((a + 1) * (360 / angularSegments) + jitter) * (Math.PI / 180);

        const p1x = 250 + rStart * Math.cos(aStart);
        const p1y = 250 + rStart * Math.sin(aStart);
        const p2x = 250 + rEnd * Math.cos(aStart);
        const p2y = 250 + rEnd * Math.sin(aStart);
        const p3x = 250 + rEnd * Math.cos(aEnd);
        const p3y = 250 + rEnd * Math.sin(aEnd);
        const p4x = 250 + rStart * Math.cos(aEnd);
        const p4y = 250 + rStart * Math.sin(aEnd);

        shardList.push({
          d: `M ${p1x} ${p1y} L ${p2x} ${p2y} A ${rEnd} ${rEnd} 0 0 1 ${p3x} ${p3y} L ${p4x} ${p4y} A ${rStart} ${rStart} 0 0 0 ${p1x} ${p1y} Z`,
          opacity: 0.1 + Math.random() * 0.15,
          brightness: 0.3 + Math.random() * 0.3
        });
      }
    }
    return shardList;
  }, [innerR, thickness, layer]);

  // สัญลักษณ์ชิ้นส่วนมีดสั้น (Stained Glass Dagger Segments)
  const DaggerSegment = () => {
    // หมุนไปที่ -90 เพื่อให้มีดตั้งตรงที่ 12 นาฬิกา
    return (
      <g transform="translate(250, 250) rotate(-90)">
        <defs>
          <linearGradient id={`bladeGrad-${layer}`} x1="0%" y1="0%" x2="0%" y2="100%">
            <stop offset="0%" stopColor="#e5e7eb" />
            <stop offset="40%" stopColor="#9ca3af" />
            <stop offset="100%" stopColor="#4b5563" />
          </linearGradient>
          <linearGradient id={`bloodGrad-${layer}`} x1="0%" y1="0%" x2="100%" y2="0%">
            <stop offset="0%" stopColor="#991b1b" />
            <stop offset="100%" stopColor="#450a0a" />
          </linearGradient>
        </defs>

        {layer === RingLayer.OUTER && (
          // 1. ปลายมีด (Tip) - รัศมี 192.5 - 237.5
          <g>
            <path d="M 235 0 L 192 -20 L 192 20 Z" fill="url(#bladeGrad-outer)" stroke="#111" strokeWidth="2" />
            <path d="M 235 0 L 210 -10 L 210 10 Z" fill="url(#bloodGrad-outer)" fillOpacity="0.8" />
          </g>
        )}

        {layer === RingLayer.OUTER_MID && (
          // 2. ใบมีดส่วนบน (Upper Blade) - รัศมี 147.5 - 192.5
          <g>
            <path d="M 192 -20 L 147 -24 L 147 24 L 192 20 Z" fill="url(#bladeGrad-outerMid)" stroke="#111" strokeWidth="2" />
            <path d="M 192 5 L 147 8 L 147 20 L 192 15 Z" fill="url(#bloodGrad-outerMid)" fillOpacity="0.6" />
          </g>
        )}

        {layer === RingLayer.MIDDLE && (
          // 3. ใบมีดส่วนล่าง + โกร่งมีด (Lower Blade & Guard) - รัศมี 102.5 - 147.5
          <g>
            {/* โคนใบมีด */}
            <path d="M 147 -24 L 115 -24 L 115 24 L 147 24 Z" fill="url(#bladeGrad-middle)" stroke="#111" strokeWidth="2" />
            {/* โกร่งมีด (Crossguard) - ออกแบบให้หนาและดูเหมือนกระจกสีดำเข้ม */}
            <rect x="102" y="-45" width="13" height="90" rx="2" fill="#0c0a09" stroke="#111" strokeWidth="2.5" />
            <path d="M 115 10 L 102 15 L 102 24 L 115 24 Z" fill="#7f1d1d" />
          </g>
        )}

        {layer === RingLayer.INNER_MID && (
          // 4. ด้ามจับ (Hilt/Handle) - รัศมี 57.5 - 102.5
          <g>
            <rect x="58" y="-16" width="44" height="32" rx="4" fill="#292524" stroke="#111" strokeWidth="2" />
            {/* ลายพันด้าม */}
            {[66, 76, 86].map(x => (
              <line key={x} x1={x} y1="-14" x2={x-5} y2="14" stroke="#111" strokeWidth="3" />
            ))}
          </g>
        )}

        {layer === RingLayer.INNER && (
          // 5. ท้ายด้าม (Pommel) - รัศมี 12.5 - 57.5
          <g>
            <rect x="35" y="-12" width="23" height="24" fill="#1c1917" stroke="#111" strokeWidth="2" />
            <circle cx="25" cy="0" r="16" fill="#0c0a09" stroke="#111" strokeWidth="2" />
            {/* ทับทิมสีเลือดกลางท้ายมีด */}
            <path d="M 25 -8 L 17 0 L 25 8 L 33 0 Z" fill="#ef4444" className="animate-pulse" />
          </g>
        )}
      </g>
    );
  };

  return (
    <g 
      className="cursor-grab active:cursor-grabbing"
      onMouseDown={(e) => onDragStart(layer, e)}
      onTouchStart={(e) => onDragStart(layer, e)}
    >
      <defs>
        <clipPath id={clipId}>
          <path d={`
            M 250, ${250 - outerR}
            A ${outerR},${outerR} 0 1,1 250,${250 + outerR}
            A ${outerR},${outerR} 0 1,1 250,${250 - outerR}
            Z
            M 250, ${250 - innerR}
            A ${innerR},${innerR} 0 1,0 250,${250 + innerR}
            A ${innerR},${innerR} 0 1,0 250,${250 - innerR}
            Z
          `} fillRule="evenodd" />
        </clipPath>
      </defs>

      <g clipPath={`url(#${clipId})`}>
        {/* พื้นหลังกระจกมืดเพื่อให้มีดเด่น */}
        <circle cx="250" cy="250" r={outerR} fill="#050505" />

        <g style={{ 
          transform: `rotate(${rotation}deg)`, 
          transformOrigin: '250px 250px',
          transition: isDragging ? 'none' : 'transform 1.5s cubic-bezier(0.16, 1, 0.3, 1)'
        }}>
          {/* Mosaic Shards */}
          <g>
            {shards.map((shard, i) => (
              <path 
                key={i} 
                d={shard.d} 
                fill={color} 
                fillOpacity={shard.opacity} 
                stroke="#000" 
                strokeWidth="0.5"
                style={{ filter: `brightness(${shard.brightness})` }}
              />
            ))}
          </g>

          <DaggerSegment />
        </g>
      </g>

      {/* กรอบตะกั่ว (Stained Glass Leading) */}
      <circle cx="250" cy="250" r={outerR} fill="none" stroke="#1a1a1a" strokeWidth="4" />
      <circle cx="250" cy="250" r={innerR} fill="none" stroke="#1a1a1a" strokeWidth="4" />
    </g>
  );
};

export default DialRing;

import React, { useState, useEffect, useCallback, useRef } from 'react';
import { RefreshCw, Eye, Skull, Fingerprint, Ghost, Timer, Search, ChevronDown } from 'lucide-react';
import DialRing from './components/DialRing';
import { generateSecretChallenge } from './services/geminiService';
import { RingLayer } from './types';

const App: React.FC = () => {
  const [loading, setLoading] = useState(false);
  const [solved, setSolved] = useState(false);
  const [secret, setSecret] = useState({ secret: "", hint: "" });
  const [showHint, setShowHint] = useState(false);

  // Mystery Palette: Focus on contrast
  const COLORS = {
    [RingLayer.OUTER]: "#18181b",     // Zinc dark
    [RingLayer.OUTER_MID]: "#450a0a", // Deep Blood
    [RingLayer.MIDDLE]: "#0c0a09",    // Stone black
    [RingLayer.INNER_MID]: "#1c1917", // Warm dark
    [RingLayer.INNER]: "#991b1b",     // Blood red
  };

  const [rotations, setRotations] = useState<Record<RingLayer, number>>({
    [RingLayer.OUTER]: (Math.floor(Math.random() * 3) + 1) * 120,
    [RingLayer.OUTER_MID]: (Math.floor(Math.random() * 3) + 1) * 120,
    [RingLayer.MIDDLE]: (Math.floor(Math.random() * 3) + 1) * 120,
    [RingLayer.INNER_MID]: (Math.floor(Math.random() * 3) + 1) * 120,
    [RingLayer.INNER]: (Math.floor(Math.random() * 3) + 1) * 120,
  });

  const [activeLayer, setActiveLayer] = useState<RingLayer | null>(null);
  const dragStartAngle = useRef<number>(0);
  const initialRotation = useRef<number>(0);
  const containerRef = useRef<HTMLDivElement>(null);

  const getAngle = (clientX: number, clientY: number) => {
    if (!containerRef.current) return 0;
    const rect = containerRef.current.getBoundingClientRect();
    const centerX = rect.left + rect.width / 2;
    const centerY = rect.top + rect.height / 2;
    const dx = clientX - centerX;
    const dy = clientY - centerY;
    return Math.atan2(dy, dx) * (180 / Math.PI);
  };

  const handleDragStart = (layer: RingLayer, e: React.MouseEvent | React.TouchEvent) => {
    if (solved || loading) return;
    const clientX = 'touches' in e ? (e as TouchEvent).touches[0].clientX : (e as MouseEvent).clientX;
    const clientY = 'touches' in e ? (e as TouchEvent).touches[0].clientY : (e as MouseEvent).clientY;
    setActiveLayer(layer);
    dragStartAngle.current = getAngle(clientX, clientY);
    initialRotation.current = rotations[layer];
  };

  const handleDragMove = useCallback((e: MouseEvent | TouchEvent) => {
    const layer = activeLayer;
    if (layer === null || solved) return;
    const clientX = 'touches' in e ? (e as TouchEvent).touches[0].clientX : (e as MouseEvent).clientX;
    const clientY = 'touches' in e ? (e as TouchEvent).touches[0].clientY : (e as MouseEvent).clientY;
    const currentAngle = getAngle(clientX, clientY);
    const angleDiff = currentAngle - dragStartAngle.current;

    setRotations(prev => ({ ...prev, [layer]: initialRotation.current + angleDiff }));
  }, [activeLayer, solved]);

  const handleDragEnd = useCallback(() => {
    const layer = activeLayer;
    if (layer === null) return;
    setRotations(prev => {
      const currentRot = prev[layer];
      const snapped = Math.round(currentRot / 120) * 120;
      const nextRotations = { ...prev, [layer]: snapped };

      const isAligned = Object.values(nextRotations).every((rot: number) => {
        const normalized = ((rot % 360) + 360) % 360;
        return normalized < 2 || normalized > 358;
      });

      if (isAligned) setSolved(true);
      return nextRotations;
    });
    setActiveLayer(null);
  }, [activeLayer]);

  useEffect(() => {
    if (activeLayer !== null) {
      window.addEventListener('mousemove', handleDragMove);
      window.addEventListener('mouseup', handleDragEnd);
      window.addEventListener('touchmove', handleDragMove, { passive: false });
      window.addEventListener('touchend', handleDragEnd);
    }
    return () => {
      window.removeEventListener('mousemove', handleDragMove);
      window.removeEventListener('mouseup', handleDragEnd);
      window.removeEventListener('touchmove', handleDragMove);
      window.removeEventListener('touchend', handleDragEnd);
    };
  }, [activeLayer, handleDragMove, handleDragEnd]);

  const initGame = async () => {
    setLoading(true);
    setSolved(false);
    setShowHint(false);
    const data = await generateSecretChallenge();
    setSecret(data);
    setRotations({
      [RingLayer.OUTER]: (Math.random() > 0.5 ? 120 : 240),
      [RingLayer.OUTER_MID]: (Math.random() > 0.5 ? 120 : 240),
      [RingLayer.MIDDLE]: (Math.random() > 0.5 ? 120 : 240),
      [RingLayer.INNER_MID]: (Math.random() > 0.5 ? 120 : 240),
      [RingLayer.INNER]: (Math.random() > 0.5 ? 120 : 240),
    });
    setLoading(false);
  };

  useEffect(() => { initGame(); }, []);

  return (
    <div className="min-h-screen flex flex-col items-center justify-center p-4 text-stone-500 overflow-hidden relative select-none bg-[#0a0908]">
      {/* Dark Ambient Layer */}
      <div className="absolute inset-0 pointer-events-none">
        <div className="absolute inset-0 bg-[radial-gradient(circle_at_50%_45%,rgba(153,27,27,0.1)_0%,transparent_100%)] opacity-50"></div>
        <div className="absolute inset-0 bg-black/40"></div>
      </div>

      <header className="text-center mb-10 z-10 font-serif">
        <div className="flex items-center justify-center gap-4 mb-3">
          <Fingerprint className="text-stone-800" size={24} />
          <Skull className="text-red-900 drop-shadow-[0_0_8px_rgba(153,27,27,0.5)]" size={48} />
          <Fingerprint className="text-stone-800" size={24} />
        </div>
        <h1 className="text-3xl md:text-5xl font-bold tracking-[0.2em] text-stone-200 uppercase mb-2">
          RECONSTRUCT <span className="text-red-900">MURDER</span>
        </h1>
        <p className="text-[10px] tracking-[0.8em] text-stone-700 uppercase font-black">Align the bloody shards to reveal the killer's secret</p>
      </header>

      <div className="relative z-10 flex flex-col items-center">
        <div className="relative">
          {/* Alignment Mark */}
          <div className="absolute -top-12 left-1/2 -translate-x-1/2 z-30 flex flex-col items-center opacity-70">
            <span className="text-[8px] text-red-900 font-black tracking-[0.4em] mb-1">TARGET POINT</span>
            <ChevronDown className="text-red-800 animate-bounce" size={32} />
          </div>

          <div 
            ref={containerRef}
            className={`relative w-[340px] h-[340px] md:w-[600px] md:h-[600px] rounded-full border-[22px] border-[#1a1817] shadow-[0_0_120px_rgba(0,0,0,1),inset_0_0_80px_rgba(0,0,0,0.9)] overflow-hidden transition-all duration-1000 ${solved ? 'border-red-900/40 ring-[15px] ring-red-900/20' : ''}`}
          >
            <svg viewBox="0 0 500 500" className={`w-full h-full transition-all duration-[2500ms] ${solved ? 'brightness-125 saturate-125' : 'brightness-[0.8] contrast-125'}`}>
              <DialRing
                layer={RingLayer.OUTER}
                radius={215}
                thickness={45}
                rotation={rotations[RingLayer.OUTER]}
                color={COLORS[RingLayer.OUTER]}
                isActive={!solved}
                isDragging={activeLayer === RingLayer.OUTER}
                onDragStart={handleDragStart}
              />
              <DialRing
                layer={RingLayer.OUTER_MID}
                radius={170}
                thickness={45}
                rotation={rotations[RingLayer.OUTER_MID]}
                color={COLORS[RingLayer.OUTER_MID]}
                isActive={!solved}
                isDragging={activeLayer === RingLayer.OUTER_MID}
                onDragStart={handleDragStart}
              />
              <DialRing
                layer={RingLayer.MIDDLE}
                radius={125}
                thickness={45}
                rotation={rotations[RingLayer.MIDDLE]}
                color={COLORS[RingLayer.MIDDLE]}
                isActive={!solved}
                isDragging={activeLayer === RingLayer.MIDDLE}
                onDragStart={handleDragStart}
              />
              <DialRing
                layer={RingLayer.INNER_MID}
                radius={80}
                thickness={45}
                rotation={rotations[RingLayer.INNER_MID]}
                color={COLORS[RingLayer.INNER_MID]}
                isActive={!solved}
                isDragging={activeLayer === RingLayer.INNER_MID}
                onDragStart={handleDragStart}
              />
              <DialRing
                layer={RingLayer.INNER}
                radius={35}
                thickness={45}
                rotation={rotations[RingLayer.INNER]}
                color={COLORS[RingLayer.INNER]}
                isActive={!solved}
                isDragging={activeLayer === RingLayer.INNER}
                onDragStart={handleDragStart}
              />

              {/* Central Pivot */}
              <circle cx="250" cy="250" r="14" fill="#000" />
              <circle cx="250" cy="250" r="4" fill={solved ? "#ef4444" : "#111"} className="transition-all duration-1000" />
            </svg>
          </div>
        </div>

        {/* UI Controls */}
        <div className="mt-12 w-full max-w-xl space-y-8 z-20">
          {solved ? (
            <div className="bg-stone-950/95 border-2 border-red-900/30 p-10 rounded-3xl shadow-2xl animate-in fade-in zoom-in duration-1000 backdrop-blur-3xl text-center relative overflow-hidden group">
              <div className="absolute top-0 left-0 w-full h-1 bg-gradient-to-r from-transparent via-red-900 to-transparent"></div>
              <h2 className="text-red-700 font-bold text-2xl mb-6 tracking-[0.5em] uppercase font-serif">
                THE TRUTH
              </h2>
              <div className="text-xl md:text-2xl font-medium text-stone-200 mb-10 py-10 px-8 bg-black/60 rounded-2xl italic border border-red-950/20 leading-relaxed font-serif tracking-wide">
                "{secret.secret}"
              </div>
              <button
                onClick={initGame}
                className="w-full bg-stone-900 hover:bg-stone-800 text-red-800 font-bold py-5 rounded-2xl border border-red-950/30 transition-all flex items-center justify-center gap-4 uppercase tracking-[0.4em] active:scale-95"
              >
                <RefreshCw size={24} className={loading ? "animate-spin" : ""} />
                RESET CASE
              </button>
            </div>
          ) : (
            <div className="space-y-6">
              {/* Evidence Hub */}
              <div className="grid grid-cols-5 gap-3">
                {[
                  { layer: RingLayer.OUTER, color: 'bg-zinc-800', label: 'TIP' },
                  { layer: RingLayer.OUTER_MID, color: 'bg-red-950', label: 'EDGE' },
                  { layer: RingLayer.MIDDLE, color: 'bg-black', label: 'GUARD' },
                  { layer: RingLayer.INNER_MID, color: 'bg-zinc-900', label: 'HILT' },
                  { layer: RingLayer.INNER, color: 'bg-red-900', label: 'POMMEL' }
                ].map((knob) => (
                  <div 
                    key={knob.layer}
                    onClick={() => setActiveLayer(knob.layer)} 
                    className={`flex flex-col items-center p-3 rounded-2xl border transition-all cursor-pointer ${activeLayer === knob.layer ? 'bg-red-950/20 border-red-900/50 shadow-lg scale-105' : 'bg-stone-950/60 border-stone-900 opacity-60'}`}
                  >
                    <div className={`w-10 h-10 rounded-full mb-2 ${knob.color} shadow-inner flex items-center justify-center border-2 border-white/5 relative`}>
                      <div className="w-1.5 h-1.5 bg-red-600/20 rounded-full blur-[1px]"></div>
                    </div>
                    <span className="text-[7px] font-black tracking-widest text-stone-700">{knob.label}</span>
                  </div>
                ))}
              </div>

              <div className="flex gap-4">
                <button
                  onClick={() => setShowHint(!showHint)}
                  className="flex-1 p-5 bg-stone-950/60 hover:bg-stone-900/80 text-stone-600 rounded-2xl border border-stone-800/40 transition-all flex items-center justify-center gap-4 text-xs font-black tracking-[0.4em] uppercase"
                >
                  <Search size={20} />
                  {showHint ? "CONCEAL CLUES" : "INSPECT EVIDENCE"}
                </button>
              </div>

              {showHint && (
                <div className="p-8 bg-black/40 border-l-4 border-red-950/60 text-stone-600 text-center text-sm italic animate-in slide-in-from-left duration-700 font-serif tracking-[0.2em] leading-relaxed">
                  "{secret.hint}"
                </div>
              )}
            </div>
          )}
        </div>
      </div>

      <footer className="mt-12 text-stone-900 text-[9px] tracking-[1.5em] uppercase z-10 font-bold opacity-20">
        ESC-RECONSTRUCTION • UNIT 09
      </footer>
    </div>
  );
};

export default App;

import React, { useMemo } from 'react';
import { RingLayer } from '../types';

interface DialRingProps {
  layer: RingLayer;
  radius: number;
  thickness: number;
  rotation: number;
  color: string;
  isActive: boolean;
  isDragging: boolean;
  onDragStart: (layer: RingLayer, e: React.MouseEvent | React.TouchEvent) => void;
}

const DialRing: React.FC<DialRingProps> = ({
  layer,
  radius,
  thickness,
  rotation,
  color,
  isActive,
  isDragging,
  onDragStart
}) => {
  const innerR = radius - thickness / 2;
  const outerR = radius + thickness / 2;
  const clipId = `clip-${layer}`;

  // สร้างเศษกระจกพื้นหลังที่มีการแบ่งช่องชัดเจน (Stained Glass Mosaic)
  const shards = useMemo(() => {
    const shardList = [];
    const radialSegments = 2; 
    const angularSegments = layer === RingLayer.OUTER ? 30 : 20; 

    for (let r = 0; r < radialSegments; r++) {
      for (let a = 0; a < angularSegments; a++) {
        const jitter = (Math.random() - 0.5) * 3;
        const rStart = innerR + (r * (thickness / radialSegments));
        const rEnd = innerR + ((r + 1) * (thickness / radialSegments));
        const aStart = (a * (360 / angularSegments) + jitter) * (Math.PI / 180);
        const aEnd = ((a + 1) * (360 / angularSegments) + jitter) * (Math.PI / 180);

        const p1x = 250 + rStart * Math.cos(aStart);
        const p1y = 250 + rStart * Math.sin(aStart);
        const p2x = 250 + rEnd * Math.cos(aStart);
        const p2y = 250 + rEnd * Math.sin(aStart);
        const p3x = 250 + rEnd * Math.cos(aEnd);
        const p3y = 250 + rEnd * Math.sin(aEnd);
        const p4x = 250 + rStart * Math.cos(aEnd);
        const p4y = 250 + rStart * Math.sin(aEnd);

        shardList.push({
          d: `M ${p1x} ${p1y} L ${p2x} ${p2y} A ${rEnd} ${rEnd} 0 0 1 ${p3x} ${p3y} L ${p4x} ${p4y} A ${rStart} ${rStart} 0 0 0 ${p1x} ${p1y} Z`,
          opacity: 0.15 + Math.random() * 0.2,
          fill: color,
          stroke: "#000",
          strokeWidth: 1.5
        });
      }
    }
    return shardList;
  }, [innerR, thickness, layer, color]);

  const DaggerSegment = () => {
    return (
      <g transform="translate(250, 250) rotate(-90)">
        <defs>
          {/* เอฟเฟกต์แสงเงาของกระจก */}
          <filter id="glassGlow" x="-20%" y="-20%" width="140%" height="140%">
            <feGaussianBlur stdDeviation="1.5" result="blur" />
            <feComposite in="SourceGraphic" in2="blur" operator="over" />
          </filter>
        </defs>

        {layer === RingLayer.OUTER && (
          // ปลายมีด (Tip) - แบ่งเป็น 3 ชิ้นย่อย
          <g filter="url(#glassGlow)">
            <path d="M 235 0 L 195 -18 L 195 0 Z" fill="#94a3b8" fillOpacity="0.8" stroke="#000" strokeWidth="3" />
            <path d="M 235 0 L 195 18 L 195 0 Z" fill="#64748b" fillOpacity="0.8" stroke="#000" strokeWidth="3" />
            <path d="M 235 0 L 210 -8 L 210 8 Z" fill="#ef4444" fillOpacity="0.7" stroke="#000" strokeWidth="2" />
          </g>
        )}

        {layer === RingLayer.OUTER_MID && (
          // ตัวใบมีดส่วนกลาง - แบ่งเป็นชิ้นกระจกทรงเหลี่ยม
          <g filter="url(#glassGlow)">
            <path d="M 195 -18 L 150 -22 L 150 0 L 195 0 Z" fill="#cbd5e1" fillOpacity="0.8" stroke="#000" strokeWidth="3" />
            <path d="M 195 18 L 150 22 L 150 0 L 195 0 Z" fill="#94a3b8" fillOpacity="0.8" stroke="#000" strokeWidth="3" />
            {/* คราบเลือดกระจกสีแดง */}
            <path d="M 170 8 L 150 12 L 150 22 Z" fill="#b91c1c" fillOpacity="0.7" stroke="#000" strokeWidth="2" />
          </g>
        )}

        {layer === RingLayer.MIDDLE && (
          // โกร่งมีด (Crossguard) - ใช้กระจกสีดำและแดงเข้ม
          <g filter="url(#glassGlow)">
            <path d="M 150 -22 L 115 -22 L 115 22 L 150 22 Z" fill="#475569" stroke="#000" strokeWidth="3" />
            {/* ตัวกั้นมือ */}
            <path d="M 115 -45 L 102 -45 L 102 45 L 115 45 Z" fill="#0f172a" stroke="#000" strokeWidth="4" />
            {/* ชิ้นกระจกตกแต่งสีแดง */}
            <rect x="104" y="-35" width="7" height="15" fill="#7f1d1d" stroke="#000" strokeWidth="1" />
            <rect x="104" y="20" width="7" height="15" fill="#7f1d1d" stroke="#000" strokeWidth="1" />
          </g>
        )}

        {layer === RingLayer.INNER_MID && (
          // ด้ามจับ - กระจกสีหินเข้มแบ่งช่อง
          <g filter="url(#glassGlow)">
            <path d="M 102 -15 L 60 -15 L 60 15 L 102 15 Z" fill="#1e293b" stroke="#000" strokeWidth="4" />
            <line x1="88" y1="-15" x2="88" y2="15" stroke="#000" strokeWidth="3" />
            <line x1="74" y1="-15" x2="74" y2="15" stroke="#000" strokeWidth="3" />
          </g>
        )}

        {layer === RingLayer.INNER && (
          // ท้ายด้าม (Pommel) - กระจกทรงกลมล้อมตะกั่ว
          <g filter="url(#glassGlow)">
            <path d="M 60 -12 L 40 -12 L 40 12 L 60 12 Z" fill="#0f172a" stroke="#000" strokeWidth="3" />
            <circle cx="28" cy="0" r="18" fill="#020617" stroke="#000" strokeWidth="4" />
            {/* อัญมณีกระจกสีแดงสว่าง */}
            <path d="M 28 -8 L 20 0 L 28 8 L 36 0 Z" fill="#f87171" stroke="#000" strokeWidth="1" className="animate-pulse" />
          </g>
        )}
      </g>
    );
  };

  return (
    <g 
      className="cursor-grab active:cursor-grabbing"
      onMouseDown={(e) => onDragStart(layer, e)}
      onTouchStart={(e) => onDragStart(layer, e)}
    >
      <defs>
        <clipPath id={clipId}>
          <path d={`
            M 250, ${250 - outerR}
            A ${outerR},${outerR} 0 1,1 250,${250 + outerR}
            A ${outerR},${outerR} 0 1,1 250,${250 - outerR}
            Z
            M 250, ${250 - innerR}
            A ${innerR},${innerR} 0 1,0 250,${250 + innerR}
            A ${innerR},${innerR} 0 1,0 250,${250 - innerR}
            Z
          `} fillRule="evenodd" />
        </clipPath>
      </defs>

      <g clipPath={`url(#${clipId})`}>
        {/* พื้นหลังกระจกเงา */}
        <circle cx="250" cy="250" r={outerR} fill="#020617" />

        <g style={{ 
          transform: `rotate(${rotation}deg)`, 
          transformOrigin: '250px 250px',
          transition: isDragging ? 'none' : 'transform 1.2s cubic-bezier(0.19, 1, 0.22, 1)'
        }}>
          {/* Shards background */}
          {shards.map((shard, i) => (
            <path 
              key={i} 
              d={shard.d} 
              fill={shard.fill} 
              fillOpacity={shard.opacity} 
              stroke={shard.stroke} 
              strokeWidth={shard.strokeWidth}
            />
          ))}

          <DaggerSegment />
        </g>
      </g>

      {/* เส้นขอบตะกั่วหลัก (The Lead Cames) */}
      <circle cx="250" cy="250" r={outerR} fill="none" stroke="#000" strokeWidth="5" />
      <circle cx="250" cy="250" r={innerR} fill="none" stroke="#000" strokeWidth="5" />
    </g>
  );
};

export default DialRing;

import React, { useMemo } from 'react';
import { RingLayer } from '../types';

interface DialRingProps {
  layer: RingLayer;
  radius: number;
  thickness: number;
  rotation: number;
  color: string;
  isActive: boolean;
  isDragging: boolean;
  onDragStart: (layer: RingLayer, e: React.MouseEvent | React.TouchEvent) => void;
}

const DialRing: React.FC<DialRingProps> = ({
  layer,
  radius,
  thickness,
  rotation,
  color,
  isActive,
  isDragging,
  onDragStart
}) => {
  const innerR = radius - thickness / 2;
  const outerR = radius + thickness / 2;
  const clipId = `clip-${layer}`;

  // สร้างเศษกระจกพื้นหลัง (Stained Glass Mosaic with varying light)
  const shards = useMemo(() => {
    const shardList = [];
    const radialSegments = 2; 
    const angularSegments = layer === RingLayer.OUTER ? 30 : 20; 

    for (let r = 0; r < radialSegments; r++) {
      for (let a = 0; a < angularSegments; a++) {
        const jitter = (Math.random() - 0.5) * 4;
        const rStart = innerR + (r * (thickness / radialSegments));
        const rEnd = innerR + ((r + 1) * (thickness / radialSegments));
        const aStart = (a * (360 / angularSegments) + jitter) * (Math.PI / 180);
        const aEnd = ((a + 1) * (360 / angularSegments) + jitter) * (Math.PI / 180);

        const p1x = 250 + rStart * Math.cos(aStart);
        const p1y = 250 + rStart * Math.sin(aStart);
        const p2x = 250 + rEnd * Math.cos(aStart);
        const p2y = 250 + rEnd * Math.sin(aStart);
        const p3x = 250 + rEnd * Math.cos(aEnd);
        const p3y = 250 + rEnd * Math.sin(aEnd);
        const p4x = 250 + rStart * Math.cos(aEnd);
        const p4y = 250 + rStart * Math.sin(aEnd);

        shardList.push({
          d: `M ${p1x} ${p1y} L ${p2x} ${p2y} A ${rEnd} ${rEnd} 0 0 1 ${p3x} ${p3y} L ${p4x} ${p4y} A ${rStart} ${rStart} 0 0 0 ${p1x} ${p1y} Z`,
          opacity: 0.3 + Math.random() * 0.4, // เพิ่มความโปร่งแสงให้สว่างขึ้น
          fill: color,
          highlightD: `M ${p1x+2} ${p1y+2} L ${p2x+2} ${p2y+2} A ${rEnd-2} ${rEnd-2} 0 0 1 ${p3x-2} ${p3y-2}` // เส้นขอบสะท้อนแสง
        });
      }
    }
    return shardList;
  }, [innerR, thickness, layer, color]);

  const DaggerSegment = () => {
    return (
      <g transform="translate(250, 250) rotate(-90)">
        <defs>
          <filter id="glassRefraction" x="-20%" y="-20%" width="140%" height="140%">
            <feGaussianBlur stdDeviation="1" result="blur" />
            <feSpecularLighting surfaceScale="5" specularConstant="1" specularExponent="20" lightingColor="#fff" in="blur" result="specOut">
              <fePointLight x="-5000" y="-10000" z="20000" />
            </feSpecularLighting>
            <feComposite in="SourceGraphic" in2="specOut" operator="arithmetic" k1="0" k2="1" k3="1" k4="0" />
          </filter>
        </defs>

        {layer === RingLayer.OUTER && (
          <g filter="url(#glassRefraction)">
            <path d="M 235 0 L 195 -18 L 195 0 Z" fill="#e2e8f0" fillOpacity="0.9" stroke="#000" strokeWidth="2.5" />
            <path d="M 235 0 L 195 18 L 195 0 Z" fill="#94a3b8" fillOpacity="0.9" stroke="#000" strokeWidth="2.5" />
            <path d="M 230 0 L 215 -6 L 215 6 Z" fill="#dc2626" fillOpacity="0.85" stroke="#000" strokeWidth="1.5" />
          </g>
        )}

        {layer === RingLayer.OUTER_MID && (
          <g filter="url(#glassRefraction)">
            <path d="M 195 -18 L 150 -22 L 150 0 L 195 0 Z" fill="#f1f5f9" fillOpacity="0.9" stroke="#000" strokeWidth="2.5" />
            <path d="M 195 18 L 150 22 L 150 0 L 195 0 Z" fill="#cbd5e1" fillOpacity="0.9" stroke="#000" strokeWidth="2.5" />
            <path d="M 175 10 L 150 14 L 150 22 Z" fill="#991b1b" fillOpacity="0.85" stroke="#000" strokeWidth="1.5" />
          </g>
        )}

        {layer === RingLayer.MIDDLE && (
          <g filter="url(#glassRefraction)">
            <path d="M 150 -22 L 115 -22 L 115 22 L 150 22 Z" fill="#64748b" stroke="#000" strokeWidth="2.5" />
            <path d="M 115 -45 L 102 -45 L 102 45 L 115 45 Z" fill="#020617" stroke="#000" strokeWidth="3.5" />
            <rect x="104" y="-38" width="7" height="18" fill="#450a0a" stroke="#000" strokeWidth="1" />
            <rect x="104" y="20" width="7" height="18" fill="#450a0a" stroke="#000" strokeWidth="1" />
          </g>
        )}

        {layer === RingLayer.INNER_MID && (
          <g filter="url(#glassRefraction)">
            <path d="M 102 -15 L 60 -15 L 60 15 L 102 15 Z" fill="#1e293b" stroke="#000" strokeWidth="3" />
            <line x1="88" y1="-15" x2="88" y2="15" stroke="#000" strokeWidth="2.5" />
            <line x1="74" y1="-15" x2="74" y2="15" stroke="#000" strokeWidth="2.5" />
            <line x1="60" y1="-15" x2="60" y2="15" stroke="#000" strokeWidth="2.5" />
          </g>
        )}

        {layer === RingLayer.INNER && (
          <g filter="url(#glassRefraction)">
            <path d="M 60 -12 L 40 -12 L 40 12 L 60 12 Z" fill="#020617" stroke="#000" strokeWidth="2.5" />
            <circle cx="28" cy="0" r="18" fill="#000" stroke="#000" strokeWidth="3" />
            <path d="M 28 -10 L 18 0 L 28 10 L 38 0 Z" fill="#ef4444" stroke="#000" strokeWidth="1.5" className="animate-pulse" />
          </g>
        )}
      </g>
    );
  };

  return (
    <g 
      className="cursor-grab active:cursor-grabbing"
      onMouseDown={(e) => onDragStart(layer, e)}
      onTouchStart={(e) => onDragStart(layer, e)}
    >
      <defs>
        <clipPath id={clipId}>
          <path d={`
            M 250, ${250 - outerR}
            A ${outerR},${outerR} 0 1,1 250,${250 + outerR}
            A ${outerR},${outerR} 0 1,1 250,${250 - outerR}
            Z
            M 250, ${250 - innerR}
            A ${innerR},${innerR} 0 1,0 250,${250 + innerR}
            A ${innerR},${innerR} 0 1,0 250,${250 - innerR}
            Z
          `} fillRule="evenodd" />
        </clipPath>

        {/* Backlight effect for this ring */}
        <radialGradient id={`backlight-${layer}`} cx="50%" cy="50%" r="50%">
          <stop offset="60%" stopColor="#1a1a1a" stopOpacity="0" />
          <stop offset="90%" stopColor={color} stopOpacity="0.1" />
          <stop offset="100%" stopColor={color} stopOpacity="0.2" />
        </radialGradient>
      </defs>

      <g clipPath={`url(#${clipId})`}>
        {/* Background 'Backlight' */}
        <circle cx="250" cy="250" r={outerR} fill={`url(#backlight-${layer})`} />
        <circle cx="250" cy="250" r={outerR} fill="#000" fillOpacity="0.6" />

        <g style={{ 
          transform: `rotate(${rotation}deg)`, 
          transformOrigin: '250px 250px',
          transition: isDragging ? 'none' : 'transform 1s cubic-bezier(0.25, 1, 0.5, 1)'
        }}>
          {/* Shards background */}
          {shards.map((shard, i) => (
            <g key={i}>
              <path 
                d={shard.d} 
                fill={shard.fill} 
                fillOpacity={shard.opacity} 
                stroke="#000" 
                strokeWidth="1.2"
              />
              {/* Highlight streak to show it's glass */}
              <path 
                d={shard.highlightD} 
                stroke="#fff" 
                strokeWidth="0.8" 
                strokeOpacity="0.15" 
                fill="none" 
              />
            </g>
          ))}

          <DaggerSegment />
        </g>
      </g>

      {/* Main Lead Frame */}
      <circle cx="250" cy="250" r={outerR} fill="none" stroke="#000" strokeWidth="4" />
      <circle cx="250" cy="250" r={innerR} fill="none" stroke="#000" strokeWidth="4" />
      {/* Small metallic reflection on the frame */}
      <circle cx="250" cy="250" r={outerR - 1} fill="none" stroke="#ffffff15" strokeWidth="1" />
    </g>
  );
};

export default DialRing;

import React, { useState, useEffect, useCallback, useRef } from 'react';
import { RefreshCw, Skull, Fingerprint, Search, ChevronDown } from 'lucide-react';
import DialRing from './components/DialRing';
import { generateSecretChallenge } from './services/geminiService';
import { RingLayer } from './types';

const App: React.FC = () => {
  const [loading, setLoading] = useState(false);
  const [solved, setSolved] = useState(false);
  const [secret, setSecret] = useState({ secret: "", hint: "" });
  const [showHint, setShowHint] = useState(false);

  // Mystery Palette: Jewels tones that pop
  const COLORS = {
    [RingLayer.OUTER]: "#1e293b",     // Slate Jewel
    [RingLayer.OUTER_MID]: "#7f1d1d", // Deep Ruby
    [RingLayer.MIDDLE]: "#111827",    // Obsidian
    [RingLayer.INNER_MID]: "#334155", // Cool Glass
    [RingLayer.INNER]: "#991b1b",     // Blood Glass
  };

  const [rotations, setRotations] = useState<Record<RingLayer, number>>({
    [RingLayer.OUTER]: (Math.floor(Math.random() * 3) + 1) * 120,
    [RingLayer.OUTER_MID]: (Math.floor(Math.random() * 3) + 1) * 120,
    [RingLayer.MIDDLE]: (Math.floor(Math.random() * 3) + 1) * 120,
    [RingLayer.INNER_MID]: (Math.floor(Math.random() * 3) + 1) * 120,
    [RingLayer.INNER]: (Math.floor(Math.random() * 3) + 1) * 120,
  });

  const [activeLayer, setActiveLayer] = useState<RingLayer | null>(null);
  const dragStartAngle = useRef<number>(0);
  const initialRotation = useRef<number>(0);
  const containerRef = useRef<HTMLDivElement>(null);

  const getAngle = (clientX: number, clientY: number) => {
    if (!containerRef.current) return 0;
    const rect = containerRef.current.getBoundingClientRect();
    const centerX = rect.left + rect.width / 2;
    const centerY = rect.top + rect.height / 2;
    const dx = clientX - centerX;
    const dy = clientY - centerY;
    return Math.atan2(dy, dx) * (180 / Math.PI);
  };

  const handleDragStart = (layer: RingLayer, e: React.MouseEvent | React.TouchEvent) => {
    if (solved || loading) return;
    const clientX = 'touches' in e ? (e as TouchEvent).touches[0].clientX : (e as MouseEvent).clientX;
    const clientY = 'touches' in e ? (e as TouchEvent).touches[0].clientY : (e as MouseEvent).clientY;
    setActiveLayer(layer);
    dragStartAngle.current = getAngle(clientX, clientY);
    initialRotation.current = rotations[layer];
  };

  const handleDragMove = useCallback((e: MouseEvent | TouchEvent) => {
    const layer = activeLayer;
    if (layer === null || solved) return;
    const clientX = 'touches' in e ? (e as TouchEvent).touches[0].clientX : (e as MouseEvent).clientX;
    const clientY = 'touches' in e ? (e as TouchEvent).touches[0].clientY : (e as MouseEvent).clientY;
    const currentAngle = getAngle(clientX, clientY);
    const angleDiff = currentAngle - dragStartAngle.current;

    setRotations(prev => ({ ...prev, [layer]: initialRotation.current + angleDiff }));
  }, [activeLayer, solved]);

  const handleDragEnd = useCallback(() => {
    const layer = activeLayer;
    if (layer === null) return;
    setRotations(prev => {
      const currentRot = prev[layer];
      const snapped = Math.round(currentRot / 120) * 120;
      const nextRotations = { ...prev, [layer]: snapped };

      const isAligned = Object.values(nextRotations).every((rot: number) => {
        const normalized = ((rot % 360) + 360) % 360;
        return normalized < 3 || normalized > 357;
      });

      if (isAligned) setSolved(true);
      return nextRotations;
    });
    setActiveLayer(null);
  }, [activeLayer]);

  useEffect(() => {
    if (activeLayer !== null) {
      window.addEventListener('mousemove', handleDragMove);
      window.addEventListener('mouseup', handleDragEnd);
      window.addEventListener('touchmove', handleDragMove, { passive: false });
      window.addEventListener('touchend', handleDragEnd);
    }
    return () => {
      window.removeEventListener('mousemove', handleDragMove);
      window.removeEventListener('mouseup', handleDragEnd);
      window.removeEventListener('touchmove', handleDragMove);
      window.removeEventListener('touchend', handleDragEnd);
    };
  }, [activeLayer, handleDragMove, handleDragEnd]);

  const initGame = async () => {
    setLoading(true);
    setSolved(false);
    setShowHint(false);
    const data = await generateSecretChallenge();
    setSecret(data);
    setRotations({
      [RingLayer.OUTER]: (Math.random() > 0.5 ? 120 : 240),
      [RingLayer.OUTER_MID]: (Math.random() > 0.5 ? 120 : 240),
      [RingLayer.MIDDLE]: (Math.random() > 0.5 ? 120 : 240),
      [RingLayer.INNER_MID]: (Math.random() > 0.5 ? 120 : 240),
      [RingLayer.INNER]: (Math.random() > 0.5 ? 120 : 240),
    });
    setLoading(false);
  };

  useEffect(() => { initGame(); }, []);

  return (
    <div className="min-h-screen flex flex-col items-center justify-center p-4 text-stone-500 overflow-hidden relative select-none bg-[#0a0808]">
      {/* Dynamic Stained Glass Glow */}
      <div className="absolute inset-0 pointer-events-none">
        <div className="absolute top-1/2 left-1/2 -translate-x-1/2 -translate-y-1/2 w-[800px] h-[800px] bg-[radial-gradient(circle,rgba(153,27,27,0.15)_0%,rgba(0,0,0,0)_70%)] opacity-60"></div>
        {/* Subtle Light Leaks */}
        <div className="absolute top-0 right-0 w-1/2 h-full bg-[linear-gradient(to_bottom_left,rgba(51,65,85,0.05),transparent)]"></div>
        <div className="absolute bottom-0 left-0 w-1/2 h-full bg-[linear-gradient(to_top_right,rgba(153,27,27,0.05),transparent)]"></div>
      </div>

      <header className="text-center mb-10 z-10 font-serif">
        <div className="flex items-center justify-center gap-4 mb-4">
          <Fingerprint className="text-stone-800" size={24} />
          <div className="relative">
            <Skull className="text-red-900 drop-shadow-[0_0_15px_rgba(153,27,27,0.8)]" size={56} />
          </div>
          <Fingerprint className="text-stone-800" size={24} />
        </div>
        <h1 className="text-4xl md:text-6xl font-bold tracking-[0.25em] text-stone-100 uppercase mb-3 drop-shadow-lg">
          STAINED <span className="text-red-800">MURDER</span>
        </h1>
        <p className="text-[11px] tracking-[0.7em] text-stone-600 uppercase font-black">Align the translucent shards to see the truth</p>
      </header>

      <div className="relative z-10 flex flex-col items-center">
        <div className="relative">
          {/* Target Pointer */}
          <div className="absolute -top-14 left-1/2 -translate-x-1/2 z-30 flex flex-col items-center">
            <span className="text-[9px] text-red-700 font-black tracking-[0.5em] mb-2 uppercase opacity-80">Alignment Point</span>
            <div className="w-[2px] h-8 bg-gradient-to-b from-red-600 to-transparent animate-pulse"></div>
            <ChevronDown className="text-red-600 mt-[-8px]" size={36} />
          </div>

          <div 
            ref={containerRef}
            className={`relative w-[340px] h-[340px] md:w-[620px] md:h-[620px] rounded-full border-[24px] border-[#121110] shadow-[0_0_150px_rgba(0,0,0,1),inset_0_0_100px_rgba(0,0,0,0.9)] overflow-hidden transition-all duration-1000 ${solved ? 'border-red-900/50 shadow-[0_0_100px_rgba(153,27,27,0.3)]' : ''}`}
          >
            <svg viewBox="0 0 500 500" className={`w-full h-full transition-all duration-[2000ms] ${solved ? 'brightness-125 saturate-150 scale-105' : 'brightness-[0.85] contrast-110'}`}>
              <DialRing
                layer={RingLayer.OUTER}
                radius={215}
                thickness={45}
                rotation={rotations[RingLayer.OUTER]}
                color={COLORS[RingLayer.OUTER]}
                isActive={!solved}
                isDragging={activeLayer === RingLayer.OUTER}
                onDragStart={handleDragStart}
              />
              <DialRing
                layer={RingLayer.OUTER_MID}
                radius={170}
                thickness={45}
                rotation={rotations[RingLayer.OUTER_MID]}
                color={COLORS[RingLayer.OUTER_MID]}
                isActive={!solved}
                isDragging={activeLayer === RingLayer.OUTER_MID}
                onDragStart={handleDragStart}
              />
              <DialRing
                layer={RingLayer.MIDDLE}
                radius={125}
                thickness={45}
                rotation={rotations[RingLayer.MIDDLE]}
                color={COLORS[RingLayer.MIDDLE]}
                isActive={!solved}
                isDragging={activeLayer === RingLayer.MIDDLE}
                onDragStart={handleDragStart}
              />
              <DialRing
                layer={RingLayer.INNER_MID}
                radius={80}
                thickness={45}
                rotation={rotations[RingLayer.INNER_MID]}
                color={COLORS[RingLayer.INNER_MID]}
                isActive={!solved}
                isDragging={activeLayer === RingLayer.INNER_MID}
                onDragStart={handleDragStart}
              />
              <DialRing
                layer={RingLayer.INNER}
                radius={35}
                thickness={45}
                rotation={rotations[RingLayer.INNER]}
                color={COLORS[RingLayer.INNER]}
                isActive={!solved}
                isDragging={activeLayer === RingLayer.INNER}
                onDragStart={handleDragStart}
              />

              {/* Core Gem */}
              <circle cx="250" cy="250" r="16" fill="#000" />
              <circle cx="250" cy="250" r="6" fill={solved ? "#ff4444" : "#221111"} className="transition-all duration-1000 shadow-[0_0_10px_red]" />
            </svg>
          </div>
        </div>

        {/* UI Controls */}
        <div className="mt-12 w-full max-w-xl space-y-8 z-20">
          {solved ? (
            <div className="bg-stone-950/95 border-2 border-red-900/40 p-12 rounded-[40px] shadow-[0_0_60px_rgba(0,0,0,0.8)] animate-in fade-in zoom-in duration-1000 backdrop-blur-3xl text-center relative overflow-hidden group">
              <div className="absolute top-0 left-0 w-full h-1 bg-gradient-to-r from-transparent via-red-800 to-transparent"></div>
              <h2 className="text-red-600 font-bold text-3xl mb-8 tracking-[0.4em] uppercase font-serif drop-shadow-sm">
                EVIDENCE REVEALED
              </h2>
              <div className="text-2xl md:text-3xl font-medium text-stone-200 mb-12 py-12 px-10 bg-black/70 rounded-3xl italic border border-red-950/30 leading-relaxed font-serif tracking-wide shadow-inner">
                "{secret.secret}"
              </div>
              <button
                onClick={initGame}
                className="w-full bg-red-950/20 hover:bg-red-950/40 text-red-500 font-bold py-6 rounded-2xl border border-red-900/40 transition-all flex items-center justify-center gap-6 uppercase tracking-[0.5em] active:scale-95 group"
              >
                <RefreshCw size={28} className={`group-hover:rotate-180 transition-transform duration-500 ${loading ? "animate-spin" : ""}`} />
                CLOSE CASE
              </button>
            </div>
          ) : (
            <div className="space-y-8">
              {/* Evidence Knobs */}
              <div className="grid grid-cols-5 gap-4">
                {[
                  { layer: RingLayer.OUTER, color: 'bg-slate-800', label: 'TIP' },
                  { layer: RingLayer.OUTER_MID, color: 'bg-red-900', label: 'BLADE' },
                  { layer: RingLayer.MIDDLE, color: 'bg-black', label: 'GUARD' },
                  { layer: RingLayer.INNER_MID, color: 'bg-slate-700', label: 'HILT' },
                  { layer: RingLayer.INNER, color: 'bg-red-800', label: 'POMMEL' }
                ].map((knob) => (
                  <div 
                    key={knob.layer}
                    onClick={() => setActiveLayer(knob.layer)} 
                    className={`flex flex-col items-center p-4 rounded-2xl border-2 transition-all cursor-pointer ${activeLayer === knob.layer ? 'bg-red-950/30 border-red-800/60 shadow-[0_0_20px_rgba(153,27,27,0.3)] scale-110' : 'bg-stone-950/70 border-stone-900/50 opacity-70'}`}
                  >
                    <div className={`w-12 h-12 rounded-full mb-3 ${knob.color} shadow-[inset_0_2px_4px_rgba(255,255,255,0.1)] flex items-center justify-center border-2 border-white/5 relative`}>
                      <div className="w-2 h-2 bg-red-500/30 rounded-full blur-[2px]"></div>
                    </div>
                    <span className="text-[8px] font-black tracking-widest text-stone-500 uppercase">{knob.label}</span>
                  </div>
                ))}
              </div>

              <div className="flex gap-4">
                <button
                  onClick={() => setShowHint(!showHint)}
                  className="flex-1 p-6 bg-stone-950/40 hover:bg-stone-900/80 text-stone-400 rounded-3xl border border-stone-800/40 transition-all flex items-center justify-center gap-5 text-sm font-black tracking-[0.4em] uppercase"
                >
                  <Search size={22} className="text-red-900" />
                  {showHint ? "CONCEAL CLUES" : "INSPECT GLASS"}
                </button>
              </div>

              {showHint && (
                <div className="p-10 bg-black/60 border-t-2 border-b-2 border-red-950/40 text-stone-400 text-center text-base italic animate-in fade-in slide-in-from-bottom duration-700 font-serif tracking-[0.15em] leading-relaxed">
                  "{secret.hint}"
                </div>
              )}
            </div>
          )}
        </div>
      </div>

      <footer className="mt-16 text-stone-900 text-[10px] tracking-[2em] uppercase z-10 font-bold opacity-30">
