              <button
                onClick={initGame}
                className="w-full bg-stone-900 hover:bg-black text-stone-200 font-bold py-4 rounded transition-all flex items-center justify-center gap-3 typewriter text-sm uppercase"
              >
                <RefreshCw size={18} className={loading ? "animate-spin" : ""} />
                OPEN NEW CASE
