import React, { useMemo } from 'react';
import { RingLayer } from '../types';

interface DialRingProps {
  layer: RingLayer;
  radius: number;
  thickness: number;
  rotation: number;
  color: string;
  isActive: boolean;
  isDragging: boolean;
  onDragStart: (layer: RingLayer, e: React.MouseEvent | React.TouchEvent) => void;
}

const DialRing: React.FC<DialRingProps> = ({
  layer,
  radius,
  thickness,
  rotation,
  color,
  isActive,
  isDragging,
  onDragStart
}) => {
  const innerR = radius - thickness / 2;
  const outerR = radius + thickness / 2;
  const clipId = `clip-${layer}`;

  const isAligned = useMemo(() => {
    const norm = ((rotation % 360) + 360) % 360;
    return norm < 4 || norm > 356;
  }, [rotation]);

  // Shards background remains for texture
  const shards = useMemo(() => {
    const shardList = [];
    const radialSegments = 2; 
    const angularSegments = layer === RingLayer.OUTER ? 32 : 24; 

    for (let r = 0; r < radialSegments; r++) {
      for (let a = 0; a < angularSegments; a++) {
        const jitter = (Math.random() - 0.5) * 5;
        const rStart = innerR + (r * (thickness / radialSegments));
        const rEnd = innerR + ((r + 1) * (thickness / radialSegments));
        const aStart = (a * (360 / angularSegments) + jitter) * (Math.PI / 180);
        const aEnd = ((a + 1) * (360 / angularSegments) + jitter) * (Math.PI / 180);

        const p1x = 250 + rStart * Math.cos(aStart);
        const p1y = 250 + rStart * Math.sin(aStart);
        const p2x = 250 + rEnd * Math.cos(aStart);
        const p2y = 250 + rEnd * Math.sin(aStart);
        const p3x = 250 + rEnd * Math.cos(aEnd);
        const p3y = 250 + rEnd * Math.sin(aEnd);
        const p4x = 250 + rStart * Math.cos(aEnd);
        const p4y = 250 + rStart * Math.sin(aEnd);

        shardList.push({
          d: `M ${p1x} ${p1y} L ${p2x} ${p2y} A ${rEnd} ${rEnd} 0 0 1 ${p3x} ${p3y} L ${p4x} ${p4y} A ${rStart} ${rStart} 0 0 0 ${p1x} ${p1y} Z`,
          opacity: 0.15 + Math.random() * 0.2,
          fill: color,
        });
      }
    }
    return shardList;
  }, [innerR, thickness, layer, color]);

  // Function to render items at specific angles
  const EvidenceItems = () => {
    const bladeColor = isAligned ? "#cbd5e1" : "#2d3748"; 
    const decoyColor = "#1a202c"; // Darker silhouette for decoys

    return (
      <g transform="translate(250, 250)">
        {/* Real Dagger Segments at -90deg (Top) */}
        <g transform="rotate(-90)">
          {layer === RingLayer.OUTER && (
            <path d="M 235 0 L 195 -18 L 195 0 Z M 235 0 L 195 18 L 195 0 Z" fill={bladeColor} fillOpacity="0.4" stroke="#000" strokeWidth="1.5" />
          )}
          {layer === RingLayer.OUTER_MID && (
            <path d="M 195 -18 L 150 -22 L 150 22 L 195 18 Z" fill={bladeColor} fillOpacity="0.4" stroke="#000" strokeWidth="1.5" />
          )}
          {layer === RingLayer.MIDDLE && (
            <path d="M 150 -25 L 115 -25 L 115 25 L 150 25 Z" fill="#111" stroke="#000" strokeWidth="2" />
          )}
          {layer === RingLayer.INNER_MID && (
            <path d="M 115 -12 L 60 -12 L 60 12 L 115 12 Z" fill="#0c0c0c" stroke="#000" strokeWidth="2" />
          )}
          {layer === RingLayer.INNER && (
            <circle cx="25" cy="0" r="14" fill="#000" stroke="#000" strokeWidth="2" />
          )}
        </g>

        {/* Decoy 1: Broken Key at 30deg */}
        <g transform="rotate(30)">
          {layer === RingLayer.OUTER && (
             <rect x="195" y="-5" width="40" height="10" fill={decoyColor} fillOpacity="0.3" stroke="#000" strokeWidth="1" />
          )}
          {layer === RingLayer.OUTER_MID && (
             <path d="M 150 -8 L 195 -8 L 195 8 L 150 8 Z" fill={decoyColor} fillOpacity="0.3" stroke="#000" strokeWidth="1" />
          )}
          {layer === RingLayer.MIDDLE && (
             <circle cx="132" cy="0" r="18" fill={decoyColor} fillOpacity="0.4" stroke="#000" strokeWidth="1.5" />
          )}
        </g>

        {/* Decoy 2: Bullet Shell at 150deg */}
        <g transform="rotate(150)">
          {layer === RingLayer.OUTER && (
            <circle cx="215" cy="0" r="8" fill={decoyColor} fillOpacity="0.3" stroke="#000" strokeWidth="1" />
          )}
          {layer === RingLayer.OUTER_MID && (
            <rect x="150" y="-10" width="45" height="20" fill={decoyColor} fillOpacity="0.3" stroke="#000" strokeWidth="1" />
          )}
          {layer === RingLayer.MIDDLE && (
            <path d="M 115 -15 L 150 -15 L 150 15 L 115 15 Z" fill={decoyColor} fillOpacity="0.4" stroke="#000" strokeWidth="1" />
          )}
        </g>

        {/* Decoy 3: Chain Fragment at 270deg */}
        <g transform="rotate(270)">
           {layer === RingLayer.OUTER && (
             <path d="M 195 -10 A 10 10 0 0 1 235 0" fill="none" stroke={decoyColor} strokeWidth="4" strokeOpacity="0.3" />
           )}
           {layer === RingLayer.OUTER_MID && (
             <circle cx="172" cy="0" r="12" fill="none" stroke={decoyColor} strokeWidth="3" strokeOpacity="0.3" />
           )}
           {layer === RingLayer.MIDDLE && (
             <path d="M 115 0 L 150 0" stroke={decoyColor} strokeWidth="5" strokeOpacity="0.4" />
           )}
        </g>
      </g>
    );
  };

  return (
    <g 
      className="cursor-grab active:cursor-grabbing"
      onMouseDown={(e) => onDragStart(layer, e)}
      onTouchStart={(e) => onDragStart(layer, e)}
    >
      <defs>
        <clipPath id={clipId}>
          <path d={`
            M 250, ${250 - outerR}
            A ${outerR},${outerR} 0 1,1 250,${250 + outerR}
            A ${outerR},${outerR} 0 1,1 250,${250 - outerR}
            Z
            M 250, ${250 - innerR}
            A ${innerR},${innerR} 0 1,0 250,${250 + innerR}
            A ${innerR},${innerR} 0 1,0 250,${250 - innerR}
            Z
          `} fillRule="evenodd" />
        </clipPath>
        <filter id="glassRefraction">
           <feGaussianBlur stdDeviation="0.5" result="blur" />
           <feSpecularLighting surfaceScale="2" specularConstant="0.8" specularExponent="15" lightingColor="#fff" in="blur" result="spec">
             <fePointLight x="-5000" y="-10000" z="10000" />
           </feSpecularLighting>
           <feComposite in="SourceGraphic" in2="spec" operator="arithmetic" k1="0" k2="1" k3="1" k4="0" />
        </filter>
      </defs>

      <g clipPath={`url(#${clipId})`}>
        <circle cx="250" cy="250" r={outerR} fill="#030303" />

        <g style={{ 
          transform: `rotate(${rotation}deg)`, 
          transformOrigin: '250px 250px',
          transition: isDragging ? 'none' : 'transform 0.8s cubic-bezier(0.4, 0, 0.2, 1)'
        }} filter="url(#glassRefraction)">
          {/* Shards background */}
          {shards.map((shard, i) => (
            <path key={i} d={shard.d} fill={shard.fill} fillOpacity={shard.opacity} stroke="#000" strokeWidth="0.5" />
          ))}

          <EvidenceItems />
        </g>
      </g>

      <circle cx="250" cy="250" r={outerR} fill="none" stroke="#1a1a1a" strokeWidth="3" />
      <circle cx="250" cy="250" r={innerR} fill="none" stroke="#1a1a1a" strokeWidth="3" />
    </g>
  );
};

