import React, { useState, useEffect, useCallback, useRef } from 'react';
import { RefreshCw, Skull, Fingerprint, Search, Book, Camera, MapPin } from 'lucide-react';
import DialRing from './components/DialRing';
import { generateSecretChallenge } from './services/geminiService';
import { RingLayer } from './types';

const App: React.FC = () => {
  const [loading, setLoading] = useState(false);
  const [solved, setSolved] = useState(false);
  const [secret, setSecret] = useState({ secret: "", hint: "" });
  const [showHint, setShowHint] = useState(false);

  // ปรับสีให้เข้มและสดขึ้นเล็กน้อยเพื่อตัดกับความมืด
  const COLORS = {
    [RingLayer.OUTER]: "#1e293b",
    [RingLayer.OUTER_MID]: "#7f1d1d",
    [RingLayer.MIDDLE]: "#111827",
    [RingLayer.INNER_MID]: "#334155",
    [RingLayer.INNER]: "#991b1b",
  };

  const [rotations, setRotations] = useState<Record<RingLayer, number>>({
    [RingLayer.OUTER]: (Math.floor(Math.random() * 3) + 1) * 120,
    [RingLayer.OUTER_MID]: (Math.floor(Math.random() * 3) + 1) * 120,
    [RingLayer.MIDDLE]: (Math.floor(Math.random() * 3) + 1) * 120,
    [RingLayer.INNER_MID]: (Math.floor(Math.random() * 3) + 1) * 120,
    [RingLayer.INNER]: (Math.floor(Math.random() * 3) + 1) * 120,
  });

  const [activeLayer, setActiveLayer] = useState<RingLayer | null>(null);
  const dragStartAngle = useRef<number>(0);
  const initialRotation = useRef<number>(0);
  const containerRef = useRef<HTMLDivElement>(null);

  const getAngle = (clientX: number, clientY: number) => {
    if (!containerRef.current) return 0;
    const rect = containerRef.current.getBoundingClientRect();
    const centerX = rect.left + rect.width / 2;
    const centerY = rect.top + rect.height / 2;
    const dx = clientX - centerX;
    const dy = clientY - centerY;
    return Math.atan2(dy, dx) * (180 / Math.PI);
  };

  const handleDragStart = (layer: RingLayer, e: React.MouseEvent | React.TouchEvent) => {
    if (solved || loading) return;
    const client = 'touches' in e ? (e as any).touches[0] : (e as any);
    setActiveLayer(layer);
    dragStartAngle.current = getAngle(client.clientX, client.clientY);
    initialRotation.current = rotations[layer];
  };

  const handleDragMove = useCallback((e: MouseEvent | TouchEvent) => {
    if (activeLayer === null || solved) return;
    const client = 'touches' in e ? (e as any).touches[0] : (e as any);
    const currentAngle = getAngle(client.clientX, client.clientY);
    const angleDiff = currentAngle - dragStartAngle.current;
    setRotations(prev => ({ ...prev, [activeLayer]: initialRotation.current + angleDiff }));
  }, [activeLayer, solved]);

  const handleDragEnd = useCallback(() => {
    if (activeLayer === null) return;
    setRotations(prev => {
      const snapped = Math.round(prev[activeLayer] / 120) * 120;
      const next = { ...prev, [activeLayer]: snapped };
      const isAligned = Object.values(next).every(r => {
        const n = ((r % 360) + 360) % 360;
        return n < 6 || n > 354;
      });
      if (isAligned) setSolved(true);
      return next;
    });
    setActiveLayer(null);
  }, [activeLayer]);

  useEffect(() => {
    window.addEventListener('mousemove', handleDragMove);
    window.addEventListener('mouseup', handleDragEnd);
    window.addEventListener('touchmove', handleDragMove, { passive: false });
    window.addEventListener('touchend', handleDragEnd);
    return () => {
      window.removeEventListener('mousemove', handleDragMove);
      window.removeEventListener('mouseup', handleDragEnd);
      window.removeEventListener('touchmove', handleDragMove);
      window.removeEventListener('touchend', handleDragEnd);
    };
  }, [handleDragMove, handleDragEnd]);

  const initGame = async () => {
    setLoading(true);
    setSolved(false);
    setShowHint(false);
    try {
      const data = await generateSecretChallenge();
      setSecret(data);
    } finally {
      setRotations({
        [RingLayer.OUTER]: (Math.floor(Math.random() * 2) + 1) * 120,
        [RingLayer.OUTER_MID]: (Math.floor(Math.random() * 2) + 1) * 120,
        [RingLayer.MIDDLE]: (Math.floor(Math.random() * 2) + 1) * 120,
        [RingLayer.INNER_MID]: (Math.floor(Math.random() * 2) + 1) * 120,
        [RingLayer.INNER]: (Math.floor(Math.random() * 2) + 1) * 120,
      });
      setLoading(false);
    }
  };

  useEffect(() => { initGame(); }, []);

  return (
    <div className="min-h-screen flex flex-col items-center justify-center p-4 relative overflow-hidden select-none">
      <div className="lamp-glow" />

      {/* Decorative Elements */}
      <div className="absolute top-10 left-10 polaroid rotate-[-8deg] hidden lg:block opacity-60">
        <div className="w-32 h-32 bg-stone-900 flex items-center justify-center text-red-900/40">
           <Camera size={40} />
        </div>
        <p className="typewriter text-[10px] text-stone-700 mt-2">SCENE RECONSTRUCTION</p>
      </div>

      <div className="absolute bottom-20 right-10 polaroid rotate-[5deg] hidden lg:block opacity-60">
        <div className="w-32 h-32 bg-stone-800 flex items-center justify-center text-stone-600">
           <MapPin size={40} />
        </div>
        <p className="typewriter text-[10px] text-stone-700 mt-2">DOWNTOWN DISTRICT</p>
      </div>

      <header className="text-center mb-8 z-10">
        <h1 className="text-4xl md:text-5xl font-bold tracking-[0.2em] text-stone-200 uppercase mb-2 drop-shadow-[0_4px_10px_rgba(0,0,0,1)]">
          STAINED <span className="text-red-800">EVIDENCE</span>
        </h1>
        <p className="typewriter text-[12px] tracking-[0.3em] text-stone-500 uppercase opacity-80">Rebuild the weapon from shattered glass</p>
      </header>

      <div className="relative z-10 flex flex-col items-center">
        {loading && (
