          <div 
            ref={containerRef}
            className={`relative w-[340px] h-[340px] md:w-[560px] md:h-[560px] rounded-full border-[10px] border-[#1a1614] shadow-[0_30px_100px_rgba(0,0,0,0.9)] overflow-hidden transition-all duration-1000 ${solved ? 'border-red-900/60 shadow-[0_0_100px_rgba(153,27,27,0.4)]' : ''}`}
          >
            <svg viewBox="0 0 500 500" className={`w-full h-full transition-opacity duration-500 ${loading ? 'opacity-20' : 'opacity-100'}`}>
              <DialRing layer={RingLayer.OUTER} radius={215} thickness={45} rotation={rotations[RingLayer.OUTER]} color={COLORS[RingLayer.OUTER]} isActive={!solved} isDragging={activeLayer === RingLayer.OUTER} onDragStart={handleDragStart} />
              <DialRing layer={RingLayer.OUTER_MID} radius={170} thickness={45} rotation={rotations[RingLayer.OUTER_MID]} color={COLORS[RingLayer.OUTER_MID]} isActive={!solved} isDragging={activeLayer === RingLayer.OUTER_MID} onDragStart={handleDragStart} />
              <DialRing layer={RingLayer.MIDDLE} radius={125} thickness={45} rotation={rotations[RingLayer.MIDDLE]} color={COLORS[RingLayer.MIDDLE]} isActive={!solved} isDragging={activeLayer === RingLayer.MIDDLE} onDragStart={handleDragStart} />
              <DialRing layer={RingLayer.INNER_MID} radius={80} thickness={45} rotation={rotations[RingLayer.INNER_MID]} color={COLORS[RingLayer.INNER_MID]} isActive={!solved} isDragging={activeLayer === RingLayer.INNER_MID} onDragStart={handleDragStart} />
              <DialRing layer={RingLayer.INNER} radius={35} thickness={45} rotation={rotations[RingLayer.INNER]} color={COLORS[RingLayer.INNER]} isActive={!solved} isDragging={activeLayer === RingLayer.INNER} onDragStart={handleDragStart} />
            </svg>
