import React, { useState, useEffect, useCallback, useRef } from 'react';
import { RefreshCw, Skull, Fingerprint, Search, Book, Camera, MapPin } from 'lucide-react';
import DialRing from './components/DialRing';
import { generateSecretChallenge } from './services/geminiService';
import { RingLayer } from './types';

const App: React.FC = () => {
  const [loading, setLoading] = useState(false);
  const [solved, setSolved] = useState(false);
  const [secret, setSecret] = useState({ secret: "", hint: "" });
  const [showHint, setShowHint] = useState(false);

  // ปรับสีให้เข้มและสดขึ้นเล็กน้อยเพื่อตัดกับความมืด
  const COLORS = {
    [RingLayer.OUTER]: "#1e293b",
    [RingLayer.OUTER_MID]: "#7f1d1d",
    [RingLayer.MIDDLE]: "#111827",
    [RingLayer.INNER_MID]: "#334155",
    [RingLayer.INNER]: "#991b1b",
  };

  const [rotations, setRotations] = useState<Record<RingLayer, number>>({
    [RingLayer.OUTER]: (Math.floor(Math.random() * 3) + 1) * 120,
    [RingLayer.OUTER_MID]: (Math.floor(Math.random() * 3) + 1) * 120,
    [RingLayer.MIDDLE]: (Math.floor(Math.random() * 3) + 1) * 120,
    [RingLayer.INNER_MID]: (Math.floor(Math.random() * 3) + 1) * 120,
    [RingLayer.INNER]: (Math.floor(Math.random() * 3) + 1) * 120,
  });

  const [activeLayer, setActiveLayer] = useState<RingLayer | null>(null);
  const dragStartAngle = useRef<number>(0);
  const initialRotation = useRef<number>(0);
  const containerRef = useRef<HTMLDivElement>(null);

  const getAngle = (clientX: number, clientY: number) => {
    if (!containerRef.current) return 0;
    const rect = containerRef.current.getBoundingClientRect();
    const centerX = rect.left + rect.width / 2;
    const centerY = rect.top + rect.height / 2;
    const dx = clientX - centerX;
    const dy = clientY - centerY;
    return Math.atan2(dy, dx) * (180 / Math.PI);
  };

  const handleDragStart = (layer: RingLayer, e: React.MouseEvent | React.TouchEvent) => {
    if (solved || loading) return;
    const client = 'touches' in e ? (e as any).touches[0] : (e as any);
    setActiveLayer(layer);
    dragStartAngle.current = getAngle(client.clientX, client.clientY);
    initialRotation.current = rotations[layer];
  };

  const handleDragMove = useCallback((e: MouseEvent | TouchEvent) => {
    if (activeLayer === null || solved) return;
    // Fix: Capture activeLayer in a constant to preserve narrowing for the state update callback
    const currentLayer = activeLayer;
    const client = 'touches' in e ? (e as any).touches[0] : (e as any);
    const currentAngle = getAngle(client.clientX, client.clientY);
    const angleDiff = currentAngle - dragStartAngle.current;
    setRotations(prev => ({ ...prev, [currentLayer]: initialRotation.current + angleDiff }));
  }, [activeLayer, solved]);

  const handleDragEnd = useCallback(() => {
    if (activeLayer === null) return;
    // Fix: Capture activeLayer in a constant to preserve narrowing for the state update callback
    const currentLayer = activeLayer;
    setRotations(prev => {
      const snapped = Math.round(prev[currentLayer] / 120) * 120;
      const next = { ...prev, [currentLayer]: snapped };
      // Fix: Explicitly cast Object.values to number[] to ensure elements are treated as numbers for arithmetic operations
      const isAligned = (Object.values(next) as number[]).every(r => {
        const n = ((r % 360) + 360) % 360;
        return n < 6 || n > 354;
      });
      if (isAligned) setSolved(true);
      return next;
    });
    setActiveLayer(null);
  }, [activeLayer]);

  useEffect(() => {
    window.addEventListener('mousemove', handleDragMove);
    window.addEventListener('mouseup', handleDragEnd);
    window.addEventListener('touchmove', handleDragMove, { passive: false });
    window.addEventListener('touchend', handleDragEnd);
    return () => {
      window.removeEventListener('mousemove', handleDragMove);
      window.removeEventListener('mouseup', handleDragEnd);
      window.removeEventListener('touchmove', handleDragMove);
      window.removeEventListener('touchend', handleDragEnd);
    };
  }, [handleDragMove, handleDragEnd]);

  const initGame = async () => {
    setLoading(true);
    setSolved(false);
    setShowHint(false);
    try {
      const data = await generateSecretChallenge();
      setSecret(data);
    } finally {
      setRotations({
        [RingLayer.OUTER]: (Math.floor(Math.random() * 2) + 1) * 120,
        [RingLayer.OUTER_MID]: (Math.floor(Math.random() * 2) + 1) * 120,
        [RingLayer.MIDDLE]: (Math.floor(Math.random() * 2) + 1) * 120,
        [RingLayer.INNER_MID]: (Math.floor(Math.random() * 2) + 1) * 120,
        [RingLayer.INNER]: (Math.floor(Math.random() * 2) + 1) * 120,
      });
      setLoading(false);
    }
  };

  useEffect(() => { initGame(); }, []);

  return (
    <div className="min-h-screen flex flex-col items-center justify-center p-4 relative overflow-hidden select-none">
      <div className="lamp-glow" />

      {/* Decorative Elements */}
      <div className="absolute top-10 left-10 polaroid rotate-[-8deg] hidden lg:block opacity-60">
        <div className="w-32 h-32 bg-stone-900 flex items-center justify-center text-red-900/40">
           <Camera size={40} />
        </div>
        <p className="typewriter text-[10px] text-stone-700 mt-2">SCENE RECONSTRUCTION</p>
      </div>

      <div className="absolute bottom-20 right-10 polaroid rotate-[5deg] hidden lg:block opacity-60">
        <div className="w-32 h-32 bg-stone-800 flex items-center justify-center text-stone-600">
           <MapPin size={40} />
        </div>
        <p className="typewriter text-[10px] text-stone-700 mt-2">DOWNTOWN DISTRICT</p>
      </div>

      <header className="text-center mb-8 z-10">
        <h1 className="text-4xl md:text-5xl font-bold tracking-[0.2em] text-stone-200 uppercase mb-2 drop-shadow-[0_4px_10px_rgba(0,0,0,1)]">
          STAINED <span className="text-red-800">EVIDENCE</span>
        </h1>
        <p className="typewriter text-[12px] tracking-[0.3em] text-stone-500 uppercase opacity-80">Rebuild the weapon from shattered glass</p>
      </header>

      <div className="relative z-10 flex flex-col items-center">
        {loading && (
          <div className="absolute inset-0 z-50 flex items-center justify-center bg-black/60 backdrop-blur-sm rounded-full">
            <div className="flex flex-col items-center gap-4">
              <div className="loader-ring"></div>
              <p className="typewriter text-stone-300 text-[10px] tracking-widest uppercase">Analyzing Evidence...</p>
            </div>
          </div>
        )}

        <div className="relative">
          {/* Top Indicator */}
          <div className="absolute -top-12 left-1/2 -translate-x-1/2 z-30 opacity-80">
            <div className="w-[1px] h-8 bg-red-800 animate-pulse mx-auto mb-1"></div>
            <Search className="text-red-900" size={24} />
          </div>

          <div 
            ref={containerRef}
            className={`relative w-[340px] h-[340px] md:w-[560px] md:h-[560px] rounded-full border-[10px] border-[#1a1614] shadow-[0_30px_100px_rgba(0,0,0,0.9)] overflow-hidden transition-all duration-1000 ${solved ? 'border-red-900/60 shadow-[0_0_100px_rgba(153,27,27,0.4)]' : ''}`}
          >
            <svg viewBox="0 0 500 500" className={`w-full h-full transition-opacity duration-500 ${loading ? 'opacity-20' : 'opacity-100'}`}>
              <DialRing layer={RingLayer.OUTER} radius={215} thickness={45} rotation={rotations[RingLayer.OUTER]} color={COLORS[RingLayer.OUTER]} isActive={!solved} isDragging={activeLayer === RingLayer.OUTER} onDragStart={handleDragStart} />
              <DialRing layer={RingLayer.OUTER_MID} radius={170} thickness={45} rotation={rotations[RingLayer.OUTER_MID]} color={COLORS[RingLayer.OUTER_MID]} isActive={!solved} isDragging={activeLayer === RingLayer.OUTER_MID} onDragStart={handleDragStart} />
              <DialRing layer={RingLayer.MIDDLE} radius={125} thickness={45} rotation={rotations[RingLayer.MIDDLE]} color={COLORS[RingLayer.MIDDLE]} isActive={!solved} isDragging={activeLayer === RingLayer.MIDDLE} onDragStart={handleDragStart} />
              <DialRing layer={RingLayer.INNER_MID} radius={80} thickness={45} rotation={rotations[RingLayer.INNER_MID]} color={COLORS[RingLayer.INNER_MID]} isActive={!solved} isDragging={activeLayer === RingLayer.INNER_MID} onDragStart={handleDragStart} />
              <DialRing layer={RingLayer.INNER} radius={35} thickness={45} rotation={rotations[RingLayer.INNER]} color={COLORS[RingLayer.INNER]} isActive={!solved} isDragging={activeLayer === RingLayer.INNER} onDragStart={handleDragStart} />
            </svg>
          </div>
        </div>

        <div className="mt-10 w-full max-w-lg space-y-6 z-20">
          {solved ? (
            <div className="bg-[#f2f0eb] p-10 rounded-sm shadow-[10px_10px_30px_rgba(0,0,0,0.5)] border-l-8 border-red-900 animate-in fade-in slide-in-from-bottom duration-700">
              <div className="flex justify-between items-center mb-6">
                 <span className="typewriter text-red-900 font-bold text-xs uppercase tracking-tighter">Confidential Evidence</span>
                 <Skull className="text-red-900" size={24} />
              </div>
              <div className="typewriter text-xl md:text-2xl text-stone-800 italic leading-relaxed mb-10 border-b border-stone-300 pb-8">
                "{secret.secret}"
              </div>
              <button
                onClick={initGame}
                className="w-full bg-stone-900 hover:bg-black text-stone-200 font-bold py-4 rounded-sm transition-all flex items-center justify-center gap-3 typewriter text-xs uppercase tracking-widest"
              >
                <RefreshCw size={16} />
                Process Next Case
              </button>
            </div>
          ) : (
            <div className="flex flex-col gap-5 items-center">
              <div className={`w-full p-6 bg-[#f2ede4] border-stone-300 border-2 shadow-xl transition-all duration-700 overflow-hidden relative ${showHint ? 'max-h-60 opacity-100 translate-y-0' : 'max-h-0 opacity-0 -translate-y-4'}`}>
                <p className="typewriter text-stone-700 text-center italic text-sm leading-relaxed">
                  "{secret.hint}"
                </p>
              </div>

              <button
                onClick={() => setShowHint(!showHint)}
                disabled={loading}
                className="group relative px-10 py-4 bg-stone-950/80 hover:bg-black text-stone-400 border border-stone-800 rounded-sm transition-all flex items-center gap-4 typewriter text-[11px] tracking-[0.3em] uppercase disabled:opacity-30"
              >
                <Search size={16} className="text-red-800" />
                {showHint ? "Close File" : "Read Clues"}
              </button>
            </div>
          )}
        </div>
      </div>

      <footer className="mt-10 typewriter text-stone-700 text-[10px] tracking-[1.2em] uppercase opacity-40 z-10">
        Crime Lab • Division 4
      </footer>
    </div>
  );
};

export default App;
