export enum RingLayer {
  INNER = 'inner',
  MIDDLE = 'middle',
  OUTER = 'outer'
}

export interface RingState {
  rotation: number;
}

import React, { useMemo } from 'react';
import { RingLayer } from '../types';

interface DialRingProps {
  layer: RingLayer;
  radius: number;
  thickness: number;
  rotation: number;
  isActive: boolean;
  isDragging: boolean;
  isSolved: boolean;
  onDragStart: (layer: RingLayer, e: React.MouseEvent | React.TouchEvent) => void;
}

const DialRing: React.FC<DialRingProps> = ({
  layer,
  radius,
  thickness,
  rotation,
  isActive,
  isDragging,
  isSolved,
  onDragStart
}) => {
  const innerR = radius - thickness / 2;
  const outerR = radius + thickness / 2;

  const isAligned = useMemo(() => {
    const norm = ((rotation % 360) + 360) % 360;
    return norm < 5 || norm > 355;
  }, [rotation]);

  const ringColor = useMemo(() => {
    if (isSolved) return "stroke-yellow-500/80";
    if (isAligned) return "stroke-red-500/60";
    return "stroke-slate-800";
  }, [isAligned, isSolved]);

  const Symbols = () => {
    const count = layer === RingLayer.OUTER ? 12 : layer === RingLayer.MIDDLE ? 8 : 4;
    return (
      <g>
        {Array.from({ length: count }).map((_, i) => {
          const angle = (i * 360) / count;
          const rad = (angle * Math.PI) / 180;
          const x = 250 + radius * Math.cos(rad);
          const y = 250 + radius * Math.sin(rad);

          // ตัวสัญลักษณ์หลักที่จะต้องหมุนให้ตรง 12 นาฬิกา (ตรงกับมุม -90 องศาใน SVG)
          const isMainSymbol = i === 0;

          return (
            <g key={i} transform={`translate(${x}, ${y}) rotate(${angle + 90})`}>
              {isMainSymbol ? (
                <path 
                  d="M -10 10 L 0 -10 L 10 10 Z" 
                  fill={isAligned ? "#ef4444" : "#475569"} 
                  className={isAligned ? "animate-pulse" : ""}
                />
              ) : (
                <circle r="3" fill="#1e293b" />
              )}
            </g>
          );
        })}
      </g>
    );
  };

  return (
    <g 
      className={`cursor-grab active:cursor-grabbing transition-opacity duration-1000 ${isSolved && !isDragging ? 'opacity-40' : 'opacity-100'}`}
      onMouseDown={(e) => !isSolved && onDragStart(layer, e)}
      onTouchStart={(e) => !isSolved && onDragStart(layer, e)}
    >
      {/* Background Ring */}
      <circle 
        cx="250" cy="250" r={radius} 
        fill="none" 
        strokeWidth={thickness} 
        className={`${ringColor} transition-colors duration-500`}
        strokeOpacity="0.2"
      />

      {/* Rotating Content */}
      <g style={{ 
        transform: `rotate(${rotation}deg)`, 
        transformOrigin: '250px 250px',
        transition: isDragging ? 'none' : 'transform 0.6s cubic-bezier(0.34, 1.56, 0.64, 1)'
      }}>
        {/* Decorative etchings */}
        <circle cx="250" cy="250" r={innerR} fill="none" stroke="#1a1a2e" strokeWidth="1" />
        <circle cx="250" cy="250" r={outerR} fill="none" stroke="#1a1a2e" strokeWidth="1" />

        <Symbols />

        {/* Connection bridge effect when aligned */}
        {isAligned && (
          <line 
            x1="250" y1={250 - innerR} 
            x2="250" y2={250 - outerR} 
            stroke="#ef4444" 
            strokeWidth="4" 
            strokeLinecap="round"
            className="animate-pulse"
          />
        )}
      </g>
    </g>
  );
};

export default DialRing;

import React, { useState, useEffect, useCallback, useRef } from 'react';
import { RefreshCw, Unlock, ShieldAlert, Sparkles, Key } from 'lucide-react';
import DialRing from './components/DialRing';
import { generateSecretChallenge } from './services/geminiService';
import { RingLayer } from './types';

const App: React.FC = () => {
  const [loading, setLoading] = useState(false);
  const [isSolved, setIsSolved] = useState(false);
  const [showResult, setShowResult] = useState(false);
  const [triggerFlash, setTriggerFlash] = useState(false);
  const [secret, setSecret] = useState({ secret: "", hint: "" });

  const [rotations, setRotations] = useState<Record<RingLayer, number>>({
    [RingLayer.OUTER]: (Math.floor(Math.random() * 8) + 1) * 45,
    [RingLayer.MIDDLE]: (Math.floor(Math.random() * 8) + 1) * 45,
    [RingLayer.INNER]: (Math.floor(Math.random() * 8) + 1) * 45,
  });

  const [activeLayer, setActiveLayer] = useState<RingLayer | null>(null);
  const dragStartAngle = useRef<number>(0);
  const initialRotation = useRef<number>(0);
  const containerRef = useRef<HTMLDivElement>(null);

  const getAngle = (clientX: number, clientY: number) => {
    if (!containerRef.current) return 0;
    const rect = containerRef.current.getBoundingClientRect();
    const centerX = rect.left + rect.width / 2;
    const centerY = rect.top + rect.height / 2;
    const dx = clientX - centerX;
    const dy = clientY - centerY;
    return Math.atan2(dy, dx) * (180 / Math.PI);
  };

  const handleDragStart = (layer: RingLayer, e: React.MouseEvent | React.TouchEvent) => {
    if (isSolved || loading) return;
    const clientX = 'touches' in e ? (e as TouchEvent).touches[0].clientX : (e as MouseEvent).clientX;
    const clientY = 'touches' in e ? (e as TouchEvent).touches[0].clientY : (e as MouseEvent).clientY;
    setActiveLayer(layer);
    dragStartAngle.current = getAngle(clientX, clientY);
    initialRotation.current = rotations[layer];
  };

  const handleDragMove = useCallback((e: MouseEvent | TouchEvent) => {
    if (activeLayer === null || isSolved) return;
    const clientX = 'touches' in e ? (e as TouchEvent).touches[0].clientX : (e as MouseEvent).clientX;
    const clientY = 'touches' in e ? (e as TouchEvent).touches[0].clientY : (e as MouseEvent).clientY;
    const currentAngle = getAngle(clientX, clientY);
    const angleDiff = currentAngle - dragStartAngle.current;

    setRotations(prev => ({ ...prev, [activeLayer]: initialRotation.current + angleDiff }));
  }, [activeLayer, isSolved]);

  const handleDragEnd = useCallback(() => {
    if (activeLayer === null) return;

    setRotations(prev => {
      const currentRot = prev[activeLayer!];
      // Snap to increments based on layer (Outer 12 segments, Middle 8, Inner 4)
      const snapPoints = activeLayer === RingLayer.OUTER ? 30 : activeLayer === RingLayer.MIDDLE ? 45 : 90;
      const snapped = Math.round(currentRot / snapPoints) * snapPoints;
      const nextRotations = { ...prev, [activeLayer!]: snapped };

      const allAligned = Object.values(nextRotations).every(rot => {
        const normalized = ((rot % 360) + 360) % 360;
        return normalized < 5 || normalized > 355;
      });

      if (allAligned) triggerUnlock();

      return nextRotations;
    });
    setActiveLayer(null);
  }, [activeLayer]);

  const triggerUnlock = () => {
    setIsSolved(true);
    setTriggerFlash(true);

    // Sequence of animations
    setTimeout(() => {
      setTriggerFlash(false);
    }, 1000);

    setTimeout(() => {
      setShowResult(true);
    }, 1500);
  };

  useEffect(() => {
    if (activeLayer !== null) {
      window.addEventListener('mousemove', handleDragMove);
      window.addEventListener('mouseup', handleDragEnd);
      window.addEventListener('touchmove', handleDragMove, { passive: false });
      window.addEventListener('touchend', handleDragEnd);
    }
    return () => {
      window.removeEventListener('mousemove', handleDragMove);
      window.removeEventListener('mouseup', handleDragEnd);
      window.removeEventListener('touchmove', handleDragMove);
      window.removeEventListener('touchend', handleDragEnd);
    };
  }, [activeLayer, handleDragMove, handleDragEnd]);

  const initGame = async () => {
    setLoading(true);
    setIsSolved(false);
    setShowResult(false);
    const data = await generateSecretChallenge();
    setSecret(data);
    setRotations({
      [RingLayer.OUTER]: (Math.floor(Math.random() * 11) + 1) * 30,
      [RingLayer.MIDDLE]: (Math.floor(Math.random() * 7) + 1) * 45,
      [RingLayer.INNER]: (Math.floor(Math.random() * 3) + 1) * 90,
    });
    setLoading(false);
  };

  useEffect(() => { initGame(); }, []);

  return (
    <div className="min-h-screen flex flex-col items-center justify-center p-6 relative overflow-hidden bg-[#0a0a0c]">
      {/* Flash effect when unlocked */}
      <div className={`flash-overlay ${triggerFlash ? 'animate-flash' : ''}`} />

      {/* Decorative Atmosphere */}
      <div className="absolute inset-0 opacity-20 pointer-events-none">
        <div className="absolute top-[-10%] left-[-10%] w-[40%] h-[40%] bg-red-900 rounded-full blur-[120px]" />
        <div className="absolute bottom-[-10%] right-[-10%] w-[40%] h-[40%] bg-blue-900 rounded-full blur-[120px]" />
      </div>

      <header className="text-center mb-12 z-20">
        <h1 className="text-4xl md:text-6xl font-bold tracking-[0.2em] text-white mystic-font mb-4 flex items-center justify-center gap-4">
          <Key className="text-yellow-600" size={32} />
          THE MYSTIC DIAL
        </h1>
        <p className="text-slate-500 tracking-[0.3em] text-xs uppercase font-medium">
          {isSolved ? "ปริศนาถูกคลี่คลายแล้ว" : "หมุนกลไกเพื่อปลดล็อกรหัสลับ"}
        </p>
      </header>

      <div className="relative z-10 flex flex-col items-center">
        <div 
          ref={containerRef}
          className={`dial-container relative w-[320px] h-[320px] md:w-[500px] md:h-[500px] rounded-full shadow-[0_0_80px_rgba(0,0,0,0.8)] border-4 border-slate-900 bg-slate-950/50 backdrop-blur-sm transition-all duration-1000 ${isSolved ? 'scale-90 opacity-60' : 'scale-100'}`}
        >
          <svg viewBox="0 0 500 500" className="w-full h-full">
            <DialRing
              layer={RingLayer.OUTER}
              radius={200}
              thickness={60}
              rotation={rotations[RingLayer.OUTER]}
              isActive={!isSolved}
              isDragging={activeLayer === RingLayer.OUTER}
              isSolved={isSolved}
              onDragStart={handleDragStart}
            />
            <DialRing
              layer={RingLayer.MIDDLE}
              radius={130}
              thickness={60}
              rotation={rotations[RingLayer.MIDDLE]}
              isActive={!isSolved}
              isDragging={activeLayer === RingLayer.MIDDLE}
              isSolved={isSolved}
              onDragStart={handleDragStart}
            />
            <DialRing
              layer={RingLayer.INNER}
              radius={60}
              thickness={60}
              rotation={rotations[RingLayer.INNER]}
              isActive={!isSolved}
              isDragging={activeLayer === RingLayer.INNER}
              isSolved={isSolved}
              onDragStart={handleDragStart}
            />

            {/* Center Core */}
            <circle cx="250" cy="250" r="20" fill="#111" stroke="#334155" strokeWidth="2" />
            <circle cx="250" cy="250" r="10" fill={isSolved ? "#eab308" : "#1e293b"} className={isSolved ? "animate-pulse" : ""} />
          </svg>
        </div>

        {/* Result Overlay */}
        {showResult && (
          <div className="absolute inset-0 z-50 flex items-center justify-center pointer-events-auto">
            <div className="reveal-card w-full max-w-lg bg-slate-900/90 border-2 border-yellow-600/30 p-10 rounded-[2rem] shadow-[0_0_100px_rgba(234,179,8,0.2)] backdrop-blur-2xl text-center">
              <div className="mb-6 inline-flex p-4 bg-yellow-600/10 rounded-full text-yellow-500">
                <Unlock size={48} className="animate-bounce" />
              </div>
              <h2 className="text-yellow-500 text-sm tracking-[0.5em] uppercase font-bold mb-6">ความลับถูกเปิดเผย</h2>
              <div className="text-xl md:text-2xl font-serif text-white italic leading-relaxed mb-10 px-4">
                "{secret.secret}"
              </div>
              <button
                onClick={initGame}
                className="group w-full py-4 bg-yellow-600 hover:bg-yellow-500 text-black font-black rounded-xl transition-all flex items-center justify-center gap-3 uppercase tracking-widest overflow-hidden relative"
              >
                <RefreshCw size={20} className={loading ? "animate-spin" : "group-hover:rotate-180 transition-transform duration-500"} />
                ท้าทายอีกครั้ง
              </button>
            </div>
          </div>
        )}

        {!isSolved && (
          <div className="mt-12 text-center max-w-md animate-pulse">
            <div className="flex items-center justify-center gap-3 text-red-500/60 mb-2">
              <ShieldAlert size={16} />
              <span className="text-[10px] tracking-[0.3em] uppercase">คำใบ้ที่พบ</span>
            </div>
            <p className="text-slate-400 text-sm font-serif italic">
              "{secret.hint}"
            </p>
          </div>
        )}
      </div>

      <footer className="mt-auto py-8 text-[9px] tracking-[1em] text-slate-800 uppercase font-bold">
        The Truth lies within the alignment
      </footer>
    </div>
  );
};

export default App;

