import React, { useMemo } from 'react';
import htm from 'htm';
import { RingLayer } from '../types.js';

const html = htm.bind(React.createElement);

const DialRing = ({
  layer,
  radius,
  thickness,
  rotation,
  isActive,
  isDragging,
  onDragStart
}) => {
  const innerR = radius - thickness / 2;
  const outerR = radius + thickness / 2;

  // ข้อมูลตามที่ผู้ใช้กำหนด
  const ringContent = useMemo(() => {
    switch (layer) {
      case RingLayer.OUTER:
        return ["30%", "50%", "72%", "85%"];
      case RingLayer.MIDDLE:
        return ["Low CCTV", "Moderate", "High Suppression", "Full Lockdown"];
      case RingLayer.INNER:
        return ["Capital ↓", "Data ↑", "Risk Hidden", "Control ↑"];
      default:
        return ["-", "-", "-", "-"];
    }
  }, [layer]);

  const segments = useMemo(() => {
    return ringContent.map((text, i) => {
      const angle = i * 90;
      // คำนวณตำแหน่งข้อความในวงแหวน
      const textAngle = (angle) * (Math.PI / 180);
      const tx = 250 + radius * Math.cos(textAngle);
      const ty = 250 + radius * Math.sin(textAngle);

      return { text, angle, tx, ty };
    });
  }, [radius, ringContent]);

  return html`
    <g 
      className="cursor-grab active:cursor-grabbing"
      onMouseDown=${(e) => onDragStart(layer, e)}
      onTouchStart=${(e) => onDragStart(layer, e)}
    >
      {/* Background Circle */}
      <circle cx="250" cy="250" r=${radius} fill="none" stroke="#0f172a" strokeWidth=${thickness} opacity="0.5" />

      {/* Grid lines */}
      <g style=${{ 
        transform: `rotate(${rotation}deg)`, 
        transformOrigin: '250px 250px',
        transition: isDragging ? 'none' : 'transform 0.5s cubic-bezier(0.175, 0.885, 0.32, 1.275)'
      }}>
        {/* Draw 4 divider lines */}
        ${[0, 90, 180, 270].map(angle => {
          const rad = angle * (Math.PI / 180);
          return html`
            <line 
              key=${angle}
              x1=${250 + innerR * Math.cos(rad)} 
              y1=${250 + innerR * Math.sin(rad)} 
              x2=${250 + outerR * Math.cos(rad)} 
              y2=${250 + outerR * Math.sin(rad)} 
              stroke="#1e293b" 
              strokeWidth="2" 
            />
          `
        })}

        {/* Text Labels */}
        ${segments.map((seg, i) => html`
          <text
            key=${i}
            x=${seg.tx}
            y=${seg.ty}
            fill=${isActive ? "#67e8f9" : "#22d3ee"}
            fontSize=${layer === RingLayer.INNER ? "14" : "16"}
            fontWeight="bold"
            textAnchor="middle"
            alignmentBaseline="middle"
            style=${{ 
              transform: `rotate(${seg.angle + 90}deg)`, 
              transformOrigin: `${seg.tx}px ${seg.ty}px`,
              filter: isActive ? 'none' : 'drop-shadow(0 0 5px #22d3ee)'
            }}
          >
            ${seg.text}
          </text>
        `)}
      </g>

      {/* Borders */}
      <circle cx="250" cy="250" r=${outerR} fill="none" stroke="#1e293b" strokeWidth="1" />
      <circle cx="250" cy="250" r=${innerR} fill="none" stroke="#1e293b" strokeWidth="1" />
    </g>
  `;
};

export default DialRing;

import React, { useState, useEffect, useCallback, useRef } from 'react';
import { RefreshCw, ShieldCheck, Activity, Terminal } from 'lucide-react';
import htm from 'htm';
import DialRing from './components/DialRing.js';
import { generateSecretChallenge } from './services/geminiService.js';
import { RingLayer } from './types.js';

const html = htm.bind(React.createElement);

const App = () => {
  const [loading, setLoading] = useState(false);
  const [solved, setSolved] = useState(false);
  const [secret, setSecret] = useState({ secret: "", hint: "" });
  const [showHint, setShowHint] = useState(false);

  // Snap settings: 4 items = 90 degrees each
  const SNAP_ANGLE = 90;

  const [rotations, setRotations] = useState({
    [RingLayer.OUTER]: (Math.floor(Math.random() * 4)) * SNAP_ANGLE,
    [RingLayer.MIDDLE]: (Math.floor(Math.random() * 4)) * SNAP_ANGLE,
    [RingLayer.INNER]: (Math.floor(Math.random() * 4)) * SNAP_ANGLE,
  });

  const [activeLayer, setActiveLayer] = useState(null);
  const dragStartAngle = useRef(0);
  const initialRotation = useRef(0);
  const containerRef = useRef(null);

  const getAngle = (clientX, clientY) => {
    if (!containerRef.current) return 0;
    const rect = containerRef.current.getBoundingClientRect();
    const centerX = rect.left + rect.width / 2;
    const centerY = rect.top + rect.height / 2;
    const dx = clientX - centerX;
    const dy = clientY - centerY;
    return Math.atan2(dy, dx) * (180 / Math.PI);
  };

  const handleDragStart = (layer, e) => {
    if (solved || loading) return;
    const clientX = 'touches' in e ? e.touches[0].clientX : e.clientX;
    const clientY = 'touches' in e ? e.touches[0].clientY : e.clientY;
    setActiveLayer(layer);
    dragStartAngle.current = getAngle(clientX, clientY);
    initialRotation.current = rotations[layer];
  };

  const handleDragMove = useCallback((e) => {
    const layer = activeLayer;
    if (layer === null || solved) return;
    const clientX = 'touches' in e ? e.touches[0].clientX : e.clientX;
    const clientY = 'touches' in e ? e.touches[0].clientY : e.clientY;
    const currentAngle = getAngle(clientX, clientY);
    const angleDiff = currentAngle - dragStartAngle.current;

    setRotations(prev => ({ ...prev, [layer]: initialRotation.current + angleDiff }));
  }, [activeLayer, solved]);

  const handleDragEnd = useCallback(() => {
    const layer = activeLayer;
    if (layer === null) return;
    setRotations(prev => {
      const currentRot = prev[layer];
      // Snap to 90 degrees
      const snapped = Math.round(currentRot / SNAP_ANGLE) * SNAP_ANGLE;
      const nextRotations = { ...prev, [layer]: snapped };

      // Check alignment: all at 270 degrees (which corresponds to index 0 at the top)
      // Since SVG coordinates start 0 at 3 o'clock, we align to 270 (-90) for top position
      const isAligned = Object.values(nextRotations).every((rot) => {
        const normalized = ((rot % 360) + 360) % 360;
        // In our setup, index 0 is at 0 deg (3 o'clock), so to get it at top ( zenith), 
        // it must be rotated 270 degrees.
        return normalized > 265 && normalized < 275;
      });

      if (isAligned) setSolved(true);
      return nextRotations;
    });
    setActiveLayer(null);
  }, [activeLayer]);

  useEffect(() => {
    if (activeLayer !== null) {
      window.addEventListener('mousemove', handleDragMove);
      window.addEventListener('mouseup', handleDragEnd);
      window.addEventListener('touchmove', handleDragMove, { passive: false });
      window.addEventListener('touchend', handleDragEnd);
    }
    return () => {
      window.removeEventListener('mousemove', handleDragMove);
      window.removeEventListener('mouseup', handleDragEnd);
      window.removeEventListener('touchmove', handleDragMove);
      window.removeEventListener('touchend', handleDragEnd);
    };
  }, [activeLayer, handleDragMove, handleDragEnd]);

  const initGame = async () => {
    setLoading(true);
    setSolved(false);
    setShowHint(false);

    const data = await generateSecretChallenge();
    setSecret(data);
    // Randomize initial positions but NOT solved
    setRotations({
      [RingLayer.OUTER]: (Math.floor(Math.random() * 3)) * SNAP_ANGLE,
      [RingLayer.MIDDLE]: (Math.floor(Math.random() * 3)) * SNAP_ANGLE,
      [RingLayer.INNER]: (Math.floor(Math.random() * 3)) * SNAP_ANGLE,
    });
    setLoading(false);
  };

  useEffect(() => { initGame(); }, []);

  return html`
    <div className="min-h-screen flex flex-col items-center justify-center p-4 overflow-hidden relative select-none hex-bg">
      <div className="scanline"></div>

      <header className="text-center mb-12 z-10">
        <div className="inline-block px-3 py-1 border border-cyan-500/30 rounded mb-4 bg-cyan-950/20">
          <span className="text-[10px] text-cyan-400 font-bold tracking-[0.3em] uppercase">Security Level: Omega</span>
        </div>
        <h1 className="text-4xl md:text-5xl font-black tracking-tighter text-white uppercase mb-2 text-glow italic">
          System <span className="text-cyan-400">Override</span>
        </h1>
        <p className="text-[10px] tracking-[0.5em] text-slate-500 uppercase font-bold">Synchronize core protocols</p>
      </header>

      <div className="relative z-10 flex flex-col items-center">
        <div className="relative">
          {/* Top Indicator */}
          <div className="absolute -top-8 left-1/2 -translate-x-1/2 z-30">
            <div className="w-1 h-12 bg-gradient-to-b from-cyan-400 to-transparent shadow-[0_0_15px_#22d3ee]"></div>
          </div>

          <div 
            ref=${containerRef}
            className=${`relative w-[340px] h-[340px] md:w-[540px] md:h-[540px] rounded-full border border-slate-800 bg-slate-950/50 shadow-[0_0_80px_rgba(0,0,0,0.5)] transition-all duration-700 ${solved ? 'ring-4 ring-cyan-500/50' : ''}`}
          >
            <svg viewBox="0 0 500 500" className="w-full h-full system-glow">
              <${DialRing}
                layer=${RingLayer.OUTER}
                radius=${210}
                thickness=${60}
                rotation=${rotations[RingLayer.OUTER]}
                isActive=${!solved}
                isDragging=${activeLayer === RingLayer.OUTER}
                onDragStart=${handleDragStart}
              />
              <${DialRing}
                layer=${RingLayer.MIDDLE}
                radius=${145}
                thickness=${60}
                rotation=${rotations[RingLayer.MIDDLE]}
                isActive=${!solved}
                isDragging=${activeLayer === RingLayer.MIDDLE}
                onDragStart=${handleDragStart}
              />
              <${DialRing}
                layer=${RingLayer.INNER}
                radius=${80}
                thickness=${60}
                rotation=${rotations[RingLayer.INNER]}
                isActive=${!solved}
                isDragging=${activeLayer === RingLayer.INNER}
                onDragStart=${handleDragStart}
              />

              {/* Center Core */}
              <circle cx="250" cy="250" r="45" fill="#020617" stroke="#1e293b" strokeWidth="2" />
              <g transform="translate(235, 235)">
                 <${Activity} size=${30} className=${`text-cyan-500/40 ${solved ? 'animate-pulse text-cyan-400' : ''}`} />
              </g>
            </svg>
          </div>
        </div>

        <div className="mt-12 w-full max-w-lg space-y-6 z-20">
          ${solved ? html`
            <div className="bg-cyan-950/30 border border-cyan-500/30 p-8 rounded-xl animate-in fade-in slide-in-from-bottom duration-500 backdrop-blur-md">
              <div className="flex items-center gap-3 mb-4">
                <${ShieldCheck} className="text-cyan-400" size=${24} />
                <span className="text-xs font-bold text-cyan-400 tracking-widest uppercase">Encryption Decoded</span>
              </div>
              <div className="text-lg md:text-xl font-bold text-white mb-6 font-mono leading-relaxed border-l-2 border-cyan-500 pl-4">
                ${secret.secret}
              </div>
              <button
                onClick=${initGame}
                className="w-full bg-cyan-600 hover:bg-cyan-500 text-white font-bold py-4 rounded-lg transition-all flex items-center justify-center gap-3 uppercase text-xs tracking-[0.2em]"
              >
                <${RefreshCw} size=${16} className=${loading ? "animate-spin" : ""} />
                Initialize New Node
              </button>
            </div>
          ` : html`
            <div className="space-y-4">
              <div className="flex gap-4">
                <button
                  onClick=${() => setShowHint(!showHint)}
                  className="flex-1 p-4 bg-slate-900/50 hover:bg-slate-800 text-slate-400 rounded-lg border border-slate-800 transition-all flex items-center justify-center gap-3 text-[10px] font-bold tracking-[0.2em] uppercase"
                >
                  <${Terminal} size=${14} />
                  ${showHint ? "Hide Console" : "Query Hint"}
                </button>
              </div>

              ${showHint && html`
                <div className="p-4 bg-slate-950/80 border border-slate-800 text-cyan-500/70 text-[11px] font-mono leading-relaxed">
                  <span className="text-slate-600">> </span> ${secret.hint}
                </div>
              `}
            </div>
          `}
        </div>
      </div>

      <footer className="mt-12 text-slate-800 text-[8px] tracking-[1em] uppercase font-bold">
        Secure Handshake Protocol v4.0.2
      </footer>
    </div>
  `;
};

export default App;

import React, { useMemo } from 'react';
import htm from 'htm';
