const html = htm.bind(React.createElement);

const DialRing = ({
  layer,
  radius,
  thickness,
  rotation,
  isActive,
  isDragging,
  onDragStart
}) => {
  const innerR = radius - thickness / 2;
  const outerR = radius + thickness / 2;
  const pathId = `path-${layer}`;

  // ข้อมูลตามที่ผู้ใช้กำหนด
  const ringContent = useMemo(() => {
    switch (layer) {
      case RingLayer.OUTER:
        return ["30%", "50%", "72%", "85%"];
      case RingLayer.MIDDLE:
        return ["Low CCTV", "Moderate", "High Suppression", "Full Lockdown"];
      case RingLayer.INNER:
        return ["Capital ↓", "Data ↑", "Risk Hidden", "Control ↑"];
      default:
        return ["-", "-", "-", "-"];
    }
  }, [layer]);

  // สร้าง Path วงกลมสำหรับ textPath (เริ่มต้นที่ 3 นาฬิกา และหมุนตามเข็มนาฬิกา)
  const textPathData = useMemo(() => {
    return `M ${250 + radius},250 A ${radius},${radius} 0 1,1 ${250 + radius},249.9 Z`;
  }, [radius]);

  const segments = useMemo(() => {
    return ringContent.map((text, i) => {
      // แบ่งวงกลมเป็น 4 ส่วน: 0%, 25%, 50%, 75% 
      // เพื่อให้ข้อความอยู่ที่ตำแหน่ง 3, 6, 9 และ 12 นาฬิกาตามลำดับ
      const offset = (i * 25) + "%";
      return { text, offset };
    });
  }, [ringContent]);

  return html`
    <g 
      className="cursor-grab active:cursor-grabbing"
      onMouseDown=${(e) => onDragStart(layer, e)}
      onTouchStart=${(e) => onDragStart(layer, e)}
    >
      <defs>
        {/* สร้างเส้นทางสำหรับการเขียนข้อความโค้ง */}
        <path id=${pathId} d=${textPathData} />
      </defs>

      {/* พื้นหลังวงแหวน */}
      <circle cx="250" cy="250" r=${radius} fill="none" stroke="#0f172a" strokeWidth=${thickness} opacity="0.3" />

      {/* กลุ่มของเส้นแบ่งและข้อความที่หมุนได้ */}
      <g style=${{ 
        transform: `rotate(${rotation}deg)`, 
        transformOrigin: '250px 250px',
        transition: isDragging ? 'none' : 'transform 0.5s cubic-bezier(0.175, 0.885, 0.32, 1.275)'
      }}>
        {/* วาดเส้นแบ่ง 4 ส่วน */}
        ${[0, 90, 180, 270].map(angle => {
          const rad = angle * (Math.PI / 180);
          return html`
            <line 
              key=${angle}
              x1=${250 + innerR * Math.cos(rad)} 
              y1=${250 + innerR * Math.sin(rad)} 
              x2=${250 + outerR * Math.cos(rad)} 
              y2=${250 + outerR * Math.sin(rad)} 
              stroke="#1e293b" 
              strokeWidth="1.5" 
              opacity="0.8"
            />
          `
        })}

        {/* ข้อความที่โค้งตามวงแหวน (Curved Text) */}
        ${segments.map((seg, i) => html`
          <text
            key=${i}
            fill=${isActive ? "#67e8f9" : "#22d3ee"}
            fontSize=${layer === RingLayer.INNER ? "12" : "13"}
            fontWeight="bold"
            letterSpacing="0.05em"
            className="select-none"
            style=${{ 
              filter: isActive ? 'none' : 'drop-shadow(0 0 5px #22d3ee)',
              textTransform: 'uppercase',
              fontFamily: 'JetBrains Mono, monospace'
            }}
          >
            <textPath 
              href=${`#${pathId}`} 
              startOffset=${seg.offset} 
              textAnchor="middle"
              dominantBaseline="central"
            >
              ${seg.text}
            </textPath>
          </text>
        `)}
      </g>

      {/* เส้นขอบวงแหวน */}
      <circle cx="250" cy="250" r=${outerR} fill="none" stroke="#1e293b" strokeWidth="1" opacity="0.4" />
      <circle cx="250" cy="250" r=${innerR} fill="none" stroke="#1e293b" strokeWidth="1" opacity="0.4" />
    </g>
  `;
};

