};

export default App;

import React, { useMemo } from 'react';
import htm from 'htm';
import { RingLayer } from '../types.js';

const html = htm.bind(React.createElement);

const DialRing = ({
  layer,
  radius,
  thickness,
  rotation,
  isActive,
  isDragging,
  onDragStart
}) => {
  const innerR = radius - thickness / 2;
  const outerR = radius + thickness / 2;
  const pathId = `path-${layer}`;

  // สีโลหะสำหรับแต่ละชั้น
  const metalColors = {
    [RingLayer.OUTER]: { base: "#1a1a1a", stroke: "#2a2a2a", text: "#a1a1aa" }, // เหล็กดำ
    [RingLayer.MIDDLE]: { base: "#262626", stroke: "#404040", text: "#d4d4d8" }, // เหล็กขัด
    [RingLayer.INNER]: { base: "#3f3f46", stroke: "#52525b", text: "#f4f4f5" }   // อลูมิเนียมเก่า
  };

  const colors = metalColors[layer] || metalColors[RingLayer.OUTER];

  const ringContent = useMemo(() => {
    switch (layer) {
      case RingLayer.OUTER:
        return ["30%", "50%", "72%", "85%"]; // ปรับค่าตามที่ระบุ
      case RingLayer.MIDDLE:
        return ["Low CCTV", "Moderate", "High Suppression", "Full Lockdown"];
      case RingLayer.INNER:
        return ["Capital ↓", "Data ↑", "Risk Hidden", "Control ↑"];
      default:
        return ["-", "-", "-", "-"];
    }
  }, [layer]);

  const textPathData = useMemo(() => {
    return `M ${250 + radius},250 A ${radius},${radius} 0 1,1 ${250 + radius},249.9 Z`;
  }, [radius]);

  const segments = useMemo(() => {
    return ringContent.map((text, i) => {
      const offset = (i * 25) + "%";
      return { text, offset };
    });
  }, [ringContent]);

  return html`
    <g 
      className="cursor-grab active:cursor-grabbing"
      onMouseDown=${(e) => onDragStart(layer, e)}
      onTouchStart=${(e) => onDragStart(layer, e)}
    >
      <defs>
        <path id=${pathId} d=${textPathData} />
        <filter id="metal-bump">
          <feTurbulence type="fractalNoise" baseFrequency="0.8" numOctaves="4" result="noise" />
          <feDiffuseLighting in="noise" lightingColor="#fff" surfaceScale="1" result="diffuse">
            <feDistantLight azimuth="45" elevation="35" />
          </feDiffuseLighting>
          <feComposite in="SourceGraphic" in2="diffuse" operator="arithmetic" k1="1" k2="0" k3="0" k4="0" />
        </filter>
      </defs>

      {/* บอดี้วงแหวนโลหะ */}
      <circle cx="250" cy="250" r=${radius} fill=${colors.base} stroke=${colors.stroke} strokeWidth=${thickness} opacity="0.9" filter="url(#metal-bump)" />

      <g style=${{ 
        transform: `rotate(${rotation}deg)`, 
        transformOrigin: '250px 250px',
        transition: isDragging ? 'none' : 'transform 0.6s cubic-bezier(0.2, 0, 0.2, 1)'
      }}>
        {/* เส้นบากกลไก */}
        ${[0, 90, 180, 270].map(angle => {
          const rad = angle * (Math.PI / 180);
          return html`
            <line 
              key=${angle}
              x1=${250 + innerR * Math.cos(rad)} 
              y1=${250 + innerR * Math.sin(rad)} 
              x2=${250 + outerR * Math.cos(rad)} 
              y2=${250 + outerR * Math.sin(rad)} 
              stroke="#000" 
              strokeWidth="2" 
              opacity="0.5"
            />
          `
        })}

        {/* ตัวอักษรที่ตอกลงในเนื้อโลหะ */}
        ${segments.map((seg, i) => html`
          <text
            key=${i}
            fill=${colors.text}
            fontSize=${layer === RingLayer.INNER ? "14" : "15"}
            fontWeight="bold"
            className="select-none"
            style=${{ 
              textShadow: '1px 1px 1px rgba(0,0,0,0.8)',
              fontFamily: "'Special Elite', cursive",
              letterSpacing: '1px'
            }}
          >
            <textPath 
              href=${`#${pathId}`} 
              startOffset=${seg.offset} 
              textAnchor="middle"
              dominantBaseline="central"
            >
              ${seg.text}
            </textPath>
          </text>
        `)}
      </g>

      {/* เส้นขอบเงาโลหะ */}
      <circle cx="250" cy="250" r=${outerR} fill="none" stroke="#000" strokeWidth="1.5" opacity="0.6" />
      <circle cx="250" cy="250" r=${innerR} fill="none" stroke="#000" strokeWidth="1.5" opacity="0.6" />
      <circle cx="250" cy="250" r=${outerR - 1} fill="none" stroke="rgba(255,255,255,0.05)" strokeWidth="1" />
    </g>
  `;
};

export default DialRing;

