const DialRing = ({
  layer,
  radius,
  thickness,
  rotation,
  isActive,
  isDragging,
  onDragStart
}) => {
  const innerR = radius - thickness / 2;
  const outerR = radius + thickness / 2;
  const pathId = `path-${layer}`;

  // สีโลหะที่ดูขลังและอ่านง่าย (Contrast สูง)
  const metalColors = {
    [RingLayer.OUTER]: { base: "#171717", stroke: "#262626", text: "#f8fafc" }, 
    [RingLayer.MIDDLE]: { base: "#262626", stroke: "#404040", text: "#f1f5f9" }, 
    [RingLayer.INNER]: { base: "#404040", stroke: "#52525b", text: "#ffffff" }   
  };

  const colors = metalColors[layer] || metalColors[RingLayer.OUTER];

  const ringContent = useMemo(() => {
    switch (layer) {
      case RingLayer.OUTER:
        return ["30%", "50%", "72%", "85%"];
      case RingLayer.MIDDLE:
        return ["LOW CCTV", "MODERATE", "HIGH SUPPRESSION", "FULL LOCKDOWN"];
      case RingLayer.INNER:
        return ["CAPITAL ↓", "DATA ↑", "RISK HIDDEN", "CONTROL ↑"];
      default:
        return ["-", "-", "-", "-"];
    }
  }, [layer]);

  // สร้าง Path วงกลมที่สมบูรณ์เพื่อให้ตัวอักษรวิ่งตามได้แม่นยำ
  // เริ่มจากจุดบนสุด (12 นาฬิกา) เพื่อให้การคำนวณ offset ง่ายขึ้น
  const textPathData = useMemo(() => {
    return `M 250, ${250 - radius} A ${radius},${radius} 0 1,1 250,${250 + radius} A ${radius},${radius} 0 1,1 250,${250 - radius}`;
  }, [radius]);

  const segments = useMemo(() => {
    return ringContent.map((text, i) => {
      // กระจายตัวอักษรที่ 0, 25, 50, 75% ของเส้นรอบวง
      const offset = (i * 25) + "%";
      return { text, offset };
    });
  }, [ringContent]);

  return html`
    <g 
      className="cursor-grab active:cursor-grabbing"
      onMouseDown=${(e) => onDragStart(layer, e)}
      onTouchStart=${(e) => onDragStart(layer, e)}
    >
      <defs>
        <path id=${pathId} d=${textPathData} />
        <filter id="engrave-effect">
          <feOffset dx="0.5" dy="0.5" in="SourceAlpha" result="offset" />
          <feGaussianBlur stdDeviation="0.5" in="offset" result="blur" />
          <feComposite operator="out" in="SourceAlpha" in2="blur" result="inverse" />
          <feFlood floodColor="black" floodOpacity="0.8" result="color" />
          <feComposite operator="in" in="color" in2="inverse" result="shadow" />
          <feComposite operator="over" in="shadow" in2="SourceGraphic" />
        </filter>
      </defs>

      {/* พื้นวงแหวนโลหะ */}
      <circle cx="250" cy="250" r=${radius} fill=${colors.base} stroke=${colors.stroke} strokeWidth=${thickness} opacity="1" />

      <g style=${{ 
        transform: `rotate(${rotation}deg)`, 
        transformOrigin: '250px 250px',
        transition: isDragging ? 'none' : 'transform 0.6s cubic-bezier(0.2, 0.8, 0.2, 1)'
      }}>
        {/* เส้นบากบอกตำแหน่ง (Tick Marks) */}
        ${[0, 90, 180, 270].map(angle => {
          const rad = (angle - 90) * (Math.PI / 180);
          return html`
            <line 
              key=${angle}
              x1=${250 + (radius - thickness/2) * Math.cos(rad)} 
              y1=${250 + (radius - thickness/2) * Math.sin(rad)} 
              x2=${250 + (radius + thickness/2) * Math.cos(rad)} 
              y2=${250 + (radius + thickness/2) * Math.sin(rad)} 
              stroke="rgba(0,0,0,0.5)" 
              strokeWidth="3" 
            />
          `
        })}

        {/* ตัวอักษรโค้งตามวงกลม (Curved Text Path) */}
        ${segments.map((seg, i) => html`
          <text
            key=${i}
            fill=${colors.text}
            fontSize=${layer === RingLayer.INNER ? "16" : "18"}
            fontWeight="bold"
            className="select-none"
            filter="url(#engrave-effect)"
            style=${{ 
              fontFamily: "'Special Elite', cursive",
              letterSpacing: '2px',
              textTransform: 'uppercase'
            }}
          >
            <textPath 
              href=${`#${pathId}`} 
              startOffset=${seg.offset} 
              textAnchor="middle"
              dominantBaseline="middle"
            >
              ${seg.text}
            </textPath>
          </text>
        `)}
      </g>

      {/* เส้นขอบตกแต่ง */}
      <circle cx="250" cy="250" r=${outerR} fill="none" stroke="rgba(0,0,0,0.4)" strokeWidth="2" />
      <circle cx="250" cy="250" r=${innerR} fill="none" stroke="rgba(0,0,0,0.4)" strokeWidth="2" />
      <circle cx="250" cy="250" r=${outerR - 1} fill="none" stroke="rgba(255,255,255,0.1)" strokeWidth="1" />
    </g>
  `;
};

