import React, { useState, useEffect, useCallback, useRef } from 'react';
import { RefreshCw, Lock, Unlock, Key } from 'lucide-react';
import htm from 'htm';
import DialRing from './components/DialRing.js';
import { generateSecretChallenge } from './services/geminiService.js';
import { RingLayer } from './types.js';

const html = htm.bind(React.createElement);

const App = () => {
  const [loading, setLoading] = useState(false);
  const [solved, setSolved] = useState(false);
  const [secret, setSecret] = useState({ secret: "", hint: "" });

  const SNAP_ANGLE = 90;

  // เริ่มต้นด้วยการสุ่มตำแหน่ง
  const [rotations, setRotations] = useState({
    [RingLayer.OUTER]: (Math.floor(Math.random() * 4)) * SNAP_ANGLE,
    [RingLayer.MIDDLE]: (Math.floor(Math.random() * 4)) * SNAP_ANGLE,
    [RingLayer.INNER]: (Math.floor(Math.random() * 4)) * SNAP_ANGLE,
  });

  const [activeLayer, setActiveLayer] = useState(null);
  const dragStartAngle = useRef(0);
  const initialRotation = useRef(0);
  const containerRef = useRef(null);

  const getAngle = (clientX, clientY) => {
    if (!containerRef.current) return 0;
    const rect = containerRef.current.getBoundingClientRect();
    const centerX = rect.left + rect.width / 2;
    const centerY = rect.top + rect.height / 2;
    const dx = clientX - centerX;
    const dy = clientY - centerY;
    return Math.atan2(dy, dx) * (180 / Math.PI);
  };

  const handleDragStart = (layer, e) => {
    if (solved || loading) return;
    const clientX = 'touches' in e ? e.touches[0].clientX : e.clientX;
    const clientY = 'touches' in e ? e.touches[0].clientY : e.clientY;
    setActiveLayer(layer);
    dragStartAngle.current = getAngle(clientX, clientY);
    initialRotation.current = rotations[layer];
  };

  const handleDragMove = useCallback((e) => {
    const layer = activeLayer;
    if (layer === null || solved) return;
    const clientX = 'touches' in e ? e.touches[0].clientX : e.clientX;
    const clientY = 'touches' in e ? e.touches[0].clientY : e.clientY;
    const currentAngle = getAngle(clientX, clientY);
    const angleDiff = currentAngle - dragStartAngle.current;

    setRotations(prev => ({ ...prev, [layer]: initialRotation.current + angleDiff }));
  }, [activeLayer, solved]);

  const handleDragEnd = useCallback(() => {
    const layer = activeLayer;
    if (layer === null) return;
    setRotations(prev => {
      const currentRot = prev[layer];
      const snapped = Math.round(currentRot / SNAP_ANGLE) * SNAP_ANGLE;
      const nextRotations = { ...prev, [layer]: snapped };

      // เช็คว่าตำแหน่ง 0% (หรือ 30%, LOW CCTV, CAPITAL) ตรงกับ 12 นาฬิกา (0 องศา)
      const isAligned = Object.values(nextRotations).every((rot) => {
        const normalized = ((rot % 360) + 360) % 360;
        return normalized < 5 || normalized > 355;
      });

      if (isAligned) setSolved(true);
      return nextRotations;
    });
    setActiveLayer(null);
  }, [activeLayer]);

  useEffect(() => {
    if (activeLayer !== null) {
      window.addEventListener('mousemove', handleDragMove);
      window.addEventListener('mouseup', handleDragEnd);
      window.addEventListener('touchmove', handleDragMove, { passive: false });
      window.addEventListener('touchend', handleDragEnd);
    }
    return () => {
      window.removeEventListener('mousemove', handleDragMove);
      window.removeEventListener('mouseup', handleDragEnd);
      window.removeEventListener('touchmove', handleDragMove);
      window.removeEventListener('touchend', handleDragEnd);
    };
  }, [activeLayer, handleDragMove, handleDragEnd]);

  const initGame = async () => {
    setLoading(true);
    setSolved(false);
    const data = await generateSecretChallenge();
    setSecret(data);
    setRotations({
      [RingLayer.OUTER]: (Math.floor(Math.random() * 3) + 1) * SNAP_ANGLE,
      [RingLayer.MIDDLE]: (Math.floor(Math.random() * 3) + 1) * SNAP_ANGLE,
      [RingLayer.INNER]: (Math.floor(Math.random() * 3) + 1) * SNAP_ANGLE,
    });
    setLoading(false);
  };

  useEffect(() => { initGame(); }, []);

  return html`
    <div className="min-h-screen vault-bg flex flex-col items-center justify-center p-4 overflow-hidden relative select-none">

      <div className="relative z-10 flex flex-col items-center">
        <div className="relative">
          {/* ตัวชี้ตำแหน่งปลดล็อก (Indicator) */}
          <div className="absolute -top-12 left-1/2 -translate-x-1/2 z-30 flex flex-col items-center">
             <div className="w-1.5 h-16 bg-red-800 shadow-[0_0_10px_rgba(153,27,27,0.5)]"></div>
             <div className="w-4 h-4 bg-red-800 rotate-45 -mt-2 shadow-[0_0_10px_rgba(153,27,27,0.5)]"></div>
          </div>

          <div 
            ref=${containerRef}
            className=${`relative w-[340px] h-[340px] md:w-[540px] md:h-[540px] rounded-full border-[15px] border-[#0c0a09] bg-stone-900 shadow-[0_0_120px_rgba(0,0,0,0.9)] transition-all duration-1000 ${solved ? 'scale-105 brightness-110 shadow-yellow-900/20' : ''}`}
          >
            <svg viewBox="0 0 500 500" className="w-full h-full">
              <${DialRing}
                layer=${RingLayer.OUTER}
                radius=${210}
                thickness=${65}
                rotation=${rotations[RingLayer.OUTER]}
                isActive=${!solved}
                isDragging=${activeLayer === RingLayer.OUTER}
                onDragStart=${handleDragStart}
              />
              <${DialRing}
                layer=${RingLayer.MIDDLE}
                radius=${140}
                thickness=${65}
                rotation=${rotations[RingLayer.MIDDLE]}
                isActive=${!solved}
                isDragging=${activeLayer === RingLayer.MIDDLE}
                onDragStart=${handleDragStart}
              />
              <${DialRing}
                layer=${RingLayer.INNER}
                radius=${70}
                thickness=${65}
                rotation=${rotations[RingLayer.INNER]}
                isActive=${!solved}
                isDragging=${activeLayer === RingLayer.INNER}
                onDragStart=${handleDragStart}
              />

              {/* แกนกลางแม่กุญแจ */}
              <circle cx="250" cy="250" r="38" fill="#0c0a09" />
              <g transform="translate(232, 232)">
                 ${solved ? 
                    html`<${Unlock} size=${36} className="text-yellow-600 animate-bounce" />` : 
                    html`<${Lock} size=${36} className="text-stone-600" />`
                 }
              </g>
            </svg>
          </div>
        </div>

        <div className="mt-16 w-full max-w-lg z-20">
          ${solved && html`
            <div className="bg-stone-900/95 border-2 border-yellow-900/30 p-8 rounded shadow-2xl animate-in fade-in slide-in-from-bottom duration-700 backdrop-blur-sm">
              <div className="flex items-center gap-3 mb-4">
                <${Key} className="text-yellow-600" size=${24} />
                <span className="text-xs font-bold text-yellow-700 tracking-[0.4em] uppercase">Vault Contents Revealed</span>
              </div>
              <div className="text-lg md:text-xl font-bold text-stone-200 mb-8 italic font-serif leading-relaxed border-l-4 border-yellow-900 pl-6">
                "${secret.secret}"
              </div>
              <button
                onClick=${initGame}
                className="w-full bg-stone-800 hover:bg-stone-700 text-stone-300 border border-stone-600 py-4 rounded transition-all flex items-center justify-center gap-3 uppercase text-[11px] tracking-[0.5em] font-bold"
              >
                <${RefreshCw} size=${14} className=${loading ? "animate-spin" : ""} />
                Reset The Lock
              </button>
            </div>
          `}
        </div>
      </div>
    </div>
  `;
};

export default App;

import React, { useMemo } from 'react';
import htm from 'htm';
import { RingLayer } from '../types.js';

const html = htm.bind(React.createElement);

const DialRing = ({
  layer,
  radius,
  thickness,
  rotation,
  isActive,
  isDragging,
  onDragStart
}) => {
  const innerR = radius - thickness / 2;
  const outerR = radius + thickness / 2;
  const pathId = `path-${layer}`;

  // สไตล์โลหะขัดเงา (Machined Metal Look)
  const vaultStyles = {
    [RingLayer.OUTER]: { base: "#334155", stroke: "#1e293b", text: "#f8fafc", glow: "rgba(255,255,255,0.1)" }, 
    [RingLayer.MIDDLE]: { base: "#475569", stroke: "#334155", text: "#f1f5f9", glow: "rgba(255,255,255,0.15)" }, 
    [RingLayer.INNER]: { base: "#64748b", stroke: "#475569", text: "#ffffff", glow: "rgba(255,255,255,0.2)" }   
  };

  const style = vaultStyles[layer] || vaultStyles[RingLayer.OUTER];

  const ringContent = useMemo(() => {
    switch (layer) {
      case RingLayer.OUTER:
        return ["30%", "50%", "72%", "85%"];
      case RingLayer.MIDDLE:
        return ["LOCK A", "LOCK B", "LOCK C", "LOCK D"];
      case RingLayer.INNER:
        return ["01", "02", "03", "04"];
      default:
        return ["", "", "", ""];
    }
  }, [layer]);

  const textPathData = useMemo(() => {
    return `M 250, ${250 - radius} A ${radius},${radius} 0 1,1 250,${250 + radius} A ${radius},${radius} 0 1,1 250,${250 - radius}`;
  }, [radius]);

  const segments = useMemo(() => {
    return ringContent.map((text, i) => {
      const offset = (i * 25) + "%";
      return { text, offset };
    });
  }, [ringContent]);

  return html`
    <g 
      className="cursor-grab active:cursor-grabbing transition-opacity duration-300"
      onMouseDown=${(e) => onDragStart(layer, e)}
      onTouchStart=${(e) => onDragStart(layer, e)}
    >
      <defs>
        <path id=${pathId} d=${textPathData} />
        <radialGradient id="grad-metal" cx="50%" cy="50%" r="50%">
          <stop offset="0%" style=${{stopColor: '#94a3b8', stopOpacity: 1}} />
          <stop offset="100%" style=${{stopColor: '#1e293b', stopOpacity: 1}} />
        </radialGradient>
        <filter id="metal-bevel">
          <feGaussianBlur in="SourceAlpha" stdDeviation="3" result="blur"/>
          <feOffset in="blur" dx="2" dy="2" result="offsetBlur"/>
          <feSpecularLighting in="blur" surfaceScale="5" specularConstant=".75" specularExponent="20" lightingColor="#bbbbbb" result="specOut">
            <fePointLight x="-5000" y="-10000" z="20000"/>
          </feSpecularLighting>
          <feComposite in="specOut" in2="SourceAlpha" operator="in" result="specOut"/>
          <feComposite in="SourceGraphic" in2="specOut" operator="arithmetic" k1="0" k2="1" k3="1" k4="0" result="litPaint"/>
        </filter>
      </defs>

      {/* วงแหวนหลัก */}
      <circle 
        cx="250" cy="250" r=${radius} 
        fill=${style.base} 
        stroke=${style.stroke} 
        strokeWidth=${thickness} 
        filter="url(#metal-bevel)"
      />

      <g style=${{ 
        transform: `rotate(${rotation}deg)`, 
        transformOrigin: '250px 250px',
        transition: isDragging ? 'none' : 'transform 0.5s cubic-bezier(0.15, 0.85, 0.35, 1)'
      }}>
        {/* สเกลตัวเลข/ขีด (Dial Increments) */}
        ${Array.from({length: 40}).map((_, i) => {
          const angle = i * 9;
          const rad = (angle - 90) * (Math.PI / 180);
          const isLarge = i % 10 === 0;
          const len = isLarge ? 12 : 6;
          return html`
            <line 
              key=${i}
              x1=${250 + (radius + thickness/2 - 2) * Math.cos(rad)} 
              y1=${250 + (radius + thickness/2 - 2) * Math.sin(rad)} 
              x2=${250 + (radius + thickness/2 - 2 - len) * Math.cos(rad)} 
              y2=${250 + (radius + thickness/2 - 2 - len) * Math.sin(rad)} 
              stroke=${isLarge ? style.text : "rgba(0,0,0,0.3)"} 
              strokeWidth=${isLarge ? "2" : "1"} 
            />
          `
        })}

        {/* ตัวหนังสือ/ตัวเลข */}
        ${segments.map((seg, i) => html`
          <text
            key=${i}
            fill=${style.text}
            fontSize=${layer === RingLayer.INNER ? "20" : "24"}
            fontWeight="bold"
            className="select-none"
            style=${{ 
              fontFamily: "'Chakra Petch', sans-serif",
              letterSpacing: '1px'
            }}
          >
            <textPath 
              href=${`#${pathId}`} 
              startOffset=${seg.offset} 
              textAnchor="middle"
              dominantBaseline="middle"
            >
              ${seg.text}
            </textPath>
          </text>
        `)}
      </g>

      {/* ขอบเงาโลหะ (Bezel) */}
      <circle cx="250" cy="250" r=${outerR} fill="none" stroke="#0f172a" strokeWidth="2" />
      <circle cx="250" cy="250" r=${innerR} fill="none" stroke="#0f172a" strokeWidth="2" />
    </g>
  `;
};

export default DialRing;

