import React, { useMemo } from 'react';
import htm from 'htm';
import { RingLayer } from '../types.js';

const html = htm.bind(React.createElement);

const DialRing = ({
  layer,
  radius,
  thickness,
  rotation,
  isActive,
  isDragging,
  onDragStart
}) => {
  const innerR = radius - thickness / 2;
  const outerR = radius + thickness / 2;
  const pathId = `path-${layer}`;

  // สไตล์โลหะขัดเงา (Machined Metal Look)
  const vaultStyles = {
    [RingLayer.OUTER]: { base: "#1e293b", stroke: "#0f172a", text: "#f8fafc" }, 
    [RingLayer.MIDDLE]: { base: "#334155", stroke: "#1e293b", text: "#f1f5f9" }, 
    [RingLayer.INNER]: { base: "#475569", stroke: "#334155", text: "#ffffff" }   
  };

  const style = vaultStyles[layer] || vaultStyles[RingLayer.OUTER];

  const ringContent = useMemo(() => {
    switch (layer) {
      case RingLayer.OUTER:
        return ["30%", "50%", "72%", "85%"];
      case RingLayer.MIDDLE:
        return ["Low CCTV", "Moderate", "High Suppression", "Full Lockdown"];
      case RingLayer.INNER:
        return ["Capital ↓", "Data ↑", "Risk Hidden", "Control ↑"];
      default:
        return ["", "", "", ""];
    }
  }, [layer]);

  // สร้าง Path วงกลมเริ่มจากจุดบนสุด (12 นาฬิกา)
  const textPathData = useMemo(() => {
    return `M 250, ${250 - radius} A ${radius},${radius} 0 1,1 250,${250 + radius} A ${radius},${radius} 0 1,1 250,${250 - radius}`;
  }, [radius]);

  const segments = useMemo(() => {
    return ringContent.map((text, i) => {
      const offset = (i * 25) + "%";
      return { text, offset };
    });
  }, [ringContent]);

  return html`
    <g 
      className="cursor-grab active:cursor-grabbing"
      onMouseDown=${(e) => onDragStart(layer, e)}
      onTouchStart=${(e) => onDragStart(layer, e)}
    >
      <defs>
        <path id=${pathId} d=${textPathData} />
        <filter id="metal-effect">
          <feGaussianBlur in="SourceAlpha" stdDeviation="2" result="blur"/>
          <feOffset in="blur" dx="1" dy="1" result="offsetBlur"/>
          <feSpecularLighting in="blur" surfaceScale="5" specularConstant=".75" specularExponent="20" lightingColor="#ffffff" result="specOut">
            <fePointLight x="-5000" y="-10000" z="20000"/>
          </feSpecularLighting>
          <feComposite in="specOut" in2="SourceAlpha" operator="in" result="specOut"/>
          <feComposite in="SourceGraphic" in2="specOut" operator="arithmetic" k1="0" k2="1" k3="1" k4="0"/>
        </filter>
      </defs>

      {/* วงแหวนโลหะ */}
      <circle 
        cx="250" cy="250" r=${radius} 
        fill=${style.base} 
        stroke=${style.stroke} 
        strokeWidth=${thickness} 
        filter="url(#metal-effect)"
      />

      <g style=${{ 
        transform: `rotate(${rotation}deg)`, 
        transformOrigin: '250px 250px',
        transition: isDragging ? 'none' : 'transform 0.5s cubic-bezier(0.15, 0.85, 0.35, 1)'
      }}>
        {/* ขีดสเกลรอบวง */}
        ${Array.from({length: 40}).map((_, i) => {
          const angle = i * 9;
          const rad = (angle - 90) * (Math.PI / 180);
          const isTick = i % 10 === 0;
          return html`
            <line 
              key=${i}
              x1=${250 + (radius + thickness/2 - 2) * Math.cos(rad)} 
              y1=${250 + (radius + thickness/2 - 2) * Math.sin(rad)} 
              x2=${250 + (radius + thickness/2 - (isTick ? 15 : 8)) * Math.cos(rad)} 
              y2=${250 + (radius + thickness/2 - (isTick ? 15 : 8)) * Math.sin(rad)} 
              stroke=${isTick ? style.text : "rgba(0,0,0,0.3)"} 
              strokeWidth=${isTick ? "2" : "1"} 
            />
          `
        })}

        {/* ตัวอักษรโค้งตามวงกลม */}
        ${segments.map((seg, i) => html`
          <text
            key=${i}
            fill=${style.text}
            fontSize=${layer === RingLayer.INNER ? "18" : "20"}
            fontWeight="bold"
            className="select-none"
            style=${{ 
              fontFamily: "'Chakra Petch', sans-serif",
              letterSpacing: '0.5px'
            }}
          >
            <textPath 
              href=${`#${pathId}`} 
              startOffset=${seg.offset} 
              textAnchor="middle"
              dominantBaseline="middle"
            >
              ${seg.text}
            </textPath>
          </text>
        `)}
      </g>
    </g>
  `;
};

