import React, { useState, useEffect, useCallback, useRef } from 'react';
import htm from 'htm';
import { RefreshCw, Lock, Unlock, Key, Shield } from 'lucide-react';
import DialRing from './components/DialRing.js';
import { generateSecretChallenge } from './services/geminiService.js';
import { RingLayer } from './types.js';

const html = htm.bind(React.createElement);

const App = () => {
  const [loading, setLoading] = useState(false);
  const [solved, setSolved] = useState(false);
  const [secret, setSecret] = useState({ secret: "", hint: "" });

  const SNAP_ANGLE = 90;

  const [rotations, setRotations] = useState({
    [RingLayer.OUTER]: (Math.floor(Math.random() * 4)) * SNAP_ANGLE,
    [RingLayer.MIDDLE]: (Math.floor(Math.random() * 4)) * SNAP_ANGLE,
    [RingLayer.INNER]: (Math.floor(Math.random() * 4)) * SNAP_ANGLE,
  });

  const [activeLayer, setActiveLayer] = useState(null);
  const dragStartAngle = useRef(0);
  const initialRotation = useRef(0);
  const containerRef = useRef(null);

  const getAngle = (clientX, clientY) => {
    if (!containerRef.current) return 0;
    const rect = containerRef.current.getBoundingClientRect();
    const centerX = rect.left + rect.width / 2;
    const centerY = rect.top + rect.height / 2;
    const dx = clientX - centerX;
    const dy = clientY - centerY;
    return Math.atan2(dy, dx) * (180 / Math.PI);
  };

  const handleDragStart = (layer, e) => {
    if (solved || loading) return;
    const clientX = 'touches' in e ? e.touches[0].clientX : e.clientX;
    const clientY = 'touches' in e ? e.touches[0].clientY : e.clientY;
    setActiveLayer(layer);
    dragStartAngle.current = getAngle(clientX, clientY);
    initialRotation.current = rotations[layer];
  };

  const handleDragMove = useCallback((e) => {
    const layer = activeLayer;
    if (layer === null || solved) return;
    const clientX = 'touches' in e ? e.touches[0].clientX : e.clientX;
    const clientY = 'touches' in e ? e.touches[0].clientY : e.clientY;
    const currentAngle = getAngle(clientX, clientY);
    const angleDiff = currentAngle - dragStartAngle.current;

    setRotations(prev => ({ ...prev, [layer]: initialRotation.current + angleDiff }));
  }, [activeLayer, solved]);

  const handleDragEnd = useCallback(() => {
    const layer = activeLayer;
    if (layer === null) return;
    setRotations(prev => {
      const currentRot = prev[layer];
      const snapped = Math.round(currentRot / SNAP_ANGLE) * SNAP_ANGLE;
      const nextRotations = { ...prev, [layer]: snapped };

      const isAligned = Object.values(nextRotations).every((rot) => {
        const normalized = ((rot % 360) + 360) % 360;
        return normalized < 5 || normalized > 355;
      });

      if (isAligned) setSolved(true);
      return nextRotations;
    });
    setActiveLayer(null);
  }, [activeLayer]);

  useEffect(() => {
    if (activeLayer !== null) {
      window.addEventListener('mousemove', handleDragMove);
      window.addEventListener('mouseup', handleDragEnd);
      window.addEventListener('touchmove', handleDragMove, { passive: false });
      window.addEventListener('touchend', handleDragEnd);
    }
    return () => {
      window.removeEventListener('mousemove', handleDragMove);
      window.removeEventListener('mouseup', handleDragEnd);
      window.removeEventListener('touchmove', handleDragMove);
      window.removeEventListener('touchend', handleDragEnd);
    };
  }, [activeLayer, handleDragMove, handleDragEnd]);

  const initGame = async () => {
    setLoading(true);
    setSolved(false);
    const data = await generateSecretChallenge();
    setSecret(data);
    setRotations({
      [RingLayer.OUTER]: (Math.floor(Math.random() * 3) + 1) * SNAP_ANGLE,
      [RingLayer.MIDDLE]: (Math.floor(Math.random() * 3) + 1) * SNAP_ANGLE,
      [RingLayer.INNER]: (Math.floor(Math.random() * 3) + 1) * SNAP_ANGLE,
    });
    setLoading(false);
  };

  useEffect(() => { initGame(); }, []);

  return html`
    <div className="min-h-screen vault-room flex flex-col items-center justify-center p-4 overflow-hidden relative select-none">

      <div className="relative z-10 flex flex-col items-center">

        <div className="relative">
          {/* ตัวชี้เข็ม (Pointer) */}
          <div className="absolute -top-16 left-1/2 -translate-x-1/2 z-40 flex flex-col items-center">
             <div className="w-2.5 h-20 bg-red-700 shadow-[0_0_20px_rgba(185,28,28,0.8)] indicator-pulse"></div>
             <div className="w-7 h-7 bg-red-700 rotate-45 -mt-3.5 shadow-[0_0_20px_rgba(185,28,28,0.8)]"></div>
          </div>

          {/* วงแหวนตู้เซฟขนาดใหญ่ (700px บนจอใหญ่) */}
          <div 
            ref=${containerRef}
            className=${`relative w-[360px] h-[360px] md:w-[700px] md:h-[700px] rounded-full border-[25px] border-[#0f172a] bg-slate-900 shadow-[0_0_150px_rgba(0,0,0,1)] transition-all duration-1000 ${solved ? 'scale-105 brightness-110' : ''}`}
          >
            <div className="reflection"></div>
            <svg viewBox="0 0 500 500" className="w-full h-full drop-shadow-2xl">
              <${DialRing}
                layer=${RingLayer.OUTER}
                radius=${210}
                thickness=${85}
                rotation=${rotations[RingLayer.OUTER]}
                isActive=${!solved}
                isDragging=${activeLayer === RingLayer.OUTER}
                onDragStart=${handleDragStart}
              />
              <${DialRing}
                layer=${RingLayer.MIDDLE}
                radius=${125}
                thickness=${85}
                rotation=${rotations[RingLayer.MIDDLE]}
                isActive=${!solved}
                isDragging=${activeLayer === RingLayer.MIDDLE}
                onDragStart=${handleDragStart}
              />
              <${DialRing}
                layer=${RingLayer.INNER}
                radius=${40}
                thickness=${85}
                rotation=${rotations[RingLayer.INNER]}
                isActive=${!solved}
                isDragging=${activeLayer === RingLayer.INNER}
                onDragStart=${handleDragStart}
              />

              {/* แกนกลาง (Lock Core) */}
              <circle cx="250" cy="250" r="30" fill="#0f172a" stroke="#1e293b" strokeWidth="2" />
              <g transform="translate(232, 232)">
                 ${solved ? 
                    html`<${Unlock} size=${36} className="text-emerald-400" />` : 
                    html`<${Lock} size=${36} className="text-slate-600" />`
                 }
              </g>

              {/* หมุดเหล็กตกแต่ง (Bolts) */}
              ${[0, 30, 60, 90, 120, 150, 180, 210, 240, 270, 300, 330].map(angle => {
                const r = 240;
                const x = 250 + r * Math.cos(angle * Math.PI / 180);
                const y = 250 + r * Math.sin(angle * Math.PI / 180);
                return html`<circle key=${angle} cx=${x} cy=${y} r="6" fill="#1e293b" stroke="#000" strokeWidth="1" />`
              })}
            </svg>
          </div>
        </div>

        {/* ส่วนแสดงผลลับ */}
        <div className="mt-20 w-full max-w-2xl z-20 px-4">
          ${solved && html`
            <div className="bg-slate-900/95 border-l-8 border-emerald-500 p-10 rounded shadow-2xl animate-in fade-in slide-in-from-bottom duration-700 backdrop-blur-md">
              <div className="flex items-center gap-4 mb-6">
                <${Shield} className="text-emerald-400" size=${32} />
                <span className="text-sm font-bold text-emerald-500 tracking-[0.6em] uppercase">Vault Access Granted</span>
              </div>
              <div className="text-xl md:text-3xl font-bold text-white mb-10 font-mono leading-tight tracking-tight">
                "${secret.secret}"
              </div>
              <button
                onClick=${initGame}
                className="w-full bg-slate-800 hover:bg-emerald-900/40 text-slate-300 hover:text-emerald-300 border border-slate-600 py-6 rounded-sm transition-all flex items-center justify-center gap-4 uppercase text-[13px] tracking-[0.8em] font-black"
              >
                <${RefreshCw} size=${20} className=${loading ? "animate-spin" : ""} />
                Re-Lock Vault
              </button>
            </div>
          `}
        </div>
      </div>
    </div>
  `;
};

export default App;

import React, { useMemo } from 'react';
import htm from 'htm';
import { RingLayer } from '../types.js';

const html = htm.bind(React.createElement);

const DialRing = ({
  layer,
  radius,
  thickness,
  rotation,
  isActive,
  isDragging,
  onDragStart
}) => {
  const innerR = radius - thickness / 2;
  const outerR = radius + thickness / 2;

  // สไตล์โลหะขัดเงา เน้นความชัดเจน (High Contrast)
  const vaultStyles = {
    [RingLayer.OUTER]: { base: "#1e293b", stroke: "#0f172a", text: "#f8fafc", fontSize: 26 }, 
    [RingLayer.MIDDLE]: { base: "#334155", stroke: "#1e293b", text: "#f1f5f9", fontSize: 22 }, 
    [RingLayer.INNER]: { base: "#475569", stroke: "#334155", text: "#ffffff", fontSize: 18 }   
  };

  const style = vaultStyles[layer] || vaultStyles[RingLayer.OUTER];

  const ringContent = useMemo(() => {
    switch (layer) {
      case RingLayer.OUTER:
        return ["30%", "50%", "72%", "85%"];
      case RingLayer.MIDDLE:
        return ["Low CCTV", "Moderate", "High Suppression", "Full Lockdown"];
      case RingLayer.INNER:
        return ["Capital ↓", "Data ↑", "Risk Hidden", "Control ↑"];
      default:
        return ["", "", "", ""];
    }
  }, [layer]);

  const segments = useMemo(() => {
    return ringContent.map((text, i) => {
      // มุมที่จะวางข้อความ (0, 90, 180, 270 องศา)
      // ปรับลบ 90 เพื่อให้ตำแหน่งแรก (index 0) อยู่ที่ 12 นาฬิกา
      const angle = (i * 90) - 90;
      return { text, angle };
    });
  }, [ringContent]);

  return html`
    <g 
      className="cursor-grab active:cursor-grabbing"
      onMouseDown=${(e) => onDragStart(layer, e)}
      onTouchStart=${(e) => onDragStart(layer, e)}
    >
      <defs>
        <filter id="text-shadow">
          <feDropShadow dx="0" dy="1" stdDeviation="2" floodOpacity="0.8"/>
        </filter>
        <filter id="metal-surface">
          <feSpecularLighting surfaceScale="5" specularConstant="0.8" specularExponent="20" lightingColor="#ffffff">
            <fePointLight x="-5000" y="-10000" z="20000"/>
          </feSpecularLighting>
          <feComposite in="SourceGraphic" operator="arithmetic" k1="0" k2="1" k3="1" k4="0"/>
        </filter>
      </defs>

      {/* พื้นหลังวงแหวนโลหะ */}
      <circle 
        cx="250" cy="250" r=${radius} 
        fill=${style.base} 
        stroke=${style.stroke} 
        strokeWidth=${thickness} 
        filter="url(#metal-surface)"
      />

      <g style=${{ 
        transform: `rotate(${rotation}deg)`, 
        transformOrigin: '250px 250px',
        transition: isDragging ? 'none' : 'transform 0.6s cubic-bezier(0.15, 0.85, 0.35, 1)'
      }}>
        {/* ขีดสเกลแบ่งโซน */}
        ${Array.from({length: 40}).map((_, i) => {
          const angle = i * 9;
          const rad = (angle - 90) * (Math.PI / 180);
          const isMain = i % 10 === 0;
          return html`
            <line 
              key=${i}
              x1=${250 + (radius + thickness/2 - 2) * Math.cos(rad)} 
              y1=${250 + (radius + thickness/2 - 2) * Math.sin(rad)} 
              x2=${250 + (radius + thickness/2 - (isMain ? 18 : 8)) * Math.cos(rad)} 
              y2=${250 + (radius + thickness/2 - (isMain ? 18 : 8)) * Math.sin(rad)} 
              stroke=${isMain ? "rgba(255,255,255,0.4)" : "rgba(0,0,0,0.3)"} 
              strokeWidth=${isMain ? "2" : "1"} 
            />
          `
        })}

        {/* ตัวอักษรขนาดใหญ่ วางตามตำแหน่งองศา */}
        ${segments.map((seg, i) => {
          const rad = (seg.angle) * (Math.PI / 180);
          const x = 250 + radius * Math.cos(rad);
          const y = 250 + radius * Math.sin(rad);

          // คำนวณการหมุนตัวอักษรให้ตั้งตรงเมื่ออยู่ที่ตำแหน่ง Pointer
          // หรือหมุนตามวงล้อ (ในที่นี้เราหมุนตามวงล้อเพื่อให้ดูเป็น Mechanical Dial)
          const textRotation = seg.angle + 90;

          return html`
            <text
              key=${i}
              x=${x}
              y=${y}
              fill=${style.text}
              fontSize=${style.fontSize}
              fontWeight="900"
              textAnchor="middle"
              dominantBaseline="middle"
              filter="url(#text-shadow)"
              className="select-none"
              style=${{ 
                fontFamily: "'Chakra Petch', sans-serif",
                transform: `rotate(${textRotation}deg)`,
                transformOrigin: `${x}px ${y}px`
              }}
            >
              ${seg.text}
            </text>
          `
        })}
      </g>
    </g>
  `;
};

