import React, { useMemo } from 'react';
import htm from 'htm';
import { RingLayer } from '../types.js';

const html = htm.bind(React.createElement);

const DialRing = ({
  layer,
  radius,
  thickness,
  rotation,
  isDragging,
  onDragStart
}) => {
  // สไตล์สีแบบ Metal Gradient
  const styles = {
    [RingLayer.OUTER]: { start: "#1e293b", end: "#0f172a", text: "#f1f5f9", fontSize: "24px" },
    [RingLayer.MIDDLE]: { start: "#334155", end: "#1e293b", text: "#e2e8f0", fontSize: "20px" },
    [RingLayer.INNER]: { start: "#475569", end: "#334155", text: "#ffffff", fontSize: "16px" }
  };

  const config = styles[layer] || styles[RingLayer.OUTER];

  const ringContent = useMemo(() => {
    switch (layer) {
      case RingLayer.OUTER: return ["30%", "50%", "72%", "85%"];
      case RingLayer.MIDDLE: return ["Low CCTV", "Moderate", "High Suppression", "Full Lockdown"];
      case RingLayer.INNER: return ["Capital ↓", "Data ↑", "Risk Hidden", "Control ↑"];
      default: return ["", "", "", ""];
    }
  }, [layer]);

  const segments = useMemo(() => {
    return ringContent.map((text, i) => ({
      text,
      angle: (i * 90) - 90 // เริ่มที่ 12 นาฬิกา
    }));
  }, [ringContent]);

  const gradId = `grad-${layer}`;

  return html`
    <g 
      className="cursor-pointer"
      onMouseDown=${(e) => onDragStart(layer, e)}
      onTouchStart=${(e) => onDragStart(layer, e)}
    >
      <defs>
        <linearGradient id=${gradId} x1="0%" y1="0%" x2="100%" y2="100%">
          <stop offset="0%" style=${{stopColor: config.start, stopOpacity: 1}} />
          <stop offset="100%" style=${{stopColor: config.end, stopOpacity: 1}} />
        </linearGradient>
      </defs>

      <circle 
        cx="250" cy="250" r=${radius} 
        fill=${`url(#${gradId})`} 
        stroke="rgba(0,0,0,0.4)" 
        strokeWidth=${thickness} 
      />

      <circle cx="250" cy="250" r=${radius + thickness/2} fill="none" stroke="rgba(255,255,255,0.05)" strokeWidth="2" />
      <circle cx="250" cy="250" r=${radius - thickness/2} fill="none" stroke="rgba(0,0,0,0.6)" strokeWidth="2" />

      <g style=${{ 
        transform: `rotate(${rotation}deg)`, 
        transformOrigin: '250px 250px',
        transition: isDragging ? 'none' : 'transform 0.5s cubic-bezier(0.2, 0.8, 0.4, 1)'
      }}>
        ${Array.from({length: 40}).map((_, i) => {
          const deg = i * 9;
          const rad = (deg - 90) * (Math.PI / 180);
          const isMain = i % 10 === 0;
          const len = isMain ? 15 : 7;
          const outerPoint = radius + thickness/2 - 2;
          return html`
            <line 
              key=${i}
              x1=${250 + outerPoint * Math.cos(rad)} 
              y1=${250 + outerPoint * Math.sin(rad)} 
              x2=${250 + (outerPoint - len) * Math.cos(rad)} 
              y2=${250 + (outerPoint - len) * Math.sin(rad)} 
              stroke=${isMain ? "rgba(255,255,255,0.3)" : "rgba(0,0,0,0.4)"} 
              strokeWidth=${isMain ? "2" : "1"} 
            />
          `
        })}

        ${segments.map((seg, i) => {
          const rad = (seg.angle) * (Math.PI / 180);
          const x = 250 + radius * Math.cos(rad);
          const y = 250 + radius * Math.sin(rad);
          const rotationAngle = seg.angle + 90;

          return html`
            <text
              key=${i}
              x=${x}
              y=${y}
              fill=${config.text}
              fontSize=${config.fontSize}
              fontWeight="700"
              textAnchor="middle"
              dominantBaseline="middle"
              className="select-none pointer-events-none"
              style=${{ 
                fontFamily: "'Chakra Petch', sans-serif",
                transform: `rotate(${rotationAngle}deg)`,
                transformOrigin: `${x}px ${y}px`,
                textShadow: '0 2px 4px rgba(0,0,0,0.5)'
              }}
            >
              ${seg.text}
            </text>
          `
        })}
      </g>
    </g>
  `;
};

