import { GoogleGenAI, Type } from "@google/genai";

const STATIC_CHALLENGES = [
  {
    secret: "The backup core is active. Access key: 9-2-4-1.",
    hint: "Align the security protocols to match the internal power levels."
  },
  {
    secret: "Confidential Data: Project X is hidden in the false floor.",
    hint: "Synchronize the suppression level with the main vault gauge."
  },
  {
    secret: "Unauthorized access detected. Clearance level: GAMMA.",
    hint: "Reset the CCTV monitoring before the lockdown is complete."
  }
];

export async function generateSecretChallenge(customTheme = "") {
  if (!process.env.API_KEY) return STATIC_CHALLENGES[Math.floor(Math.random() * STATIC_CHALLENGES.length)];

  const ai = new GoogleGenAI({ apiKey: process.env.API_KEY });
  const themePrompt = customTheme 
    ? `The theme for this discovery is: "${customTheme}". Make it very exciting and related to this theme.`
    : "Generate a cryptic discovery found inside a high-tech corporate vault.";

  try {
    const response = await ai.models.generateContent({
      model: "gemini-3-flash-preview",
      contents: `Generate a cryptic discovery (max 15 words) and a hint. ${themePrompt} Return in JSON. Language: Thai or English (as appropriate to theme).`,
      config: {
        responseMimeType: "application/json",
        responseSchema: {
          type: Type.OBJECT,
          properties: {
            secret: { type: Type.STRING },
            hint: { type: Type.STRING },
          },
          required: ["secret", "hint"],
        },
      },
    });

    if (response.text) {
      return JSON.parse(response.text.trim());
    }
  } catch (error) {
    console.warn("Gemini API Error, using fallback.");
  }

  return STATIC_CHALLENGES[Math.floor(Math.random() * STATIC_CHALLENGES.length)];
}

import React, { useMemo } from 'react';
import htm from 'htm';
import { RingLayer } from '../types.js';

const html = htm.bind(React.createElement);

const DialRing = ({
  layer,
  radius,
  thickness,
  rotation,
  isDragging,
  isSolved,
  onDragStart
}) => {
  const styles = {
    [RingLayer.OUTER]: { start: "#1e293b", end: "#0f172a", text: "#f1f5f9", fontSize: "24px" },
    [RingLayer.MIDDLE]: { start: "#334155", end: "#1e293b", text: "#e2e8f0", fontSize: "20px" },
    [RingLayer.INNER]: { start: "#475569", end: "#334155", text: "#ffffff", fontSize: "16px" }
  };

  const config = styles[layer] || styles[RingLayer.OUTER];

  const ringContent = useMemo(() => {
    switch (layer) {
      case RingLayer.OUTER: return ["30%", "50%", "72%", "85%"];
      case RingLayer.MIDDLE: return ["Low CCTV", "Moderate", "High Suppression", "Full Lockdown"];
      case RingLayer.INNER: return ["Capital ↓", "Data ↑", "Risk Hidden", "Control ↑"];
      default: return ["", "", "", ""];
    }
  }, [layer]);

  const segments = useMemo(() => {
    return ringContent.map((text, i) => ({
      text,
      angle: (i * 90) - 90
    }));
  }, [ringContent]);

  const gradId = `grad-${layer}`;
  const solveGradId = `solve-grad-${layer}`;

  // ทิศทางการหมุนในโหมด Overdrive
  const overdriveClass = useMemo(() => {
    if (!isSolved) return "";
    if (layer === RingLayer.OUTER) return "overdrive-right";
    if (layer === RingLayer.MIDDLE) return "overdrive-left";
    if (layer === RingLayer.INNER) return "overdrive-right";
    return "";
  }, [isSolved, layer]);

  return html`
    <g 
      className=${`cursor-pointer transition-opacity duration-500 ${isSolved ? 'opacity-100' : 'opacity-90 hover:opacity-100'}`}
      onMouseDown=${(e) => onDragStart(layer, e)}
      onTouchStart=${(e) => onDragStart(layer, e)}
    >
      <defs>
        <linearGradient id=${gradId} x1="0%" y1="0%" x2="100%" y2="100%">
          <stop offset="0%" style=${{stopColor: config.start, stopOpacity: 1}} />
          <stop offset="100%" style=${{stopColor: config.end, stopOpacity: 1}} />
        </linearGradient>
        <linearGradient id=${solveGradId} x1="0%" y1="0%" x2="100%" y2="100%">
          <stop offset="0%" style=${{stopColor: "#059669", stopOpacity: 1}} />
          <stop offset="100%" style=${{stopColor: "#064e3b", stopOpacity: 1}} />
        </linearGradient>
      </defs>

      <circle 
        cx="250" cy="250" r=${radius} 
        fill=${isSolved ? `url(#${solveGradId})` : `url(#${gradId})`} 
        stroke=${isSolved ? "rgba(16, 185, 129, 0.8)" : "rgba(0,0,0,0.4)"} 
        strokeWidth=${thickness}
        className="transition-all duration-1000"
      />

      <g 
        className=${overdriveClass}
        style=${{ 
          transform: isSolved ? undefined : `rotate(${rotation}deg)`, 
          transformOrigin: '250px 250px',
          transition: isDragging ? 'none' : 'transform 0.6s cubic-bezier(0.34, 1.56, 0.64, 1)'
        }}
      >
        ${Array.from({length: 40}).map((_, i) => {
          const deg = i * 9;
          const rad = (deg - 90) * (Math.PI / 180);
          const isMain = i % 10 === 0;
          const len = isMain ? 15 : 7;
          const outerPoint = radius + thickness/2 - 2;
          return html`
            <line 
              key=${i}
              x1=${250 + outerPoint * Math.cos(rad)} 
              y1=${250 + outerPoint * Math.sin(rad)} 
              x2=${250 + (outerPoint - len) * Math.cos(rad)} 
              y2=${250 + (outerPoint - len) * Math.sin(rad)} 
              stroke=${isSolved ? "rgba(16, 185, 129, 0.6)" : (isMain ? "rgba(255,255,255,0.3)" : "rgba(0,0,0,0.4)")} 
              strokeWidth=${isMain ? "2" : "1"} 
            />
          `
        })}

        ${segments.map((seg, i) => {
          const rad = (seg.angle) * (Math.PI / 180);
          const x = 250 + radius * Math.cos(rad);
          const y = 250 + radius * Math.sin(rad);
          const rotationAngle = seg.angle + 90;

          return html`
            <text
              key=${i}
              x=${x}
              y=${y}
              fill=${isSolved ? "#34d399" : config.text}
              fontSize=${config.fontSize}
              fontWeight="700"
              textAnchor="middle"
              dominantBaseline="middle"
              className="select-none pointer-events-none"
              style=${{ 
                fontFamily: "'Chakra Petch', sans-serif",
                transform: `rotate(${rotationAngle}deg)`,
                transformOrigin: `${x}px ${y}px`,
                textShadow: isSolved ? '0 0 15px rgba(16, 185, 129, 0.9)' : '0 2px 4px rgba(0,0,0,0.5)'
              }}
            >
              ${seg.text}
            </text>
          `
        })}
      </g>
    </g>
  `;
};

export default DialRing;

import React, { useState, useEffect, useCallback, useRef } from 'react';
import htm from 'htm';
import { RefreshCw, Lock, Unlock, ShieldAlert, Terminal, Cpu, Zap, Activity } from 'lucide-react';
import DialRing from './components/DialRing.js';
import { generateSecretChallenge } from './services/geminiService.js';
import { RingLayer } from './types.js';

const html = htm.bind(React.createElement);

const App = () => {
  const [loading, setLoading] = useState(false);
  const [solved, setSolved] = useState(false);
  const [triggerFlash, setTriggerFlash] = useState(false);
  const [showResult, setShowResult] = useState(false);
  const [showOverride, setShowOverride] = useState(false);
  const [overrideInput, setOverrideInput] = useState("");
  const [secret, setSecret] = useState({ secret: "Vault Secured. Ready for Input.", hint: "" });

  const SNAP_ANGLE = 90;

  const [rotations, setRotations] = useState({
    [RingLayer.OUTER]: (Math.floor(Math.random() * 4)) * SNAP_ANGLE,
    [RingLayer.MIDDLE]: (Math.floor(Math.random() * 4)) * SNAP_ANGLE,
    [RingLayer.INNER]: (Math.floor(Math.random() * 4)) * SNAP_ANGLE,
  });

  const [activeLayer, setActiveLayer] = useState(null);
  const dragStartAngle = useRef(0);
  const initialRotation = useRef(0);
  const containerRef = useRef(null);

  const getAngle = (clientX, clientY) => {
    if (!containerRef.current) return 0;
    const rect = containerRef.current.getBoundingClientRect();
    const centerX = rect.left + rect.width / 2;
    const centerY = rect.top + rect.height / 2;
    const dx = clientX - centerX;
    const dy = clientY - centerY;
    return Math.atan2(dy, dx) * (180 / Math.PI);
  };

  const handleDragStart = (layer, e) => {
    if (solved || loading) return;
    const clientX = 'touches' in e ? e.touches[0].clientX : e.clientX;
    const clientY = 'touches' in e ? e.touches[0].clientY : e.clientY;
    setActiveLayer(layer);
    dragStartAngle.current = getAngle(clientX, clientY);
    initialRotation.current = rotations[layer];
  };

  const handleDragMove = useCallback((e) => {
    const layer = activeLayer;
    if (layer === null || solved) return;
    const clientX = 'touches' in e ? e.touches[0].clientX : e.clientX;
    const clientY = 'touches' in e ? e.touches[0].clientY : e.clientY;
    const currentAngle = getAngle(clientX, clientY);
    const angleDiff = currentAngle - dragStartAngle.current;

    setRotations(prev => ({ ...prev, [layer]: initialRotation.current + angleDiff }));
  }, [activeLayer, solved]);

  const handleDragEnd = useCallback(() => {
    const layer = activeLayer;
    if (layer === null) return;
    setRotations(prev => {
      const currentRot = prev[layer];
      const snapped = Math.round(currentRot / SNAP_ANGLE) * SNAP_ANGLE;
      const nextRotations = { ...prev, [layer]: snapped };

      const isAligned = Object.values(nextRotations).every((rot) => {
        const normalized = ((rot % 360) + 360) % 360;
        return normalized < 5 || normalized > 355;
      });

      if (isAligned && !solved) {
        setSolved(true);
        setTriggerFlash(true);
        // ลำดับแอนิเมชัน: Flash ทันที -> หมุน Overdrive -> หลังจาก 3 วิ ค่อยโชว์ข้อความ
        setTimeout(() => setShowResult(true), 3500);
      }
      return nextRotations;
    });
    setActiveLayer(null);
  }, [activeLayer, solved]);

  useEffect(() => {
    if (activeLayer !== null) {
      window.addEventListener('mousemove', handleDragMove);
      window.addEventListener('mouseup', handleDragEnd);
      window.addEventListener('touchmove', handleDragMove, { passive: false });
      window.addEventListener('touchend', handleDragEnd);
    }
    return () => {
      window.removeEventListener('mousemove', handleDragMove);
      window.removeEventListener('mouseup', handleDragEnd);
      window.removeEventListener('touchmove', handleDragMove);
      window.removeEventListener('touchend', handleDragEnd);
    };
  }, [activeLayer, handleDragMove, handleDragEnd]);

  const initGame = async (customTheme = "") => {
    setLoading(true);
    setSolved(false);
    setShowResult(false);
    setTriggerFlash(false);
    setShowOverride(false);
    try {
      const data = await generateSecretChallenge(customTheme);
      setSecret(data);
    } catch (e) { console.warn(e); }
    setRotations({
      [RingLayer.OUTER]: (Math.floor(Math.random() * 3) + 1) * SNAP_ANGLE,
      [RingLayer.MIDDLE]: (Math.floor(Math.random() * 3) + 1) * SNAP_ANGLE,
      [RingLayer.INNER]: (Math.floor(Math.random() * 3) + 1) * SNAP_ANGLE,
    });
    setLoading(false);
  };

  useEffect(() => { initGame(); }, []);

  return html`
    <div className=${`min-h-screen vault-room grid-bg flex flex-col items-center justify-center p-4 transition-colors duration-2000 ${solved ? 'bg-emerald-950/30' : ''}`}>

      {/* Mega Flash Overlay */}
      <div className=${`flash-overlay ${triggerFlash ? 'trigger-flash' : ''}`}></div>

      <div className=${`relative flex flex-col items-center ${solved ? 'solved' : ''}`}>

        {/* Scanline Effect */}
        <div className="scan-overlay"></div>

        {/* Header HUD */}
        <div className="absolute -top-24 left-0 w-full flex justify-between items-center px-4 opacity-50">
           <div className="flex items-center gap-2 text-[10px] tracking-widest text-emerald-500 uppercase font-bold">
              <${Activity} size=${14} className=${solved ? "animate-pulse" : ""} /> 
              ${solved ? "OVERDRIVE ACTIVE" : "SYSTEM STABLE"}
           </div>
           <div className="text-[10px] tracking-widest text-slate-500 uppercase font-bold">
              ENCRYPTION: ${solved ? "BREACHED" : "AES-256-VAULT"}
           </div>
        </div>

        <div 
          ref=${containerRef}
          className=${`relative w-[340px] h-[340px] sm:w-[500px] sm:h-[500px] lg:w-[720px] lg:h-[720px] rounded-full border-[15px] transition-all duration-1000 ${solved ? 'border-emerald-400 shadow-[0_0_200px_rgba(16,185,129,0.5)] scale-105' : 'border-[#0c111d] bg-slate-900 shadow-[0_0_100px_rgba(0,0,0,0.8)]'}`}
        >
          <svg viewBox="0 0 500 500" className="w-full h-full">
            <${DialRing}
              layer=${RingLayer.OUTER}
              radius=${200}
              thickness=${80}
              rotation=${rotations[RingLayer.OUTER]}
              isDragging=${activeLayer === RingLayer.OUTER}
              isSolved=${solved}
              onDragStart=${handleDragStart}
            />
            <${DialRing}
              layer=${RingLayer.MIDDLE}
              radius=${125}
              thickness=${65}
              rotation=${rotations[RingLayer.MIDDLE]}
              isDragging=${activeLayer === RingLayer.MIDDLE}
              isSolved=${solved}
              onDragStart=${handleDragStart}
            />
            <${DialRing}
              layer=${RingLayer.INNER}
              radius=${65}
              thickness=${50}
              rotation=${rotations[RingLayer.INNER]}
              isDragging=${activeLayer === RingLayer.INNER}
              isSolved=${solved}
              onDragStart=${handleDragStart}
            />

            {/* Core Area */}
            <circle 
              cx="250" cy="250" 
              r=${solved ? 50 : 35} 
              fill="#064e3b" 
              stroke=${solved ? "#34d399" : "#1e293b"} 
              strokeWidth="4" 
              className="transition-all duration-1000"
            />
            <g transform="translate(250, 250)">
               <g className=${solved ? "animate-[core-pulse_1.5s_infinite]" : ""} style=${{ transform: 'translate(-50%, -50%)' }}>
                  ${solved ? 
                     html`<${Unlock} size=${solved ? 48 : 36} className="text-emerald-300 drop-shadow-[0_0_20px_rgba(52,211,153,1)]" />` : 
                     html`<${Lock} size=${36} className="text-slate-600" />`
                  }
               </g>
            </g>

            {/* หมุดรอบวง */}
            ${[0, 45, 90, 135, 180, 225, 270, 315].map(angle => {
              const r = 242;
              const x = 250 + r * Math.cos(angle * Math.PI / 180);
              const y = 250 + r * Math.sin(angle * Math.PI / 180);
              return html`<circle key=${angle} cx=${x} cy=${y} r="6" fill=${solved ? "#34d399" : "#1e293b"} stroke="#000" className="transition-colors duration-1000" />`
            })}
          </svg>
        </div>

        {/* Control Center */}
        <div className="mt-12 w-full max-w-xl z-20 space-y-4">
          ${showResult ? html`
            <div className="bg-slate-900/95 border-t-4 border-emerald-400 p-8 rounded shadow-2xl backdrop-blur-xl text-center animate-in zoom-in fade-in duration-700">
              <div className="flex items-center justify-center gap-3 mb-6">
                <${ShieldAlert} className="text-emerald-400 animate-pulse" size=${28} />
                <span className="text-sm font-bold text-emerald-400 tracking-[0.6em] uppercase">ACCESS GRANTED</span>
              </div>
              <div className="text-2xl md:text-4xl font-bold text-white mb-10 font-mono tracking-tight leading-relaxed glitch-reveal">
                "${secret.secret}"
              </div>
              <div className="flex gap-4">
                <button
                  onClick=${() => initGame()}
                  className="flex-1 bg-emerald-700 hover:bg-emerald-600 text-white py-5 rounded-lg transition-all flex items-center justify-center gap-3 uppercase text-[12px] tracking-[0.5em] font-bold shadow-xl shadow-emerald-900/40"
                >
                  <${RefreshCw} size=${18} className=${loading ? "animate-spin" : ""} />
                  SECURE VAULT
                </button>
                <button
                  onClick=${() => setShowOverride(true)}
                  className="bg-slate-800 hover:bg-slate-700 text-emerald-400 px-6 rounded-lg transition-all border border-emerald-900/30"
                >
                  <${Terminal} size=${24} />
                </button>
              </div>
            </div>
          ` : !solved && html`
            <div className="flex justify-center">
               <button 
                  onClick=${() => setShowOverride(!showOverride)}
                  className="text-slate-600 hover:text-emerald-500 transition-colors flex items-center gap-2 text-[10px] uppercase tracking-[0.3em] font-bold"
               >
                  <${Terminal} size=${14} /> Override Protocol
               </button>
            </div>
          `}

          {/* Override Modal */}
          ${showOverride && !showResult && html`
            <div className="bg-slate-900 border border-slate-700 p-8 rounded-xl animate-in slide-in-from-top duration-500 shadow-2xl backdrop-blur-md">
              <div className="flex items-center gap-3 mb-5 text-emerald-400">
                <${Zap} size=${20} />
                <span className="text-xs font-bold tracking-widest uppercase">Encryption Injection Point</span>
              </div>
              <p className="text-[12px] text-slate-400 mb-6 font-mono leading-relaxed">
                Define the secret nature to be encrypted within the vault core...
              </p>
              <div className="flex gap-3">
                <input 
                  type="text" 
                  value=${overrideInput}
                  onChange=${(e) => setOverrideInput(e.target.value)}
                  placeholder="e.g. Area 51, Hidden Treasure, Bitcoin Key..."
                  className="flex-1 bg-black/60 border border-slate-700 rounded-lg p-4 text-sm font-mono text-emerald-400 focus:outline-none focus:border-emerald-500 transition-all shadow-inner"
                />
                <button 
                  onClick=${() => initGame(overrideInput)}
                  disabled=${loading}
                  className="bg-emerald-600 hover:bg-emerald-500 disabled:opacity-50 text-white px-8 rounded-lg text-xs font-bold uppercase tracking-widest transition-all shadow-lg"
                >
                  Inject
                </button>
              </div>
            </div>
          `}
        </div>
      </div>

      {/* Background Decorative */}
      <footer className="fixed bottom-8 text-slate-900 text-[10px] tracking-[2.5em] uppercase font-black pointer-events-none select-none">
        X-CORE OVERDRIVE ENGINE ACTIVE
      </footer>
    </div>
  `;
};

export default App;

