  const iconSize = solved ? 64 : 44;
  const iconPos = 250 - (iconSize / 2);

  return html`
    <div className=${`min-h-screen vault-room grid-bg flex flex-col items-center justify-center p-4 transition-colors duration-2000 ${solved ? 'bg-emerald-950/40' : ''}`}>

