          className=${`relative w-[340px] h-[340px] sm:w-[500px] sm:h-[500px] lg:w-[720px] lg:h-[720px] rounded-full border-[15px] transition-all duration-1000 ${solved ? 'border-emerald-400 shadow-[0_0_200px_rgba(16,185,129,0.6)] scale-105' : 'border-[#0c111d] bg-slate-900 shadow-[0_0_100px_rgba(0,0,0,0.8)]'}`}
        >
          <svg viewBox="0 0 500 500" className="w-full h-full">
            <${DialRing}
              layer=${RingLayer.OUTER}
              radius=${200}
              thickness=${80}
              rotation=${rotations[RingLayer.OUTER]}
              isDragging=${activeLayer === RingLayer.OUTER}
              isSolved=${solved}
              onDragStart=${handleDragStart}
            />
            <${DialRing}
              layer=${RingLayer.MIDDLE}
              radius=${125}
              thickness=${65}
              rotation=${rotations[RingLayer.MIDDLE]}
              isDragging=${activeLayer === RingLayer.MIDDLE}
              isSolved=${solved}
              onDragStart=${handleDragStart}
            />
            <${DialRing}
              layer=${RingLayer.INNER}
              radius=${65}
              thickness=${50}
              rotation=${rotations[RingLayer.INNER]}
              isDragging=${activeLayer === RingLayer.INNER}
              isSolved=${solved}
              onDragStart=${handleDragStart}
            />

            <circle 
              cx="250" cy="250" 
              r=${solved ? 60 : 40} 
              fill="#064e3b" 
              stroke=${solved ? "#34d399" : "#1e293b"} 
              strokeWidth="4" 
              className="transition-all duration-1000"
            />

            <g className=${solved ? "core-solved-anim" : ""} transform-origin="250 250">
               <g transform=${`translate(${iconPos}, ${iconPos})`}>
                  ${solved ? 
                     html`<${Unlock} size=${iconSize} className="text-emerald-300 drop-shadow-[0_0_25px_rgba(52,211,153,1)]" />` : 
                     html`<${Lock} size=${iconSize} className="text-slate-600" />`
                  }
               </g>
            </g>

            ${[0, 45, 90, 135, 180, 225, 270, 315].map(angle => {
              const r = 242;
              const x = 250 + r * Math.cos(angle * Math.PI / 180);
              const y = 250 + r * Math.sin(angle * Math.PI / 180);
              return html`<circle key=${angle} cx=${x} cy=${y} r="6" fill=${solved ? "#34d399" : "#1e293b"} stroke="#000" className="transition-colors duration-1000" />`
            })}
          </svg>
        </div>

        <div className="mt-12 w-full max-w-xl z-20 space-y-4">
          ${showResult ? html`
            <div className="bg-slate-950/95 border-t-4 border-emerald-400 p-10 rounded-2xl shadow-[0_0_100px_rgba(16,185,129,0.2)] backdrop-blur-2xl text-center animate-in zoom-in fade-in duration-1000">
              <div className="flex items-center justify-center gap-3 mb-6">
                <${ShieldAlert} className="text-emerald-400 animate-pulse" size=${32} />
                <span className="text-sm font-bold text-emerald-400 tracking-[0.6em] uppercase">SYSTEM BREACHED</span>
              </div>
              <div className="text-2xl md:text-5xl font-bold text-white mb-12 font-mono tracking-tighter leading-tight glitch-reveal">
                "${secret.secret}"
