  const [showOverride, setShowOverride] = useState(false);
  const [overrideInput, setOverrideInput] = useState("");
  const [secret, setSecret] = useState({ secret: "Vault Secured. Ready for Input.", hint: "" });

  const SNAP_ANGLE = 90;

  const [rotations, setRotations] = useState({
    [RingLayer.OUTER]: (Math.floor(Math.random() * 4)) * SNAP_ANGLE,
    [RingLayer.MIDDLE]: (Math.floor(Math.random() * 4)) * SNAP_ANGLE,
    [RingLayer.INNER]: (Math.floor(Math.random() * 4)) * SNAP_ANGLE,
  });

  const [activeLayer, setActiveLayer] = useState(null);
  const dragStartAngle = useRef(0);
  const initialRotation = useRef(0);
  const containerRef = useRef(null);

  const getAngle = (clientX, clientY) => {
    if (!containerRef.current) return 0;
    const rect = containerRef.current.getBoundingClientRect();
    const centerX = rect.left + rect.width / 2;
    const centerY = rect.top + rect.height / 2;
    const dx = clientX - centerX;
    const dy = clientY - centerY;
    return Math.atan2(dy, dx) * (180 / Math.PI);
  };

  const handleDragStart = (layer, e) => {
    if (solved || loading) return;
    const clientX = 'touches' in e ? e.touches[0].clientX : e.clientX;
    const clientY = 'touches' in e ? e.touches[0].clientY : e.clientY;
    setActiveLayer(layer);
    dragStartAngle.current = getAngle(clientX, clientY);
    initialRotation.current = rotations[layer];
  };

  const handleDragMove = useCallback((e) => {
    const layer = activeLayer;
    if (layer === null || solved) return;
    const clientX = 'touches' in e ? e.touches[0].clientX : e.clientX;
    const clientY = 'touches' in e ? e.touches[0].clientY : e.clientY;
    const currentAngle = getAngle(clientX, clientY);
    const angleDiff = currentAngle - dragStartAngle.current;

    setRotations(prev => ({ ...prev, [layer]: initialRotation.current + angleDiff }));
  }, [activeLayer, solved]);

  const handleDragEnd = useCallback(() => {
    const layer = activeLayer;
    if (layer === null) return;
    setRotations(prev => {
      const currentRot = prev[layer];
      const snapped = Math.round(currentRot / SNAP_ANGLE) * SNAP_ANGLE;
      const nextRotations = { ...prev, [layer]: snapped };

      const isAligned = Object.values(nextRotations).every((rot) => {
        const normalized = ((rot % 360) + 360) % 360;
        return normalized < 5 || normalized > 355;
      });

      if (isAligned && !solved) {
        setSolved(true);
        // เมื่อปลดล็อก: กุญแจขยายใหญ่ทันที
        // เว้นจังหวะให้กุญแจจางหายไปจนเกือบหมดก่อนเด้งป๊อปอัพ
        setTimeout(() => setShowResult(true), 1100);
      }
      return nextRotations;
    });
    setActiveLayer(null);
  }, [activeLayer, solved]);

  useEffect(() => {
    if (activeLayer !== null) {
      window.addEventListener('mousemove', handleDragMove);
      window.addEventListener('mouseup', handleDragEnd);
      window.addEventListener('touchmove', handleDragMove, { passive: false });
      window.addEventListener('touchend', handleDragEnd);
    }
    return () => {
      window.removeEventListener('mousemove', handleDragMove);
      window.removeEventListener('mouseup', handleDragEnd);
      window.removeEventListener('touchmove', handleDragMove);
      window.removeEventListener('touchend', handleDragEnd);
    };
  }, [activeLayer, handleDragMove, handleDragEnd]);

  const initGame = async (customTheme = "") => {
    setLoading(true);
    setSolved(false);
    setShowResult(false);
    setShowOverride(false);
    try {
      const data = await generateSecretChallenge(customTheme);
      setSecret(data);
    } catch (e) { console.warn(e); }
    setRotations({
      [RingLayer.OUTER]: (Math.floor(Math.random() * 3) + 1) * SNAP_ANGLE,
      [RingLayer.MIDDLE]: (Math.floor(Math.random() * 3) + 1) * SNAP_ANGLE,
      [RingLayer.INNER]: (Math.floor(Math.random() * 3) + 1) * SNAP_ANGLE,
    });
    setLoading(false);
  };

  useEffect(() => { initGame(); }, []);

  const iconSize = 48;
  const iconOffset = 250 - (iconSize / 2);

  return html`
    <div className=${`min-h-screen vault-room grid-bg flex flex-col items-center justify-center p-4 transition-colors duration-1000 ${solved ? 'solved-bg-pulse' : ''}`}>

      <div className=${`relative flex flex-col items-center ${solved ? 'solved' : ''}`}>

        <div className="scan-overlay"></div>

        <div className="absolute -top-20 left-0 w-full flex justify-between items-center px-4 opacity-40">
           <div className="flex items-center gap-2 text-[10px] tracking-widest text-emerald-500 uppercase font-bold">
              <${Activity} size=${14} /> 
              ${solved ? "BREACHING PROTOCOL..." : "STABLE"}
           </div>
           <div className="text-[10px] tracking-widest text-slate-500 uppercase font-bold font-mono">
              ${solved ? "OVERDRIVE" : "LOCKED"}
           </div>
        </div>

        <div 
          ref=${containerRef}
          className=${`relative w-[340px] h-[340px] sm:w-[500px] sm:h-[500px] lg:w-[650px] lg:h-[650px] rounded-full border-[12px] transition-all duration-700 ${solved ? 'border-emerald-400 shadow-[0_0_150px_rgba(16,185,129,0.5)]' : 'border-[#0c111d] bg-slate-900 shadow-[0_0_80px_rgba(0,0,0,0.8)] overflow-hidden'}`}
        >
          <svg viewBox="0 0 500 500" className="w-full h-full" style=${{ overflow: 'visible' }}>
            <${DialRing}
              layer=${RingLayer.OUTER}
              radius=${200}
              thickness=${80}
              rotation=${rotations[RingLayer.OUTER]}
              isDragging=${activeLayer === RingLayer.OUTER}
              isSolved=${solved}
              onDragStart=${handleDragStart}
            />
            <${DialRing}
              layer=${RingLayer.MIDDLE}
              radius=${125}
              thickness=${65}
              rotation=${rotations[RingLayer.MIDDLE]}
              isDragging=${activeLayer === RingLayer.MIDDLE}
              isSolved=${solved}
              onDragStart=${handleDragStart}
            />
            <${DialRing}
              layer=${RingLayer.INNER}
              radius=${65}
              thickness=${50}
              rotation=${rotations[RingLayer.INNER]}
              isDragging=${activeLayer === RingLayer.INNER}
              isSolved=${solved}
              onDragStart=${handleDragStart}
            />

            <circle 
              cx="250" cy="250" 
              r="45" 
              fill="#064e3b" 
              stroke=${solved ? "#34d399" : "#1e293b"} 
              strokeWidth="4" 
              className="transition-all duration-700"
            />

            <g className=${solved ? "core-breach-anim" : ""}>
               <g transform=${`translate(${iconOffset}, ${iconOffset})`}>
                  ${solved ? 
                     html`<${Unlock} size=${iconSize} className="text-emerald-300" />` : 
                     html`<${Lock} size=${iconSize} className="text-slate-600" />`
                  }
               </g>
            </g>

            ${[0, 45, 90, 135, 180, 225, 270, 315].map(angle => {
              const r = 242;
              const x = 250 + r * Math.cos(angle * Math.PI / 180);
              const y = 250 + r * Math.sin(angle * Math.PI / 180);
              return html`<circle key=${angle} cx=${x} cy=${y} r="5" fill=${solved ? "#34d399" : "#1e293b"} className="transition-colors duration-700" />`
            })}
          </svg>
        </div>

        <div className="mt-10 w-full max-w-xl z-20 space-y-4 text-center">
          ${!solved && html`
            <div className="flex justify-center">
               <button 
                  onClick=${() => setShowOverride(!showOverride)}
                  className="text-slate-600 hover:text-emerald-500 transition-colors flex items-center gap-2 text-[10px] uppercase tracking-[0.3em] font-bold"
               >
                  <${Terminal} size=${14} /> Override Protocol
               </button>
            </div>
          `}
        </div>
      </div>

      ${showResult && html`
        <div className="fixed inset-0 z-[100] flex items-center justify-center p-6 sm:p-12">
          <div className="absolute inset-0 bg-slate-950/70 backdrop-blur-2xl animate-in fade-in duration-500" onClick=${() => setShowResult(false)}></div>

          <div className="relative w-full max-w-2xl bg-slate-900/95 border-t-4 border-emerald-400 rounded-3xl shadow-[0_0_200px_rgba(16,185,129,0.5)] p-10 md:p-14 text-center animate-modal-pop">
            <button 
              onClick=${() => setShowResult(false)}
              className="absolute top-6 right-6 text-slate-500 hover:text-white transition-colors"
            >
              <${X} size=${24} />
            </button>

            <div className="flex items-center justify-center gap-3 mb-10">
              <${ShieldAlert} className="text-emerald-400" size=${32} />
              <span className="text-xs font-bold text-emerald-400 tracking-[0.7em] uppercase font-mono">ENCRYPTED DATA UNLOCKED</span>
            </div>

            <div className="text-2xl md:text-5xl font-bold text-white mb-14 font-mono leading-tight px-4">
              "${secret.secret}"
            </div>

            <div className="flex flex-col sm:flex-row gap-5 justify-center">
              <button
                onClick=${() => initGame()}
                className="flex-1 bg-emerald-800 hover:bg-emerald-700 text-white py-6 rounded-2xl transition-all flex items-center justify-center gap-4 uppercase text-[12px] tracking-[0.5em] font-bold shadow-[0_10px_30px_rgba(6,78,59,0.5)] hover:translate-y-[-2px] active:translate-y-[1px]"
              >
                <${RefreshCw} size=${22} className=${loading ? "animate-spin" : ""} />
                RESET SYSTEM
              </button>
              <button
                onClick=${() => { setShowResult(false); setShowOverride(true); }}
                className="bg-slate-950 hover:bg-slate-800 text-emerald-400 px-10 py-6 rounded-2xl border border-emerald-900/30 flex items-center justify-center gap-4 uppercase text-[12px] tracking-[0.3em] font-bold transition-all hover:border-emerald-500"
              >
                <${Terminal} size=${22} />
                COMMAND
              </button>
            </div>
          </div>
        </div>
      `}

      ${showOverride && !showResult && html`
        <div className="fixed inset-0 z-[100] flex items-center justify-center p-4">
          <div className="absolute inset-0 bg-black/80 backdrop-blur-md animate-in fade-in duration-300" onClick=${() => setShowOverride(false)}></div>
          <div className="relative w-full max-w-md bg-slate-900 border border-emerald-900/30 p-8 rounded-2xl animate-modal-pop shadow-2xl">
            <div className="flex items-center gap-2 mb-6 text-emerald-500">
