              const y = 250 + r * Math.sin(angle * Math.PI / 180);
              return html`<circle key=${angle} cx=${x} cy=${y} r="5" fill=${solved ? "#34d399" : "#1e293b"} className="transition-colors duration-1000" />`
            })}
