               <button 
                  onClick=${() => setShowOverride(!showOverride)}
                  className="text-slate-600 hover:text-emerald-500 transition-colors flex items-center gap-2 text-[11px] font-orbitron uppercase tracking-[0.5em] font-bold"
               >
                  <${Terminal} size={14} /> Override Protocol
               </button>
            </div>
