import React, { useMemo } from 'react';
import htm from 'htm';
import { RingLayer } from '../types.js';

const html = htm.bind(React.createElement);

const DialRing = ({
  layer,
  radius,
  thickness,
  rotation,
  isDragging,
  isSolved,
  onDragStart
}) => {
  const styles = {
    [RingLayer.OUTER]: { start: "#1e293b", end: "#0f172a", text: "#f1f5f9", fontSize: "22px" },
    [RingLayer.MIDDLE]: { start: "#334155", end: "#1e293b", text: "#e2e8f0", fontSize: "18px" },
    [RingLayer.INNER]: { start: "#475569", end: "#334155", text: "#ffffff", fontSize: "15px" }
  };

  const config = styles[layer] || styles[RingLayer.OUTER];

  const ringContent = useMemo(() => {
    switch (layer) {
      case RingLayer.OUTER: return ["30%", "50%", "72%", "85%"];
      case RingLayer.MIDDLE: return ["SEC_LOW", "SEC_MED", "SUP_HIGH", "LOCKED"];
      case RingLayer.INNER: return ["CAPITAL", "DATA", "RISK", "CONTROL"];
      default: return ["", "", "", ""];
    }
  }, [layer]);

  const segments = useMemo(() => {
    return ringContent.map((text, i) => ({
      text,
      angle: (i * 90) - 90
    }));
  }, [ringContent]);

  const gradId = `grad-${layer}`;
  const solveGradId = `solve-grad-${layer}`;

  const overdriveClass = useMemo(() => {
    if (!isSolved) return "";
    if (layer === RingLayer.OUTER) return "overdrive-right";
    if (layer === RingLayer.MIDDLE) return "overdrive-left";
    if (layer === RingLayer.INNER) return "overdrive-right";
    return "";
  }, [isSolved, layer]);

  return html`
    <g 
      className=${`cursor-pointer transition-opacity duration-500 ${isSolved ? 'opacity-100' : 'opacity-90 hover:opacity-100'}`}
      onMouseDown=${(e) => onDragStart(layer, e)}
      onTouchStart=${(e) => onDragStart(layer, e)}
    >
      <defs>
        <linearGradient id=${gradId} x1="0%" y1="0%" x2="100%" y2="100%">
          <stop offset="0%" style=${{stopColor: config.start, stopOpacity: 1}} />
          <stop offset="100%" style=${{stopColor: config.end, stopOpacity: 1}} />
        </linearGradient>
        <linearGradient id=${solveGradId} x1="0%" y1="0%" x2="100%" y2="100%">
          <stop offset="0%" style=${{stopColor: "#059669", stopOpacity: 1}} />
          <stop offset="100%" style=${{stopColor: "#064e3b", stopOpacity: 1}} />
        </linearGradient>
      </defs>

      <circle 
        cx="250" cy="250" r=${radius} 
        fill=${isSolved ? `url(#${solveGradId})` : `url(#${gradId})`} 
        stroke=${isSolved ? "rgba(16, 185, 129, 0.8)" : "rgba(0,0,0,0.4)"} 
        strokeWidth=${thickness}
        className="transition-all duration-1000"
      />

      <g 
        className=${overdriveClass}
        style=${{ 
          transform: isSolved ? undefined : `rotate(${rotation}deg)`, 
          transformOrigin: '250px 250px',
          transition: isDragging ? 'none' : 'transform 0.8s cubic-bezier(0.34, 1.56, 0.64, 1)'
        }}
      >
        ${Array.from({length: 40}).map((_, i) => {
          const deg = i * 9;
          const rad = (deg - 90) * (Math.PI / 180);
          const isMain = i % 10 === 0;
          const len = isMain ? 15 : 7;
          const outerPoint = radius + thickness/2 - 2;
          return html`
            <line 
              key=${i}
              x1=${250 + outerPoint * Math.cos(rad)} 
              y1=${250 + outerPoint * Math.sin(rad)} 
              x2=${250 + (outerPoint - len) * Math.cos(rad)} 
              y2=${250 + (outerPoint - len) * Math.sin(rad)} 
              stroke=${isSolved ? "rgba(16, 185, 129, 0.6)" : (isMain ? "rgba(255,255,255,0.3)" : "rgba(0,0,0,0.4)")} 
              strokeWidth=${isMain ? "2" : "1"} 
            />
          `
        })}

        ${segments.map((seg, i) => {
          const rad = (seg.angle) * (Math.PI / 180);
          const x = 250 + radius * Math.cos(rad);
          const y = 250 + radius * Math.sin(rad);
          const rotationAngle = seg.angle + 90;

          return html`
            <text
              key=${i}
              x=${x}
              y=${y}
              fill=${isSolved ? "#34d399" : config.text}
              fontSize=${config.fontSize}
              fontWeight="700"
              textAnchor="middle"
              dominantBaseline="middle"
              className="select-none pointer-events-none font-orbitron"
              style=${{ 
                transform: `rotate(${rotationAngle}deg)`,
                transformOrigin: `${x}px ${y}px`,
                letterSpacing: '0.05em',
                textShadow: isSolved ? '0 0 15px rgba(16, 185, 129, 0.9)' : '0 2px 4px rgba(0,0,0,0.5)'
              }}
            >
              ${seg.text}
            </text>
          `
        })}
      </g>
    </g>
  `;
};

export default DialRing;

import React, { useState, useEffect, useCallback, useRef } from 'react';
import htm from 'htm';
import { RefreshCw, Lock, Unlock, ShieldAlert, Terminal, Zap, Activity, X } from 'lucide-react';
import DialRing from './components/DialRing.js';
import { generateSecretChallenge } from './services/geminiService.js';
import { RingLayer } from './types.js';

const html = htm.bind(React.createElement);

const App = () => {
  const [loading, setLoading] = useState(false);
  const [solved, setSolved] = useState(false);
  const [showResult, setShowResult] = useState(false);
  const [showOverride, setShowOverride] = useState(false);
  const [overrideInput, setOverrideInput] = useState("");
  const [secret, setSecret] = useState({ secret: "Vault Secured. Ready for Input.", hint: "" });

  // ลบ SNAP_ANGLE ออกเพื่อให้หมุนได้อิสระทุกองศา
  const [rotations, setRotations] = useState({
    [RingLayer.OUTER]: Math.random() * 360,
    [RingLayer.MIDDLE]: Math.random() * 360,
    [RingLayer.INNER]: Math.random() * 360,
  });

  const [activeLayer, setActiveLayer] = useState(null);
  const dragStartAngle = useRef(0);
  const initialRotation = useRef(0);
  const containerRef = useRef(null);

  const getAngle = (clientX, clientY) => {
    if (!containerRef.current) return 0;
    const rect = containerRef.current.getBoundingClientRect();
    const centerX = rect.left + rect.width / 2;
    const centerY = rect.top + rect.height / 2;
    const dx = clientX - centerX;
    const dy = clientY - centerY;
    return Math.atan2(dy, dx) * (180 / Math.PI);
  };

  const handleDragStart = (layer, e) => {
    if (solved || loading) return;
    const clientX = 'touches' in e ? e.touches[0].clientX : e.clientX;
    const clientY = 'touches' in e ? e.touches[0].clientY : e.clientY;
    setActiveLayer(layer);
    dragStartAngle.current = getAngle(clientX, clientY);
    initialRotation.current = rotations[layer];
  };

  const handleDragMove = useCallback((e) => {
    const layer = activeLayer;
    if (layer === null || solved) return;
    const clientX = 'touches' in e ? e.touches[0].clientX : e.clientX;
    const clientY = 'touches' in e ? e.touches[0].clientY : e.clientY;
    const currentAngle = getAngle(clientX, clientY);
    const angleDiff = currentAngle - dragStartAngle.current;

    setRotations(prev => ({ ...prev, [layer]: initialRotation.current + angleDiff }));
  }, [activeLayer, solved]);

  const handleDragEnd = useCallback(() => {
    // ไม่มีการ Snap และไม่มีการเช็ค isAligned อีกต่อไป
    setActiveLayer(null);
  }, [activeLayer]);

  // ฟังก์ชันสำหรับสั่ง Breach ด้วยตัวเอง (เพื่อดูแอนิเมชันที่ต้องการ)
  const initiateBreach = () => {
    if (solved || loading) return;
    setSolved(true);
    setTimeout(() => setShowResult(true), 1800);
  };

  useEffect(() => {
    if (activeLayer !== null) {
      window.addEventListener('mousemove', handleDragMove);
      window.addEventListener('mouseup', handleDragEnd);
      window.addEventListener('touchmove', handleDragMove, { passive: false });
      window.addEventListener('touchend', handleDragEnd);
    }
    return () => {
      window.removeEventListener('mousemove', handleDragMove);
      window.removeEventListener('mouseup', handleDragEnd);
      window.removeEventListener('touchmove', handleDragMove);
      window.removeEventListener('touchend', handleDragEnd);
    };
  }, [activeLayer, handleDragMove, handleDragEnd]);

  const initGame = async (customTheme = "") => {
    setLoading(true);
    setSolved(false);
    setShowResult(false);
    setShowOverride(false);
    try {
      const data = await generateSecretChallenge(customTheme);
      setSecret(data);
    } catch (e) { console.warn(e); }
    setRotations({
      [RingLayer.OUTER]: Math.random() * 360,
      [RingLayer.MIDDLE]: Math.random() * 360,
      [RingLayer.INNER]: Math.random() * 360,
    });
    setLoading(false);
  };

  useEffect(() => { initGame(); }, []);

  const iconSize = 48;
  const iconOffset = 250 - (iconSize / 2);

  return html`
    <div className=${`min-h-screen vault-room grid-bg flex flex-col items-center justify-center p-4 transition-colors duration-1000 ${solved ? 'solved-bg-pulse' : ''}`}>

      <div className=${`relative flex flex-col items-center ${solved ? 'solved' : ''}`}>

        <div className="scan-overlay"></div>

        <div className="absolute -top-20 left-0 w-full flex justify-between items-center px-4 opacity-60">
           <div className="flex items-center gap-2 text-[10px] tracking-[0.4em] text-emerald-500 font-orbitron font-bold uppercase">
              <${Activity} size={14} /> 
              ${solved ? "BREACHING PROTOCOL..." : "SYSTEM READY"}
           </div>
           <div className="text-[10px] tracking-[0.4em] text-slate-500 font-mono font-bold uppercase">
              ${solved ? "OVERDRIVE_MODE" : "FREE_ROTATION"}
           </div>
        </div>

        <div 
          ref=${containerRef}
          className=${`relative w-[340px] h-[340px] sm:w-[500px] sm:h-[500px] lg:w-[650px] lg:h-[650px] rounded-full border-[12px] transition-all duration-1000 ${solved ? 'border-emerald-400 shadow-[0_0_150px_rgba(16,185,129,0.5)]' : 'border-[#0c111d] bg-slate-900 shadow-[0_0_80px_rgba(0,0,0,0.8)] overflow-hidden'}`}
        >
          <svg viewBox="0 0 500 500" className="w-full h-full" style=${{ overflow: 'visible' }}>
            <${DialRing}
              layer=${RingLayer.OUTER}
              radius=${200}
              thickness=${80}
              rotation=${rotations[RingLayer.OUTER]}
              isDragging=${activeLayer === RingLayer.OUTER}
              isSolved=${solved}
              onDragStart=${handleDragStart}
            />
            <${DialRing}
              layer=${RingLayer.MIDDLE}
              radius=${125}
              thickness=${65}
              rotation=${rotations[RingLayer.MIDDLE]}
              isDragging=${activeLayer === RingLayer.MIDDLE}
              isSolved=${solved}
              onDragStart=${handleDragStart}
            />
            <${DialRing}
              layer=${RingLayer.INNER}
              radius=${65}
              thickness=${50}
              rotation=${rotations[RingLayer.INNER]}
              isDragging=${activeLayer === RingLayer.INNER}
              isSolved=${solved}
              onDragStart=${handleDragStart}
            />

            <circle 
              cx="250" cy="250" 
              r="45" 
              fill="#064e3b" 
              stroke=${solved ? "#34d399" : "#1e293b"} 
              strokeWidth="4" 
              className="transition-all duration-1000"
            />

            <g className=${solved ? "core-breach-anim" : ""}>
               <g transform=${`translate(${iconOffset}, ${iconOffset})`}>
                  ${solved ? 
                     html`<${Unlock} size=${iconSize} className="text-emerald-300" />` : 
                     html`<${Lock} size=${iconSize} className="text-slate-600" />`
                  }
               </g>
            </g>

            ${[0, 45, 90, 135, 180, 225, 270, 315].map(angle => {
              const r = 242;
              const x = 250 + r * Math.cos(angle * Math.PI / 180);
              const y = 250 + r * Math.sin(angle * Math.PI / 180);
              return html`<circle key=${angle} cx=${x} cy=${y} r="5" fill=${solved ? "#34d399" : "#1e293b"} className="transition-colors duration-1000" />`
            })}
          </svg>
        </div>

        <div className="mt-14 w-full flex flex-col items-center gap-6 z-20">
          ${!solved && html`
            <button 
              onClick=${initiateBreach}
              className="group relative flex items-center justify-center overflow-hidden rounded-full px-12 py-5 transition-all active:scale-95"
            >
              <div className="absolute inset-0 bg-emerald-500 opacity-10 group-hover:opacity-20"></div>
              <div className="absolute inset-0 border border-emerald-500/50 rounded-full group-hover:border-emerald-400"></div>
              <span className="relative flex items-center gap-3 text-emerald-400 font-orbitron text-sm font-bold tracking-[0.4em] uppercase">
                <${Zap} size={18} className="animate-pulse" />
                Initiate Breach
              </span>
            </button>

            <button 
                onClick=${() => setShowOverride(!showOverride)}
                className="text-slate-600 hover:text-emerald-500 transition-colors flex items-center gap-2 text-[11px] font-orbitron uppercase tracking-[0.5em] font-bold"
            >
                <${Terminal} size={14} /> Override Protocol
            </button>
          `}
        </div>
      </div>

      ${showResult && html`
        <div className="fixed inset-0 z-[100] flex items-center justify-center p-6 sm:p-12">
          <div className="absolute inset-0 bg-slate-950/80 backdrop-blur-2xl animate-in fade-in duration-1000" onClick=${() => setShowResult(false)}></div>

          <div className="relative w-full max-w-2xl bg-slate-900/95 border-t-4 border-emerald-400 rounded-3xl shadow-[0_0_200px_rgba(16,185,129,0.5)] p-10 md:p-14 text-center animate-modal-pop">
            <button 
              onClick=${() => setShowResult(false)}
              className="absolute top-6 right-6 text-slate-500 hover:text-white transition-colors"
            >
              <${X} size=${24} />
            </button>

            <div className="flex items-center justify-center gap-3 mb-10">
              <${ShieldAlert} className="text-emerald-400" size=${32} />
              <span className="text-xs font-bold text-emerald-400 font-orbitron tracking-[0.8em] uppercase">DECRYPTION SUCCESSFUL</span>
            </div>

            <div className="text-2xl md:text-5xl font-bold text-white mb-14 font-serif leading-tight px-4">
              "${secret.secret}"
            </div>

            <div className="flex flex-col sm:flex-row gap-5 justify-center">
              <button
                onClick=${() => initGame()}
                className="flex-1 bg-emerald-800 hover:bg-emerald-700 text-white py-6 rounded-2xl transition-all flex items-center justify-center gap-4 uppercase text-[12px] font-orbitron tracking-[0.5em] font-bold shadow-[0_10px_30px_rgba(6,78,59,0.5)] hover:translate-y-[-2px]"
              >
                <${RefreshCw} size=${22} className=${loading ? "animate-spin" : ""} />
                RESET SYSTEM
              </button>
              <button
                onClick=${() => { setShowResult(false); setShowOverride(true); }}
                className="bg-slate-950 hover:bg-slate-800 text-emerald-400 px-10 py-6 rounded-2xl border border-emerald-900/30 flex items-center justify-center gap-4 uppercase text-[12px] font-orbitron tracking-[0.3em] font-bold transition-all hover:border-emerald-500"
              >
                <${Terminal} size=${22} />
                COMMAND
              </button>
            </div>
          </div>
