import React, { useState, useEffect, useCallback, useRef } from 'react';
import htm from 'htm';
import { RefreshCw, Lock, Unlock, ShieldAlert, Terminal, Zap, Activity, X, ShieldCheck, Cpu, Database, Network } from 'lucide-react';
import DialRing from './components/DialRing.js';
import { generateSecretChallenge } from './services/geminiService.js';
import { RingLayer } from './types.js';

const html = htm.bind(React.createElement);

const App = () => {
  const [loading, setLoading] = useState(false);
  const [gameSolved, setGameSolved] = useState(false); // ปลดล็อคใหญ่
  const [activeNodeIndex, setActiveNodeIndex] = useState(0); // หน้าปัจจุบัน (0, 1, 2)
  const [showResult, setShowResult] = useState(false);
  const [showOverride, setShowOverride] = useState(false);
  const [overrideInput, setOverrideInput] = useState("");
  const [secret, setSecret] = useState({ secret: "Access Denied. Complete all nodes.", hint: "" });

  const SNAP_ANGLE = 90;
  const NODE_NAMES = ["ALPHA", "BETA", "GAMMA"];
  const NODE_ICONS = [Cpu, Database, Network];

  const getRandomRotations = () => ({
    [RingLayer.OUTER]: (Math.floor(Math.random() * 3) + 1) * SNAP_ANGLE,
    [RingLayer.MIDDLE]: (Math.floor(Math.random() * 3) + 1) * SNAP_ANGLE,
    [RingLayer.INNER]: (Math.floor(Math.random() * 3) + 1) * SNAP_ANGLE,
  });

  // เก็บสถานะการหมุนของทั้ง 3 หน้า
  const [nodesData, setNodesData] = useState([
    { rotations: getRandomRotations(), solved: false },
    { rotations: getRandomRotations(), solved: false },
    { rotations: getRandomRotations(), solved: false }
  ]);

  const [activeLayer, setActiveLayer] = useState(null);
  const dragStartAngle = useRef(0);
  const initialRotation = useRef(0);
  const containerRef = useRef(null);

  const getAngle = (clientX, clientY) => {
    if (!containerRef.current) return 0;
    const rect = containerRef.current.getBoundingClientRect();
    const centerX = rect.left + rect.width / 2;
    const centerY = rect.top + rect.height / 2;
    const dx = clientX - centerX;
    const dy = clientY - centerY;
    return Math.atan2(dy, dx) * (180 / Math.PI);
  };

  const handleDragStart = (layer, e) => {
    if (nodesData[activeNodeIndex].solved || loading || gameSolved) return;
    const clientX = 'touches' in e ? e.touches[0].clientX : e.clientX;
    const clientY = 'touches' in e ? e.touches[0].clientY : e.clientY;
    setActiveLayer(layer);
    dragStartAngle.current = getAngle(clientX, clientY);
    initialRotation.current = nodesData[activeNodeIndex].rotations[layer];
  };

  const handleDragMove = useCallback((e) => {
    const layer = activeLayer;
    if (layer === null || gameSolved) return;
    const clientX = 'touches' in e ? e.touches[0].clientX : e.clientX;
    const clientY = 'touches' in e ? e.touches[0].clientY : e.clientY;
    const currentAngle = getAngle(clientX, clientY);
    const angleDiff = currentAngle - dragStartAngle.current;

    setNodesData(prev => {
      const newNodes = [...prev];
      newNodes[activeNodeIndex] = {
        ...newNodes[activeNodeIndex],
        rotations: {
          ...newNodes[activeNodeIndex].rotations,
          [layer]: initialRotation.current + angleDiff
        }
      };
      return newNodes;
    });
  }, [activeLayer, activeNodeIndex, gameSolved]);

  const handleDragEnd = useCallback(() => {
    const layer = activeLayer;
    if (layer === null) return;

    setNodesData(prev => {
      const newNodes = [...prev];
      const node = newNodes[activeNodeIndex];
      const currentRot = node.rotations[layer];
      const snapped = Math.round(currentRot / SNAP_ANGLE) * SNAP_ANGLE;

      const nextRotations = { ...node.rotations, [layer]: snapped };

      const isAligned = Object.values(nextRotations).every((rot) => {
        const normalized = ((rot % 360) + 360) % 360;
        return normalized < 5 || normalized > 355;
      });

      newNodes[activeNodeIndex] = {
        ...node,
        rotations: nextRotations,
        solved: isAligned || node.solved
      };

      // ตรวจสอบว่าปลดล็อคครบทุกหน้าหรือยัง
      const allSolved = newNodes.every(n => n.solved);
      if (allSolved && !gameSolved) {
        setGameSolved(true);
        setTimeout(() => setShowResult(true), 1800);
      }

      return newNodes;
    });

    setActiveLayer(null);
  }, [activeLayer, activeNodeIndex, gameSolved]);

  useEffect(() => {
    if (activeLayer !== null) {
      window.addEventListener('mousemove', handleDragMove);
      window.addEventListener('mouseup', handleDragEnd);
      window.addEventListener('touchmove', handleDragMove, { passive: false });
      window.addEventListener('touchend', handleDragEnd);
    }
    return () => {
      window.removeEventListener('mousemove', handleDragMove);
      window.removeEventListener('mouseup', handleDragEnd);
      window.removeEventListener('touchmove', handleDragMove);
      window.removeEventListener('touchend', handleDragEnd);
    };
  }, [activeLayer, handleDragMove, handleDragEnd]);

  const initGame = async (customTheme = "") => {
    setLoading(true);
    setGameSolved(false);
    setActiveNodeIndex(0);
    setShowResult(false);
    setShowOverride(false);
    try {
      const data = await generateSecretChallenge(customTheme);
      setSecret(data);
    } catch (e) { console.warn(e); }

    setNodesData([
      { rotations: getRandomRotations(), solved: false },
      { rotations: getRandomRotations(), solved: false },
      { rotations: getRandomRotations(), solved: false }
    ]);
    setLoading(false);
  };

  useEffect(() => { initGame(); }, []);

  const iconSize = 48;
  const iconOffset = 250 - (iconSize / 2);
  const currentNode = nodesData[activeNodeIndex];

  return html`
    <div className=${`min-h-screen vault-room grid-bg flex flex-col items-center justify-center p-4 transition-colors duration-1000 ${gameSolved ? 'solved-bg-pulse' : ''}`}>

      <div className=${`relative flex flex-col items-center ${gameSolved ? 'solved' : ''}`}>

        <div className="scan-overlay"></div>

        <div className="absolute -top-28 left-0 w-full flex flex-col items-center gap-3">
           <div className="flex items-center gap-2 text-[10px] tracking-[0.5em] text-emerald-500 font-orbitron font-black uppercase bg-emerald-950/30 px-6 py-2 rounded-full border border-emerald-500/20">
              <${Activity} size={14} className=${gameSolved ? 'animate-pulse' : ''} /> 
              ACTIVE NODE: ${NODE_NAMES[activeNodeIndex]}
           </div>

           <!-- Status LEDs -->
           <div className="flex gap-4">
              ${nodesData.map((node, i) => html`
                <div key=${i} className="flex flex-col items-center gap-1">
                  <div className=${`w-8 h-1 rounded-full transition-all duration-500 ${node.solved ? 'bg-emerald-400 shadow-[0_0_10px_#34d399]' : 'bg-slate-800'}`}></div>
                  <span className=${`text-[7px] font-orbitron tracking-tighter ${node.solved ? 'text-emerald-400' : 'text-slate-600'}`}>${NODE_NAMES[i]}</span>
                </div>
              `)}
           </div>
        </div>

        <div 
          ref=${containerRef}
          className=${`relative w-[320px] h-[320px] sm:w-[480px] sm:h-[480px] lg:w-[600px] lg:h-[600px] rounded-full border-[10px] transition-all duration-1000 ${gameSolved ? 'border-emerald-400 shadow-[0_0_150px_rgba(16,185,129,0.5)]' : 'border-[#0c111d] bg-slate-900 shadow-[0_0_80px_rgba(0,0,0,0.8)] overflow-hidden'}`}
        >
          <svg viewBox="0 0 500 500" className="w-full h-full" style=${{ overflow: 'visible' }}>
            <${DialRing}
              layer=${RingLayer.OUTER}
              radius=${200}
              thickness=${80}
              rotation=${currentNode.rotations[RingLayer.OUTER]}
              isDragging=${activeLayer === RingLayer.OUTER}
              isSolved=${currentNode.solved || gameSolved}
              onDragStart=${handleDragStart}
            />
            <${DialRing}
              layer=${RingLayer.MIDDLE}
              radius=${125}
              thickness=${65}
              rotation=${currentNode.rotations[RingLayer.MIDDLE]}
              isDragging=${activeLayer === RingLayer.MIDDLE}
              isSolved=${currentNode.solved || gameSolved}
              onDragStart=${handleDragStart}
            />
            <${DialRing}
              layer=${RingLayer.INNER}
              radius=${65}
              thickness=${50}
              rotation=${currentNode.rotations[RingLayer.INNER]}
              isDragging=${activeLayer === RingLayer.INNER}
              isSolved=${currentNode.solved || gameSolved}
              onDragStart=${handleDragStart}
            />

            <circle 
              cx="250" cy="250" 
              r="45" 
              fill="#064e3b" 
              stroke=${gameSolved ? "#34d399" : "#1e293b"} 
              strokeWidth="4" 
              className="transition-all duration-1000"
            />

            <g className=${gameSolved ? "core-breach-anim" : ""}>
               <g transform=${`translate(${iconOffset}, ${iconOffset})`}>
                  ${gameSolved ? 
                     html`<${Unlock} size=${iconSize} className="text-emerald-300" />` : 
                     currentNode.solved ? 
                     html`<${ShieldCheck} size=${iconSize} className="text-emerald-500 animate-pulse" />` :
                     html`<${Lock} size=${iconSize} className="text-slate-600" />`
                  }
               </g>
            </g>

            ${[0, 45, 90, 135, 180, 225, 270, 315].map(angle => {
              const r = 242;
              const x = 250 + r * Math.cos(angle * Math.PI / 180);
              const y = 250 + r * Math.sin(angle * Math.PI / 180);
              return html`<circle key=${angle} cx=${x} cy=${y} r="5" fill=${gameSolved ? "#34d399" : "#1e293b"} className="transition-colors duration-1000" />`
            })}
          </svg>
        </div>

        <!-- Node Selector Navigation -->
        <div className="mt-12 w-full flex justify-center items-center gap-3 sm:gap-6 z-20">
          ${nodesData.map((node, i) => {
            const Icon = NODE_ICONS[i];
            return html`
              <button 
                key=${i}
                onClick=${() => !gameSolved && setActiveNodeIndex(i)}
                className=${`group relative flex flex-col items-center justify-center w-24 sm:w-32 py-4 rounded-xl border transition-all duration-300 ${activeNodeIndex === i ? 'bg-emerald-950/40 border-emerald-500 shadow-[0_0_20px_rgba(16,185,129,0.2)]' : 'bg-slate-950/40 border-slate-800 hover:border-slate-600'}`}
              >
                <div className=${`mb-2 ${activeNodeIndex === i ? 'text-emerald-400' : 'text-slate-500'}`}>
                  <${Icon} size=${20} />
                </div>
                <span className=${`text-[9px] font-orbitron font-black tracking-widest ${activeNodeIndex === i ? 'text-emerald-400' : 'text-slate-500'}`}>
                  ${NODE_NAMES[i]}
                </span>
                ${node.solved && html`
                  <div className="absolute -top-1 -right-1 bg-emerald-500 rounded-full p-0.5 shadow-lg">
                    <${ShieldCheck} size=${10} className="text-white" />
                  </div>
                `}
              </button>
            `
          })}
        </div>

        <div className="mt-8 flex flex-col items-center gap-4">
           <button 
                onClick=${() => setShowOverride(!showOverride)}
                className="text-slate-700 hover:text-emerald-500 transition-colors flex items-center gap-2 text-[10px] font-orbitron uppercase tracking-[0.4em] font-bold"
            >
                <${Terminal} size={12} /> Injection Port
            </button>
        </div>
      </div>

      ${showResult && html`
        <div className="fixed inset-0 z-[100] flex items-center justify-center p-6 sm:p-12">
          <div className="absolute inset-0 bg-slate-950/80 backdrop-blur-2xl animate-in fade-in duration-1000" onClick=${() => setShowResult(false)}></div>

          <div className="relative w-full max-w-2xl bg-slate-900/95 border-t-4 border-emerald-400 rounded-3xl shadow-[0_0_200px_rgba(16,185,129,0.5)] p-10 md:p-14 text-center animate-modal-pop">
            <button 
              onClick=${() => setShowResult(false)}
              className="absolute top-6 right-6 text-slate-500 hover:text-white transition-colors"
            >
              <${X} size=${24} />
            </button>

            <div className="flex items-center justify-center gap-3 mb-10">
              <${ShieldAlert} className="text-emerald-400" size=${32} />
              <span className="text-xs font-bold text-emerald-400 font-orbitron tracking-[0.8em] uppercase">FULL MATRIX BREACH</span>
            </div>

            <div className="text-2xl md:text-5xl font-bold text-white mb-14 font-serif leading-tight px-4">
              "${secret.secret}"
            </div>

            <div className="flex flex-col sm:flex-row gap-5 justify-center">
              <button
                onClick=${() => initGame()}
                className="flex-1 bg-emerald-800 hover:bg-emerald-700 text-white py-6 rounded-2xl transition-all flex items-center justify-center gap-4 uppercase text-[12px] font-orbitron tracking-[0.5em] font-bold shadow-[0_10px_30px_rgba(6,78,59,0.5)] hover:translate-y-[-2px]"
              >
                <${RefreshCw} size={22} className=${loading ? "animate-spin" : ""} />
                SYSTEM REBOOT
              </button>
            </div>
          </div>
        </div>
      `}

      ${showOverride && !showResult && html`
        <div className="fixed inset-0 z-[100] flex items-center justify-center p-4">
          <div className="absolute inset-0 bg-black/80 backdrop-blur-md animate-in fade-in duration-500" onClick=${() => setShowOverride(false)}></div>
          <div className="relative w-full max-w-md bg-slate-900 border border-emerald-900/30 p-8 rounded-2xl animate-modal-pop shadow-2xl">
            <div className="flex items-center gap-2 mb-6 text-emerald-500">
              <${Zap} size={16} />
              <span className="text-[10px] font-bold tracking-[0.3em] font-orbitron uppercase">Override Input</span>
            </div>
            <div className="flex flex-col gap-4">
              <input 
                type="text" 
                value=${overrideInput}
                onChange=${(e) => setOverrideInput(e.target.value)}
                placeholder="Target theme..."
                className="w-full bg-black/60 border border-slate-700 rounded-lg p-4 text-xs font-mono text-emerald-400 focus:outline-none focus:border-emerald-500 transition-all shadow-inner"
              />
              <button 
                onClick=${() => initGame(overrideInput)}
                disabled=${loading}
                className="w-full bg-emerald-700 hover:bg-emerald-600 disabled:opacity-50 text-white py-4 rounded-lg text-[11px] font-orbitron font-bold uppercase tracking-widest transition-all"
              >
                ${loading ? "Initiating..." : "Execute Override"}
              </button>
            </div>
          </div>
        </div>
      `}

      <footer className="fixed bottom-6 text-slate-900 text-[10px] tracking-[2.5em] uppercase font-orbitron font-black pointer-events-none select-none opacity-10">
        MATRIX SYNC ACTIVE
      </footer>
    </div>
  `;
