import React, { useMemo } from 'react';
import htm from 'htm';
import { RingLayer } from '../types.js';

const html = htm.bind(React.createElement);

const DialRing = ({
  layer,
  radius,
  thickness,
  rotation,
  isDragging,
  isSolved,
  onDragStart
}) => {
  const isInner = layer === RingLayer.INNER;

  const styles = {
    [RingLayer.OUTER]: { start: "#1e293b", end: "#0f172a", text: "#f1f5f9", fontSize: "22px" },
    [RingLayer.MIDDLE]: { start: "#334155", end: "#1e293b", text: "#e2e8f0", fontSize: "18px" },
    [RingLayer.INNER]: { start: "#020617", end: "#0f172a", text: "#4ade80", fontSize: "15px" }
  };

  const config = styles[layer] || styles[RingLayer.OUTER];

  const ringContent = useMemo(() => {
    switch (layer) {
      case RingLayer.OUTER: return ["SYNC", "LEVEL", "GRID", "VOLT"];
      case RingLayer.MIDDLE: return ["SEC_LOW", "SEC_MED", "SUP_HIGH", "LOCKED"];
      case RingLayer.INNER: return ["CORE", "FIXED", "CORE", "FIXED"];
      default: return ["", "", "", ""];
    }
  }, [layer]);

  const segments = useMemo(() => {
    return ringContent.map((text, i) => ({
      text,
      angle: (i * 90) - 90
    }));
  }, [ringContent]);

  const gradId = `grad-${layer}`;
  const solveGradId = `solve-grad-${layer}`;

  const overdriveClass = useMemo(() => {
    if (!isSolved) return "";
    if (layer === RingLayer.OUTER) return "overdrive-right";
    if (layer === RingLayer.MIDDLE) return "overdrive-left";
    if (layer === RingLayer.INNER) return "overdrive-right";
    return "";
  }, [isSolved, layer]);

  return html`
    <g 
      className=${`${isInner ? 'cursor-default' : 'cursor-pointer'} transition-opacity duration-500 ${isSolved ? 'opacity-100' : 'opacity-90 hover:opacity-100'}`}
      onMouseDown=${(e) => !isInner && onDragStart(layer, e)}
      onTouchStart=${(e) => !isInner && onDragStart(layer, e)}
    >
      <defs>
        <linearGradient id=${gradId} x1="0%" y1="0%" x2="100%" y2="100%">
          <stop offset="0%" style=${{stopColor: config.start, stopOpacity: 1}} />
          <stop offset="100%" style=${{stopColor: config.end, stopOpacity: 1}} />
        </linearGradient>
        <linearGradient id=${solveGradId} x1="0%" y1="0%" x2="100%" y2="100%">
          <stop offset="0%" style=${{stopColor: "#059669", stopOpacity: 1}} />
          <stop offset="100%" style=${{stopColor: "#064e3b", stopOpacity: 1}} />
        </linearGradient>
      </defs>

      <circle 
        cx="250" cy="250" r=${radius} 
        fill=${isSolved ? `url(#${solveGradId})` : `url(#${gradId})`} 
        stroke=${isSolved ? "rgba(16, 185, 129, 0.8)" : isInner ? "rgba(74, 222, 128, 0.2)" : "rgba(0,0,0,0.4)"} 
        strokeWidth=${thickness}
        className="transition-all duration-1000"
      />

      <g 
        className=${overdriveClass}
        style=${{ 
          transform: isSolved ? undefined : `rotate(${rotation}deg)`, 
          transformOrigin: '250px 250px',
          transition: isDragging ? 'none' : 'transform 0.8s cubic-bezier(0.34, 1.56, 0.64, 1)'
        }}
      >
        ${Array.from({length: 40}).map((_, i) => {
          const deg = i * 9;
          const rad = (deg - 90) * (Math.PI / 180);
          const isMain = i % 10 === 0;
          const len = isMain ? 15 : 7;
          const outerPoint = radius + thickness/2 - 2;
          return html`
            <line 
              key=${i}
              x1=${250 + outerPoint * Math.cos(rad)} 
              y1=${250 + outerPoint * Math.sin(rad)} 
              x2=${250 + (outerPoint - len) * Math.cos(rad)} 
              y2=${250 + (outerPoint - len) * Math.sin(rad)} 
              stroke=${isSolved ? "rgba(16, 185, 129, 0.6)" : (isMain ? "rgba(255,255,255,0.3)" : "rgba(0,0,0,0.4)")} 
              strokeWidth=${isMain ? "2" : "1"} 
            />
          `
        })}

        ${segments.map((seg, i) => {
          const rad = (seg.angle) * (Math.PI / 180);
          const x = 250 + radius * Math.cos(rad);
          const y = 250 + radius * Math.sin(rad);
          const rotationAngle = seg.angle + 90;

          return html`
            <text
              key=${i}
              x=${x}
              y=${y}
              fill=${isSolved ? "#34d399" : config.text}
              fontSize=${config.fontSize}
              fontWeight="700"
              textAnchor="middle"
              dominantBaseline="middle"
              className=${`select-none pointer-events-none font-orbitron ${isInner ? 'opacity-50' : ''}`}
              style=${{ 
                transform: `rotate(${rotationAngle}deg)`,
                transformOrigin: `${x}px ${y}px`,
                letterSpacing: '0.05em',
                textShadow: isSolved ? '0 0 15px rgba(16, 185, 129, 0.9)' : '0 2px 4px rgba(0,0,0,0.5)'
              }}
            >
              ${seg.text}
            </text>
          `
        })}
      </g>
    </g>
  `;
};

export default DialRing;

import React, { useState, useEffect, useCallback, useRef } from 'react';
import htm from 'htm';
import { ShieldCheck, RefreshCw } from 'lucide-react';
import DialRing from './components/DialRing.js';
import { RingLayer } from './types.js';

const html = htm.bind(React.createElement);

const App = () => {
  const [activeNodeIndex, setActiveNodeIndex] = useState(0);
  const [gameSolved, setGameSolved] = useState(false);

  const SNAP_ANGLE = 90;
  const NODE_NAMES = ["ALPHA", "BETA", "GAMMA"];

  const getRandomRotations = () => ({
    [RingLayer.OUTER]: (Math.floor(Math.random() * 3) + 1) * SNAP_ANGLE,
    [RingLayer.MIDDLE]: (Math.floor(Math.random() * 3) + 1) * SNAP_ANGLE,
    [RingLayer.INNER]: 0, // Innermost is always locked at 0
  });

  const [nodesData, setNodesData] = useState([
    { rotations: getRandomRotations(), solved: false },
    { rotations: getRandomRotations(), solved: false },
    { rotations: getRandomRotations(), solved: false }
  ]);

  const [activeLayer, setActiveLayer] = useState(null);
  const dragStartAngle = useRef(0);
  const initialRotation = useRef(0);
  const containerRef = useRef(null);

  const getAngle = (clientX, clientY) => {
    if (!containerRef.current) return 0;
    const rect = containerRef.current.getBoundingClientRect();
    const centerX = rect.left + rect.width / 2;
    const centerY = rect.top + rect.height / 2;
    const dx = clientX - centerX;
    const dy = clientY - centerY;
    return Math.atan2(dy, dx) * (180 / Math.PI);
  };

  const handleDragStart = (layer, e) => {
    // Only outer and middle rings are draggable
    if (layer === RingLayer.INNER || nodesData[activeNodeIndex].solved || gameSolved) return;

    const clientX = 'touches' in e ? e.touches[0].clientX : e.clientX;
    const clientY = 'touches' in e ? e.touches[0].clientY : e.clientY;
    setActiveLayer(layer);
    dragStartAngle.current = getAngle(clientX, clientY);
    initialRotation.current = nodesData[activeNodeIndex].rotations[layer];
  };

  const handleDragMove = useCallback((e) => {
    const layer = activeLayer;
    if (layer === null || gameSolved) return;
    const clientX = 'touches' in e ? e.touches[0].clientX : e.clientX;
    const clientY = 'touches' in e ? e.touches[0].clientY : e.clientY;
    const currentAngle = getAngle(clientX, clientY);
    const angleDiff = currentAngle - dragStartAngle.current;

    setNodesData(prev => {
      const newNodes = [...prev];
      newNodes[activeNodeIndex] = {
        ...newNodes[activeNodeIndex],
        rotations: {
          ...newNodes[activeNodeIndex].rotations,
          [layer]: initialRotation.current + angleDiff
        }
      };
      return newNodes;
    });
  }, [activeLayer, activeNodeIndex, gameSolved]);

  const handleDragEnd = useCallback(() => {
    const layer = activeLayer;
    if (layer === null) return;

    setNodesData(prev => {
      const newNodes = [...prev];
      const node = newNodes[activeNodeIndex];
      const snapped = Math.round(node.rotations[layer] / SNAP_ANGLE) * SNAP_ANGLE;

      const nextRotations = { ...node.rotations, [layer]: snapped };

      const isAligned = Object.values(nextRotations).every((rot) => {
        const normalized = ((rot % 360) + 360) % 360;
        return normalized < 5 || normalized > 355;
      });

      newNodes[activeNodeIndex] = {
        ...node,
        rotations: nextRotations,
        solved: isAligned || node.solved
      };

      if (newNodes.every(n => n.solved) && !gameSolved) {
        setGameSolved(true);
      }

      return newNodes;
    });

    setActiveLayer(null);
  }, [activeLayer, activeNodeIndex, gameSolved]);

  useEffect(() => {
    if (activeLayer !== null) {
      window.addEventListener('mousemove', handleDragMove);
      window.addEventListener('mouseup', handleDragEnd);
      window.addEventListener('touchmove', handleDragMove, { passive: false });
      window.addEventListener('touchend', handleDragEnd);
    }
    return () => {
      window.removeEventListener('mousemove', handleDragMove);
      window.removeEventListener('mouseup', handleDragEnd);
      window.removeEventListener('touchmove', handleDragMove);
      window.removeEventListener('touchend', handleDragEnd);
    };
  }, [activeLayer, handleDragMove, handleDragEnd]);

  const resetGame = () => {
    setGameSolved(false);
    setActiveNodeIndex(0);
    setNodesData([
      { rotations: getRandomRotations(), solved: false },
      { rotations: getRandomRotations(), solved: false },
      { rotations: getRandomRotations(), solved: false }
    ]);
  };

  const currentNode = nodesData[activeNodeIndex];

  return html`
    <div className="puzzle-container p-4">
      <!-- Top Status -->
      <div className="mb-12 text-center">
        <div className="text-[10px] tracking-[0.5em] text-emerald-500 font-bold uppercase mb-4 opacity-70">
          NODE SYNCHRONIZATION MATRIX
        </div>
        <div className="flex gap-4 justify-center">
          ${nodesData.map((node, i) => html`
            <div key=${i} className="flex flex-col items-center gap-2">
              <div className=${`w-12 h-1 rounded-full transition-all duration-500 ${node.solved ? 'bg-emerald-400 shadow-[0_0_15px_#34d399]' : 'bg-slate-800'}`}></div>
              <span className=${`text-[8px] tracking-widest ${activeNodeIndex === i ? 'text-white' : 'text-slate-600'}`}>${NODE_NAMES[i]}</span>
            </div>
          `)}
        </div>
      </div>

      <!-- Main Dial -->
      <div 
        ref=${containerRef}
        className=${`relative w-[300px] h-[300px] sm:w-[450px] sm:h-[450px] lg:w-[550px] lg:h-[550px] rounded-full border-[8px] transition-all duration-1000 ${gameSolved ? 'border-emerald-400 solved-glow' : 'border-slate-900 bg-slate-950 shadow-2xl'}`}
      >
        <svg viewBox="0 0 500 500" className="w-full h-full" style=${{ overflow: 'visible' }}>
          <${DialRing}
            layer=${RingLayer.OUTER}
            radius=${200}
            thickness=${80}
            rotation=${currentNode.rotations[RingLayer.OUTER]}
            isDragging=${activeLayer === RingLayer.OUTER}
            isSolved=${currentNode.solved}
            onDragStart=${handleDragStart}
          />
          <${DialRing}
            layer=${RingLayer.MIDDLE}
            radius=${125}
            thickness=${65}
            rotation=${currentNode.rotations[RingLayer.MIDDLE]}
            isDragging=${activeLayer === RingLayer.MIDDLE}
            isSolved=${currentNode.solved}
            onDragStart=${handleDragStart}
          />
          <${DialRing}
            layer=${RingLayer.INNER}
            radius=${65}
            thickness=${50}
            rotation=${currentNode.rotations[RingLayer.INNER]}
            isDragging=${activeLayer === RingLayer.INNER}
            isSolved=${currentNode.solved}
            onDragStart=${handleDragStart}
          />

          <circle cx="250" cy="250" r="40" fill="#020617" stroke=${gameSolved ? "#34d399" : "#1e293b"} strokeWidth="2" />
          <g transform="translate(230, 230)">
            ${gameSolved ? 
              html`<${ShieldCheck} size={40} className="text-emerald-400" />` : 
              html`<div className="w-10 h-10 rounded-full border border-slate-800 animate-pulse"></div>`
            }
          </g>
        </svg>
      </div>

      <!-- Navigation Buttons -->
      <div className="mt-16 flex gap-4">
        ${nodesData.map((node, i) => html`
          <button 
            key=${i}
            onClick=${() => !gameSolved && setActiveNodeIndex(i)}
            className=${`px-6 py-3 rounded-lg border text-[10px] font-bold tracking-[0.2em] transition-all duration-300 ${activeNodeIndex === i ? 'bg-emerald-950/20 border-emerald-500 text-emerald-400' : 'bg-transparent border-slate-800 text-slate-500 hover:border-slate-600'}`}
          >
            NODE ${NODE_NAMES[i]}
          </button>
        `)}
      </div>

      <!-- Success Message -->
      ${gameSolved && html`
        <div className="mt-12 text-center animate-bounce">
          <div className="text-emerald-400 text-sm tracking-[0.4em] font-bold mb-4 uppercase">ACCESS GRANTED</div>
          <button onClick=${resetGame} className="text-slate-500 hover:text-white transition-colors flex items-center gap-2 text-[10px] tracking-widest uppercase">
            <${RefreshCw} size={14} /> Restart System
          </button>
        </div>
      `}
    </div>
  `;
};

export default App;

