    if (layer === null || gameSolved) return;
    const clientX = 'touches' in e ? e.touches[0].clientX : e.clientX;
    const clientY = 'touches' in e ? e.touches[0].clientY : e.clientY;
    const currentAngle = getAngle(clientX, clientY);
    const angleDiff = currentAngle - dragStartAngle.current;

    setNodesData(prev => {
      const newNodes = [...prev];
      newNodes[activeNodeIndex] = {
        ...newNodes[activeNodeIndex],
        rotations: {
          ...newNodes[activeNodeIndex].rotations,
          [layer]: initialRotation.current + angleDiff
        }
      };
      return newNodes;
    });
  }, [activeLayer, activeNodeIndex, gameSolved]);

  const handleDragEnd = useCallback(() => {
    const layer = activeLayer;
    if (layer === null) return;

    setNodesData(prev => {
      const newNodes = [...prev];
      const node = newNodes[activeNodeIndex];
      const snapped = Math.round(node.rotations[layer] / SNAP_ANGLE) * SNAP_ANGLE;
      const nextRotations = { ...node.rotations, [layer]: snapped };

      const isAligned = Object.values(nextRotations).every((rot) => {
        const normalized = ((rot % 360) + 360) % 360;
        return normalized < 5 || normalized > 355;
      });

      newNodes[activeNodeIndex] = {
        ...node,
        rotations: nextRotations,
        solved: isAligned || node.solved
      };

      if (newNodes.every(n => n.solved) && !gameSolved) {
        setGameSolved(true);
      }
      return newNodes;
    });
    setActiveLayer(null);
  }, [activeLayer, activeNodeIndex, gameSolved]);

  useEffect(() => {
    if (activeLayer !== null) {
      window.addEventListener('mousemove', handleDragMove);
      window.addEventListener('mouseup', handleDragEnd);
      window.addEventListener('touchmove', handleDragMove, { passive: false });
      window.addEventListener('touchend', handleDragEnd);
    }
    return () => {
      window.removeEventListener('mousemove', handleDragMove);
      window.removeEventListener('mouseup', handleDragEnd);
      window.removeEventListener('touchmove', handleDragMove);
      window.removeEventListener('touchend', handleDragEnd);
    };
  }, [activeLayer, handleDragMove, handleDragEnd]);

  const resetGame = () => {
    setGameSolved(false);
    setActiveNodeIndex(0);
    setNodesData([
      { rotations: getRandomRotations(), solved: false, content: NODE_CONTENT[0] },
      { rotations: getRandomRotations(), solved: false, content: NODE_CONTENT[1] },
      { rotations: getRandomRotations(), solved: false, content: NODE_CONTENT[2] }
    ]);
  };

  const currentNode = nodesData[activeNodeIndex];

  return html`
    <div className="puzzle-container">
      <div 
        ref=${containerRef}
        className=${`relative w-[320px] h-[320px] sm:w-[480px] sm:h-[480px] lg:w-[580px] lg:h-[580px] rounded-full border-[6px] transition-all duration-1000 ${gameSolved ? 'border-emerald-400 solved-glow scale-105' : 'border-slate-800 bg-slate-950 shadow-2xl'}`}
      >
        <svg viewBox="0 0 500 500" className="w-full h-full" style=${{ overflow: 'visible' }}>
          <${DialRing}
            layer=${RingLayer.OUTER}
            radius=${200}
            thickness=${80}
            rotation=${currentNode.rotations[RingLayer.OUTER]}
            isDragging=${activeLayer === RingLayer.OUTER}
            isSolved=${currentNode.solved}
            labels=${currentNode.content[RingLayer.OUTER]}
            onDragStart=${handleDragStart}
          />
          <${DialRing}
            layer=${RingLayer.MIDDLE}
            radius=${125}
            thickness=${65}
            rotation=${currentNode.rotations[RingLayer.MIDDLE]}
            isDragging=${activeLayer === RingLayer.MIDDLE}
            isSolved=${currentNode.solved}
            labels=${currentNode.content[RingLayer.MIDDLE]}
            onDragStart=${handleDragStart}
          />
          <${DialRing}
            layer=${RingLayer.INNER}
            radius=${65}
            thickness=${50}
            rotation=${currentNode.rotations[RingLayer.INNER]}
            isDragging=${activeLayer === RingLayer.INNER}
            isSolved=${currentNode.solved}
            labels=${currentNode.content[RingLayer.INNER]}
            onDragStart=${handleDragStart}
          />

          <circle cx="250" cy="250" r="35" fill="#020617" stroke=${gameSolved ? "#34d399" : "#1e293b"} strokeWidth="1" />
          <g transform="translate(232, 232)">
            ${gameSolved && html`<${ShieldCheck} size={36} className="text-emerald-400" />`}
          </g>
        </svg>
      </div>

      <div className="mt-12 flex gap-3">
        ${nodesData.map((node, i) => html`
          <button 
            key=${i}
            onClick=${() => !gameSolved && setActiveNodeIndex(i)}
            className=${`px-5 py-3 rounded-md border text-[11px] font-bold tracking-[0.1em] transition-all duration-200 ${activeNodeIndex === i ? 'bg-emerald-500 text-slate-950 border-emerald-400 shadow-[0_0_15px_rgba(16,185,129,0.4)]' : 'bg-transparent border-slate-800 text-slate-400 hover:border-slate-600'}`}
