import React, { useMemo } from 'react';
import htm from 'htm';
import { RingLayer } from '../types.js';

const html = htm.bind(React.createElement);

const DialRing = ({
  layer,
  radius,
  thickness,
  rotation,
  isDragging,
  isSolved,
  labels,
  onDragStart
}) => {
  const isInner = layer === RingLayer.INNER;

  const styles = {
    [RingLayer.OUTER]: { start: "#1e293b", end: "#0f172a", text: "#f1f5f9", fontSize: "20px" },
    [RingLayer.MIDDLE]: { start: "#334155", end: "#1e293b", text: "#cbd5e1", fontSize: "16px" },
    [RingLayer.INNER]: { start: "#020617", end: "#0f172a", text: "#4ade80", fontSize: "14px" }
  };

  const config = styles[layer] || styles[RingLayer.OUTER];

  const segments = useMemo(() => {
    const list = labels || ["", "", "", ""];
    return list.map((text, i) => ({
      text,
      angle: (i * 90) - 90
    }));
  }, [labels]);

  const gradId = `grad-${layer}`;
  const solveGradId = `solve-grad-${layer}`;

  return html`
    <g 
      className=${`${isInner ? 'cursor-default' : 'cursor-pointer'} transition-opacity duration-300 ${isSolved ? 'opacity-100' : 'opacity-90 hover:opacity-100'}`}
      onMouseDown=${(e) => !isInner && onDragStart(layer, e)}
      onTouchStart=${(e) => !isInner && onDragStart(layer, e)}
    >
      <defs>
        <linearGradient id=${gradId} x1="0%" y1="0%" x2="100%" y2="100%">
          <stop offset="0%" style=${{stopColor: config.start, stopOpacity: 1}} />
          <stop offset="100%" style=${{stopColor: config.end, stopOpacity: 1}} />
        </linearGradient>
        <linearGradient id=${solveGradId} x1="0%" y1="0%" x2="100%" y2="100%">
          <stop offset="0%" style=${{stopColor: "#065f46", stopOpacity: 1}} />
          <stop offset="100%" style=${{stopColor: "#022c22", stopOpacity: 1}} />
        </linearGradient>
      </defs>

      <circle 
        cx="250" cy="250" r=${radius} 
        fill=${isSolved ? `url(#${solveGradId})` : `url(#${gradId})`} 
        stroke=${isSolved ? "rgba(16, 185, 129, 0.4)" : "rgba(255,255,255,0.05)"} 
        strokeWidth=${thickness}
        className="transition-all duration-500"
      />

      <g 
        style=${{ 
          transform: `rotate(${rotation}deg)`, 
          transformOrigin: '250px 250px',
          transition: isDragging ? 'none' : 'transform 0.6s cubic-bezier(0.34, 1.56, 0.64, 1)'
        }}
      >
        ${Array.from({length: 40}).map((_, i) => {
          const deg = i * 9;
          const rad = (deg - 90) * (Math.PI / 180);
          const isMain = i % 10 === 0;
          const len = isMain ? 12 : 5;
          const outerPoint = radius + thickness/2 - 2;
          return html`
            <line 
              key=${i}
              x1=${250 + outerPoint * Math.cos(rad)} 
              y1=${250 + outerPoint * Math.sin(rad)} 
              x2=${250 + (outerPoint - len) * Math.cos(rad)} 
              y2=${250 + (outerPoint - len) * Math.sin(rad)} 
              stroke=${isSolved ? "rgba(52, 211, 153, 0.4)" : "rgba(255,255,255,0.1)"} 
              strokeWidth=${isMain ? "1.5" : "0.5"} 
            />
          `
        })}

        ${segments.map((seg, i) => {
          const rad = (seg.angle) * (Math.PI / 180);
          const x = 250 + radius * Math.cos(rad);
          const y = 250 + radius * Math.sin(rad);
          const rotationAngle = seg.angle + 90;

          return html`
            <text
              key=${i}
              x=${x}
              y=${y}
              fill=${isSolved ? "#34d399" : config.text}
              fontSize=${config.fontSize}
              fontWeight="700"
              textAnchor="middle"
              dominantBaseline="middle"
              className="select-none pointer-events-none font-orbitron"
              style=${{ 
                transform: `rotate(${rotationAngle}deg)`,
                transformOrigin: `${x}px ${y}px`,
                letterSpacing: '0.05em',
                opacity: isInner ? 0.6 : 1
              }}
            >
              ${seg.text}
            </text>
          `
        })}
      </g>
    </g>
  `;
};

