import React, { useMemo } from 'react';
import htm from 'htm';
import { RingLayer } from '../types.js';

const html = htm.bind(React.createElement);

const DialRing = ({
  layer,
  radius,
  thickness,
  rotation,
  isDragging,
  labels,
  onDragStart
}) => {
  const isInner = layer === RingLayer.INNER;

  const styles = {
    [RingLayer.OUTER]: { start: "#1e293b", end: "#0f172a", text: "#ffffff", fontSize: "17px" },
    [RingLayer.MIDDLE]: { start: "#334155", end: "#1e293b", text: "#f1f5f9", fontSize: "14px" },
    [RingLayer.INNER]: { start: "#020617", end: "#0f172a", text: "#94a3b8", fontSize: "11px" }
  };

  const config = styles[layer] || styles[RingLayer.OUTER];

  const segments = useMemo(() => {
    const list = labels || ["", "", "", ""];
    return list.map((text, i) => {
      const baseAngle = (i * 90) - 90;
      // เพิ่มส่วนโค้งให้กว้างขึ้นเล็กน้อยเพื่อรองรับข้อความยาวๆ อย่าง "Local Trial"
      const startAngle = baseAngle - 42;
      const endAngle = baseAngle + 42;

      const startRad = (startAngle * Math.PI) / 180;
      const endRad = (endAngle * Math.PI) / 180;

      const x1 = 250 + radius * Math.cos(startRad);
      const y1 = 250 + radius * Math.sin(startRad);
      const x2 = 250 + radius * Math.cos(endRad);
      const y2 = 250 + radius * Math.sin(endRad);

      const d = `M ${x1} ${y1} A ${radius} ${radius} 0 0 1 ${x2} ${y2}`;

      return { text, d, id: `path-${layer}-${i}` };
    });
  }, [labels, radius, layer]);

  const gradId = `grad-${layer}`;

  return html`
    <g 
      className=${`${isInner ? 'cursor-default' : 'cursor-pointer'} transition-opacity duration-300 opacity-95 hover:opacity-100`}
      onMouseDown=${(e) => !isInner && onDragStart(layer, e)}
      onTouchStart=${(e) => !isInner && onDragStart(layer, e)}
    >
      <defs>
        <linearGradient id=${gradId} x1="0%" y1="0%" x2="100%" y2="100%">
          <stop offset="0%" style=${{stopColor: config.start, stopOpacity: 1}} />
          <stop offset="100%" style=${{stopColor: config.end, stopOpacity: 1}} />
        </linearGradient>

        ${segments.map(seg => html`
          <path id=${seg.id} d=${seg.d} fill="none" key=${seg.id} />
        `)}
      </defs>

      <circle 
        cx="250" cy="250" r=${radius} 
        fill=${`url(#${gradId})`} 
        stroke="rgba(255,255,255,0.08)" 
        strokeWidth=${thickness}
        className="transition-all duration-500"
      />

      <g 
        style=${{ 
          transform: `rotate(${rotation}deg)`, 
          transformOrigin: '250px 250px',
          transition: isDragging ? 'none' : 'transform 0.6s cubic-bezier(0.34, 1.56, 0.64, 1)'
        }}
      >
        ${Array.from({length: 40}).map((_, i) => {
          const deg = i * 9;
          const rad = (deg - 90) * (Math.PI / 180);
          const isMain = i % 10 === 0;
          const len = isMain ? 10 : 4;
          const outerPoint = radius + thickness/2 - 2;
          return html`
            <line 
              key=${i}
              x1=${250 + outerPoint * Math.cos(rad)} 
              y1=${250 + outerPoint * Math.sin(rad)} 
              x2=${250 + (outerPoint - len) * Math.cos(rad)} 
              y2=${250 + (outerPoint - len) * Math.sin(rad)} 
              stroke="rgba(255,255,255,0.12)" 
              strokeWidth=${isMain ? "2" : "1"} 
            />
          `
        })}

        ${segments.map((seg, i) => html`
          <text
            key=${i}
            fill=${config.text}
            fontSize=${config.fontSize}
            fontWeight="900"
            className="select-none pointer-events-none font-orbitron"
            style=${{ 
              letterSpacing: '0.12em',
              textShadow: '0 1px 3px rgba(0,0,0,0.6)'
            }}
          >
            <textPath 
              href=${`#${seg.id}`} 
              startOffset="50%" 
              textAnchor="middle"
              dominantBaseline="middle"
            >
              ${seg.text}
            </textPath>
          </text>
        `)}
      </g>
    </g>
  `;
};

