      <circle cx="250" cy="250" r=${radius + thickness/2} fill="none" stroke="rgba(255,255,255,0.05)" strokeWidth="1" />
      <circle cx="250" cy="250" r=${radius - thickness/2} fill="none" stroke="rgba(255,255,255,0.05)" strokeWidth="1" />

